import '../../src/ignore-warnings';

import { DefaultTheme, ThemeProvider as NavigationThemeProvider } from '@react-navigation/native';
import { Stack } from 'expo-router';
// app/(app)/_layout.tsx - Optimized Premium Layout
import React from 'react';
import { SafeAreaProvider } from 'react-native-safe-area-context';

import { AppProvider } from '../../src/providers/AppProvider';
import { DesignSystem } from '../../src/theme/DesignSystem';

// ContextNavigator primary color hatası için özel navigasyon teması
const customTheme = {
  ...DefaultTheme,
  colors: {
    ...DefaultTheme.colors,
    primary: DesignSystem.colors.sage[600],
    background: DesignSystem.colors.surface.primary,
    card: DesignSystem.colors.surface.secondary,
    text: DesignSystem.colors.text.primary,
    border: DesignSystem.colors.border.primary,
    notification: DesignSystem.colors.coral[500],
  },
};

export default function AppLayout() {
  return (
    <SafeAreaProvider>
      <AppProvider>
        <NavigationThemeProvider value={customTheme}>
          <Stack screenOptions={{ headerShown: false }} />
        </NavigationThemeProvider>
      </AppProvider>
    </SafeAreaProvider>
  );
}

// AYNA Mirror Main Screen - Integrated with expo-router
import { useTheme } from '@shopify/restyle';
import { Redirect, useLocalSearchParams } from 'expo-router';
import React, { useEffect } from 'react';
import { StyleSheet, View } from 'react-native';

import { useAuth } from '@/context/AuthContext';
import { AynaMirrorScreen } from '@/screens/AynaMirrorScreen';
import deepLinkService, {
  processDeepLinkParams as _processDeepLinkParams,
} from '@/services/deepLinkService';
import { DesignSystemType } from '@/theme/DesignSystem';
import { logger } from '@/utils/logger';

// Safe resolver for backward compatibility (default or named export)
const processDeepLinkParams =
  (deepLinkService && deepLinkService.processDeepLinkParams) ||
  _processDeepLinkParams ||
  ((p: unknown) => p ?? {});

export default function AynaMirrorPage() {
  const { user, session, loading } = useAuth();
  const theme = useTheme<DesignSystemType>();
  const { colors } = theme;

  const params = useLocalSearchParams();

  useEffect(() => {
    try {
      if (params && Object.keys(params).length > 0) {
        deepLinkService.processDeepLinkParams({
          feedback: typeof params.feedback === 'string' ? params.feedback : undefined,
          outfit: typeof params.outfit === 'string' ? params.outfit : undefined,
          item: typeof params.item === 'string' ? params.item : undefined,
        });
      }
    } catch (err) {
      logger.error('deep link params processing error', { err, context: 'AynaMirrorPage' });
    }
  }, [params]);

  // Redirect to auth if not authenticated
  if (loading) {
    return (
      <View style={[styles.container, { backgroundColor: colors.background.primary }]}>
        {/* Loading handled by AynaMirrorScreen */}
      </View>
    );
  }

  if (!session || !user) {
    return <Redirect href="/auth/sign-in" />;
  }

  return (
    <View style={[styles.container, { backgroundColor: colors.background.primary }]}>
      <AynaMirrorScreen userId={user.id} />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
  },
});

// Discover Screen - The New Paradigm for Interaction
// Complete Discovery Engine with Tinder-style swipe mechanism

import React, { useState } from 'react';
import { Alert, StatusBar, StyleSheet, Text, View } from 'react-native';
import { useSafeAreaInsets } from 'react-native-safe-area-context';

import DiscoveryEngine from '@/components/discovery/DiscoveryEngine';
import { supabase } from '@/config/supabaseClient';
import { logger } from '@/lib/logger';
import { DesignSystem } from '@/theme/DesignSystem';

interface ProductItem {
  id: string;
  title: string;
  brand: string;
  price: number;
  originalPrice?: number;
  image: string;
  boutique: string;
  category: string;
  colors: string[];
  style: string[];
}

export default function DiscoverScreen() {
  const insets = useSafeAreaInsets();
  const [_likedItems, setLikedItems] = useState<ProductItem[]>([]);
  const [_dislikedItems, setDislikedItems] = useState<ProductItem[]>([]);
  const [favoriteBoutiques, setFavoriteBoutiques] = useState<string[]>([]);

  // Sample discovery items - discounted products from various boutiques
  const discoveryItems: ProductItem[] = [
    {
      id: 'd1',
      title: 'Designer Silk Scarf with Abstract Print',
      brand: 'Luxury Atelier',
      price: 89,
      originalPrice: 145,
      image:
        'https://images.unsplash.com/photo-1515886657613-9f3515b0c78f?w=400&h=600&fit=crop&q=80',
      boutique: 'Madison Avenue Boutique',
      category: 'accessories',
      colors: ['blue', 'gold', 'white'],
      style: ['elegant', 'sophisticated'],
    },
    {
      id: 'd2',
      title: 'Tailored Wool Coat with Belt',
      brand: 'Urban Elegance',
      price: 245,
      originalPrice: 380,
      image:
        'https://images.unsplash.com/photo-1581044777550-4cfa6ce670c0?w=400&h=600&fit=crop&q=80',
      boutique: 'Executive Style Co.',
      category: 'outerwear',
      colors: ['navy', 'black'],
      style: ['professional', 'classic'],
    },
    {
      id: 'd3',
      title: 'Flowing Midi Dress with Floral Pattern',
      brand: 'Bohemian Dreams',
      price: 125,
      originalPrice: 195,
      image:
        'https://images.unsplash.com/photo-1594619336195-39a8f2712533?w=400&h=600&fit=crop&q=80',
      boutique: 'Boutique Bella',
      category: 'dresses',
      colors: ['pink', 'green', 'cream'],
      style: ['romantic', 'feminine'],
    },
    {
      id: 'd4',
      title: 'Leather Ankle Boots with Block Heel',
      brand: 'Artisan Footwear',
      price: 165,
      originalPrice: 220,
      image:
        'https://images.unsplash.com/photo-1469334031218-e382a71b716b?w=400&h=600&fit=crop&q=80',
      boutique: 'Sole Society',
      category: 'shoes',
      colors: ['brown', 'black'],
      style: ['casual', 'versatile'],
    },
    {
      id: 'd5',
      title: 'Cashmere Blend Sweater',
      brand: 'Cozy Luxe',
      price: 98,
      originalPrice: 140,
      image:
        'https://images.unsplash.com/photo-1515886657613-9f3515b0c78f?w=400&h=600&fit=crop&q=80',
      boutique: 'Madison Avenue Boutique',
      category: 'knitwear',
      colors: ['cream', 'beige'],
      style: ['comfortable', 'elegant'],
    },
    {
      id: 'd6',
      title: 'Statement Gold Necklace',
      brand: 'Jewelry Atelier',
      price: 75,
      originalPrice: 120,
      image:
        'https://images.unsplash.com/photo-1581044777550-4cfa6ce670c0?w=400&h=600&fit=crop&q=80',
      boutique: 'Gilded Gallery',
      category: 'jewelry',
      colors: ['gold'],
      style: ['bold', 'statement'],
    },
    {
      id: 'd7',
      title: 'High-Waisted Trousers',
      brand: 'Modern Minimalist',
      price: 110,
      originalPrice: 160,
      image:
        'https://images.unsplash.com/photo-1594619336195-39a8f2712533?w=400&h=600&fit=crop&q=80',
      boutique: 'Executive Style Co.',
      category: 'bottoms',
      colors: ['black', 'navy'],
      style: ['professional', 'modern'],
    },
    {
      id: 'd8',
      title: 'Vintage-Inspired Handbag',
      brand: 'Heritage Collection',
      price: 185,
      originalPrice: 275,
      image:
        'https://images.unsplash.com/photo-1469334031218-e382a71b716b?w=400&h=600&fit=crop&q=80',
      boutique: 'Boutique Bella',
      category: 'bags',
      colors: ['burgundy', 'brown'],
      style: ['vintage', 'classic'],
    },
  ];

  // Handle like action - learn user preferences
  const handleLike = (item: ProductItem) => {
    // Add to liked items
    setLikedItems((prev) => [item, ...prev]);

    // Update machine learning algorithm
    updateUserPreferences(item, 'like').catch((err) => {
      logger.error('updateUserPreferences like failed', { err, context: 'DiscoverScreen' });
    });

    // Check if we should show similar items
    if (Math.random() > 0.7) {
      // 30% chance to show similar items
      showSimilarItems(item);
    }
  };

  const handleDislike = (item: ProductItem) => {
    // Add to disliked items
    setDislikedItems((prev) => [item, ...prev]);

    // Update machine learning algorithm
    updateUserPreferences(item, 'dislike').catch((err) => {
      logger.error('updateUserPreferences dislike failed', { err, context: 'DiscoverScreen' });
    });
  };

  const handleBoutiqueFavorite = (boutique: string) => {
    // Add to favorite boutiques
    if (!favoriteBoutiques.includes(boutique)) {
      setFavoriteBoutiques((prev) => [...prev, boutique]);

      // Show notification
      Alert.alert(
        'Boutique Favorited',
        `You'll now receive notifications when ${boutique} adds new items!`,
        [{ text: 'Great!' }],
      );
    }
  };

  // Helper functions
  const updateUserPreferences = async (item: ProductItem, action: 'like' | 'dislike') => {
    try {
      const {
        data: { user },
      } = await supabase.auth.getUser();
      if (!user) {
        return;
      }

      // Log the interaction in the database
      const { error } = await supabase.from('product_interactions').insert({
        user_id: user.id,
        product_id: item.id,
        interaction_type: action,
        interaction_date: new Date().toISOString(),
        product_category: item.category,
        product_colors: item.colors,
        product_style: item.style,
      });

      if (error) {
        throw error;
      }

      logger.info(`User preference updated: ${action} for ${item.title}`);
    } catch (error) {
      logger.error('Error updating user preferences', { error, context: 'DiscoverScreen' });
    }
  };

  const showSimilarItems = (item: ProductItem) => {
    // In a real implementation, this would fetch similar items from the API
    Alert.alert(
      'Similar Items',
      `We found 5 items similar to "${item.title}" that you might like!`,
      [
        { text: 'Show Me', onPress: () => {} },
        { text: 'Later', style: 'cancel' },
      ],
    );
  };

  return (
    <View style={styles.container}>
      <StatusBar barStyle="dark-content" backgroundColor={DesignSystem.colors.background.primary} />

      {/* Header */}
      <View style={[styles.header, { paddingTop: insets.top + 20 }]}>
        <Text style={styles.headerTitle}>Discover</Text>
        <Text style={styles.headerSubtitle}>Swipe right to love, left to pass</Text>
      </View>

      {/* Discovery Engine */}
      <DiscoveryEngine
        items={discoveryItems}
        onLike={handleLike}
        onDislike={handleDislike}
        onBoutiqueFavorite={handleBoutiqueFavorite}
        style={styles.discoveryEngine}
      />
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    backgroundColor: DesignSystem.colors.background.primary,
    flex: 1,
  },
  discoveryEngine: {
    flex: 1,
  },
  header: {
    alignItems: 'center',
    marginBottom: DesignSystem.spacing.lg,
    paddingHorizontal: DesignSystem.spacing.xl,
  },
  headerSubtitle: {
    ...DesignSystem.typography.body1,
    color: DesignSystem.colors.text.secondary,
    textAlign: 'center',
  },
  headerTitle: {
    ...DesignSystem.typography.scale.hero,
    color: DesignSystem.colors.text.primary,
    fontSize: 32,
    marginBottom: 8,
  },
});

import { Ionicons } from '@expo/vector-icons';
import { BlurView } from 'expo-blur';
import { LinearGradient } from 'expo-linear-gradient';
import { useRouter } from 'expo-router';
import React, { memo, useCallback, useMemo } from 'react';
import { Dimensions, ScrollView, StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import Animated, { FadeInDown, FadeInUp } from 'react-native-reanimated';
import { useSafeAreaInsets } from 'react-native-safe-area-context';

import { AynamodaColors } from '@/theme/AynamodaColors';
import { DesignSystem } from '@/theme/DesignSystem';
import { LazyComponents } from '@/utils/dynamicImports';
const { PremiumBrandShowcase, ProductCardShowcase } = LazyComponents;
import { startupPerformanceService } from '@/services/startupPerformanceService';

import {
  getResponsivePadding,
  isTablet,
  responsiveFontSize,
  responsiveSpacing,
} from '../../src/utils/responsiveUtils';

const { width: screenWidth } = Dimensions.get('window');

const HomeScreen: React.FC = () => {
  const insets = useSafeAreaInsets();
  const router = useRouter();

  // Mark startup complete when home screen is fully loaded
  React.useEffect(() => {
    const timer = setTimeout(() => {
      startupPerformanceService.markStartupComplete();
    }, 50); // Minimal delay for faster startup

    return () => clearTimeout(timer);
  }, []);

  // Memoized product data for better performance
  const FEATURED_PRODUCTS = useMemo(
    () => [
      {
        id: 1,
        name: 'ARUOM',
        subtitle: 'Elegant Dress',
        price: '$89',
        image: 'https://images.unsplash.com/photo-1594633312681-425c7b97ccd1?w=300&h=400&fit=crop',
        color: AynamodaColors.primary[400],
        category: 'POPULER',
      },
      {
        id: 2,
        name: 'FIRED',
        subtitle: 'Casual Coat',
        price: '$120',
        image: 'https://images.unsplash.com/photo-1551698618-1dfe5d97d256?w=300&h=400&fit=crop',
        color: AynamodaColors.neutral[700],
        category: 'POPULER',
      },
      {
        id: 3,
        name: 'AYNAMODA',
        subtitle: 'Premium Set',
        price: '$159',
        image: 'https://images.unsplash.com/photo-1515372039744-b8f02a3ae446?w=300&h=400&fit=crop',
        color: AynamodaColors.secondary[400],
        category: 'POPULER',
      },
      {
        id: 4,
        name: 'MORS',
        subtitle: 'Stylish Outfit',
        price: '$95',
        image: 'https://images.unsplash.com/photo-1469334031218-e382a71b716b?w=300&h=400&fit=crop',
        color: AynamodaColors.primary[600],
        category: 'POPULER',
      },
    ],
    [],
  );

  // Optimized product card renderer
  const renderProductCard = useCallback(
    (product: (typeof FEATURED_PRODUCTS)[0], index: number): React.ReactElement => {
      const cardRotation = index === 0 ? 0 : index === 1 ? -3 : index === 2 ? 2 : -1;
      const cardScale = 1 - index * 0.05;
      const cardTranslateY = index * 15;
      const cardOpacity = 1 - index * 0.1;

      return (
        <Animated.View
          key={product.id}
          entering={FadeInUp.delay(index * 150).springify()}
          style={[
            styles.productCard,
            {
              transform: [
                { rotate: `${cardRotation}deg` },
                { scale: cardScale },
                { translateY: cardTranslateY },
              ],
              opacity: cardOpacity,
              zIndex: FEATURED_PRODUCTS.length - index,
            },
          ]}
        >
          <TouchableOpacity
            activeOpacity={0.9}
            onPress={() => router.push('/(app)/ayna-mirror')}
            style={styles.cardTouchable}
          >
            <BlurView intensity={20} style={styles.cardBlur}>
              <LinearGradient
                colors={[AynamodaColors.background.elevated, AynamodaColors.background.secondary]}
                style={styles.cardGradient}
              >
                {/* Category Badge */}
                <View style={styles.categoryBadge}>
                  <Text style={styles.categoryText}>{product.category}</Text>
                </View>

                {/* Product Image */}
                <View style={styles.imageContainer}>
                  <View style={[styles.imagePlaceholder, { backgroundColor: product.color }]}>
                    <Ionicons name="shirt-outline" size={40} color={AynamodaColors.text.inverse} />
                  </View>
                </View>

                {/* Product Info */}
                <View style={styles.productInfo}>
                  <Text style={styles.productName}>{product.name}</Text>
                  <Text style={styles.productSubtitle}>{product.subtitle}</Text>

                  {/* Price */}
                  <View style={styles.priceContainer}>
                    <Text style={styles.price}>{product.price}</Text>
                    <TouchableOpacity style={styles.favoriteButton}>
                      <Ionicons name="heart-outline" size={16} color={AynamodaColors.text.accent} />
                    </TouchableOpacity>
                  </View>
                </View>
              </LinearGradient>
            </BlurView>
          </TouchableOpacity>
        </Animated.View>
      );
    },
    [router],
  );

  return (
    <View style={[styles.container, { paddingTop: insets.top }]}>
      <LinearGradient colors={DesignSystem.colors.gradientNeutral} style={styles.gradient}>
        <ScrollView
          contentContainerStyle={styles.scrollContent}
          showsVerticalScrollIndicator={false}
        >
          {/* Header */}
          <Animated.View entering={FadeInDown.springify()} style={styles.header}>
            <View style={styles.headerTop}>
              <TouchableOpacity style={styles.menuButton}>
                <Ionicons name="menu" size={24} color={AynamodaColors.text.primary} />
              </TouchableOpacity>
              <TouchableOpacity style={styles.searchButton}>
                <Ionicons name="search" size={24} color={AynamodaColors.text.primary} />
              </TouchableOpacity>
            </View>
          </Animated.View>

          {/* Central Diamond Logo */}
          <Animated.View
            entering={FadeInUp.delay(200).springify()}
            style={styles.centralDiamondContainer}
          >
            <LinearGradient colors={AynamodaColors.gradients.primary} style={styles.centralDiamond}>
              <Ionicons name="diamond" size={40} color={AynamodaColors.text.inverse} />
            </LinearGradient>
            <Text style={styles.logoText}>AYNAMODA</Text>
          </Animated.View>

          {/* Stacked Product Cards */}
          <View style={styles.stackedCardsContainer}>
            {FEATURED_PRODUCTS.map((product, index) => renderProductCard(product, index))}
          </View>

          {/* Premium Brand Showcase */}
          <Animated.View entering={FadeInUp.delay(300).springify()} style={styles.premiumSection}>
            <Text style={styles.sectionTitle}>Premium Koleksiyonlar</Text>
            <PremiumBrandShowcase />
          </Animated.View>

          {/* Product Showcase */}
          <Animated.View
            entering={FadeInUp.delay(400).springify()}
            style={styles.productShowcaseSection}
          >
            <ProductCardShowcase
              title="Öne Çıkan Ürünler"
              subtitle="En beğenilen parçalar"
              variant="standard"
              size="medium"
              layout="grid"
              numColumns={2}
              showFilters={false}
              showSort={false}
            />
          </Animated.View>

          {/* Quick Actions */}
          <Animated.View entering={FadeInUp.delay(400).springify()} style={styles.quickActions}>
            <TouchableOpacity
              style={styles.actionButton}
              onPress={() => router.push('/(app)/ayna-mirror')}
            >
              <LinearGradient
                colors={AynamodaColors.gradients.primary}
                style={styles.actionGradient}
              >
                <Ionicons name="camera" size={24} color={AynamodaColors.text.inverse} />
                <Text style={styles.actionText}>AI Ayna</Text>
              </LinearGradient>
            </TouchableOpacity>

            <TouchableOpacity
              style={styles.actionButton}
              onPress={() => router.push('/(app)/wardrobe')}
            >
              <LinearGradient
                colors={AynamodaColors.gradients.secondary}
                style={styles.actionGradient}
              >
                <Ionicons name="shirt" size={24} color={AynamodaColors.text.primary} />
                <Text style={[styles.actionText, { color: AynamodaColors.text.primary }]}>
                  Gardırop
                </Text>
              </LinearGradient>
            </TouchableOpacity>
          </Animated.View>
        </ScrollView>
      </LinearGradient>
    </View>
  );
};

const createResponsiveStyles = () => {
  const padding = getResponsivePadding();
  const isTabletDevice = isTablet();

  return StyleSheet.create({
    container: {
      backgroundColor: AynamodaColors.background.primary,
