    // Header Styles
    header: {
      paddingBottom: DesignSystem.spacing.lg,
      paddingTop: DesignSystem.spacing.lg,
    },
    headerTop: {
      flexDirection: 'row',
      justifyContent: 'space-between',
      marginBottom: DesignSystem.spacing.md,
      width: '100%',
    },
    menuButton: {
