      elevation: 3,
      padding: DesignSystem.spacing.md,
      shadowColor: AynamodaColors.shadow.light,
      shadowOffset: { width: 0, height: 2 },
      shadowOpacity: 0.1,
      shadowRadius: 8,
    },
    searchButton: {
      backgroundColor: AynamodaColors.background.elevated,
      borderRadius: DesignSystem.borderRadius.md,
      elevation: 3,
      padding: DesignSystem.spacing.md,
      shadowColor: AynamodaColors.shadow.light,
      shadowOffset: { width: 0, height: 2 },
      shadowOpacity: 0.1,
      shadowRadius: 8,
    },
    // Central Diamond Logo
    centralDiamondContainer: {
      alignItems: 'center',
      marginVertical: DesignSystem.spacing.xxl,
      zIndex: 10,
    },
    centralDiamond: {
      alignItems: 'center',
      borderRadius: 50,
      elevation: 12,
      height: 100,
      justifyContent: 'center',
      marginBottom: DesignSystem.spacing.md,
      shadowColor: AynamodaColors.shadow.colored,
      shadowOffset: { width: 0, height: 8 },
      shadowOpacity: 0.3,
      shadowRadius: 20,
      width: 100,
    },
    logoText: {
      color: AynamodaColors.text.primary,
      fontSize: responsiveFontSize(28),
      fontWeight: '300',
      letterSpacing: isTabletDevice ? 6 : 4,
      textAlign: 'center',
    },

    // Stacked Cards Container
    stackedCardsContainer: {
      alignItems: 'center',
      height: 380,
      justifyContent: 'center',
      marginBottom: 40,
      paddingHorizontal: 20,
      position: 'relative',
    },
    productCard: {
