      width: screenWidth * 0.75,
    },
    cardTouchable: {
      borderRadius: 20,
      overflow: 'hidden',
    },
    cardBlur: {
      borderRadius: 20,
      overflow: 'hidden',
    },
    cardGradient: {
      borderColor: AynamodaColors.border.primary,
      borderRadius: 24,
