      elevation: 10,
      minHeight: 320,
      padding: 20,
      shadowColor: AynamodaColors.shadow.medium,
      shadowOffset: { width: 0, height: 12 },
      shadowOpacity: 0.2,
      shadowRadius: 20,
    },

    // Category Badge
    categoryBadge: {
      backgroundColor: AynamodaColors.primary[500],
      borderRadius: 8,
      left: 12,
      paddingHorizontal: 8,
      paddingVertical: 4,
      position: 'absolute',
      top: 12,
      zIndex: 1,
    },
    categoryText: {
      color: AynamodaColors.text.inverse,
      fontSize: 10,
      fontWeight: '600',
      letterSpacing: 0.5,
    },

    // Product Image
    imageContainer: {
      alignItems: 'center',
      marginBottom: 20,
      marginTop: 30,
    },
    imagePlaceholder: {
      alignItems: 'center',
      borderRadius: 16,
      elevation: 6,
      height: 140,
      justifyContent: 'center',
      shadowColor: AynamodaColors.shadow.light,
      shadowOffset: { width: 0, height: 6 },
      shadowOpacity: 0.15,
      shadowRadius: 12,
      width: 120,
    },

    // Product Info
    productInfo: {
      flex: 1,
      justifyContent: 'flex-end',
    },
    productName: {
      color: AynamodaColors.text.primary,
      fontSize: 22,
      fontWeight: '700',
      letterSpacing: 1,
      marginBottom: 6,
      textAlign: 'center',
    },
    productSubtitle: {
      color: AynamodaColors.text.tertiary,
      fontSize: 14,
      marginBottom: 16,
      textAlign: 'center',
    },
    priceContainer: {
      alignItems: 'center',
      flexDirection: 'row',
      justifyContent: 'space-between',
    },
    price: {
      color: AynamodaColors.text.accent,
      fontSize: 20,
      fontWeight: '800',
