type SettingsRowProps = {
  icon: keyof typeof Ionicons.glyphMap;
  label: string;
  description?: string;
  type: 'toggle' | 'navigate';
  value?: boolean;
  onValueChange?: (value: boolean) => void;
  onPress?: () => void;
  isLast?: boolean;
};

const SettingsRow = ({
  icon,
  label,
  description,
  type,
  value,
  onValueChange,
  onPress,
  isLast,
}: SettingsRowProps) => (
  <View>
    <TouchableOpacity
      style={styles.menuItem}
      onPress={onPress}
      disabled={type === 'toggle'}
      accessibilityRole={type === 'toggle' ? 'switch' : 'button'}
      accessibilityLabel={label}
      accessibilityHint={
        description || (type === 'navigate' ? `Navigate to ${label}` : `Toggle ${label}`)
      }
      accessibilityState={type === 'toggle' ? { checked: value } : undefined}
    >
      <View style={styles.menuIconContainer}>
        <Ionicons name={icon} size={22} color={DesignSystem.colors.text.primary} />
      </View>
      <View style={styles.menuItemTextContainer}>
        <Text style={styles.menuItemText}>{label}</Text>
        {description && <Text style={styles.menuItemDescription}>{description}</Text>}
      </View>
      {type === 'navigate' && (
        <Ionicons name="chevron-forward" size={22} color={DesignSystem.colors.neutral[300]} />
      )}
      {type === 'toggle' && (
        <Switch
          value={value}
          onValueChange={onValueChange}
          trackColor={{
            false: DesignSystem.colors.neutral[200],
            true: DesignSystem.colors.accent.gold,
          }}
          thumbColor={
            value ? DesignSystem.colors.text.primary : DesignSystem.colors.background.primary
          }
          accessibilityLabel={`${label} toggle`}
          accessibilityHint={`Double tap to ${value ? 'disable' : 'enable'} ${label}`}
        />
      )}
    </TouchableOpacity>
    {!isLast && <View style={styles.separator} />}
  </View>
);

