import { Inter_400Regular, Inter_500Medium, useFonts } from '@expo-google-fonts/inter';
import { ThemeProvider } from '@shopify/restyle';
import { Slot } from 'expo-router';
import * as SplashScreen from 'expo-splash-screen';
import { StatusBar } from 'expo-status-bar';
import React, { useEffect, useState } from 'react';
import { GestureHandlerRootView } from 'react-native-gesture-handler';

import { startupPerformanceService } from '@/services/startupPerformanceService';
import { DesignSystem } from '@/theme/DesignSystem';

SplashScreen.preventAutoHideAsync();

export default function RootLayout() {
  // Mark app start for performance monitoring
  React.useEffect(() => {
    startupPerformanceService.markAppStart();
  }, []);

  // Load critical fonts first for faster startup
  const [criticalFontsLoaded, criticalFontError] = useFonts({
    Inter_400Regular, // Most used font
    Inter_500Medium, // Secondary critical font
  });

  // Load non-critical fonts after critical ones
  const [allFontsLoaded, setAllFontsLoaded] = useState(false);

  useEffect(() => {
    if (criticalFontsLoaded || criticalFontError) {
      // Mark critical fonts loaded for performance tracking
      startupPerformanceService.markCriticalFontsLoaded();

      // Hide splash screen as soon as critical fonts are loaded
      SplashScreen.hideAsync();

      // Load remaining fonts asynchronously
      const loadRemainingFonts = async () => {
        try {
          // Dynamically import and load non-critical fonts
          const { useFonts: useRemainingFonts } = await import('@expo-google-fonts/inter');
          const { useFonts: usePlayfairFonts } = await import(
            '@expo-google-fonts/playfair-display'
          );

          // Load remaining fonts in background
          setTimeout(() => {
            setAllFontsLoaded(true);
          }, 100);
        } catch (error) {
          console.warn('Failed to load non-critical fonts:', error);
          setAllFontsLoaded(true);
        }
      };

      loadRemainingFonts();
    }
  }, [criticalFontsLoaded, criticalFontError]);

  if (!criticalFontsLoaded && !criticalFontError) {
    return null;
  }

  // Mark first screen render when layout is ready
  React.useEffect(() => {
    if (criticalFontsLoaded || criticalFontError) {
      // Use a small delay to ensure the screen is actually rendered
      const timer = setTimeout(() => {
        startupPerformanceService.markFirstScreenRender();
      }, 100);

      return () => clearTimeout(timer);
    }
  }, [criticalFontsLoaded, criticalFontError]);

  return (
    <GestureHandlerRootView style={{ flex: 1 }}>
      <ThemeProvider theme={DesignSystem}>
        <StatusBar style="dark" />
        <Slot />
      </ThemeProvider>
    </GestureHandlerRootView>
  );
}

import { Ionicons } from '@expo/vector-icons';
import { BlurView } from 'expo-blur';
import * as Haptics from 'expo-haptics';
import { router } from 'expo-router';
import React, { useState } from 'react';
import {
  ActivityIndicator,
  Alert,
  Dimensions,
  KeyboardAvoidingView,
  Platform,
  SafeAreaView,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from 'react-native';
import { useSafeAreaInsets } from 'react-native-safe-area-context';

import { useAuth } from '@/context/AuthContext';

const { width, height } = Dimensions.get('window');

const SignUpScreen = () => {
  const [firstName, setFirstName] = useState('');
  const [lastName, setLastName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');

  const [showPassword, setShowPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);

  const [isLoading, setIsLoading] = useState(false);
  const [isGoogleLoading, setGoogleLoading] = useState(false);
  const [isAppleLoading, setAppleLoading] = useState(false);

  const { signUp, signInWithGoogle, signInWithApple } = useAuth();

  const handleSignUp = async () => {
    if (!firstName || !lastName || !email || !password || !confirmPassword) {
      void Haptics.notificationAsync(Haptics.NotificationFeedbackType.Warning);
      Alert.alert('Eksik Bilgi', 'Lütfen tüm alanları doldurun.');
      return;
    }
    if (password !== confirmPassword) {
      void Haptics.notificationAsync(Haptics.NotificationFeedbackType.Warning);
      Alert.alert('Şifreler Eşleşmiyor', 'Girdiğiniz şifreler birbiriyle uyuşmuyor.');
      return;
    }

    setIsLoading(true);
    void Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Medium);

    try {
      await signUp(email, password, firstName, lastName);
      void Haptics.notificationAsync(Haptics.NotificationFeedbackType.Success);
      Alert.alert('Hoş Geldiniz!', 'Hesabınız başarıyla oluşturuldu. Lütfen giriş yapın.');
      router.replace('/auth/sign-in');
    } catch (error: unknown) {
      void Haptics.notificationAsync(Haptics.NotificationFeedbackType.Error);
      Alert.alert('Kayıt Başarısız', error instanceof Error ? error.message : 'Bir hata oluştu.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleGoogleSignUp = async () => {
    setGoogleLoading(true);
    try {
      // Assuming signInWithGoogle also handles sign-up flows
      await signInWithGoogle();
    } catch (error: unknown) {
      Alert.alert(
        'Google ile Kayıt Başarısız',
        error instanceof Error ? error.message : 'Bir hata oluştu.',
      );
    } finally {
      setGoogleLoading(false);
    }
  };

  const handleAppleSignUp = async () => {
    setAppleLoading(true);
    try {
      // Assuming signInWithApple also handles sign-up flows
      await signInWithApple();
    } catch (error: unknown) {
      Alert.alert(
        'Apple ile Kayıt Başarısız',
        error instanceof Error ? error.message : 'Bir hata oluştu.',
      );
    } finally {
      setAppleLoading(false);
    }
  };

  const insets = useSafeAreaInsets();

  return (
    <View style={styles.container}>
      {/* Background */}
      <View style={[StyleSheet.absoluteFill, styles.background]}>
        <View style={styles.circle1} />
        <View style={styles.circle2} />
        <View style={styles.circle3} />
      </View>

      <SafeAreaView style={styles.safeArea}>
        <KeyboardAvoidingView
          style={{ flex: 1 }}
          behavior={Platform.OS === 'ios' ? 'padding' : undefined}
          keyboardVerticalOffset={0}
        >
          <ScrollView
            contentContainerStyle={[
              styles.contentContainer,
              { paddingBottom: insets.bottom > 0 ? insets.bottom : 24 },
            ]}
            keyboardShouldPersistTaps="handled"
            showsVerticalScrollIndicator={false}
          >
            {/* Header */}
            <View style={styles.header}>
              <Text style={styles.appName}>AYNAMODA</Text>
              <Text style={styles.welcomeText}>Hesabınızı Oluşturun</Text>
              <Text style={styles.tagline}>Ayrıcalıklı moda dünyasına katılın.</Text>
            </View>

            {/* Form */}
            <BlurView intensity={70} tint="light" style={styles.formContainer}>
              <View style={styles.inputContainer}>
                <Ionicons
                  name="person-outline"
                  size={22}
                  color={DesignSystem.colors.text.tertiary}
                  style={styles.inputIcon}
                />
                <TextInput
                  style={styles.input}
                  placeholder="Ad"
                  placeholderTextColor={DesignSystem.colors.text.tertiary}
                  value={firstName}
                  onChangeText={setFirstName}
                  autoCapitalize="words"
                  returnKeyType="next"
                />
              </View>

              <View style={styles.inputContainer}>
                <Ionicons
                  name="person-outline"
                  size={22}
                  color="#6E7191"
                  style={styles.inputIcon}
                />
                <TextInput
                  style={styles.input}
                  placeholder="Soyad"
                  placeholderTextColor="#6E7191"
                  value={lastName}
                  onChangeText={setLastName}
                  autoCapitalize="words"
                  returnKeyType="next"
                />
              </View>

              <View style={styles.inputContainer}>
                <Ionicons
                  name="mail-outline"
                  size={22}
                  color={DesignSystem.colors.text.tertiary}
                  style={styles.inputIcon}
                />
                <TextInput
                  style={styles.input}
                  placeholder="E-posta"
                  placeholderTextColor="#6E7191"
                  value={email}
                  onChangeText={setEmail}
                  keyboardType="email-address"
                  autoCapitalize="none"
                  returnKeyType="next"
                />
              </View>

              <View style={styles.inputContainer}>
                <Ionicons
                  name="lock-closed-outline"
                  size={22}
                  color="#6E7191"
                  style={styles.inputIcon}
                />
                <TextInput
                  style={styles.input}
                  placeholder="Şifre"
                  placeholderTextColor="#6E7191"
                  value={password}
                  onChangeText={setPassword}
                  secureTextEntry={!showPassword}
                  returnKeyType="next"
                />
                <TouchableOpacity
                  style={styles.passwordToggle}
                  onPress={() => setShowPassword(!showPassword)}
                >
                  <Ionicons
                    name={showPassword ? 'eye-off-outline' : 'eye-outline'}
                    size={22}
                    color={DesignSystem.colors.text.tertiary}
                  />
                </TouchableOpacity>
              </View>

              <View style={styles.inputContainer}>
                <Ionicons
                  name="lock-closed-outline"
                  size={22}
                  color="#6E7191"
                  style={styles.inputIcon}
                />
                <TextInput
                  style={styles.input}
                  placeholder="Şifreyi Onayla"
                  placeholderTextColor="#6E7191"
                  value={confirmPassword}
                  onChangeText={setConfirmPassword}
                  secureTextEntry={!showConfirmPassword}
                  returnKeyType="done"
                  onSubmitEditing={() => void handleSignUp()}
                />
                <TouchableOpacity
                  style={styles.passwordToggle}
                  onPress={() => setShowConfirmPassword(!showConfirmPassword)}
                >
                  <Ionicons
                    name={showConfirmPassword ? 'eye-off-outline' : 'eye-outline'}
                    size={22}
                    color="#6E7191"
                  />
                </TouchableOpacity>
              </View>

              <TouchableOpacity
                style={styles.signUpButton}
                onPress={() => void handleSignUp()}
                disabled={isLoading}
              >
                {isLoading ? (
                  <ActivityIndicator color={DesignSystem.colors.text.inverse} />
                ) : (
                  <Text style={styles.signUpButtonText}>KAYIT OL</Text>
                )}
              </TouchableOpacity>
            </BlurView>

            {/* Social Logins */}
            <View style={styles.socialLoginContainer}>
              <Text style={styles.socialLoginText}>veya şununla devam et</Text>
              <View style={styles.socialButtonsContainer}>
                <TouchableOpacity
                  style={styles.socialButton}
                  onPress={() => void handleGoogleSignUp()}
                  disabled={isGoogleLoading || isAppleLoading}
                >
                  {isGoogleLoading ? (
                    <ActivityIndicator color={DesignSystem.colors.error[500]} />
                  ) : (
                    <Ionicons name="logo-google" size={24} color={DesignSystem.colors.error[500]} />
                  )}
                </TouchableOpacity>
                <TouchableOpacity
                  style={styles.socialButton}
                  onPress={() => void handleAppleSignUp()}
                  disabled={isGoogleLoading || isAppleLoading}
                >
                  {isAppleLoading ? (
                    <ActivityIndicator color={DesignSystem.colors.text.primary} />
                  ) : (
                    <Ionicons
                      name="logo-apple"
                      size={28}
                      color={DesignSystem.colors.text.primary}
                    />
                  )}
                </TouchableOpacity>
              </View>
            </View>

            {/* Sign In Link */}
            <View style={styles.signInContainer}>
              <Text style={styles.signInText}>Zaten hesabın var mı? </Text>
              <TouchableOpacity
                onPress={() => router.replace('/auth/sign-in')}
                accessibilityRole="button"
                accessibilityLabel="Go to sign in"
                accessibilityHint="Navigate to the sign in screen"
              >
                <Text style={styles.signInLink}>Giriş Yap</Text>
              </TouchableOpacity>
            </View>
          </ScrollView>
        </KeyboardAvoidingView>
      </SafeAreaView>
    </View>
  );
};

