import { Ionicons } from '@expo/vector-icons';
import React, { useEffect, useRef } from 'react';
import {
  Animated,
  Dimensions,
  Modal,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
} from 'react-native';

import { DesignSystem } from '@/theme/DesignSystem';

const { width: _screenWidth } = Dimensions.get('window');

interface Button {
  text: string;
  onPress?: () => void;
  style?: 'default' | 'cancel' | 'destructive' | 'primary';
  icon?: keyof typeof Ionicons.glyphMap;
}

interface CustomModalProps {
  visible: boolean;
  title: string;
  message: string;
  buttons: Button[];
  onClose: () => void;
  type?: 'default' | 'success' | 'warning' | 'error' | 'info';
}

export default function CustomModal({
  visible,
  title,
  message,
  buttons = [],
  onClose,
  type = 'default',
}: CustomModalProps) {
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const scaleAnim = useRef(new Animated.Value(0.8)).current;
  const slideAnim = useRef(new Animated.Value(50)).current;

  useEffect(() => {
    if (visible) {
      Animated.parallel([
        Animated.timing(fadeAnim, {
          toValue: 1,
          duration: 400,
          useNativeDriver: true,
        }),
        Animated.spring(scaleAnim, {
          toValue: 1,
          tension: 100,
          friction: 8,
          useNativeDriver: true,
        }),
        Animated.timing(slideAnim, {
          toValue: 0,
          duration: 400,
          useNativeDriver: true,
        }),
      ]).start();
    } else {
      Animated.parallel([
        Animated.timing(fadeAnim, {
          toValue: 0,
          duration: 250,
          useNativeDriver: true,
        }),
        Animated.timing(scaleAnim, {
          toValue: 0.8,
          duration: 250,
          useNativeDriver: true,
        }),
        Animated.timing(slideAnim, {
          toValue: 50,
          duration: 250,
          useNativeDriver: true,
        }),
      ]).start();
    }
  }, [visible]);

  const getTypeIcon = () => {
    switch (type) {
      case 'success':
        return 'checkmark-circle';
      case 'warning':
        return 'warning';
      case 'error':
        return 'close-circle';
      case 'info':
        return 'information-circle';
      default:
        return 'chatbubble-ellipses';
    }
  };

  const getTypeColor = () => {
    switch (type) {
      case 'success':
        return DesignSystem.colors.success[500];
      case 'warning':
        return DesignSystem.colors.warning[500];
      case 'error':
        return DesignSystem.colors.error[600];
      case 'info':
        return DesignSystem.colors.info[600];
      default:
        return DesignSystem.colors.sage[500];
    }
  };

  return (
    <Modal animationType="none" transparent={true} visible={visible} onRequestClose={onClose}>
      <View style={styles.modalOverlay}>
        <Animated.View
          style={[
            styles.modalContainer,
            {
              opacity: fadeAnim,
              transform: [{ scale: scaleAnim }, { translateY: slideAnim }],
            },
          ]}
        >
          {/* Header with Icon */}
          <View style={[styles.modalHeader, { borderBottomColor: getTypeColor() + '20' }]}>
            <View style={[styles.iconContainer, { backgroundColor: getTypeColor() + '15' }]}>
              <Ionicons name={getTypeIcon()} size={28} color={getTypeColor()} />
            </View>
            <Text style={styles.modalTitle}>{title}</Text>
          </View>

          {/* Body */}
          <View style={styles.modalBody}>
            <Text style={styles.modalMessage}>{message}</Text>
          </View>

          {/* Buttons */}
          <View style={styles.modalButtons}>
            {(buttons || []).length <= 2 ? (
              // Horizontal layout for 1-2 buttons
              <View style={styles.horizontalButtons}>
                {(buttons || []).map((button, index) => (
                  <TouchableOpacity
                    key={index}
                    style={[
                      styles.modernButton,
                      button.style === 'primary' && styles.primaryButton,
                      button.style === 'destructive' && styles.destructiveButton,
                      button.style === 'cancel' && styles.cancelButton,
                      styles.flexButton,
                      index > 0 && styles.buttonMarginLeft,
                    ]}
                    onPress={button.onPress || onClose}
                    activeOpacity={0.8}
                  >
                    {button.icon && (
                      <Ionicons
                        name={button.icon}
                        size={16}
                        color={
                          button.style === 'primary'
                            ? '#FFFFFF'
                            : button.style === 'destructive'
                              ? '#FFFFFF'
                              : '#B8918F'
                        }
                        style={styles.buttonIcon}
                      />
                    )}
                    <Text
                      style={[
                        styles.buttonText,
                        button.style === 'primary' && styles.primaryButtonText,
                        button.style === 'destructive' && styles.destructiveButtonText,
                        button.style === 'cancel' && styles.cancelButtonText,
                      ]}
                    >
                      {button.text}
                    </Text>
                  </TouchableOpacity>
                ))}
              </View>
            ) : (
              // Vertical layout for 3+ buttons
              <View style={styles.verticalButtons}>
                {(buttons || []).map((button, index) => (
                  <TouchableOpacity
                    key={index}
                    style={[
                      styles.modernButton,
                      styles.verticalButton,
                      button.style === 'primary' && styles.primaryButton,
                      button.style === 'destructive' && styles.destructiveButton,
                      button.style === 'cancel' && styles.cancelButton,
                      index < (buttons || []).length - 1 && styles.verticalButtonMargin,
                    ]}
                    onPress={button.onPress || onClose}
                    activeOpacity={0.8}
                  >
                    {button.icon && (
                      <Ionicons
                        name={button.icon}
                        size={16}
                        color={
                          button.style === 'primary'
                            ? '#FFFFFF'
                            : button.style === 'destructive'
                              ? '#FFFFFF'
                              : '#B8918F'
                        }
                        style={styles.buttonIcon}
                      />
                    )}
                    <Text
                      style={[
                        styles.buttonText,
                        button.style === 'primary' && styles.primaryButtonText,
                        button.style === 'destructive' && styles.destructiveButtonText,
                        button.style === 'cancel' && styles.cancelButtonText,
                      ]}
                    >
                      {button.text}
                    </Text>
                  </TouchableOpacity>
                ))}
              </View>
            )}
          </View>
        </Animated.View>
      </View>
    </Modal>
  );
}

