import { Ionicons } from '@expo/vector-icons';
import * as Haptics from 'expo-haptics';
import { LinearGradient } from 'expo-linear-gradient';
import React from 'react';
import { StyleSheet, TouchableOpacity } from 'react-native';

import { SPACING } from '@/constants/AppConstants';
import { DesignSystem } from '@/theme/DesignSystem';

interface FloatingActionButtonProps {
  onPress: () => void;
  icon?: keyof typeof Ionicons.glyphMap;
  size?: number;
  disabled?: boolean;
}

export default function FloatingActionButton({
  onPress,
  icon = 'add',
  size = 56,
  disabled = false,
}: FloatingActionButtonProps) {
  const handlePress = () => {
    if (!disabled) {
      void Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
      onPress();
    }
  };

  return (
    <TouchableOpacity
      style={[
        styles.container,
        {
          width: size,
          height: size,
          borderRadius: size / 2,
        },
        disabled && styles.disabled,
        DesignSystem.elevation.soft,
      ]}
      onPress={handlePress}
      disabled={disabled}
      activeOpacity={0.8}
      accessibilityRole="button"
      accessibilityLabel="Floating action button"
      accessibilityHint="Tap to perform the primary action"
      accessibilityState={{ disabled }}
    >
      <LinearGradient
        colors={
          [
            DesignSystem.colors.primaryIndexed[500] || '#007AFF',
            DesignSystem.colors.gold[500] || '#C9A227',
          ] as const
        }
        style={[
          styles.gradient,
          {
            width: size,
            height: size,
            borderRadius: size / 2,
          },
        ]}
        start={{ x: 0, y: 0 }}
        end={{ x: 1, y: 1 }}
      >
        <Ionicons name={icon} size={size * 0.4} color="#FFFFFF" />
      </LinearGradient>
    </TouchableOpacity>
  );
}

const styles = StyleSheet.create({
  container: {
    bottom: SPACING.xl,
    overflow: 'hidden',
    position: 'absolute',
    right: SPACING.xl,
  },
  disabled: {
    opacity: 0.5,
  },
  gradient: {
    alignItems: 'center',
    justifyContent: 'center',
  },
});

import { LinearGradient } from 'expo-linear-gradient';
import React, { useEffect } from 'react';
import { StyleSheet, View } from 'react-native';
import Animated, {
  Easing,
  Extrapolate,
  interpolate,
  useAnimatedStyle,
  useSharedValue,
  withRepeat,
  withTiming,
} from 'react-native-reanimated';

import { useSafeTheme } from '@/hooks/useSafeTheme';
import { DesignSystem } from '@/theme/DesignSystem';

interface GoldShimmerProps {
  width: number;
  height: number;
  intensity?: 'subtle' | 'medium' | 'strong';
  delay?: number;
}

export default function GoldShimmer({
  width,
  height,
  intensity = 'subtle',
  delay = 0,
}: GoldShimmerProps) {
  const theme = useSafeTheme();
  const { colors: _colors } = theme;
  const shimmerPosition = useSharedValue(0);

  useEffect(() => {
    const startAnimation = () => {
      shimmerPosition.value = withRepeat(
        withTiming(1, {
          duration: intensity === 'subtle' ? 3000 : intensity === 'medium' ? 2500 : 2000,
          easing: Easing.inOut(Easing.ease),
        }),
        -1,
        false,
      );
    };

    if (delay > 0) {
      setTimeout(startAnimation, delay);
    } else {
      startAnimation();
    }
  }, [intensity, delay]);

  const animatedStyle = useAnimatedStyle(() => {
    const translateX = interpolate(
      shimmerPosition.value,
      [0, 1],
      [-width * 1.5, width * 2.5],
      Extrapolate.CLAMP,
    );

    return {
      transform: [{ translateX }],
    };
  });

  const opacityMap = {
    subtle: 0.2,
    medium: 0.4,
    strong: 0.6,
  };

  const baseOpacity = opacityMap[intensity];

  return (
    <View style={[styles.container, { width, height }]} pointerEvents="none">
      <Animated.View style={[styles.shimmer, { width: width * 3 }, animatedStyle]}>
        <LinearGradient
          colors={
            [
              'transparent',
              'transparent',
              `${DesignSystem.colors.primary[500]}${Math.round(baseOpacity * 0.3 * 255)
                .toString(16)
                .padStart(2, '0')}`,
              `${DesignSystem.colors.primary[500]}${Math.round(baseOpacity * 0.7 * 255)
                .toString(16)
                .padStart(2, '0')}`,
              `${DesignSystem.colors.primary[500]}${Math.round(baseOpacity * 0.5 * 255)
                .toString(16)
                .padStart(2, '0')}`,
              `${DesignSystem.colors.primary[500]}${Math.round(baseOpacity * 0.7 * 255)
                .toString(16)
                .padStart(2, '0')}`,
              `${DesignSystem.colors.primary[500]}${Math.round(baseOpacity * 0.3 * 255)
                .toString(16)
                .padStart(2, '0')}`,
              'transparent',
              'transparent',
            ] as const
          }
          start={{ x: 0, y: 0 }}
          end={{ x: 1, y: 0 }}
          style={styles.gradient}
        />
      </Animated.View>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    borderRadius: 8,
    overflow: 'hidden',
    position: 'absolute',
  },
  gradient: {
    flex: 1,
  },
  shimmer: {
    height: '100%',
  },
});

import { Ionicons } from '@expo/vector-icons';
import React, { useEffect, useRef } from 'react';
import {
  Animated,
  Dimensions,
  Modal,
  PanResponder,
  ScrollView,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
} from 'react-native';

import { DesignSystem } from '@/theme/DesignSystem';

const { height: screenHeight, width: _screenWidth } = Dimensions.get('window');

interface ActionSheetOption {
  title: string;
  icon?: keyof typeof Ionicons.glyphMap;
  onPress: () => void;
  style?: 'default' | 'destructive' | 'primary';
  subtitle?: string;
}

interface ModernActionSheetProps {
  visible: boolean;
  title?: string;
  subtitle?: string;
  options: ActionSheetOption[];
  onClose: () => void;
  showCancel?: boolean;
}

export default function ModernActionSheet({
  visible,
  title,
  subtitle,
  options = [],
  onClose,
  showCancel = true,
}: ModernActionSheetProps) {
  const fadeAnim = useRef(new Animated.Value(0)).current;
  const slideAnim = useRef(new Animated.Value(screenHeight)).current;

  useEffect(() => {
    if (visible) {
      Animated.parallel([
        Animated.timing(fadeAnim, {
          toValue: 1,
          duration: 300,
          useNativeDriver: true,
        }),
        Animated.spring(slideAnim, {
          toValue: 0,
          tension: 100,
          friction: 8,
          useNativeDriver: true,
        }),
      ]).start();
    } else {
      Animated.parallel([
        Animated.timing(fadeAnim, {
          toValue: 0,
          duration: 250,
          useNativeDriver: true,
        }),
        Animated.timing(slideAnim, {
          toValue: screenHeight,
          duration: 250,
          useNativeDriver: true,
        }),
      ]).start();
    }
  }, [visible]);

  const panResponder = PanResponder.create({
    onStartShouldSetPanResponder: () => true,
    onMoveShouldSetPanResponder: (_, gestureState) => {
      return Math.abs(gestureState.dy) > 10;
    },
    onPanResponderMove: (_, gestureState) => {
      if (gestureState.dy > 0) {
        slideAnim.setValue(gestureState.dy);
      }
    },
    onPanResponderRelease: (_, gestureState) => {
      if (gestureState.dy > 100) {
        onClose();
      } else {
        Animated.spring(slideAnim, {
          toValue: 0,
          tension: 100,
          friction: 8,
          useNativeDriver: true,
        }).start();
      }
    },
  });

  const handleOptionPress = (onPress: () => void) => {
    onPress();
    onClose();
  };

  return (
    <Modal animationType="none" transparent={true} visible={visible} onRequestClose={onClose}>
      <View style={styles.overlay}>
        <Animated.View style={[styles.backdrop, { opacity: fadeAnim }]} />
        <TouchableOpacity
          style={styles.backdropTouchable}
          activeOpacity={1}
          onPress={onClose}
          accessibilityRole="button"
          accessibilityLabel="Close action sheet"
          accessibilityHint="Tap to close the action sheet"
        />

        <Animated.View
          style={[
            styles.container,
            {
              transform: [{ translateY: slideAnim }],
            },
          ]}
          {...panResponder.panHandlers}
        >
          {/* Drag Indicator */}
          <View style={styles.handle} />

          {/* Header */}
          {(title || subtitle) && (
            <View style={styles.header}>
              {title && <Text style={styles.title}>{title}</Text>}
              {subtitle && <Text style={styles.subtitle}>{subtitle}</Text>}
            </View>
          )}

          {/* Options */}
          <ScrollView style={styles.optionsContainer} showsVerticalScrollIndicator={false}>
            {(options || []).map((option, index) => (
              <TouchableOpacity
                key={index}
                style={[
                  styles.option,
                  option.style === 'destructive' && styles.destructiveOption,
                  option.style === 'primary' && styles.primaryOption,
                ]}
                onPress={() => handleOptionPress(option.onPress)}
                activeOpacity={0.7}
                accessibilityRole="button"
                accessibilityLabel={option.title}
                accessibilityHint={option.subtitle || `Tap to ${option.title.toLowerCase()}`}
              >
                <View style={styles.optionContent}>
                  {option.icon && (
                    <View
                      style={[
                        styles.optionIcon,
                        option.style === 'destructive' && styles.destructiveIconContainer,
                        option.style === 'primary' && styles.primaryIconContainer,
                      ]}
                    >
                      <Ionicons
                        name={option.icon}
                        size={20}
                        color={
                          option.style === 'destructive'
                            ? '#D32F2F'
                            : option.style === 'primary'
                              ? '#B8918F'
                              : '#7A6B56'
                        }
                      />
                    </View>
                  )}
                  <View style={styles.textContainer}>
                    <Text
                      style={[
                        styles.optionText,
                        option.style === 'destructive' && styles.destructiveText,
                        option.style === 'primary' && styles.primaryText,
                      ]}
                    >
                      {option.title}
                    </Text>
                    {option.subtitle && (
                      <Text style={styles.optionSubtitle}>{option.subtitle}</Text>
                    )}
                  </View>
                </View>
                <Ionicons name="chevron-forward" size={16} color="#B5A3BC" />
              </TouchableOpacity>
            ))}
          </ScrollView>

          {/* Cancel Button */}
          {showCancel && (
            <View style={styles.cancelContainer}>
              <TouchableOpacity
                style={styles.cancelButton}
                onPress={onClose}
                activeOpacity={0.7}
                accessibilityRole="button"
                accessibilityLabel="Cancel"
                accessibilityHint="Tap to cancel and close the action sheet"
              >
                <Text style={styles.cancelText}>Cancel</Text>
              </TouchableOpacity>
            </View>
          )}
        </Animated.View>
      </View>
    </Modal>
  );
}

