import * as ImagePicker from 'expo-image-picker';
import * as Location from 'expo-location';
import React, { useEffect, useState } from 'react';

import CustomModal from '@/components/CustomModal';
import { errorInDev, warnInDev } from '@/utils/consoleSuppress';

interface PermissionManagerProps {
  children: React.ReactNode;
}

export interface PermissionManagerRef {
  requestCameraPermission: () => Promise<boolean>;
  requestLocationPermission: () => Promise<boolean>;
  openCamera: () => Promise<void>;
  openImagePicker: () => Promise<void>;
}

export default function PermissionManager({ children }: PermissionManagerProps) {
  const [modalVisible, setModalVisible] = useState(false);
  const [modalContent, setModalContent] = useState({
    title: '',
    message: '',
    buttons: [] as Array<{
      text: string;
      onPress?: () => void;
      style?: 'default' | 'cancel' | 'destructive';
    }>,
  });

  const showPermissionDialog = (
    title: string,
    message: string,
    buttons: Array<{
      text: string;
      onPress?: () => void;
      style?: 'default' | 'cancel' | 'destructive';
    }>,
  ) => {
    setModalContent({ title, message, buttons });
    setModalVisible(true);
  };

  const hideModal = () => {
    setModalVisible(false);
  };

  const checkInitialPermissions = async () => {
    // Check camera permission
    const { status: cameraStatus } = await ImagePicker.getCameraPermissionsAsync();

    // Check location permission
    const { status: locationStatus } = await Location.getForegroundPermissionsAsync();

    // If any permissions are not granted, show welcome dialog
    if (
      cameraStatus !== ImagePicker.PermissionStatus.GRANTED ||
      locationStatus !== Location.PermissionStatus.GRANTED
    ) {
      setTimeout(() => {
        showPermissionDialog(
          'Welcome to AynaModa! 💖',
          "To give you the best fashion experience, we'd like permission to:\n\n📸 Camera - Take photos of your outfits\n📍 Location - Find local boutiques and deals\n🔔 Notifications - Alert you about sales\n\nYou can always change these in settings later.",
          [
            {
              text: 'Allow Permissions',
              onPress: () => {
                void requestPermissionsIndividually();
              },
            },
            {
              text: 'Maybe Later',
              style: 'cancel',
            },
          ],
        );
      }, 1000);
    }
  };

  // Check and request initial permissions on app startup
  useEffect(() => {
    void checkInitialPermissions();
  }, []);

  const requestAllPermissions = async () => {
    try {
      // Request camera permission
      const cameraResult = await ImagePicker.requestCameraPermissionsAsync();

      // Request location permission
      const locationResult = await Location.requestForegroundPermissionsAsync();

      if (
        cameraResult.status === ImagePicker.PermissionStatus.GRANTED &&
        locationResult.status === Location.PermissionStatus.GRANTED
      ) {
        showPermissionDialog(
          'Permissions Granted! 🎉',
          'Thank you! You can now take photos and find local boutiques.',
          [{ text: 'Great!', onPress: hideModal }],
        );
      } else {
        showPermissionDialog(
          'Permissions Needed',
          'Some permissions were not granted. You can still use the app, but some features may be limited.',
          [{ text: 'OK', onPress: hideModal }],
        );
      }
    } catch (error) {
      errorInDev('Error requesting permissions:', error);
    }
  };

  const requestPermissionsIndividually = () => {
    // Camera permission
    showPermissionDialog(
      'Camera Permission 📸',
      'Allow AynaModa to access your camera to take photos of your outfits and add them to your wardrobe?',
      [
        {
          text: 'Allow',
          onPress: () => {
            hideModal();
            void (async () => {
              await requestCameraPermission();
              setTimeout(() => requestLocationPermissionDialog(), 500);
            })();
          },
        },
        {
          text: "Don't Allow",
          onPress: () => {
            hideModal();
            setTimeout(() => requestLocationPermissionDialog(), 500);
          },
          style: 'cancel',
        },
      ],
    );
  };

  const requestLocationPermissionDialog = () => {
    showPermissionDialog(
      'Location Permission 📍',
      'Allow AynaModa to access your location to find nearby boutiques, local deals, and delivery options?',
      [
        {
          text: 'Allow',
          onPress: () => {
            hideModal();
            void requestLocationPermission();
          },
        },
        { text: "Don't Allow", onPress: hideModal, style: 'cancel' },
      ],
    );
  };

  const requestCameraPermission = async (): Promise<boolean> => {
    try {
      const { status } = await ImagePicker.requestCameraPermissionsAsync();

      if (status === ImagePicker.PermissionStatus.GRANTED) {
        return true;
      } else {
        showPermissionDialog(
          'Camera Access Needed 📸',
          'To take photos of your outfits, please enable camera permission in your device settings.\n\nSettings > AynaModa > Camera',
          [{ text: 'OK', onPress: hideModal }],
        );
        return false;
      }
    } catch (error) {
      warnInDev('Camera permission error:', error);
      return false;
    }
  };

  const requestLocationPermission = async (): Promise<boolean> => {
    try {
      const { status } = await Location.requestForegroundPermissionsAsync();

      if (status === Location.PermissionStatus.GRANTED) {
        return true;
      } else {
        showPermissionDialog(
          'Location Access Needed 📍',
          'To find nearby boutiques and local deals, please enable location permission in your device settings.\n\nSettings > AynaModa > Location',
          [{ text: 'OK', onPress: hideModal }],
        );
        return false;
      }
    } catch (error) {
      warnInDev('Location permission error:', error);
      return false;
    }
  };

  const openCamera = async (): Promise<void> => {
    const hasPermission = await requestCameraPermission();

    if (hasPermission) {
      try {
        const result = await ImagePicker.launchCameraAsync({
          mediaTypes: ImagePicker.MediaTypeOptions.Images,
          allowsEditing: true,
          aspect: [4, 3],
          quality: 0.8,
        });

        if (!result.canceled && result.assets[0]) {
          // Handle the captured image
          showPermissionDialog(
            'Photo Captured! 📸',
            'Your outfit photo has been taken! Would you like to add it to your wardrobe?',
            [
              { text: 'Add to Wardrobe', onPress: hideModal },
              {
                text: 'Retake Photo',
                onPress: () => {
                  hideModal();
                  setTimeout(() => void openCamera(), 500);
                },
              },
              { text: 'Cancel', onPress: hideModal, style: 'cancel' },
            ],
          );
        }
      } catch (error) {
        warnInDev('Camera launch error:', error);
        showPermissionDialog(
          'Camera Error',
          'There was an issue opening the camera. Please try again.',
          [{ text: 'OK', onPress: hideModal }],
        );
      }
    }
  };

  const openImagePicker = async (): Promise<void> => {
    try {
      const result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ImagePicker.MediaTypeOptions.Images,
        allowsEditing: true,
        aspect: [4, 3],
        quality: 0.8,
      });

      if (!result.canceled && result.assets[0]) {
        // Handle the selected image
        showPermissionDialog(
          'Photo Selected! 🖼️',
          'Perfect choice! Would you like to add this photo to your wardrobe?',
          [
            { text: 'Add to Wardrobe', onPress: hideModal },
            {
              text: 'Choose Different',
              onPress: () => {
                hideModal();
                setTimeout(() => void openImagePicker(), 500);
              },
            },
            { text: 'Cancel', onPress: hideModal, style: 'cancel' },
          ],
        );
      }
    } catch (error) {
      warnInDev('Image picker error:', error);
      showPermissionDialog(
        'Gallery Error',
        'There was an issue opening your photo gallery. Please try again.',
        [{ text: 'OK', onPress: hideModal }],
      );
    }
  };

  // Global permission manager instance
  React.useEffect(() => {
    (global as Record<string, unknown>).permissionManager = {
      requestCameraPermission,
      requestLocationPermission,
      openCamera,
      openImagePicker,
    };
  }, []);

  return (
    <>
      {children}
      <CustomModal
        visible={modalVisible}
        title={modalContent.title}
        message={modalContent.message}
        buttons={modalContent.buttons}
        onClose={hideModal}
      />
    </>
  );
}

import { Ionicons } from '@expo/vector-icons';
import { LinearGradient } from 'expo-linear-gradient';
import React, { useEffect } from 'react';
import { StyleSheet, Text, View } from 'react-native';
import Animated, {
  Easing,
  interpolate,
  useAnimatedStyle,
  useSharedValue,
  withSpring,
  withTiming,
} from 'react-native-reanimated';

import { useSafeTheme } from '@/hooks/useSafeTheme';
import { DesignSystem } from '@/theme/DesignSystem';

interface StyleMatchCircleProps {
  percentage: number;
  size?: number;
  showLabel?: boolean;
  animated?: boolean;
  confidence?: 'high' | 'medium' | 'low';
}

const StyleMatchCircle: React.FC<StyleMatchCircleProps> = ({
  percentage,
  size = 48,
  showLabel = true,
  animated = true,
  confidence = 'high',
}) => {
  const theme = useSafeTheme();
  const { colors } = theme;

  // Animation values
  const progress = useSharedValue(0);
  const scale = useSharedValue(0.8);
  const opacity = useSharedValue(0);
  const sparkleRotation = useSharedValue(0);

  useEffect(() => {
    if (animated) {
      progress.value = withTiming(percentage / 100, {
        duration: 1000,
      });

      scale.value = withSpring(1.1, {}, () => {
        scale.value = withSpring(1);
      });

      opacity.value = withTiming(1, {
        duration: 800,
      });

      sparkleRotation.value = withTiming(
        1,
        {
          duration: 2000,
          easing: Easing.linear,
        },
        () => {
          sparkleRotation.value = 0;
          sparkleRotation.value = withTiming(1, {
            duration: 2000,
            easing: Easing.linear,
          });
        },
      );
    }
  }, [percentage, animated, opacity, progress, scale, sparkleRotation]);

  const animatedContainerStyle = useAnimatedStyle(() => ({
    transform: [{ scale: scale.value }],
    opacity: opacity.value,
  }));

  const animatedProgressStyle = useAnimatedStyle(() => {
    const rotation = interpolate(progress.value, [0, 1], [0, 360 * progress.value]);

    return {
      transform: [{ rotate: `${rotation}deg` }],
    };
  });

  const animatedSparkleStyle = useAnimatedStyle(() => ({
    transform: [{ rotate: `${sparkleRotation.value}deg` }],
  }));

  const getConfidenceColor = () => {
    if (percentage >= 90) {
      return DesignSystem.colors.primary;
    } // Antique Gold
    if (percentage >= 70) {
      return colors.accent.coral;
    } // Dusty Rose
    return colors.textSecondary; // Taupe
  };

  const getConfidenceGradient = (): readonly [string, string] => {
    if (percentage >= 90) {
      return [
        DesignSystem.colors.primary || '#007AFF',
        DesignSystem.colors.gold[300] || '#C9A227',
      ] as const;
    }
    if (percentage >= 70) {
      return [colors.accent.coral, DesignSystem.colors.sage[100]] as const;
    }
    return [colors.textSecondary, DesignSystem.colors.neutral.mist] as const;
  };

  const strokeWidth = size * 0.08;
  const radius = (size - strokeWidth) / 2;
  const _circumference = 2 * Math.PI * radius;

  return (
    <Animated.View style={[styles.container, animatedContainerStyle]}>
      {/* Background Circle */}
      <View
        style={[
          styles.backgroundCircle,
          {
            width: size,
            height: size,
            borderRadius: size / 2,
            backgroundColor: colors.surface,
          },
        ]}
      >
        {/* Progress Circle */}
        <Animated.View
          style={[
            styles.progressCircle,
            {
              width: size,
              height: size,
              borderRadius: size / 2,
              borderWidth: strokeWidth,
              borderColor: getConfidenceColor(),
            },
            animatedProgressStyle,
          ]}
        />

        {/* Gradient Overlay */}
        <LinearGradient
          colors={getConfidenceGradient()}
          style={[
            styles.gradientOverlay,
            {
              width: size - strokeWidth * 2,
              height: size - strokeWidth * 2,
              borderRadius: (size - strokeWidth * 2) / 2,
            },
          ]}
        />

        {/* Content */}
        <View style={styles.content}>
          {/* Percentage Text */}
          <Text
            style={[
              styles.percentageText,
              {
                color: colors.background,
                fontSize: size * 0.25,
                lineHeight: size * 0.28,
              },
            ]}
          >
            {Math.round(percentage)}
          </Text>

          {/* Sparkle Icon for High Confidence */}
          {percentage >= 90 && (
            <Animated.View style={[styles.sparkle, animatedSparkleStyle]}>
              <Ionicons name="sparkles" size={size * 0.15} color={colors.background} />
            </Animated.View>
          )}
        </View>

        {/* Confidence Badge */}
        {showLabel && (
          <View
            style={[
              styles.confidenceBadge,
              {
                backgroundColor: getConfidenceColor(),
                bottom: -size * 0.15,
              },
            ]}
          >
            <Text
              style={[
                styles.confidenceText,
                {
                  color: colors.background,
                  fontSize: size * 0.12,
                },
              ]}
            >
              {percentage >= 90 ? 'PERFECT' : percentage >= 70 ? 'GREAT' : 'GOOD'} MATCH
            </Text>
          </View>
        )}
      </View>
    </Animated.View>
  );
};

const styles = StyleSheet.create({
  container: {
    backgroundColor: DesignSystem.colors.background.primary,
    padding: DesignSystem.spacing.medium,
  },
  backgroundCircle: {
    alignItems: 'center',
