/**
 * Button Atom
 *
 * The most fundamental interactive element in the design system.
 * Follows the unified design system and supports all button variants.
 */

import React from 'react';
import { StyleSheet, Text, TouchableOpacity } from 'react-native';

import { useHapticFeedback } from '@/hooks/useHapticFeedback';
import { DesignSystem } from '@/theme/DesignSystem';
import { ButtonComponentProps } from '@/types/componentProps';

export interface ButtonProps extends Omit<ButtonComponentProps, 'variant'> {
  title: string;
  onPress: () => void;
  variant?: 'primary' | 'secondary' | 'ghost' | 'luxury';
  size?: 'small' | 'medium' | 'large';
  fullWidth?: boolean;
  leftIcon?: React.ReactNode;
  rightIcon?: React.ReactNode;
  loading?: boolean;
  disabled?: boolean;
}

const Button: React.FC<ButtonProps> = ({
  title,
  onPress,
  variant = 'primary',
  size = 'medium',
  fullWidth = false,
  leftIcon,
  rightIcon,
  loading = false,
  disabled = false,
  style,
  testID,
  accessibilityLabel,
  ...props
}) => {
  const { trigger } = useHapticFeedback();

  const handlePress = () => {
    if (!disabled && !loading) {
      trigger('light');
      onPress();
    }
  };

  const buttonStyle = [
    styles.base,
    styles[variant],
    styles[size],
    fullWidth && styles.fullWidth,
    (disabled || loading) && styles.disabled,
    style,
  ];

  const textStyle = [
    styles.text,
    variant === 'primary' && styles.primaryText,
    variant === 'secondary' && styles.secondaryText,
    variant === 'ghost' && styles.ghostText,
    variant === 'luxury' && styles.luxuryText,
    styles[`${size}Text`],
    (disabled || loading) && styles.disabledText,
  ];

  return (
    <TouchableOpacity
      style={buttonStyle}
      onPress={handlePress}
      disabled={disabled || loading}
      testID={testID}
      accessibilityLabel={accessibilityLabel || title}
      accessibilityRole="button"
      accessibilityState={{ disabled: disabled || loading }}
      {...props}
    >
      {leftIcon && <Text style={styles.icon}>{leftIcon}</Text>}
      <Text style={textStyle}>{loading ? 'Loading...' : title}</Text>
      {rightIcon && <Text style={styles.icon}>{rightIcon}</Text>}
    </TouchableOpacity>
  );
};

