/**
 * Original Input Component
 *
 * A reusable input component that matches the original AynaModa login design.
 * Features clean styling, icon support, and proper focus states.
 */

import { Ionicons } from '@expo/vector-icons';
import React, { forwardRef, useState } from 'react';
import {
  Animated,
  NativeSyntheticEvent,
  StyleSheet,
  Text,
  TextInput,
  TextInputFocusEventData,
  TouchableOpacity,
  View,
  ViewStyle,
} from 'react-native';

import {
  ACCESSIBILITY_LABELS,
  ORIGINAL_ANIMATIONS,
  ORIGINAL_COLORS,
  ORIGINAL_DIMENSIONS,
  originalLoginStyles,
} from '@/components/auth/originalLoginStyles';
import { DEFAULT_PROPS, InputComponentProps } from '@/types/componentProps';

export interface OriginalInputProps extends Omit<InputComponentProps, 'variant' | 'size'> {
  /** Custom container style */
  containerStyle?: ViewStyle;

  /** Whether the input is currently focused (controlled) */
  focused?: boolean;

  /** Callback when focus state changes */
  onFocusChange?: (focused: boolean) => void;

  /** Original theme variant */
  variant?: 'default' | 'focused' | 'error';
}

export const OriginalInput = forwardRef<TextInput, OriginalInputProps>(
  (
    {
      leftIcon,
      isPassword = DEFAULT_PROPS.disabled,
      error,
      label,
      hint,
      required = DEFAULT_PROPS.required,
      containerStyle,
      focused: controlledFocused,
      onFocusChange,
      onFocus,
      onBlur,
      style,
      inputStyle,
      labelStyle,
      errorStyle,
      testID,
      accessibilityLabel,
      disabled = DEFAULT_PROPS.disabled,
      variant = 'default',
      ...textInputProps
    },
    ref,
  ) => {
    const [internalFocused, setInternalFocused] = useState(false);
    const [showPassword, setShowPassword] = useState(false);
    const [focusAnimation] = useState(new Animated.Value(0));

    const isFocused = controlledFocused !== undefined ? controlledFocused : internalFocused;

    const handleFocus = (e: NativeSyntheticEvent<TextInputFocusEventData>) => {
      if (controlledFocused === undefined) {
        setInternalFocused(true);
      }
      onFocusChange?.(true);
      onFocus?.(e);

      // Animate border color on focus
      Animated.timing(focusAnimation, {
        toValue: 1,
        duration: ORIGINAL_ANIMATIONS.inputFocus.duration,
        useNativeDriver: false,
      }).start();
    };

    const handleBlur = (e: NativeSyntheticEvent<TextInputFocusEventData>) => {
      if (controlledFocused === undefined) {
        setInternalFocused(false);
      }
      onFocusChange?.(false);
      onBlur?.(e);

      // Animate border color on blur
      Animated.timing(focusAnimation, {
        toValue: 0,
        duration: ORIGINAL_ANIMATIONS.inputFocus.duration,
        useNativeDriver: false,
      }).start();
    };

    const togglePasswordVisibility = () => {
      setShowPassword(!showPassword);
    };

    const animatedBorderColor = focusAnimation.interpolate({
      inputRange: [0, 1],
      outputRange: [ORIGINAL_COLORS.inputBorder, ORIGINAL_COLORS.inputBorderFocused],
    });

    return (
      <View style={containerStyle}>
        {/* Label */}
        {label && (
          <Text style={[originalLoginStyles.subtitle, { marginBottom: 8, textAlign: 'left' }]}>
            {label}
          </Text>
        )}

        {/* Input Container */}
        <Animated.View
          style={[
            originalLoginStyles.inputContainer,
            {
              borderColor: animatedBorderColor,
            },
            error && { borderColor: ORIGINAL_COLORS.errorColor },
          ]}
        >
          {/* Left Icon */}
          {leftIcon && (
            <Ionicons
              name={leftIcon}
              size={ORIGINAL_DIMENSIONS.inputIconSize}
              style={originalLoginStyles.inputIcon}
            />
          )}

          {/* Text Input */}
          <TextInput
            ref={ref}
            style={originalLoginStyles.input}
            placeholderTextColor={ORIGINAL_COLORS.placeholderText}
            secureTextEntry={isPassword && !showPassword}
            onFocus={handleFocus}
            onBlur={handleBlur}
            accessibilityLabel={
              isPassword ? ACCESSIBILITY_LABELS.passwordInput : ACCESSIBILITY_LABELS.emailInput
            }
            {...textInputProps}
          />

          {/* Password Toggle */}
          {isPassword && (
            <TouchableOpacity
              style={originalLoginStyles.passwordToggle}
              onPress={togglePasswordVisibility}
              accessibilityLabel={ACCESSIBILITY_LABELS.passwordToggle}
              accessibilityRole="button"
            >
              <Ionicons
                name={showPassword ? 'eye-off-outline' : 'eye-outline'}
                size={ORIGINAL_DIMENSIONS.inputIconSize}
                color={ORIGINAL_COLORS.placeholderText}
              />
            </TouchableOpacity>
          )}
        </Animated.View>

        {/* Error Message */}
        {error && (
          <Animated.View style={styles.errorContainer}>
            <Text style={originalLoginStyles.errorText}>{error}</Text>
          </Animated.View>
        )}
      </View>
    );
  },
);

OriginalInput.displayName = 'OriginalInput';

const styles = StyleSheet.create({
  errorContainer: {
    opacity: 1,
  },
});

export default OriginalInput;

/**
 * Original Login Form Component
 *
 * A complete login form that matches the original AynaModa design.
 * Combines email and password inputs with proper validation and Turkish error messages.
 */

import React, { useState } from 'react';
import { ActivityIndicator, Alert, StyleSheet, Text, TouchableOpacity, View } from 'react-native';

import { OriginalInput } from '@/components/auth/OriginalInput';
import {
  ACCESSIBILITY_LABELS,
  ORIGINAL_COLORS,
  originalLoginStyles,
  TURKISH_TEXT,
  VALIDATION_MESSAGES,
} from '@/components/auth/originalLoginStyles';
import { errorInDev } from '@/utils/consoleSuppress';

export interface OriginalLoginFormProps {
  /** Callback when login is attempted */
  onLogin: (email: string, password: string) => Promise<void>;

  /** Whether the form is currently loading */
  loading?: boolean;

  /** Error message to display */
  error?: string;

  /** Callback when forgot password is pressed */
  onForgotPassword?: () => void;
}

export const OriginalLoginForm: React.FC<OriginalLoginFormProps> = ({
  onLogin,
  loading = false,
  error,
  onForgotPassword,
}) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [emailError, setEmailError] = useState<string>();
  const [passwordError, setPasswordError] = useState<string>();
  const [isSubmitting, setIsSubmitting] = useState(false);

  // Form validation functions
  const validateEmail = (email: string): string | undefined => {
    if (!email.trim()) {
      return VALIDATION_MESSAGES.emailRequired;
    }

    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(email.trim())) {
      return VALIDATION_MESSAGES.emailInvalid;
    }

    return undefined;
  };

  const validatePassword = (password: string): string | undefined => {
    if (!password) {
      return VALIDATION_MESSAGES.passwordRequired;
    }

    if (password.length < 6) {
      return VALIDATION_MESSAGES.passwordTooShort;
    }

    return undefined;
  };

  const validateForm = (): boolean => {
    const emailErr = validateEmail(email);
    const passwordErr = validatePassword(password);

    setEmailError(emailErr);
    setPasswordError(passwordErr);

    return !emailErr && !passwordErr;
  };

  const handleEmailChange = (text: string) => {
    setEmail(text);
    // Clear email error when user starts typing
    if (emailError) {
      setEmailError(undefined);
    }
  };

  const handlePasswordChange = (text: string) => {
    setPassword(text);
    // Clear password error when user starts typing
    if (passwordError) {
      setPasswordError(undefined);
    }
  };

  const handleSubmit = async () => {
    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    try {
      await onLogin(email.trim(), password);
    } catch (err: unknown) {
      // Error handling is done by parent component
      errorInDev('Login error:', err instanceof Error ? err : String(err));
    } finally {
      setIsSubmitting(false);
    }
  };

  const handleForgotPassword = () => {
    if (onForgotPassword) {
      onForgotPassword();
    } else {
      Alert.alert('Şifremi Unuttum', 'Şifre sıfırlama özelliği yakında gelecek!', [
        { text: 'Tamam' },
      ]);
    }
  };

  const isLoading = loading || isSubmitting;

  return (
    <View style={styles.container}>
      {/* Global Error Message */}
      {error && (
        <View style={styles.globalErrorContainer}>
          <Text style={styles.globalErrorText}>{error}</Text>
        </View>
      )}

      {/* Email Input */}
      <OriginalInput
        leftIcon="mail-outline"
        placeholder={TURKISH_TEXT.emailLabel}
        value={email}
        onChangeText={handleEmailChange}
        error={emailError}
        keyboardType="email-address"
        autoCapitalize="none"
        autoComplete="email"
        returnKeyType="next"
        editable={!isLoading}
        accessibilityLabel={ACCESSIBILITY_LABELS.emailInput}
      />

      {/* Password Input */}
      <OriginalInput
        leftIcon="lock-closed-outline"
        placeholder={TURKISH_TEXT.passwordLabel}
        value={password}
        onChangeText={handlePasswordChange}
        error={passwordError}
        isPassword
        autoComplete="password"
        returnKeyType="done"
        onSubmitEditing={handleSubmit}
        editable={!isLoading}
        accessibilityLabel={ACCESSIBILITY_LABELS.passwordInput}
      />

      {/* Forgot Password Link */}
      <TouchableOpacity
        style={styles.forgotPasswordButton}
        onPress={handleForgotPassword}
        disabled={isLoading}
        accessibilityLabel={ACCESSIBILITY_LABELS.forgotPassword}
        accessibilityRole="button"
      >
        <Text style={[styles.forgotPasswordText, isLoading && styles.disabledText]}>
          {TURKISH_TEXT.forgotPassword}
        </Text>
      </TouchableOpacity>

      {/* Login Button */}
      <TouchableOpacity
        style={[styles.loginButton, isLoading && styles.loginButtonDisabled]}
        onPress={handleSubmit}
        disabled={isLoading}
        accessibilityLabel={ACCESSIBILITY_LABELS.loginButton}
        accessibilityRole="button"
      >
        {isLoading ? (
          <ActivityIndicator color={ORIGINAL_COLORS.primaryButtonText} size="small" />
        ) : (
          <Text style={styles.loginButtonText}>{TURKISH_TEXT.loginButton}</Text>
        )}
      </TouchableOpacity>
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    maxWidth: 400,
    width: '100%',
  },

  disabledText: {
    opacity: 0.5,
  },

  forgotPasswordButton: {
    alignSelf: 'flex-end',
    marginBottom: 24,
    paddingHorizontal: 4,
    paddingVertical: 8,
  },

  forgotPasswordText: {
    ...originalLoginStyles.forgotPasswordText,
  },

  globalErrorContainer: {
    backgroundColor: 'rgba(255, 68, 68, 0.1)',
    borderColor: ORIGINAL_COLORS.errorColor,
    borderRadius: 8,
    borderWidth: 1,
    marginBottom: 16,
    padding: 12,
  },

  globalErrorText: {
    color: ORIGINAL_COLORS.errorColor,
    fontSize: 14,
    fontWeight: '500',
    textAlign: 'center',
  },

  loginButton: {
    ...originalLoginStyles.loginButton,
  },

  loginButtonDisabled: {
    ...originalLoginStyles.loginButtonDisabled,
  },

  loginButtonText: {
    ...originalLoginStyles.loginButtonText,
  },
});

export default OriginalLoginForm;

import { Ionicons } from '@expo/vector-icons';
import * as Haptics from 'expo-haptics';
import { router } from 'expo-router';
import React, { useState } from 'react';
import {
  ActivityIndicator,
  Alert,
  KeyboardAvoidingView,
  Platform,
  SafeAreaView,
  ScrollView,
  StyleSheet,
  Text,
  TextInput,
  TouchableOpacity,
  View,
} from 'react-native';
import { useSafeAreaInsets } from 'react-native-safe-area-context';

import { useAuth } from '@/context/AuthContext';

// Turkish text constants - exactly as specified
const TURKISH_TEXT = {
  title: 'AYNAMODA',
  welcome: 'Kişisel Sığınağınıza Hoş Geldiniz',
  subtitle: 'Stilin kesinlikle buluştuğu yer.',
  emailLabel: 'E-posta',
  passwordLabel: 'Şifre',
  loginButton: 'GİRİŞ YAP',
  forgotPassword: 'Şifrenizi mi unuttunuz?',
  continueWith: 'veya şununla devam et',
  noAccount: 'Hesabın yok mu?',
  signup: 'Kayıt Ol',
} as const;

interface OriginalLoginScreenProps {
  onLogin?: (email: string, password: string) => void;
  onGoogleLogin?: () => void;
  onAppleLogin?: () => void;
  onForgotPassword?: () => void;
  onSignup?: () => void;
  loading?: boolean;
  error?: string;
}

const OriginalLoginScreen: React.FC<OriginalLoginScreenProps> = () => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [isGoogleLoading, setGoogleLoading] = useState(false);
  const [isAppleLoading, setAppleLoading] = useState(false);
  const [emailError, setEmailError] = useState<string>();
  const [passwordError, setPasswordError] = useState<string>();

  const { signIn, signInWithGoogle, signInWithApple } = useAuth();
  const insets = useSafeAreaInsets();

  // Form validation
  const validateEmail = (email: string): string | undefined => {
    if (!email) {
      return 'E-posta adresi gerekli';
    }
    if (!/\S+@\S+\.\S+/.test(email)) {
      return 'Geçerli bir e-posta adresi girin';
    }
    return undefined;
  };

  const validatePassword = (password: string): string | undefined => {
    if (!password) {
      return 'Şifre gerekli';
    }
    if (password.length < 6) {
      return 'Şifre en az 6 karakter olmalı';
    }
    return undefined;
  };

  const handleSignIn = async () => {
    // Clear previous errors
    setEmailError(undefined);
    setPasswordError(undefined);

    // Validate form
    const emailErr = validateEmail(email);
    const passwordErr = validatePassword(password);

    if (emailErr || passwordErr) {
      setEmailError(emailErr);
      setPasswordError(passwordErr);
      void Haptics.notificationAsync(Haptics.NotificationFeedbackType.Warning);
      return;
    }

    setIsLoading(true);
    void Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Medium);

    try {
      await signIn(email, password);
      void Haptics.notificationAsync(Haptics.NotificationFeedbackType.Success);
    } catch (error: unknown) {
      void Haptics.notificationAsync(Haptics.NotificationFeedbackType.Error);
      Alert.alert('Giriş Başarısız', error instanceof Error ? error.message : 'Bir hata oluştu.');
    } finally {
      setIsLoading(false);
    }
  };

  const handleGoogleSignIn = async () => {
    setGoogleLoading(true);
    try {
      await signInWithGoogle();
    } catch (error: unknown) {
      Alert.alert(
        'Google Giriş Başarısız',
        error instanceof Error ? error.message : 'Bir hata oluştu.',
      );
    } finally {
      setGoogleLoading(false);
    }
  };

  const handleAppleSignIn = async () => {
    setAppleLoading(true);
    try {
      await signInWithApple();
    } catch (error: unknown) {
      Alert.alert(
        'Apple Giriş Başarısız',
        error instanceof Error ? error.message : 'Bir hata oluştu.',
      );
    } finally {
      setAppleLoading(false);
    }
  };

  const handleForgotPassword = () => {
    Alert.alert('Şifremi Unuttum', 'Bu özellik yakında gelecek!');
  };

  const handleSignup = () => {
    router.push('/auth/sign-up');
  };

  return (
    <View style={styles.container}>
      <SafeAreaView style={styles.safeArea}>
        <KeyboardAvoidingView
          style={styles.keyboardAvoidingView}
          behavior={Platform.OS === 'ios' ? 'padding' : undefined}
          keyboardVerticalOffset={0}
        >
          <ScrollView
            contentContainerStyle={styles.contentContainer}
            keyboardShouldPersistTaps="handled"
            showsVerticalScrollIndicator={false}
          >
            {/* Brand Section */}
            <View style={styles.brandSection}>
              <Text style={styles.title}>{TURKISH_TEXT.title}</Text>
              <Text style={styles.welcome}>{TURKISH_TEXT.welcome}</Text>
              <Text style={styles.subtitle}>{TURKISH_TEXT.subtitle}</Text>
            </View>

            {/* Form Section */}
            <View style={styles.formSection}>
              {/* Email Input */}
              <View style={styles.inputContainer}>
                <Ionicons name="mail-outline" size={20} color="#999999" style={styles.inputIcon} />
                <TextInput
                  style={styles.input}
                  placeholder={TURKISH_TEXT.emailLabel}
                  placeholderTextColor="#999999"
                  value={email}
                  onChangeText={(text) => {
                    setEmail(text);
                    if (emailError) {
                      setEmailError(undefined);
                    }
                  }}
                  keyboardType="email-address"
                  autoCapitalize="none"
                  autoComplete="email"
                  returnKeyType="next"
                />
              </View>
              {emailError && <Text style={styles.errorText}>{emailError}</Text>}

              {/* Password Input */}
              <View style={styles.inputContainer}>
                <Ionicons
                  name="lock-closed-outline"
                  size={20}
                  color="#999999"
                  style={styles.inputIcon}
                />
                <TextInput
                  style={styles.input}
                  placeholder={TURKISH_TEXT.passwordLabel}
                  placeholderTextColor="#999999"
                  value={password}
                  onChangeText={(text) => {
                    setPassword(text);
                    if (passwordError) {
                      setPasswordError(undefined);
                    }
                  }}
                  secureTextEntry={!showPassword}
                  autoComplete="password"
                  returnKeyType="done"
                  onSubmitEditing={() => void handleSignIn()}
                />
                <TouchableOpacity
                  style={styles.passwordToggle}
                  onPress={() => setShowPassword(!showPassword)}
                >
                  <Ionicons
                    name={showPassword ? 'eye-off-outline' : 'eye-outline'}
                    size={20}
                    color="#999999"
                  />
                </TouchableOpacity>
              </View>
              {passwordError && <Text style={styles.errorText}>{passwordError}</Text>}

              {/* Forgot Password */}
              <TouchableOpacity
                style={styles.forgotPasswordButton}
                onPress={() => void handleForgotPassword()}
              >
                <Text style={styles.forgotPasswordText}>{TURKISH_TEXT.forgotPassword}</Text>
              </TouchableOpacity>

              {/* Login Button */}
              <TouchableOpacity
                style={[styles.loginButton, isLoading && styles.loginButtonDisabled]}
                onPress={() => void handleSignIn()}
                disabled={isLoading}
              >
                {isLoading ? (
                  <ActivityIndicator color="#FFFFFF" />
                ) : (
                  <Text style={styles.loginButtonText}>{TURKISH_TEXT.loginButton}</Text>
                )}
              </TouchableOpacity>
            </View>

            {/* Social Login Section */}
            <View style={styles.socialSection}>
              <Text style={styles.dividerText}>{TURKISH_TEXT.continueWith}</Text>
              <View style={styles.socialButtonsContainer}>
                <TouchableOpacity
                  style={styles.socialButton}
                  onPress={() => void handleGoogleSignIn()}
                  disabled={isGoogleLoading || isAppleLoading}
                >
                  {isGoogleLoading ? (
                    <ActivityIndicator color="#DB4437" />
                  ) : (
                    <Ionicons name="logo-google" size={24} color="#DB4437" />
                  )}
                </TouchableOpacity>
                <TouchableOpacity
                  style={styles.socialButton}
                  onPress={() => void handleAppleSignIn()}
                  disabled={isGoogleLoading || isAppleLoading}
                >
                  {isAppleLoading ? (
                    <ActivityIndicator color="#000000" />
                  ) : (
                    <Ionicons name="logo-apple" size={28} color="#000000" />
                  )}
                </TouchableOpacity>
              </View>
            </View>

            {/* Registration Section */}
            <View style={[styles.registrationSection, { marginBottom: insets.bottom || 24 }]}>
              <Text style={styles.signupPrompt}>
                {TURKISH_TEXT.noAccount}{' '}
                <TouchableOpacity
                  onPress={handleSignup}
                  accessibilityRole="button"
                  accessibilityLabel="Kayıt Ol"
                  accessibilityHint="Kayıt olma ekranına git"
                >
                  <Text style={styles.signupLink}>{TURKISH_TEXT.signup}</Text>
                </TouchableOpacity>
              </Text>
            </View>
          </ScrollView>
        </KeyboardAvoidingView>
      </SafeAreaView>
    </View>
  );
};

