              </View>

              <View style={styles.metricRow}>
                <Text style={styles.metricLabel}>Average Interaction Time</Text>
                <Text
                  style={[
                    styles.metricValue,
                    {
                      color: getPerformanceColor(performanceData.averageInteractionTime, {
                        good: 50,
