              <Text style={styles.thresholdText}>🟡 Interaction: 50-100ms</Text>
              <Text style={styles.thresholdText}>🔴 Interaction: {'>'}100ms</Text>
            </View>
          </ScrollView>
        </View>
      </View>
    </Modal>
  );

  return (
    <>
      {renderCompactView()}
      {renderExpandedView()}
    </>
  );
};

