      {/* Similar Items Modal */}
      <Modal
        visible={showSimilarModal}
        animationType="slide"
        presentationStyle="pageSheet"
        onRequestClose={() => setShowSimilarModal(false)}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalHeader}>
