            showsHorizontalScrollIndicator={false}
            contentContainerStyle={styles.storySelector}
          >
            {allStories.map((story) => (
              <TouchableOpacity
                key={story.id}
                style={[
                  styles.storyButton,
                  {
                    backgroundColor:
                      currentStory.id === story.id
                        ? story.accentColor + '20'
