import * as Haptics from 'expo-haptics';
import { LinearGradient } from 'expo-linear-gradient';
import React, { useCallback, useEffect, useState } from 'react';
import { Dimensions, ScrollView, StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import Animated, {
  interpolate,
  runOnJS,
  useAnimatedStyle,
  useSharedValue,
  withSequence,
  withTiming,
} from 'react-native-reanimated';

import { warnInDev } from '../../utils/consoleSuppress';

const { width, height } = Dimensions.get('window');

interface StyleDNA {
  style_energy?: { value: string };
  color_palette?: { value: string };
  [key: string]: { value: string } | undefined;
}

interface OutfitItem {
  type: string;
  name: string;
  color: string;
}

interface Outfit {
  id: string;
  name: string;
  description: string;
  items: OutfitItem[];
  confidence: number;
  styleMatch: string;
}

interface WelcomeGiftProps {
  styleDNA: StyleDNA;
  onComplete: () => void;
  onOutfitSelect: (outfit: Outfit, feedback: string) => void;
}

// Generate personalized outfits based on Style DNA
const generatePersonalizedOutfits = (styleDNA: StyleDNA): Outfit[] => {
  const outfits = [
    {
