const MOODS: { tag: MoodTag; icon: IoniconsName; description: string }[] = [
  {
    tag: 'Serene & Grounded',
    icon: 'leaf-outline',
    description: 'Calm, peaceful looks that center your spirit',
  },
  {
    tag: 'Luminous & Confident',
    icon: 'sunny-outline',
    description: 'Radiant outfits that amplify your inner light',
  },
  {
