        {/* Mindful Selection Indicator */}
        {isSelected && (
          <View style={styles.sanctuarySelectionOverlay}>
            <View style={styles.sanctuarySelectionIndicator}>
              <Ionicons
                name="checkmark-circle"
                size={20}
                color={DesignSystem.colors.primary[600]}
              />
            </View>
          </View>
        )}
      </LinearGradient>
    </TouchableOpacity>
  );
};

