import React, { useCallback } from 'react';
import { FlatList, ListRenderItem, StyleSheet, View } from 'react-native';
import Animated, { useAnimatedStyle } from 'react-native-reanimated';
// AYNAMODA Color Palette
const COLORS = {
  primary: '#8B6F47',
  secondary: '#B8A082',
  background: '#F5F1E8',
  surface: '#FFFFFF',
  text: '#2C2C2C',
  textLight: '#B8A082',
  border: '#E8DCC6',
  accent: '#D4AF37',
};
import { OutfitRecommendationCard } from '@/components/aynaMirror/OutfitRecommendationCard';
import { OutfitRecommendation } from '@/types/aynaMirror';

interface RecommendationsListProps {
  recommendations: OutfitRecommendation[];
  selectedRecommendation: OutfitRecommendation | null;
  onRecommendationSelect: (recommendation: OutfitRecommendation) => void;
  contentTranslateY: Animated.SharedValue<number>;
  dimensions: {
    cardSpacing: number;
  };
}

export const RecommendationsList: React.FC<RecommendationsListProps> = ({
  recommendations,
  selectedRecommendation,
  onRecommendationSelect,
  contentTranslateY,
  dimensions,
}) => {
  const contentAnimatedStyle = useAnimatedStyle(() => ({
    transform: [{ translateY: contentTranslateY.value }],
  }));

  const renderRecommendation: ListRenderItem<OutfitRecommendation> = useCallback(
    ({ item: recommendation, index }) => (
      <OutfitRecommendationCard
        recommendation={recommendation}
        isSelected={selectedRecommendation?.id === recommendation.id}
        onSelect={() => onRecommendationSelect(recommendation)}
        showInlineActions={false}
        showConfidenceNote={false}
        a11yLabelPrefix={
          selectedRecommendation?.id === recommendation.id
            ? 'Outfit recommendation'
            : 'Recommendation'
        }
        animationDelay={index * 100}
      />
    ),
    [selectedRecommendation?.id, onRecommendationSelect],
  );

  const keyExtractor = useCallback((item: OutfitRecommendation) => item.id, []);

  const getItemLayout = useCallback(
    (_: ArrayLike<OutfitRecommendation> | null | undefined, index: number) => ({
      length: 200, // Estimated item height
      offset: 200 * index + dimensions.cardSpacing * index,
      index,
    }),
    [dimensions.cardSpacing],
  );

  return (
    <Animated.View style={[styles.content, contentAnimatedStyle]}>
      <FlatList
        data={recommendations}
        renderItem={renderRecommendation}
        keyExtractor={keyExtractor}
        style={styles.scrollView}
        contentContainerStyle={styles.scrollContent}
        showsVerticalScrollIndicator={false}
        // Performance optimizations
        removeClippedSubviews={true}
        maxToRenderPerBatch={5}
        windowSize={10}
        initialNumToRender={3}
        updateCellsBatchingPeriod={50}
        getItemLayout={getItemLayout}
        // Memory optimization
        disableVirtualization={false}
        legacyImplementation={false}
        scrollEventThrottle={16}
        ItemSeparatorComponent={() => <View style={{ height: dimensions.cardSpacing }} />}
        // Prevent nested VirtualizedList warnings
        nestedScrollEnabled={true}
        scrollEnabled={true}
      />
    </Animated.View>
  );
};

const styles = StyleSheet.create({
  content: {
    flex: 1,
  },
  scrollContent: {
    paddingBottom: 48,
    paddingHorizontal: 24,
    paddingTop: 24,
  },
  scrollView: {
    flex: 1,
  },
});

const recommendationsContainer = (dimensions: { cardSpacing: number }) => ({
  gap: dimensions.cardSpacing,
  marginBottom: 32,
});

/**
 * Standardized Button Component
 *
 * A reusable button component that follows AYNAMODA's design system
 * and implements the standardized ButtonComponentProps interface.
 */

import { Ionicons } from '@expo/vector-icons';
import * as Haptics from 'expo-haptics';
import { LinearGradient } from 'expo-linear-gradient';
import React from 'react';
import {
  ActivityIndicator,
  StyleSheet,
  Text,
  TextStyle,
  TouchableOpacity,
  View,
  ViewStyle,
} from 'react-native';

import { DesignSystem } from '@/theme/DesignSystem';
import { ButtonComponentProps, DEFAULT_PROPS } from '@/types/componentProps';
import { warnInDev } from '@/utils/consoleSuppress';

export interface StandardButtonProps extends ButtonComponentProps {
  /** Button gradient colors for luxury variants */
  gradientColors?: readonly [string, string, ...string[]];
  /** Shadow configuration */
  shadow?: boolean;
}

const StandardButton: React.FC<StandardButtonProps> = ({
  title,
  children,
  onPress,
  variant = DEFAULT_PROPS.variant,
  size = DEFAULT_PROPS.size,
  icon,
  iconPosition = 'left',
  disabled = DEFAULT_PROPS.disabled,
  loading = DEFAULT_PROPS.loading,
  hapticFeedback = DEFAULT_PROPS.hapticFeedback,
  fullWidth = DEFAULT_PROPS.fullWidth,
  style,
  textStyle,
  testID,
  accessibilityLabel,
  gradientColors,
  shadow = true,
  ...props
}) => {
  // Validate required props
  React.useEffect(() => {
    if (!title && !children) {
      warnInDev('StandardButton: Either title or children prop is required');
    }
  }, [title, children]);

  const handlePress = () => {
    if (disabled || loading) {
      return;
    }

    // Provide haptic feedback
    if (hapticFeedback !== 'none') {
      switch (hapticFeedback) {
        case 'light':
          Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
          break;
        case 'medium':
          Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Medium);
          break;
        case 'heavy':
          Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Heavy);
          break;
      }
    }

    onPress?.();
  };

  const getButtonStyles = (): ViewStyle => {
    const baseStyle = {
      ...styles.button,
      ...styles[`button_${size}`],
      ...(fullWidth && styles.fullWidth),
      ...(disabled && styles.disabled),
    };

    switch (variant) {
      case 'primary':
        return { ...baseStyle, ...styles.primary };
      case 'secondary':
        return { ...baseStyle, ...styles.secondary };
      case 'ghost':
        return { ...baseStyle, ...styles.ghost };
      case 'glass':
        return { ...baseStyle, ...styles.glass };
      case 'luxury':
        return { ...baseStyle, ...styles.luxury };
      case 'minimal':
        return { ...baseStyle, ...styles.minimal };
      default:
        return baseStyle;
    }
  };

  const getTextStyles = (): TextStyle => {
    const baseStyle = {
      ...styles.text,
      ...styles[`text_${size}`],
      ...(disabled && styles.textDisabled),
    };

    switch (variant) {
      case 'primary':
        return { ...baseStyle, ...styles.textPrimary };
      case 'secondary':
        return { ...baseStyle, ...styles.textSecondary };
      case 'ghost':
        return { ...baseStyle, ...styles.textGhost };
      case 'glass':
        return { ...baseStyle, ...styles.textGlass };
      case 'luxury':
        return { ...baseStyle, ...styles.textLuxury };
      case 'minimal':
        return { ...baseStyle, ...styles.textMinimal };
      default:
        return baseStyle;
    }
  };

  const renderContent = () => {
    if (loading) {
      return (
        <View style={styles.loadingContainer}>
          <ActivityIndicator
            size={size === 'small' ? 'small' : 'large'}
            color={variant === 'primary' ? '#FFFFFF' : DesignSystem.colors.sage[500]}
          />
          {title && <Text style={[getTextStyles(), textStyle, styles.loadingText]}>{title}</Text>}
        </View>
      );
    }

    return (
      <View style={styles.contentContainer}>
        {icon && iconPosition === 'left' && (
          <Ionicons
            name={icon}
            size={size === 'small' ? 16 : size === 'medium' ? 20 : 24}
            color={getTextStyles().color}
            style={styles.iconLeft}
          />
        )}

        {children || <Text style={[getTextStyles(), textStyle]}>{title}</Text>}

        {icon && iconPosition === 'right' && (
          <Ionicons
            name={icon}
            size={size === 'small' ? 16 : size === 'medium' ? 20 : 24}
            color={getTextStyles().color}
            style={styles.iconRight}
          />
        )}
      </View>
    );
  };

  const buttonStyle = [getButtonStyles(), style];
  const shouldUseGradient = variant === 'luxury' || gradientColors;
  const finalGradientColors = gradientColors ?? [
    DesignSystem.colors.sage[500],
    DesignSystem.colors.sage[600],
  ];

  if (shouldUseGradient) {
    return (
      <TouchableOpacity
        onPress={handlePress}
        disabled={disabled || loading}
        style={[buttonStyle, shadow && styles.shadow]}
        testID={testID}
        accessibilityLabel={accessibilityLabel || title}
        accessibilityRole="button"
        accessibilityState={{ disabled: disabled || loading }}
        {...props}
      >
        <LinearGradient colors={finalGradientColors} style={styles.gradientContainer}>
          {renderContent()}
        </LinearGradient>
      </TouchableOpacity>
    );
  }

  return (
    <TouchableOpacity
      onPress={handlePress}
      disabled={disabled || loading}
      style={[buttonStyle, shadow && styles.shadow]}
      testID={testID}
      accessibilityLabel={accessibilityLabel || title}
      accessibilityRole="button"
      accessibilityState={{ disabled: disabled || loading }}
      {...props}
    >
      {renderContent()}
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  button: {
    alignItems: 'center',
    borderRadius: DesignSystem.radius.md,
    flexDirection: 'row',
    justifyContent: 'center',
  },
  button_large: {
    minHeight: 52,
    paddingHorizontal: DesignSystem.spacing.xl,
    paddingVertical: DesignSystem.spacing.lg,
  },
  button_medium: {
    minHeight: 44,
    paddingHorizontal: DesignSystem.spacing.lg,
    paddingVertical: DesignSystem.spacing.md,
  },
  button_small: {
    minHeight: 36,
    paddingHorizontal: DesignSystem.spacing.md,
    paddingVertical: DesignSystem.spacing.sm,
  },
  contentContainer: {
    alignItems: 'center',
    flexDirection: 'row',
    justifyContent: 'center',
  },
  disabled: {
    opacity: 0.5,
  },
  fullWidth: {
    width: '100%',
  },
  ghost: {
    backgroundColor: 'transparent',
    borderColor: DesignSystem.colors.sage[500],
    borderWidth: 1,
  },
  glass: {
    backgroundColor: DesignSystem.colors.background.glass,
    borderColor: DesignSystem.colors.border.glass,
    borderWidth: 1,
  },
  gradientContainer: {
    alignItems: 'center',
    borderRadius: DesignSystem.radius.md,
    flex: 1,
    justifyContent: 'center',
  },
  iconLeft: {
    marginRight: DesignSystem.spacing.sm,
  },
  iconRight: {
    marginLeft: DesignSystem.spacing.sm,
  },
  loadingContainer: {
    alignItems: 'center',
    flexDirection: 'row',
    justifyContent: 'center',
  },
  loadingText: {
    marginLeft: DesignSystem.spacing.sm,
  },
  luxury: {
    backgroundColor: 'transparent',
  },
  minimal: {
    backgroundColor: 'transparent',
  },
  primary: {
    backgroundColor: DesignSystem.colors.sage[500],
  },
  secondary: {
    backgroundColor: DesignSystem.colors.background.secondary,
    borderColor: DesignSystem.colors.border.primary,
    borderWidth: 1,
  },
  shadow: {
    ...DesignSystem.elevation.soft,
  },
  text: {
    ...DesignSystem.typography.button.medium,
    textAlign: 'center',
  },
  textDisabled: {
    opacity: 0.6,
  },
  textGhost: {
    color: DesignSystem.colors.sage[500],
  },
  textGlass: {
    color: DesignSystem.colors.text.primary,
  },
  textLuxury: {
    color: DesignSystem.colors.text.inverse,
  },
  textMinimal: {
    color: DesignSystem.colors.text.primary,
  },
  textPrimary: {
    color: DesignSystem.colors.text.inverse,
  },
  textSecondary: {
    color: DesignSystem.colors.text.primary,
  },
  text_large: {
    ...DesignSystem.typography.button.large,
  },
  text_medium: {
    ...DesignSystem.typography.button.medium,
  },
  text_small: {
    ...DesignSystem.typography.button.small,
  },
});

// Defaults via parameter defaults are already applied at usage sites

export default StandardButton;

/**
 * Standardized Card Component
 *
 * A reusable card component that follows AYNAMODA's design system
 * and implements the standardized CardComponentProps interface.
 */

import { Ionicons } from '@expo/vector-icons';
import { BlurView } from 'expo-blur';
import * as Haptics from 'expo-haptics';
import { LinearGradient } from 'expo-linear-gradient';
import React from 'react';
import { StyleSheet, Text, TextStyle, TouchableOpacity, View, ViewStyle } from 'react-native';

import { DesignSystem } from '@/theme/DesignSystem';
import { CardComponentProps, DEFAULT_PROPS } from '@/types/componentProps';

export interface StandardCardProps extends Omit<CardComponentProps, 'children'> {
  /** Card header content */
  header?: React.ReactNode;
  /** Card footer content */
  footer?: React.ReactNode;
  /** Card body content */
  children?: React.ReactNode;
  /** Card title */
  title?: string;
  /** Card subtitle */
  subtitle?: string;
  /** Card description */
  description?: string;
  /** Left icon */
  leftIcon?: keyof typeof Ionicons.glyphMap;
  /** Right icon */
  rightIcon?: keyof typeof Ionicons.glyphMap;
  /** Right icon press handler */
  onRightIconPress?: () => void;
  /** Whether card has border */
  bordered?: boolean;
  /** Custom header style */
  headerStyle?: ViewStyle;
  /** Custom body style */
  bodyStyle?: ViewStyle;
  /** Custom footer style */
  footerStyle?: ViewStyle;
  /** Custom title style */
  titleStyle?: TextStyle;
  /** Custom subtitle style */
  subtitleStyle?: TextStyle;
  /** Custom description style */
  descriptionStyle?: TextStyle;
  /** Gradient colors for luxury variant */
  gradientColors?: readonly [string, string, ...string[]];
  /** Blur intensity for glass variant */
  blurIntensity?: number;
  /** Size preset */
  size?: 'small' | 'medium' | 'large';
  /** Elevation level */
  elevation?: 'none' | 'soft' | 'medium' | 'high' | 'organic' | 'floating' | 'subtle' | 'large';
}

const StandardCard: React.FC<StandardCardProps> = ({
  header,
  footer,
  children,
  title,
  subtitle,
  description,
  leftIcon,
  rightIcon,
  onRightIconPress,
  onPress,
  variant = DEFAULT_PROPS.variant,
  size = DEFAULT_PROPS.size,
  elevation = 'soft',
  bordered = false,
  disabled = DEFAULT_PROPS.disabled,
  loading = DEFAULT_PROPS.loading,
  hapticFeedback = DEFAULT_PROPS.hapticFeedback,
  style,
  headerStyle,
  bodyStyle,
  footerStyle,
  titleStyle,
  subtitleStyle,
  descriptionStyle,
  testID,
  accessibilityLabel,
  gradientColors,
  blurIntensity = 10,
  ...props
}) => {
  const handlePress = () => {
    if (disabled || loading || !onPress) {
      return;
    }

    // Provide haptic feedback
    if (hapticFeedback !== 'none') {
      switch (hapticFeedback) {
        case 'light':
          Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);
          break;
        case 'medium':
          Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Medium);
          break;
        case 'heavy':
          Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Heavy);
          break;
      }
    }

    onPress();
  };

  const getCardStyles = (): ViewStyle => {
    const baseStyle = {
      ...styles.card,
      ...styles[`card_${size}`],
      ...(bordered && styles.bordered),
      ...(disabled && styles.disabled),
      ...(DesignSystem.elevation[elevation as keyof typeof DesignSystem.elevation] || {}),
    };

    switch (variant) {
      case 'primary':
        return { ...baseStyle, ...styles.primary };
      case 'secondary':
        return { ...baseStyle, ...styles.secondary };
      case 'ghost':
        return { ...baseStyle, ...styles.ghost };
      case 'glass':
        return { ...baseStyle, ...styles.glass };
      case 'luxury':
        return { ...baseStyle, ...styles.luxury };
      case 'minimal':
        return { ...baseStyle, ...styles.minimal };
      default:
        return { ...baseStyle, ...styles.default };
    }
  };

  const renderHeader = () => {
    if (!header && !title && !subtitle && !leftIcon && !rightIcon) {
      return null;
    }

    return (
      <View style={[styles.header, headerStyle]}>
        <View style={styles.headerLeft}>
          {leftIcon && (
            <Ionicons
              name={leftIcon}
              size={size === 'small' ? 16 : size === 'medium' ? 20 : 24}
              color={DesignSystem.colors.text.primary}
              style={styles.leftIcon}
            />
          )}

          <View style={styles.headerText}>
            {title && (
              <Text
                style={[
                  styles.title,
                  size === 'small'
                    ? styles.title_small
                    : size === 'medium'
                      ? styles.title_medium
                      : styles.title_large,
                  titleStyle,
                ]}
              >
                {title}
              </Text>
            )}
            {subtitle && (
              <Text
                style={[
                  styles.subtitle,
                  size === 'small'
                    ? styles.subtitle_small
                    : size === 'medium'
                      ? styles.subtitle_medium
                      : styles.subtitle_large,
                  subtitleStyle,
                ]}
              >
                {subtitle}
              </Text>
            )}
          </View>
        </View>

        {rightIcon && (
          <TouchableOpacity
            onPress={onRightIconPress}
            style={styles.rightIconContainer}
            disabled={disabled}
          >
            <Ionicons
              name={rightIcon}
              size={size === 'small' ? 16 : size === 'medium' ? 20 : 24}
              color={DesignSystem.colors.text.secondary}
            />
          </TouchableOpacity>
        )}

        {header}
      </View>
    );
  };

  const renderBody = () => {
    if (!children && !description) {
      return null;
    }

    return (
      <View style={[styles.body, bodyStyle]}>
        {description && (
          <Text
            style={[
              styles.description,
              styles[`description_${size}` as keyof typeof styles] as TextStyle,
              descriptionStyle,
            ]}
          >
            {description}
          </Text>
        )}
        {children}
      </View>
    );
  };

  const renderFooter = () => {
    if (!footer) {
      return null;
    }

    return <View style={[styles.footer, footerStyle]}>{footer}</View>;
  };

  const cardStyle = [getCardStyles(), style];
  const shouldUseGradient = variant === 'luxury' || gradientColors;
  const shouldUseBlur = variant === 'glass';
  const finalGradientColors = gradientColors || [
    DesignSystem.colors.sage[500],
    DesignSystem.colors.sage[600],
  ];

  const cardContent = (
    <>
      {renderHeader()}
      {renderBody()}
      {renderFooter()}
    </>
  );

  if (shouldUseBlur) {
    const CardWrapper = onPress ? TouchableOpacity : View;
    return (
      <CardWrapper
        onPress={onPress ? handlePress : undefined}
        disabled={disabled || loading}
        style={cardStyle}
        testID={testID}
        accessibilityLabel={accessibilityLabel || title}
        accessibilityRole={onPress ? 'button' : undefined}
        accessibilityState={{ disabled: disabled || loading }}
        {...props}
      >
        <BlurView intensity={blurIntensity} style={styles.blurContainer}>
          {cardContent}
        </BlurView>
      </CardWrapper>
    );
  }

  if (shouldUseGradient) {
    const CardWrapper = onPress ? TouchableOpacity : View;
    return (
      <CardWrapper
        onPress={onPress ? handlePress : undefined}
        disabled={disabled || loading}
        style={cardStyle}
        testID={testID}
        accessibilityLabel={accessibilityLabel || title}
        accessibilityRole={onPress ? 'button' : undefined}
        accessibilityState={{ disabled: disabled || loading }}
        {...props}
      >
        <LinearGradient colors={finalGradientColors} style={styles.gradientContainer}>
          {cardContent}
        </LinearGradient>
      </CardWrapper>
    );
  }

  if (onPress) {
    return (
      <TouchableOpacity
        onPress={handlePress}
        disabled={disabled || loading}
        style={cardStyle}
        testID={testID}
        accessibilityLabel={accessibilityLabel || title}
        accessibilityRole="button"
        accessibilityState={{ disabled: disabled || loading }}
        {...props}
      >
        {cardContent}
      </TouchableOpacity>
    );
  }

  return (
    <View
      style={cardStyle}
      testID={testID}
      accessibilityLabel={accessibilityLabel || title}
      {...props}
    >
      {cardContent}
    </View>
  );
};

