import type { BottomTabBarProps } from '@react-navigation/bottom-tabs';
import * as React from 'react';
import { StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import Animated, { useAnimatedStyle, useSharedValue, withTiming } from 'react-native-reanimated';
import { SafeAreaView } from 'react-native-safe-area-context';

import { DesignSystem } from '../../theme/DesignSystem';

// const { width: screenWidth } = Dimensions.get('window');
const AnimatedTouchableOpacity = Animated.createAnimatedComponent(TouchableOpacity);

interface TabItem {
  key: string;
  title: string;
  icon: keyof typeof Ionicons.glyphMap;
}

interface UltraPremiumTabBarProps {
  state: BottomTabBarProps['state'];
  descriptors: BottomTabBarProps['descriptors'];
  navigation: BottomTabBarProps['navigation'];
}

const tabs: TabItem[] = [
  {
    key: 'index',
    title: 'Home',
    icon: 'home-outline',
  },
  {
    key: 'wardrobe',
    title: 'Wardrobe',
    icon: 'shirt-outline',
  },
  {
    key: 'ayna-mirror',
    title: 'Mirror',
    icon: 'glasses-outline',
  },
  {
    key: 'discover',
    title: 'Discover',
    icon: 'sparkles-outline',
  },
  {
    key: 'profile',
    title: 'Profile',
    icon: 'person-outline',
  },
];

// Dedicated tab button to keep hooks at component top-level
const TabButton: React.FC<{
  tab: TabItem;
  isActive: boolean;
  onPress: () => void;
  onLongPress: () => void;
}> = ({ tab, isActive, onPress, onLongPress }) => {
  const scale = useSharedValue(1);
  const opacity = useSharedValue(0.6);

  React.useEffect(() => {
    scale.value = withTiming(isActive ? 1.05 : 1, { duration: 200 });
    opacity.value = withTiming(isActive ? 1 : 0.6, { duration: 200 });
  }, [isActive, opacity, scale]);

  const animatedStyle = useAnimatedStyle(() => ({
    transform: [{ scale: scale.value }],
    opacity: opacity.value,
  }));

  return React.createElement(
    AnimatedTouchableOpacity,
    {
      style: [styles.tab, animatedStyle],
      onPress,
      onLongPress,
      activeOpacity: 0.8,
    },
    React.createElement(
      View,
      { style: [styles.tabContent, isActive && styles.tabContentActive] },
      React.createElement(Ionicons, {
        name: tab.icon,
        size: 20,
        color: isActive ? DesignSystem.colors.text.primary : DesignSystem.colors.text.tertiary,
      }),
      React.createElement(
        Text,
        {
          style: [styles.tabLabel, isActive ? styles.tabLabelActive : styles.tabLabelInactive],
        },
        tab.title,
      ),
    ),
  );
};

const UltraPremiumTabBar: React.FC<UltraPremiumTabBarProps> = ({
  state,
  descriptors: _descriptors,
  navigation,
}) => {
  const activeIndex = state.index;

  return React.createElement(
    SafeAreaView,
    { style: styles.container, edges: ['bottom'] },
    React.createElement(
      View,
      { style: styles.tabBar },
      React.createElement(
        View,
        { style: styles.tabsContainer },
        state.routes.map((route, index) => {
          const tab = tabs[index];
          if (!tab) {
            return null;
          }
          const isActive = index === activeIndex;

          const onPress = () => {
            const event = navigation.emit({
              type: 'tabPress',
              target: route.key,
              canPreventDefault: true,
            });

            if (!isActive && !event.defaultPrevented) {
              navigation.navigate(route.name);
            }
          };

          const onLongPress = () => {
            navigation.emit({
              type: 'tabLongPress',
              target: route.key,
            });
          };

          return React.createElement(TabButton, {
            key: tab.key,
            tab,
            isActive,
            onPress,
            onLongPress,
          });
        }),
      ),
    ),
  );
};

