import React from 'react';
import { StyleSheet, Text, TouchableOpacity, View, ViewStyle } from 'react-native';

import { useHapticFeedback } from '@/hooks/useHapticFeedback';
import { DesignSystem } from '@/theme/DesignSystem';

export interface AddItemButtonProps {
  onPress: () => void;
  title?: string;
  icon?: string;
  style?: ViewStyle;
  disabled?: boolean;
  variant?: 'primary' | 'secondary' | 'floating';
}

const AddItemButton: React.FC<AddItemButtonProps> = ({
  onPress,
  title = 'Add Item',
  icon = '+',
  style,
  disabled = false,
  variant = 'primary',
}) => {
  const { triggerSelection } = useHapticFeedback();

  const handlePress = () => {
    if (!disabled) {
      triggerSelection();
      onPress();
    }
  };

  const getButtonStyle = () => {
    switch (variant) {
      case 'floating':
        return styles.floatingButton;
      case 'secondary':
        return styles.secondaryButton;
      default:
        return styles.primaryButton;
    }
  };

  const getTextStyle = () => {
    switch (variant) {
      case 'secondary':
        return styles.secondaryButtonText;
      default:
        return styles.primaryButtonText;
    }
  };

  if (variant === 'floating') {
    return (
      <TouchableOpacity
        style={[getButtonStyle(), disabled && styles.disabledButton, style]}
        onPress={handlePress}
        disabled={disabled}
        activeOpacity={0.8}
        accessibilityRole="button"
        accessibilityLabel={title || 'Add item'}
        accessibilityHint="Tap to add a new item to your wardrobe"
        accessibilityState={{ disabled }}
      >
        <Text style={[styles.floatingButtonIcon, disabled && styles.disabledText]}>{icon}</Text>
      </TouchableOpacity>
    );
  }

  return (
    <TouchableOpacity
      style={[getButtonStyle(), disabled && styles.disabledButton, style]}
      onPress={handlePress}
      disabled={disabled}
      activeOpacity={0.8}
      accessibilityRole="button"
      accessibilityLabel={title || 'Add item'}
      accessibilityHint="Tap to add a new item to your wardrobe"
      accessibilityState={{ disabled }}
    >
      <View style={styles.buttonContent}>
        <Text style={[styles.buttonIcon, getTextStyle(), disabled && styles.disabledText]}>
          {icon}
        </Text>
        <Text style={[getTextStyle(), disabled && styles.disabledText]}>{title}</Text>
      </View>
    </TouchableOpacity>
  );
};

