/**
 * Circuit Breaker Hook
 */
interface CircuitBreakerConfigLegacy {
  failureThreshold?: number;
  timeout?: number; // legacy key for resetTimeout
  monitoringPeriod?: number;
}

type CircuitState = 'closed' | 'open' | 'half-open';

// Backward compatibility: tests may call useCircuitBreaker({ failureThreshold: 2 }) expecting config-only form
export function useCircuitBreaker<T>(
  operationOrConfig?: (() => Promise<T>) | CircuitBreakerConfigLegacy,
  maybeConfig: CircuitBreakerConfigLegacy = {
    failureThreshold: 5,
    timeout: 60000,
    monitoringPeriod: 300000,
  },
) {
  const isFunction = typeof operationOrConfig === 'function';
  const operation =
    (isFunction ? (operationOrConfig as () => Promise<T>) : undefined) ||
    (async () => Promise.resolve(null as T));
  const config =
    (isFunction ? maybeConfig : (operationOrConfig as CircuitBreakerConfigLegacy)) || maybeConfig;
  const internalConfig = {
    failureThreshold: config.failureThreshold ?? 5,
    resetTimeout: config.timeout ?? 60000,
    monitoringPeriod: config.monitoringPeriod ?? 300000,
  };

  const [state, setState] = useState<CircuitState>('closed');
  const [failures, setFailures] = useState(0);
  const [lastFailureTime, setLastFailureTime] = useState(0);
  const resetTimeoutRef = useRef<ReturnType<typeof setTimeout> | null>(null);

  const operationRef = useRef(operation);
  const execute = useCallback(
    async (opOverride?: () => Promise<T>): Promise<T> => {
      if (opOverride) {
        operationRef.current = opOverride;
      }
      const now = Date.now();
      // Reset failures if monitoring period has passed
      if (now - lastFailureTime > internalConfig.monitoringPeriod) {
        setFailures(0);
      }
      // Check circuit state
      if (state === 'open') {
        if (now - lastFailureTime < internalConfig.resetTimeout) {
          throw {
            id: 'CIRCUIT_BREAKER_OPEN',
            message: 'Service temporarily unavailable',
            userMessage: 'The service is currently unavailable. Please try again later.',
            category: ErrorCategory.UNKNOWN,
            severity: ErrorSeverity.HIGH,
            context: { timestamp: Date.now(), platform: 'unknown' },
            isRecoverable: true,
            retryable: false,
            reportable: true,
          } as AppError;
        } else {
          setState('half-open');
        }
      }
      try {
        const result = await operationRef.current();
        if (state === 'half-open') {
          setState('closed');
          setFailures(0);
        }
        return result;
      } catch (error) {
        const newFailures = failures + 1;
        setFailures(newFailures);
        setLastFailureTime(now);
        if (newFailures >= internalConfig.failureThreshold) {
          setState('open');
          if (resetTimeoutRef.current) {
            clearTimeout(resetTimeoutRef.current);
          }
          resetTimeoutRef.current = setTimeout(() => {
            setState('half-open');
          }, internalConfig.resetTimeout);
        }
        throw error;
      }
    },
    [
      state,
      failures,
      lastFailureTime,
      internalConfig.failureThreshold,
      internalConfig.resetTimeout,
      internalConfig.monitoringPeriod,
    ],
  );

  const reset = useCallback(() => {
    setState('closed');
    setFailures(0);
    setLastFailureTime(0);
    if (resetTimeoutRef.current) {
      clearTimeout(resetTimeoutRef.current);
    }
  }, []);

  useEffect(() => {
    return () => {
      if (resetTimeoutRef.current) {
        clearTimeout(resetTimeoutRef.current);
      }
    };
  }, []);

  return {
    execute,
    reset,
    state,
    canExecute: state !== 'open',
    failureCount: failures, // legacy alias
    failures,
    isOpen: state === 'open',
    isHalfOpen: state === 'half-open',
  };
}

/**
 * App State Recovery Hook - handles app backgrounding/foregrounding
 */
export function useAppStateRecovery<T>(
  operationOrOptions?:
    | (() => Promise<T>)
    | {
        retryOnForeground?: boolean;
        resetOnBackground?: boolean;
        onForeground?: () => void;
        onBackground?: () => void;
      },
  maybeOptions?: {
    retryOnForeground?: boolean;
    resetOnBackground?: boolean;
    onForeground?: () => void;
    onBackground?: () => void;
  },
) {
  const isFunction = typeof operationOrOptions === 'function';
  const options = (isFunction ? maybeOptions : operationOrOptions) || {};
  const operation =
    (isFunction ? (operationOrOptions as () => Promise<T>) : undefined) ||
    (async () => Promise.resolve(null as T));
  const {
    retryOnForeground = true,
    resetOnBackground = false,
    onForeground,
    onBackground,
  } = options;
  const recovery = useErrorRecovery(operation);
  const appStateRef = useRef<AppStateStatus>(AppState.currentState);

  useEffect(() => {
    const handleAppStateChange = (nextAppState: AppStateStatus) => {
      if (appStateRef.current.match(/inactive|background/) && nextAppState === 'active') {
        onForeground?.();
        if (retryOnForeground && recovery.lastError && recovery.canRetry) {
          recovery.retry();
        }
      } else if (appStateRef.current === 'active' && nextAppState.match(/inactive|background/)) {
        onBackground?.();
        if (resetOnBackground) {
          recovery.reset();
        }
      }
      appStateRef.current = nextAppState;
    };
    const subscription = AppState.addEventListener('change', handleAppStateChange);
    return () => subscription?.remove();
  }, [recovery, retryOnForeground, resetOnBackground, onForeground, onBackground]);

  return recovery;
}

/**
 * Batch Recovery Hook - for handling multiple operations
 */
export function useBatchRecovery<T>(
  initialOperations?: (() => Promise<T>)[],
  config?: {
    failFast?: boolean;
    maxConcurrent?: number;
    retryConfig?: Partial<RetryConfig>;
  },
) {
  const { failFast = false, maxConcurrent = 3, retryConfig } = config || {};
  const [results, setResults] = useState<(T | null)[]>([]);
  const [errors, setErrors] = useState<(AppError | null)[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [progress, setProgress] = useState(0);
  const operationsRef = useRef<(() => Promise<T>)[]>(initialOperations || []);

  const run = useCallback(
    async (
      ops?: (() => Promise<T>)[],
      options?: { continueOnError?: boolean; retryFailures?: boolean; maxRetries?: number },
    ) => {
      const operations = ops || operationsRef.current;
      operationsRef.current = operations;
      setIsLoading(true);
      setResults(new Array(operations.length).fill(null));
      setErrors(new Array(operations.length).fill(null));
      setProgress(0);

      const { continueOnError = true, retryFailures = false, maxRetries = 0 } = options || {};

      const executeOperation = async (index: number) => {
        const op = operations[index];
        if (!op) {
          return;
        }
        let attempts = 0;
        const maxAttempts = maxRetries + 1; // total attempts = initial + retries

        while (attempts < maxAttempts) {
          try {
            const result = await op();
            setResults((prev) => {
              const next = [...prev];
              next[index] = result;
              return next;
            });
            setProgress((p) => p + 1);
            break;
          } catch (err) {
            attempts += 1;
            if (retryFailures && attempts < maxAttempts) {
              // simple fixed delay retry (tests advance by 1000ms)
              await new Promise((res) => setTimeout(res, 1000));
            } else {
              setErrors((prev) => {
                const next = [...prev];
                next[index] = err as AppError;
                return next;
              });
              if (failFast && !continueOnError) {
                throw err;
              }
              break;
            }
          }
        }
      };

      try {
        const chunks: Array<typeof operations> = [];
        for (let i = 0; i < operations.length; i += maxConcurrent) {
          chunks.push(operations.slice(i, i + maxConcurrent));
        }
        for (const chunk of chunks) {
          await Promise.all(
            chunk.map((_, chunkIndex) => {
              const globalIndex = chunks.indexOf(chunk) * maxConcurrent + chunkIndex;
              return executeOperation(globalIndex);
            }),
          );
        }
      } finally {
        setIsLoading(false);
      }
      // Return the final results after all operations complete
      return new Promise((resolve) => {
        setTimeout(() => {
          setResults((currentResults) => {
            resolve(currentResults);
            return currentResults;
          });
        }, 0);
      });
    },
    [failFast, maxConcurrent, retryConfig],
  );

  const executeBatch = run;
  const execute = run; // generic alias

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      // Component is unmounting, cancel any pending operations
      setIsLoading(false);
    };
  }, []);

  return {
    executeBatch,
    execute,
    results,
    errors,
    isLoading,
    progress,
    total: operationsRef.current.length,
    completed: progress,
    completedCount: progress,
    failedCount: errors.filter((e) => e).length,
    totalCount: operationsRef.current.length,
    isExecuting: isLoading,
    hasErrors: errors.some((e) => e !== null),
  };
}

export default {
  useErrorRecovery,
  useNetworkErrorRecovery,
  useAIServiceErrorRecovery,
  useImageErrorRecovery,
  useCircuitBreaker,
  useAppStateRecovery,
  useBatchRecovery,
};

// Haptic Hook - React hook for haptic feedback integration
import { useCallback, useContext, useEffect, useRef } from 'react';

import { AnimationContext } from '@/providers/AnimationProvider';
import hapticService, { HapticIntensity, HapticType } from '@/services/HapticService';

/**
 * Haptic hook options
 */
interface UseHapticOptions {
  enabled?: boolean;
  intensity?: number;
  respectReducedMotion?: boolean;
  throttleMs?: number;
}

/**
 * Haptic hook return type
 */
interface UseHapticReturn {
  // Basic haptic triggers (now synchronous since underlying service is sync)
  trigger: (type: HapticType, customIntensity?: number) => void;
  triggerSequence: (types: HapticType[], delay?: number) => void;

  // Convenience methods for common patterns
  gentleTap: () => void;
  softPulse: () => void;
  lightImpact: () => void;
  mediumImpact: () => void;
  selection: () => void;
  confirmation: () => void;
  success: () => void;
  error: () => void;
  luxuryTouch: () => void;

  // State and configuration
  isEnabled: boolean;
  isAvailable: boolean;
  setEnabled: (enabled: boolean) => void;
  setIntensity: (intensity: number) => void;
}

/**
 * Main haptic hook
 */
export const useHaptic = (options: UseHapticOptions = {}): UseHapticReturn => {
  const { enabled = true, intensity = 1.0, respectReducedMotion = true, throttleMs = 50 } = options;

  const animationContext = useContext(AnimationContext);
  const lastTriggerTime = useRef<number>(0);
  const isEnabledRef = useRef<boolean>(enabled);
  const intensityRef = useRef<number>(intensity);

  // Check if haptics should be disabled due to accessibility settings
  const shouldRespectReducedMotion = !!(
    respectReducedMotion && animationContext?.settings?.accessibility?.reduceMotion
  );

  /**
   * Check if haptic should be triggered based on throttling and settings
   */
  const shouldTrigger = useCallback((): boolean => {
    if (!isEnabledRef.current || !hapticService.isHapticAvailable()) {
      return false;
    }

    if (shouldRespectReducedMotion) {
      return false;
    }

    // Throttle haptic feedback
    const now = Date.now();
    if (now - lastTriggerTime.current < throttleMs) {
      return false;
    }

    lastTriggerTime.current = now;
    return true;
  }, [shouldRespectReducedMotion, throttleMs]);

  /**
   * Trigger haptic feedback
   */
  const trigger = useCallback(
    (type: HapticType, customIntensity?: number): void => {
      if (!shouldTrigger()) {
        return;
      }

      const effectiveIntensity = customIntensity ?? intensityRef.current;
      hapticService.trigger(type, effectiveIntensity);
    },
    [shouldTrigger],
  );

  /**
   * Trigger haptic sequence
   */
  const triggerSequence = useCallback(
    (types: HapticType[], delay: number = 100): void => {
      if (!shouldTrigger()) {
        return;
      }

      // Still async internally for sequencing but we intentionally don't expose a Promise
      void (async () => {
        await hapticService.triggerSequence(types, delay);
      })();
    },
    [shouldTrigger],
  );

  /**
   * Convenience methods for common haptic patterns
   */
  const gentleTap = useCallback(() => trigger(HapticType.GENTLE_TAP), [trigger]);
  const softPulse = useCallback(() => trigger(HapticType.SOFT_PULSE), [trigger]);
  const lightImpact = useCallback(() => trigger(HapticType.LIGHT_IMPACT), [trigger]);
  const mediumImpact = useCallback(() => trigger(HapticType.MEDIUM_IMPACT), [trigger]);
  const selection = useCallback(() => trigger(HapticType.SELECTION), [trigger]);
  const confirmation = useCallback(() => trigger(HapticType.CONFIRMATION), [trigger]);
  const success = useCallback(() => trigger(HapticType.SUCCESS), [trigger]);
  const error = useCallback(() => trigger(HapticType.ERROR), [trigger]);
  const luxuryTouch = useCallback(() => trigger(HapticType.LUXURY_TOUCH), [trigger]);

  /**
   * Set enabled state
   */
  const setEnabled = useCallback((newEnabled: boolean) => {
    isEnabledRef.current = newEnabled;
    hapticService.setEnabled(newEnabled);
  }, []);

  /**
   * Set intensity
   */
  const setIntensity = useCallback((newIntensity: number) => {
    intensityRef.current = Math.max(0, Math.min(1, newIntensity));
    hapticService.setGlobalIntensity(intensityRef.current);
  }, []);

  // Initialize haptic service settings
  useEffect(() => {
    hapticService.setEnabled(isEnabledRef.current);
    hapticService.setGlobalIntensity(intensityRef.current);
  }, []);

  // Update accessibility mode based on animation context
  useEffect(() => {
    if (animationContext?.settings?.accessibility) {
      hapticService.setAccessibilityMode(
        !!(
          animationContext.settings?.accessibility?.highContrast ||
          animationContext.settings?.accessibility?.reduceMotion
        ),
      );
    }
  }, [animationContext?.settings?.accessibility]);

  return {
    trigger,
    triggerSequence,
    gentleTap,
    softPulse,
    lightImpact,
    mediumImpact,
    selection,
    confirmation,
    success,
    error,
    luxuryTouch,
    isEnabled: isEnabledRef.current,
    isAvailable: hapticService.isHapticAvailable(),
    setEnabled,
    setIntensity,
  };
};

/**
 * Hook for button haptic feedback
 */
export const useButtonHaptic = (type: 'gentle' | 'standard' | 'luxury' = 'standard') => {
  const { trigger } = useHaptic();

  const onPress = useCallback(() => {
    switch (type) {
      case 'gentle':
        return trigger(HapticType.GENTLE_TAP);
      case 'luxury':
        return trigger(HapticType.LUXURY_TOUCH);
      default:
        return trigger(HapticType.LIGHT_IMPACT);
    }
  }, [trigger, type]);

  const onLongPress = useCallback(() => {
    return trigger(HapticType.MEDIUM_IMPACT);
  }, [trigger]);

  return { onPress, onLongPress };
};

