// Interface for UI callbacks that the hook will use
export interface UICallbacks {
  showLoading: (title: string, subtitle?: string) => void;
  hideLoading: () => void;
  showCustomAlert: (
    title: string,
    message: string,
    buttons: Array<{
      text: string;
      onPress?: () => void;
      style?: 'default' | 'cancel' | 'destructive' | 'primary';
      icon?: keyof typeof Ionicons.glyphMap;
    }>,
    type?: 'default' | 'success' | 'warning' | 'error' | 'info',
  ) => void;
  hideCustomAlert: () => void;
}

export const useImageUploader = (callbacks: UICallbacks) => {
  const { user } = useAuth();
  const { showLoading, hideLoading, showCustomAlert, hideCustomAlert } = callbacks;

  // Camera upload function
  const pickImageWithCameraAsync = async () => {
    try {
      // Request camera permissions
      const { status } = await ImagePicker.requestCameraPermissionsAsync();
      if (status !== 'granted') {
        showCustomAlert(
          'Camera Permission Required',
          'We need access to your camera to take photos of your wardrobe items. Please enable camera permissions in your device settings.',
          [{ text: 'OK', onPress: hideCustomAlert }],
          'warning',
        );
        return;
      }

      showLoading('Opening Camera', 'Get ready to capture your item');

      const result = await ImagePicker.launchCameraAsync({
        mediaTypes: ['images'],
        allowsEditing: true,
        aspect: IMAGE_CONFIG.ASPECT_RATIO,
        quality: IMAGE_CONFIG.QUALITY,
      });

      hideLoading();

      if (!result.canceled && result.assets && result.assets.length > 0) {
        const asset = result.assets[0];
        if (asset?.uri) {
          await uploadImage(asset.uri);
        }
      }
    } catch (error) {
      logger.error('Camera error', { error, context: 'useImageUploader.pickImageWithCameraAsync' });
      hideLoading();
      showCustomAlert(
        'Camera Error',
        'There was an issue accessing your camera. Please try again.',
        [{ text: 'OK', onPress: hideCustomAlert }],
        'error',
      );
    }
  };

  // Gallery upload function
  const pickImageWithGalleryAsync = async () => {
    try {
      // Request media library permissions
      const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
      if (status !== 'granted') {
        showCustomAlert(
          'Gallery Permission Required',
          'We need access to your photo gallery to select images of your wardrobe items. Please enable gallery permissions in your device settings.',
          [{ text: 'OK', onPress: hideCustomAlert }],
          'warning',
        );
        return;
      }

      showLoading('Opening Gallery', 'Select your wardrobe item photo');

      const result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ['images'],
        allowsEditing: true,
        aspect: IMAGE_CONFIG.ASPECT_RATIO,
        quality: IMAGE_CONFIG.QUALITY,
      });

      hideLoading();

      if (!result.canceled && result.assets && result.assets.length > 0) {
        const asset = result.assets[0];
        if (asset?.uri) {
          await uploadImage(asset.uri);
        }
      }
    } catch (error) {
      logger.error('Gallery error', {
        error,
        context: 'useImageUploader.pickImageWithGalleryAsync',
      });
      hideLoading();
      showCustomAlert(
        'Gallery Error',
        'There was an issue accessing your photo gallery. Please try again.',
        [{ text: 'OK', onPress: hideCustomAlert }],
        'error',
      );
    }
  };

  // Upload image to Supabase and analyze with AI
  const uploadImage = async (imageUri: string) => {
    try {
      if (!user) {
        showCustomAlert(
          'Authentication Required',
          'Please sign in to upload images to your wardrobe.',
          [{ text: 'OK', onPress: hideCustomAlert }],
          'warning',
        );
        return;
      }

      showLoading('Processing Image', 'Uploading and analyzing your item...');

      // Create a unique filename
      const filename = `wardrobe_${user.id}_${Date.now()}.jpg`;

      // Convert image to blob for upload
      const response = await fetch(imageUri);
      const blob = await response.blob();

      // Upload to Supabase Storage
      logger.info('Uploading image to Supabase', {
        bucket: API_CONFIG.SUPABASE_STORAGE_BUCKET,
        userId: user.id,
        context: 'useImageUploader.uploadImage',
      });
      const { data: uploadData, error: uploadError } = await ((supabase as any).storage
        .from(API_CONFIG.SUPABASE_STORAGE_BUCKET)
        .upload(filename, blob, {
          contentType: 'image/jpeg',
          upsert: false,
        }) as any);

      if (uploadError) {
        logger.error('Upload error', { uploadError, context: 'useImageUploader.uploadImage' });
        throw new Error(`Upload failed: ${uploadError.message}`);
      }

      logger.info('Image uploaded successfully', { path: uploadData?.path });

      // Get the public URL for the uploaded image
      const { data: urlData } = (supabase as any).storage
        .from(API_CONFIG.SUPABASE_STORAGE_BUCKET)
        .getPublicUrl(uploadData.path) as any;

      const imageUrl = urlData.publicUrl;
      logger.debug('Public URL generated', { imageUrl });

      // Analyze the image with AI
      await analyzeImageWithAI(imageUrl);
    } catch (error) {
      logger.error('Upload process error', { error, context: 'useImageUploader.uploadImage' });
      hideLoading();

      showCustomAlert(
        'Upload Failed',
        ERROR_MESSAGES.NETWORK_ERROR,
        [{ text: 'OK', onPress: hideCustomAlert }],
        'error',
      );
    }
  };

  // Helper function to ask specific questions to the VQA model
  const askQuestionToAI = async (imageUrl: string, question: string): Promise<string> => {
    // For cost-efficiency, prefer text reasoning via OpenRouter. For VQA (image+question),
    // use OpenAI vision via ai-proxy, sending the image URL and question.
    try {
      const { aiProxyChatCompletion } = await import('@/config/aiProxy');
      const completion = await aiProxyChatCompletion({
        provider: 'openai',
        model: 'gpt-4o',
        messages: [
          {
            role: 'user',
            content: [
              { type: 'text', text: `Answer concisely: ${question}` },
              { type: 'image_url', image_url: { url: imageUrl } },
            ],
          },
        ],
        max_tokens: 200,
        temperature: 0.1,
      });
      const content = completion?.choices?.[0]?.message?.content?.trim();
      return content || 'unknown';
    } catch (err) {
      logger.warn('VQA via ai-proxy failed, returning unknown', {
        error: err,
        context: 'useImageUploader.askQuestionToAI',
      });
      return 'unknown';
    }
  };

  // Analyze image with AI using Visual Question Answering
  const analyzeImageWithAI = async (imageUrl: string) => {
    try {
      logger.info('Starting AI analysis for image', { imageUrl });

      // Ask specific questions to get structured data
      const categoryQuestion = 'What is the category of this clothing item?';
      const colorQuestion = 'What are the main colors as comma separated list?';

      // Get answers from the VQA model
      const categoryAnswer = await askQuestionToAI(imageUrl, categoryQuestion);
      const colorAnswer = await askQuestionToAI(imageUrl, colorQuestion);

      logger.info('AI Analysis Results', {
        category: categoryAnswer,
        colors: colorAnswer,
      });

      // Process the colors into an array
      const colorsArray = colorAnswer
        .split(',')
        .map((color) => color.trim().toLowerCase())
        .filter((color) => color.length > 0 && color !== 'unknown');

      // Prepare the data for database insertion
      const wardrobeItem = {
        user_id: user?.id,
        image_url: imageUrl,
        category: categoryAnswer !== 'unknown' ? categoryAnswer : 'clothing',
        colors: colorsArray.length > 0 ? colorsArray : ['unspecified'],
        description: categoryAnswer !== 'unknown' ? categoryAnswer : 'clothing item',
        created_at: new Date().toISOString(),
      };

      logger.info('Saving wardrobe item', {
        userId: user?.id,
        category: wardrobeItem.category,
        colors: wardrobeItem.colors,
      });

      // Save to Supabase database
      const { error: dbError } = await (supabase as any)
        .from('wardrobe_items')
        .insert([wardrobeItem]);

      if (dbError) {
        logger.error('Database error saving wardrobe item', {
          dbError,
          context: 'useImageUploader.analyzeImageWithAI',
        });
        throw new Error(`Failed to save to database: ${dbError.message}`);
      }

      hideLoading();

      showCustomAlert(
        'Item Added Successfully! ✨',
        `Your ${wardrobeItem.category} has been analyzed and added to your wardrobe with colors: ${wardrobeItem.colors.join(', ')}.`,
        [{ text: 'Awesome!', onPress: hideCustomAlert }],
        'success',
      );
    } catch (error) {
      logger.error('AI analysis error', { error, context: 'useImageUploader.analyzeImageWithAI' });
      hideLoading();

      showCustomAlert(
        'AI Analysis Failed',
        "We couldn't analyze your image with AI right now. The image was uploaded but you may need to edit the details manually.",
        [{ text: 'OK', onPress: hideCustomAlert }],
        'warning',
      );
    }
  };

  // Return separate functions for camera and gallery uploads
  return {
    handleCameraUpload: pickImageWithCameraAsync,
    handleGalleryUpload: pickImageWithGalleryAsync,
  };
};

/**
 * useKVKK Hook - KVKK compliance ve rıza yönetimi için React hook
 *
 * Bu hook:
 * - KVKK rızalarını yönetir
 * - Compliance durumunu kontrol eder
 * - Veri haklarını sağlar
 * - Real-time güncellemeler sunar
 */

import { useCallback, useEffect, useState } from 'react';
import { Alert } from 'react-native';

import { errorInDev } from '@/utils/consoleSuppress';

import {
  ConsentType,
  DataProcessingPurpose,
  KVKKConsent,
  kvkkConsentService,
  KVKKSettings,
  LegalBasis,
} from '../services/kvkkConsentService';
import { useAuth } from './useAuth';

interface UseKVKKReturn {
  // Consent Management
  consents: KVKKConsent[];
  hasConsent: (type: ConsentType) => boolean;
  grantConsent: (
    type: ConsentType,
    purpose: DataProcessingPurpose,
    legalBasis: LegalBasis,
    validityDays?: number,
  ) => Promise<void>;
  withdrawConsent: (type: ConsentType) => Promise<void>;

  // Compliance Status
  isCompliant: boolean;
  complianceScore: number;
  missingConsents: ConsentType[];

  // Data Rights
  exportData: () => Promise<void>;
  deleteData: () => Promise<void>;

  // Settings
  settings: KVKKSettings | null;
  updateSettings: (newSettings: Partial<KVKKSettings>) => Promise<void>;

  // State
  loading: boolean;
  error: string | null;
  refreshConsents: () => Promise<void>;
}

interface KVKKComplianceCheck {
  isCompliant: boolean;
  score: number;
  missingConsents: ConsentType[];
  recommendations: string[];
}

// Gerekli rızalar ve ağırlıkları
const REQUIRED_CONSENTS: Record<ConsentType, { weight: number; required: boolean }> = {
  [ConsentType.AI_PROCESSING]: { weight: 30, required: true },
  [ConsentType.ANALYTICS]: { weight: 20, required: false },
  [ConsentType.CRASH_REPORTING]: { weight: 15, required: false },
  [ConsentType.NOTIFICATIONS]: { weight: 10, required: false },
  [ConsentType.LOCATION]: { weight: 15, required: false },
  [ConsentType.MARKETING]: { weight: 10, required: false },
  [ConsentType.PERSONALIZATION]: { weight: 15, required: false },
  [ConsentType.DATA_SHARING]: { weight: 5, required: false },
};

export const useKVKK = (): UseKVKKReturn => {
  const { user } = useAuth();
  const [consents, setConsents] = useState<KVKKConsent[]>([]);
  const [settings, setSettings] = useState<KVKKSettings | null>(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  // Rızaları yükle
  const loadConsents = useCallback(async () => {
    if (!user) {
      setConsents([]);
      setSettings(null);
      setLoading(false);
      return;
    }

    try {
      setLoading(true);
      setError(null);

      const [userConsents, userSettings] = await Promise.all([
        kvkkConsentService.getConsentHistory(user.id),
        kvkkConsentService.getKVKKSettings(user.id),
      ]);

      setConsents(userConsents);
      setSettings(userSettings);
    } catch (err) {
      errorInDev('KVKK verileri yüklenirken hata:', err);
      setError('KVKK verileri yüklenirken bir hata oluştu.');
    } finally {
      setLoading(false);
    }
  }, [user]);

  // İlk yükleme
  useEffect(() => {
    loadConsents();
  }, [loadConsents]);

  // Rıza kontrolü
  const hasConsent = useCallback(
    (type: ConsentType): boolean => {
      if (!user) return false;
      return kvkkConsentService.hasValidConsent(user.id, type);
    },
    [user, consents],
  );

  // Rıza verme
  const grantConsent = useCallback(
    async (
      type: ConsentType,
      purpose: DataProcessingPurpose,
      legalBasis: LegalBasis,
      validityDays: number = 365,
    ) => {
      if (!user) throw new Error('Kullanıcı oturumu bulunamadı');

      try {
        setError(null);
        await kvkkConsentService.grantConsent(user.id, type, purpose, legalBasis, validityDays);
        await loadConsents(); // Güncel verileri yükle
      } catch (err) {
        errorInDev('Rıza verme hatası:', err);
        setError('Rıza verilirken bir hata oluştu.');
        throw err;
      }
    },
    [user, loadConsents],
  );

  // Rıza geri çekme
  const withdrawConsent = useCallback(
    async (type: ConsentType) => {
      if (!user) throw new Error('Kullanıcı oturumu bulunamadı');

      try {
        setError(null);
        await kvkkConsentService.withdrawConsent(user.id, type);
        await loadConsents(); // Güncel verileri yükle
      } catch (err) {
        errorInDev('Rıza geri çekme hatası:', err);
        setError('Rıza geri çekilirken bir hata oluştu.');
        throw err;
      }
    },
    [user, loadConsents],
  );

  // Compliance durumu hesaplama
  const calculateCompliance = useCallback((): KVKKComplianceCheck => {
    if (!user) {
      return {
        isCompliant: false,
        score: 0,
        missingConsents: Object.keys(REQUIRED_CONSENTS) as ConsentType[],
        recommendations: ['Lütfen giriş yapın'],
      };
    }

    let totalScore = 0;
    let maxScore = 0;
    const missingConsents: ConsentType[] = [];
    const recommendations: string[] = [];

    for (const [consentType, config] of Object.entries(REQUIRED_CONSENTS)) {
      const type = consentType as ConsentType;
      maxScore += config.weight;

      if (hasConsent(type)) {
        totalScore += config.weight;
      } else {
        if (config.required) {
          missingConsents.push(type);
          recommendations.push(`${type} rızası gereklidir`);
        }
      }
    }

    const score = Math.round((totalScore / maxScore) * 100);
    const isCompliant = missingConsents.length === 0 && score >= 70;

    if (!isCompliant) {
      if (missingConsents.length > 0) {
        recommendations.push('Eksik zorunlu rızaları tamamlayın');
      }
      if (score < 70) {
        recommendations.push('Daha iyi deneyim için ek rızaları değerlendirin');
      }
    }

    return {
      isCompliant,
      score,
      missingConsents,
      recommendations,
    };
  }, [user, hasConsent]);

  const complianceCheck = calculateCompliance();

  // Veri dışa aktarma
  const exportData = useCallback(async () => {
    if (!user) throw new Error('Kullanıcı oturumu bulunamadı');

    try {
      setError(null);
      await kvkkConsentService.exportUserData(user.id);

      Alert.alert(
        'Veri Dışa Aktarma',
        'Verileriniz işleniyor. E-posta adresinize gönderilecektir.',
        [{ text: 'Tamam' }],
      );
    } catch (err) {
      errorInDev('Veri dışa aktarma hatası:', err);
      setError('Veri dışa aktarılırken bir hata oluştu.');
      throw err;
    }
  }, [user]);

  // Veri silme
  const deleteData = useCallback(async () => {
    if (!user) throw new Error('Kullanıcı oturumu bulunamadı');

    return new Promise<void>((resolve, reject) => {
      Alert.alert(
        'Veri Silme Onayı',
        'Bu işlem geri alınamaz! Tüm verileriniz kalıcı olarak silinecektir.',
        [
          {
            text: 'İptal',
            style: 'cancel',
            onPress: () => reject(new Error('Kullanıcı iptal etti')),
          },
          {
            text: 'Sil',
            style: 'destructive',
            onPress: async () => {
              try {
                setError(null);
                await kvkkConsentService.deleteUserData(user.id);
                resolve();
              } catch (err) {
                errorInDev('Veri silme hatası:', err);
                setError('Veri silinirken bir hata oluştu.');
                reject(err);
              }
            },
          },
        ],
      );
    });
  }, [user]);

  // Ayarları güncelleme
  const updateSettings = useCallback(
    async (newSettings: Partial<KVKKSettings>) => {
      if (!user) throw new Error('Kullanıcı oturumu bulunamadı');

      try {
        setError(null);
        const updatedSettings = { ...settings, ...newSettings } as KVKKSettings;
        await kvkkConsentService.updateKVKKSettings(user.id, updatedSettings);
        setSettings(updatedSettings);
      } catch (err) {
        errorInDev('KVKK ayarları güncelleme hatası:', err);
        setError('Ayarlar güncellenirken bir hata oluştu.');
        throw err;
      }
    },
    [user, settings],
  );

  // Rızaları yenileme
  const refreshConsents = useCallback(async () => {
    await loadConsents();
  }, [loadConsents]);

  return {
    // Consent Management
    consents,
    hasConsent,
    grantConsent,
    withdrawConsent,

    // Compliance Status
    isCompliant: complianceCheck.isCompliant,
    complianceScore: complianceCheck.score,
    missingConsents: complianceCheck.missingConsents,

    // Data Rights
    exportData,
    deleteData,

    // Settings
    settings,
    updateSettings,

    // State
    loading,
    error,
    refreshConsents,
  };
};

export default useKVKK;

// Loading Hook - React hook for managing loading states
import { useCallback, useState } from 'react';

export interface UseLoadingReturn {
  isLoading: boolean;
  showLoading: (title?: string, subtitle?: string) => void;
  hideLoading: () => void;
}

export function useLoading(): UseLoadingReturn {
  const [isLoading, setIsLoading] = useState(false);
  const [loadingTitle, setLoadingTitle] = useState<string>('Loading...');
  const [loadingSubtitle, setLoadingSubtitle] = useState<string | undefined>();

  const showLoading = useCallback((title?: string, subtitle?: string) => {
    setLoadingTitle(title || 'Loading...');
    setLoadingSubtitle(subtitle);
    setIsLoading(true);
  }, []);

  const hideLoading = useCallback(() => {
    setIsLoading(false);
    setLoadingTitle('Loading...');
    setLoadingSubtitle(undefined);
  }, []);

  return {
    isLoading,
    showLoading,
    hideLoading,
  };
}

export default useLoading;

/**
 * Performance Monitoring Hook
 * Real-time performance tracking for React Native components and app startup
 */
import { useCallback, useEffect, useRef, useState } from 'react';
import { InteractionManager } from 'react-native';

import { StartupMetrics, startupPerformanceService } from '@/services/startupPerformanceService';
import { errorInDev } from '@/utils/consoleSuppress';

interface PerformanceMetrics {
  renderTime: number;
  interactionTime: number;
  memoryUsage?: number;
  frameDrops: number;
  componentMountTime: number;
}

interface PerformanceConfig {
  trackRenderTime?: boolean;
  trackInteractions?: boolean;
  trackMemory?: boolean;
  trackFrameDrops?: boolean;
  sampleRate?: number; // 0-1, percentage of renders to track
  onMetricsUpdate?: (metrics: PerformanceMetrics) => void;
}

interface UsePerformanceMonitorReturn {
  metrics: PerformanceMetrics;
  startMeasurement: (label: string) => void;
  endMeasurement: (label: string) => number;
  trackInteraction: (interactionName: string, callback: () => void) => void;
  getPerformanceReport: () => PerformanceReport;
  // Startup performance monitoring
  startupMetrics: StartupMetrics | null;
  startupSummary: {
    averageStartupTime: number;
    bestStartupTime: number;
    worstStartupTime: number;
    trendDirection: 'improving' | 'degrading' | 'stable';
    meetsTarget: boolean;
  } | null;
  refreshStartupMetrics: () => Promise<void>;
  formatTime: (milliseconds: number) => string;
  getStartupStatus: () => 'excellent' | 'good' | 'warning' | 'critical';
  getOptimizationTips: () => string[];
}

interface PerformanceReport {
  averageRenderTime: number;
  averageInteractionTime: number;
  totalFrameDrops: number;
  peakMemoryUsage: number;
  slowestOperations: Array<{ name: string; duration: number }>;
  recommendations: string[];
}

const DEFAULT_CONFIG: Required<PerformanceConfig> = {
  trackRenderTime: true,
  trackInteractions: true,
  trackMemory: false, // Expensive operation, disabled by default
  trackFrameDrops: true,
  sampleRate: 0.1, // Track 10% of renders by default
  onMetricsUpdate: () => {},
};

// Global performance store
class PerformanceStore {
  private measurements = new Map<string, number>();
  private renderTimes: number[] = [];
  private interactionTimes: number[] = [];
  private frameDropCount = 0;
  private memoryPeaks: number[] = [];
  private slowOperations: Array<{ name: string; duration: number }> = [];

  startMeasurement(label: string): void {
    this.measurements.set(label, performance.now());
  }

  endMeasurement(label: string): number {
    const startTime = this.measurements.get(label);
    if (!startTime) return 0;

    const duration = performance.now() - startTime;
    this.measurements.delete(label);

    // Track slow operations (>100ms)
    if (duration > 100) {
      this.slowOperations.push({ name: label, duration });
      // Keep only the 10 slowest operations
      this.slowOperations.sort((a, b) => b.duration - a.duration);
      this.slowOperations = this.slowOperations.slice(0, 10);
    }

    return duration;
  }

  addRenderTime(time: number): void {
    this.renderTimes.push(time);
    // Keep only last 100 render times
    if (this.renderTimes.length > 100) {
      this.renderTimes.shift();
    }
  }

  addInteractionTime(time: number): void {
    this.interactionTimes.push(time);
    // Keep only last 50 interaction times
    if (this.interactionTimes.length > 50) {
      this.interactionTimes.shift();
    }
  }

  incrementFrameDrops(): void {
    this.frameDropCount++;
  }

  addMemoryUsage(usage: number): void {
    this.memoryPeaks.push(usage);
    // Keep only last 20 memory readings
    if (this.memoryPeaks.length > 20) {
      this.memoryPeaks.shift();
    }
  }

  getReport(): PerformanceReport {
    const avgRenderTime =
      this.renderTimes.length > 0
        ? this.renderTimes.reduce((a, b) => a + b, 0) / this.renderTimes.length
        : 0;

    const avgInteractionTime =
      this.interactionTimes.length > 0
        ? this.interactionTimes.reduce((a, b) => a + b, 0) / this.interactionTimes.length
        : 0;

    const peakMemory = this.memoryPeaks.length > 0 ? Math.max(...this.memoryPeaks) : 0;

    const recommendations: string[] = [];

    if (avgRenderTime > 16.67) {
      // 60fps threshold
      recommendations.push(
        'Average render time exceeds 16.67ms - consider optimizing render logic',
      );
    }

    if (avgInteractionTime > 100) {
      recommendations.push(
        'Slow interaction response time - consider debouncing or async processing',
      );
    }

    if (this.frameDropCount > 10) {
      recommendations.push('High frame drop count - review animations and heavy computations');
    }

    if (this.slowOperations.length > 5) {
      recommendations.push(
        'Multiple slow operations detected - consider code splitting or lazy loading',
      );
    }

    return {
      averageRenderTime: avgRenderTime,
      averageInteractionTime: avgInteractionTime,
      totalFrameDrops: this.frameDropCount,
      peakMemoryUsage: peakMemory,
      slowestOperations: [...this.slowOperations],
      recommendations,
    };
  }

  reset(): void {
    this.measurements.clear();
    this.renderTimes = [];
    this.interactionTimes = [];
    this.frameDropCount = 0;
    this.memoryPeaks = [];
    this.slowOperations = [];
  }
}

const globalPerformanceStore = new PerformanceStore();

/**
 * Hook for monitoring component and interaction performance
 */
export function usePerformanceMonitor(
  componentName: string,
  config: PerformanceConfig = {},
): UsePerformanceMonitorReturn {
  const mergedConfig = { ...DEFAULT_CONFIG, ...config };
  const mountTimeRef = useRef<number>(performance.now());
  const renderCountRef = useRef<number>(0);
  const lastFrameTimeRef = useRef<number>(performance.now());

  const [metrics, setMetrics] = useState<PerformanceMetrics>({
    renderTime: 0,
    interactionTime: 0,
    frameDrops: 0,
    componentMountTime: 0,
  });

  // Startup performance state
  const [startupMetrics, setStartupMetrics] = useState<StartupMetrics | null>(null);
  const [startupSummary, setStartupSummary] = useState<{
    averageStartupTime: number;
    bestStartupTime: number;
    worstStartupTime: number;
    trendDirection: 'improving' | 'degrading' | 'stable';
    meetsTarget: boolean;
  } | null>(null);

  // Track component mount time
  useEffect(() => {
    const mountTime = performance.now() - mountTimeRef.current;
    setMetrics((prev) => ({ ...prev, componentMountTime: mountTime }));

    // Component mount time tracked
  }, [componentName]);

  // Track render performance
  useEffect(() => {
    if (!mergedConfig.trackRenderTime) return;

    renderCountRef.current++;

    // Sample renders based on sampleRate
    if (Math.random() > mergedConfig.sampleRate) return;

    const renderStart = performance.now();

    // Use requestAnimationFrame to measure actual render time
    const frameId = requestAnimationFrame(() => {
      const renderTime = performance.now() - renderStart;
      globalPerformanceStore.addRenderTime(renderTime);

      setMetrics((prev) => {
        const newMetrics = { ...prev, renderTime };
        mergedConfig.onMetricsUpdate(newMetrics);
        return newMetrics;
      });

      // Track frame drops
      if (mergedConfig.trackFrameDrops) {
        const currentTime = performance.now();
        const timeSinceLastFrame = currentTime - lastFrameTimeRef.current;

        // If more than 16.67ms (60fps) + 5ms tolerance
        if (timeSinceLastFrame > 21.67) {
          globalPerformanceStore.incrementFrameDrops();
          setMetrics((prev) => ({ ...prev, frameDrops: prev.frameDrops + 1 }));
        }

        lastFrameTimeRef.current = currentTime;
      }
    });

    return () => cancelAnimationFrame(frameId);
  });

  // Track memory usage (if enabled)
  useEffect(() => {
    if (!mergedConfig.trackMemory) return;

    const interval = setInterval(() => {
      // @ts-ignore - performance.memory is available in some environments
      if (typeof performance !== 'undefined' && performance.memory) {
        // @ts-ignore
        const memoryUsage = performance.memory.usedJSHeapSize;
        globalPerformanceStore.addMemoryUsage(memoryUsage);
        setMetrics((prev) => ({ ...prev, memoryUsage }));
      }
    }, 5000); // Check every 5 seconds

    return () => clearInterval(interval);
  }, [mergedConfig.trackMemory]);

  const startMeasurement = useCallback(
    (label: string) => {
      globalPerformanceStore.startMeasurement(`${componentName}:${label}`);
    },
    [componentName],
  );

  const endMeasurement = useCallback(
    (label: string): number => {
      return globalPerformanceStore.endMeasurement(`${componentName}:${label}`);
    },
    [componentName],
  );

  const trackInteraction = useCallback(
    (interactionName: string, callback: () => void) => {
      if (!mergedConfig.trackInteractions) {
        callback();
        return;
      }

      const interactionStart = performance.now();

      const wrappedCallback = () => {
        callback();

        InteractionManager.runAfterInteractions(() => {
          const interactionTime = performance.now() - interactionStart;
          globalPerformanceStore.addInteractionTime(interactionTime);

          setMetrics((prev) => {
            const newMetrics = { ...prev, interactionTime };
            mergedConfig.onMetricsUpdate(newMetrics);
            return newMetrics;
          });

          // Interaction time tracked
        });
      };

      wrappedCallback();
    },
    [componentName, mergedConfig],
  );

  const getPerformanceReport = useCallback((): PerformanceReport => {
    return globalPerformanceStore.getReport();
  }, []);

  // Startup performance monitoring functions
  const loadStartupMetrics = useCallback(async () => {
    try {
      const currentMetrics = startupPerformanceService.getCurrentMetrics();
      const summary = await startupPerformanceService.getPerformanceSummary(10);

      setStartupMetrics(currentMetrics);
      setStartupSummary(summary);
    } catch (error) {
      errorInDev('Failed to load startup metrics:', error);
    }
  }, []);

  const refreshStartupMetrics = useCallback(async () => {
    await loadStartupMetrics();
  }, [loadStartupMetrics]);

  const formatTime = useCallback((milliseconds: number): string => {
    if (milliseconds < 1000) {
      return `${milliseconds}ms`;
    }
    return `${(milliseconds / 1000).toFixed(2)}s`;
  }, []);

  const getStartupStatus = useCallback((): 'excellent' | 'good' | 'warning' | 'critical' => {
    if (!startupSummary) {
      return 'warning';
    }

    const { averageStartupTime } = startupSummary;

    if (averageStartupTime <= 1500) {
      return 'excellent';
    } else if (averageStartupTime <= 2000) {
      return 'good';
    } else if (averageStartupTime <= 2500) {
      return 'warning';
    } else {
      return 'critical';
    }
  }, [startupSummary]);

  const getOptimizationTips = useCallback((): string[] => {
    const tips: string[] = [];

    if (!startupMetrics || !startupSummary) {
      return ['Enable performance monitoring to get optimization tips'];
    }

    const status = getStartupStatus();

    switch (status) {
      case 'critical':
        tips.push('🚨 Critical: Startup time exceeds 2.5s target');
        tips.push('Consider implementing more aggressive lazy loading');
        tips.push('Review and optimize heavy synchronous operations');
        break;

      case 'warning':
        tips.push('⚠️ Warning: Approaching 2.5s startup time limit');
        tips.push('Monitor performance trends closely');
        break;

      case 'good':
        tips.push('✅ Good: Performance within acceptable range');
        tips.push('Continue monitoring for regressions');
        break;

      case 'excellent':
        tips.push('🎉 Excellent: Outstanding startup performance!');
        tips.push('Consider sharing optimization techniques with team');
        break;
    }

    if (startupMetrics.criticalFontsLoadTime > 500) {
      tips.push('📝 Font loading is slow - consider reducing font variants');
    }

    if (startupMetrics.bundleLoadTime > 1000) {
      tips.push('📦 Bundle size is large - implement more code splitting');
    }

    if (startupMetrics.jsExecutionTime > 800) {
      tips.push('⚡ JS execution is slow - optimize synchronous operations');
    }

    if (startupMetrics.firstScreenRenderTime > 1500) {
      tips.push('🎨 First render is slow - simplify initial screen complexity');
    }

    if (startupSummary.trendDirection === 'degrading') {
      tips.push('📉 Performance is degrading - investigate recent changes');
    } else if (startupSummary.trendDirection === 'improving') {
      tips.push('📈 Performance is improving - great work!');
    }

    return tips;
  }, [startupMetrics, startupSummary, getStartupStatus]);

  // Load startup metrics on mount
  useEffect(() => {
    loadStartupMetrics();
  }, [loadStartupMetrics]);

  return {
    metrics,
    startMeasurement,
    endMeasurement,
    trackInteraction,
    getPerformanceReport,
    // Startup performance monitoring
    startupMetrics,
    startupSummary,
    refreshStartupMetrics,
    formatTime,
    getStartupStatus,
    getOptimizationTips,
  };
}

/**
 * Global performance utilities
 */
export const PerformanceMonitor = {
