}

function TabPanel(props: TabPanelProps) {
  const { children, value, index, ...other } = props;

  return (
    <View style={{ display: value !== index ? 'none' : 'flex' }} {...other}>
      {value === index && <View style={{ padding: DesignSystem.spacing.lg }}>{children}</View>}
    </View>
  );
}

