        })
        .select()
        .single();

      if (error) {
        throw error;
      }

      const typedData = data as {
        user_id: string;
        naming_style: string;
        include_brand: boolean;
        include_color: boolean;
        include_material: boolean;
        include_style: boolean;
        preferred_language: string;
        auto_accept_ai_names: boolean;
        created_at: string;
        updated_at: string;
      };

      return {
        userId: typedData.user_id,
        namingStyle: typedData.naming_style as NamingStyle,
        includeBrand: typedData.include_brand,
        includeColor: typedData.include_color,
        includeMaterial: typedData.include_material,
        includeStyle: typedData.include_style,
        preferredLanguage: typedData.preferred_language,
        autoAcceptAINames: typedData.auto_accept_ai_names,
        createdAt: new Date(typedData.created_at),
        updatedAt: new Date(typedData.updated_at),
      };
    } catch (error) {
      errorInDev(
        '[AINameingService] Error updating preferences:',
        error instanceof Error ? error : String(error),
      );
      throw error;
    }
  }

  /**
   * Save naming history
   */
  static async saveNamingHistory(
    itemId: string,
    userId: string,
    aiGeneratedName: string,
    userProvidedName?: string,
    analysisData?: AIAnalysisData,
  ): Promise<void> {
    try {
      const histRes = await wrap(
        async () =>
          await supabase
            .from('item_naming_history')
            .insert({
              item_id: itemId,
              user_id: userId,
              ai_generated_name: aiGeneratedName,
              user_provided_name: userProvidedName,
              naming_confidence: analysisData?.confidence || 0.5,
              ai_tags: analysisData?.detectedTags || [],
              visual_features: analysisData?.visualFeatures || {},
            })
            .select('*')
            .single(),
      );
      if (!isSupabaseOk(histRes)) {
        errorInDev('[AINameingService] Failed to save naming history', histRes.error);
      }
    } catch (error) {
      errorInDev(
        '[AINameingService] Error saving naming history:',
        error instanceof Error ? error : String(error),
      );
    }
  }

  /**
   * Get effective item name (user name or AI name)
   */
  static getEffectiveItemName(
    name?: string,
    aiGeneratedName?: string,
    category?: string,
    colors?: string[],
  ): string {
    if (name && name.trim()) {
      return name;
    }

    if (aiGeneratedName && aiGeneratedName.trim()) {
      return aiGeneratedName;
    }

    // Fallback
    if (colors && colors.length > 0 && typeof colors[0] === 'string' && category) {
      return `${this.capitalizeFirst(colors[0])} ${this.capitalizeFirst(category)}`;
    }

    return category ? this.capitalizeFirst(category) : 'Item';
  }

  /**
   * Utility: Capitalize first letter
   */
  private static capitalizeFirst(str: string): string {
    return str.charAt(0).toUpperCase() + str.slice(1).toLowerCase();
  }
}

import { supabase } from '@/config/supabaseClient';
import type { WardrobeItem } from '@/services/wardrobeService';
import * as WardrobeModule from '@/services/wardrobeService';
import { errorInDev, warnInDev } from '@/utils/consoleSuppress';
import { isSupabaseOk, wrap } from '@/utils/supabaseResult';

export interface ShopYourClosetRecommendation {
