  costPerWear: number;
  totalWears: number;
  purchasePrice: number;
  daysSincePurchase: number;
  projectedCostPerWear: number;
}

export interface RediscoveryChallenge {
  id: string;
  userId: string;
  challengeType: 'neglected_items' | 'color_exploration' | 'style_mixing';
  title: string;
  description: string;
  targetItems: WardrobeItem[];
  progress: number;
  totalItems: number;
  reward: string;
  expiresAt: Date;
  completedAt?: Date;
  createdAt: Date;
}

