/**
 * Deep Link parsing & generation service
 * Public named export: parseDeepLink(url)
 * Default export: { parse, isValid, toURL, processDeepLinkParams }
 *
 * Rules:
 * - Scheme must be aynamoda:// (case-insensitive)
 * - Allowed hosts: item, home, settings, promo
 * - item requires id param
 * - Fallback: { name: 'Home' } on any invalid condition
 * - All params URL-decoded
 */
import { logger } from '@/utils/logger';

export interface DeepLinkResult {
  name: string;
  params?: Record<string, string | number | boolean | null | undefined>;
  /** Internal validity marker (not part of public API docs). */
  __valid?: true;
}

type AllowedHost = 'item' | 'home' | 'settings' | 'promo';

interface HostRouteConfig {
  routeName: string;
  requires?: Array<{ key: string }>;
}

const SCHEME = 'aynamoda://';

const HOST_ROUTE_MAP: Record<AllowedHost, HostRouteConfig> = {
  item: { routeName: 'Item', requires: [{ key: 'id' }] },
  home: { routeName: 'Home' },
  settings: { routeName: 'Settings' },
  promo: { routeName: 'Promo' },
};

// Fallback singleton (no __valid flag) distinguished from successful parses.
const FALLBACK: DeepLinkResult = { name: 'Home' };

/**
 * Parse query string into a param record with URL decoding.
 */
function parseQueryParams(query: string): Record<string, string> {
  if (!query) {
    return {};
  }
  return query
    .replace(/^\?/, '')
    .split('&')
    .filter(Boolean)
    .reduce<Record<string, string>>((acc, pair) => {
      const [rawK, rawV = ''] = pair.split('=');
      try {
        if (rawK === undefined) {
          return acc;
        } // safety guard
        const k = decodeURIComponent(rawK);
        const v = decodeURIComponent(rawV);
        acc[k] = v;
      } catch {
        // Skip malformed components silently
      }
      return acc;
    }, {});
}

/**
 * Internal core parser (no logging).
 */
function coreParse(url: string): DeepLinkResult {
  if (typeof url !== 'string' || !url) {
    return FALLBACK;
  }

  const lower = url.toLowerCase();
  if (!lower.startsWith(SCHEME)) {
    return FALLBACK;
  }

  const withoutScheme = url.slice(SCHEME.length);

  const firstSepIdx = withoutScheme.search(/[/?]/);
  const hostRaw = firstSepIdx === -1 ? withoutScheme : withoutScheme.slice(0, firstSepIdx);
  const host = hostRaw.toLowerCase() as AllowedHost;

  if (!Object.prototype.hasOwnProperty.call(HOST_ROUTE_MAP, host)) {
    return FALLBACK;
  }

  let query = '';
  const qIndex = withoutScheme.indexOf('?');
  if (qIndex >= 0) {
    query = withoutScheme.slice(qIndex);
  }

  const params = parseQueryParams(query);
  const cfg = HOST_ROUTE_MAP[host];

  if (cfg.requires) {
    for (const req of cfg.requires) {
      if (!params[req.key]) {
        return FALLBACK;
      }
    }
  }

  const base: DeepLinkResult = Object.keys(params).length
    ? { name: cfg.routeName, params }
    : { name: cfg.routeName };
  base.__valid = true; // explicit marker for validity checks
  return base;
}

/**
 * Public API: Parse a deep link URL string into a route + params.
 * Fallbacks to { name: 'Home' } when invalid.
 */
export function parseDeepLink(url: string): DeepLinkResult {
  const result = coreParse(url);
  if (result === FALLBACK) {
    logger?.warn?.('deep_link_invalid', {
      url,
      reason: 'invalid_scheme_host_or_params',
    });
  } else {
    logger?.info?.('deep_link_parsed', {
      url,
      name: result.name,
      hasParams: Boolean(result.params && Object.keys(result.params).length),
    });
  }
  return result;
}

/**
 * Validate a deep link without returning parsed params.
 * Valid only if parsing did not yield the shared FALLBACK reference.
 */
function isValid(url: string): boolean {
  const parsed = coreParse(url);
  return parsed.__valid === true; // explicit flag instead of reference identity
}

/**
 * Generate a deep link URL for a given route name and optional params.
 * Unknown names fallback to home.
 */
function toURL(
  name: string,
  params?: Record<string, string | number | boolean | null | undefined>,
): string {
  const entry = (Object.entries(HOST_ROUTE_MAP) as Array<[AllowedHost, HostRouteConfig]>).find(
    ([, cfg]) => cfg.routeName === name,
  );
  const host: AllowedHost = entry ? entry[0] : 'home';

  let query = '';
  if (params && Object.keys(params).length) {
    const qp = Object.entries(params)
      .filter(([, v]) => v !== undefined)
      .map(
        ([k, v]) => `${encodeURIComponent(k)}=${encodeURIComponent(v === null ? '' : String(v))}`,
      )
      .join('&');
    if (qp) {
      query = `?${qp}`;
    }
  }
  return `${SCHEME}${host}${query}`;
}

/* -------------------------------------------------------------------------- */
/* Backward-compatible deep link params adapter                               */
/* -------------------------------------------------------------------------- */

export type DeepLinkInput = {
  feedback?: string;
  outfit?: string;
  item?: string;
  [key: string]: unknown;
};

export type DeepLinkNormalized = {
  feedback?: string;
  outfitId?: string;
  itemId?: string;
  extras?: Record<string, unknown>;
};

/**
 * Normalize legacy / raw deep link param objects to consistent shape.
 * - Trims & URL-decodes string fields
 * - Renames outfit -> outfitId, item -> itemId
 * - Collects unknown keys into extras
 * - Returns {} if nothing useful
 */
export function processDeepLinkParams(input?: DeepLinkInput | null): DeepLinkNormalized {
  if (!input || typeof input !== 'object') {
    return {};
  }

  const out: DeepLinkNormalized = {};
  const extras: Record<string, unknown> = {};

  const decodeValue = (val: string): string => {
    const trimmed = val.trim();
    if (!trimmed) {
      return '';
    }
    try {
      return decodeURIComponent(trimmed);
    } catch {
      return trimmed;
    }
  };

  if (typeof input.feedback === 'string') {
    const v = decodeValue(input.feedback);
    if (v) {
      out.feedback = v;
    }
  }
  if (typeof input.outfit === 'string') {
    const v = decodeValue(input.outfit);
    if (v) {
      out.outfitId = v;
    }
  }
  if (typeof input.item === 'string') {
    const v = decodeValue(input.item);
    if (v) {
      out.itemId = v;
    }
  }

  for (const [k, v] of Object.entries(input)) {
    if (k === 'feedback' || k === 'outfit' || k === 'item') {
      continue;
    }
    if (v !== undefined) {
      extras[k] = v;
    }
  }

  if (Object.keys(extras).length) {
    out.extras = extras;
  }

  const hasUseful =
    !!out.feedback ||
    !!out.outfitId ||
    !!out.itemId ||
    (out.extras ? Object.keys(out.extras).length > 0 : false);

  logger?.info?.('deep_link_params_processed', {
    hasFeedback: !!out.feedback,
    hasOutfitId: !!out.outfitId,
    hasItemId: !!out.itemId,
  });

  return hasUseful ? out : {};
}

// Default export (stable public API)
const deepLinkAPI = {
  parse: parseDeepLink,
  isValid,
  toURL,
  processDeepLinkParams,
};

export default deepLinkAPI;

/**
 * Design System Validation Service
 * Validates all aspects of the AYNAMODA design system for consistency,
 * accessibility, performance, and adherence to design specifications
 */

import { Dimensions, PixelRatio } from 'react-native';

import {
  BORDER_RADIUS,
  ELEVATION,
  GLASSMORPHISM,
  SPACING,
  TYPOGRAPHY,
  UNIFIED_COLORS,
} from '@/theme/DesignSystem';

import { errorInDev, warnInDev } from '../utils/consoleSuppress';

interface ValidationResult {
  isValid: boolean;
  score: number; // 0-100
  issues: ValidationIssue[];
  recommendations: string[];
}

interface ValidationIssue {
  severity: 'error' | 'warning' | 'info';
  category: string;
  message: string;
  location?: string;
  fix?: string;
}

interface ColorContrastResult {
  ratio: number;
  wcagAA: boolean;
  wcagAAA: boolean;
}

interface PerformanceMetrics {
  themeLoadTime: number;
  animationFrameRate: number;
  memoryUsage: number;
  bundleSize: number;
}

interface ComponentValidationResult {
  component: string;
  variants: string[];
  accessibility: {
    touchTargetSize: boolean;
    colorContrast: boolean;
    semanticLabels: boolean;
  };
  performance: {
    renderTime: number;
    memoryFootprint: number;
  };
}

class DesignSystemValidationService {
  private validationResults: Map<string, ValidationResult> = new Map();
  private performanceMetrics: PerformanceMetrics | null = null;
  private validationInterval: NodeJS.Timeout | null = null; // OPERASYON DİSİPLİN: Memory leak önleme

  /**
   * Run comprehensive design system validation
   */
  validateDesignSystem(): ValidationResult {
    const results: ValidationResult[] = [];

    // Core foundation validations
    results.push(this.validateColors());
    results.push(this.validateTypography());
    results.push(this.validateSpacing());
    results.push(this.validateElevation());
    results.push(this.validateBorderRadius());

    // Component validations
    results.push(this.validateComponents());

    // Accessibility validations
    results.push(this.validateAccessibility());

    // Performance validations
    results.push(this.validatePerformance());

    // Layout system validations
    results.push(this.validateLayoutSystems());

    return this.aggregateResults(results);
  }

  /**
   * Validate color system consistency and accessibility
   */
  private validateColors(): ValidationResult {
    const issues: ValidationIssue[] = [];
    const recommendations: string[] = [];
    let score = 100;

    // Check color contrast ratios
    const contrastResults = this.checkColorContrasts();
    contrastResults.forEach((result) => {
      if (!result.wcagAA) {
        issues.push({
          severity: 'error',
          category: 'Accessibility',
          message: `Color contrast ratio ${result.ratio.toFixed(2)} does not meet WCAG AA standards (4.5:1)`,
          fix: 'Adjust color values to improve contrast',
        });
        score -= 15;
      }
    });

    // Validate color palette completeness
    const requiredColors = ['background', 'sage', 'gold', 'text', 'functional'];
    requiredColors.forEach((colorGroup) => {
      if (!UNIFIED_COLORS[colorGroup as keyof typeof UNIFIED_COLORS]) {
        issues.push({
          severity: 'error',
          category: 'Color System',
          message: `Missing required color group: ${colorGroup}`,
          fix: 'Add missing color definitions to UNIFIED_COLORS',
        });
        score -= 20;
      }
    });

    // Check for hardcoded color values in components
    const hardcodedColors = this.scanForHardcodedColors();
    if (hardcodedColors.length > 0) {
      issues.push({
        severity: 'warning',
        category: 'Color System',
        message: `Found ${hardcodedColors.length} hardcoded color values`,
        fix: 'Replace hardcoded colors with design system tokens',
      });
      score -= 5 * hardcodedColors.length;
    }

    if (score > 90) {
      recommendations.push('Color system is well-implemented and accessible');
    } else if (score > 70) {
      recommendations.push('Consider improving color contrast ratios for better accessibility');
    } else {
      recommendations.push(
        'Significant color system improvements needed for accessibility compliance',
      );
    }

    return {
      isValid: score >= 70,
      score: Math.max(0, score),
      issues,
      recommendations,
    };
  }

  /**
   * Validate typography system consistency
   */
  private validateTypography(): ValidationResult {
    const issues: ValidationIssue[] = [];
    const recommendations: string[] = [];
    let score = 100;

    // Check typography scale completeness
    const requiredScales = [
      'display',
      'headline',
      'title',
      'body1',
      'body2',
      'caption',
      'overline',
    ];
    requiredScales.forEach((scale) => {
      if (!TYPOGRAPHY.scale[scale as keyof typeof TYPOGRAPHY.scale]) {
        issues.push({
          severity: 'error',
          category: 'Typography',
          message: `Missing typography scale: ${scale}`,
          fix: 'Add missing typography scale to TYPOGRAPHY.scale',
        });
        score -= 10;
      }
    });

    // Validate font loading and availability
    const fontAvailability = this.checkFontAvailability();
    if (!fontAvailability.playfairDisplay) {
      issues.push({
        severity: 'warning',
        category: 'Typography',
        message: 'Playfair Display font may not be loaded properly',
        fix: 'Ensure Playfair Display is properly loaded in app.json',
      });
      score -= 10;
    }

    // Check for consistent line height ratios
    const lineHeightIssues = this.validateLineHeights();
    if (lineHeightIssues.length > 0) {
      issues.push(...lineHeightIssues);
      score -= 5 * lineHeightIssues.length;
    }

    recommendations.push(
      score > 90
        ? 'Typography system is well-structured'
        : score > 70
          ? 'Consider improving font loading and line height consistency'
          : 'Typography system needs significant improvements',
    );

    return {
      isValid: score >= 70,
      score: Math.max(0, score),
      issues,
      recommendations,
    };
  }

  /**
   * Validate spacing system consistency
   */
  private validateSpacing(): ValidationResult {
    const issues: ValidationIssue[] = [];
    const recommendations: string[] = [];
    let score = 100;

    // Check spacing scale completeness
    const requiredSpacing = ['xs', 'sm', 'md', 'lg', 'xl', '2xl', '3xl'];
    requiredSpacing.forEach((size) => {
      if (!SPACING[size as keyof typeof SPACING]) {
        issues.push({
          severity: 'error',
          category: 'Spacing',
          message: `Missing spacing size: ${size}`,
          fix: 'Add missing spacing size to SPACING system',
        });
        score -= 10;
      }
    });

    // Validate spacing consistency (8px base unit)
    const spacingValues = Object.values(SPACING).filter((val) => typeof val === 'number');
    const inconsistentSpacing = spacingValues.filter((val) => val % 4 !== 0);
    if (inconsistentSpacing.length > 0) {
      issues.push({
        severity: 'warning',
        category: 'Spacing',
        message: `Found ${inconsistentSpacing.length} spacing values not following 4px grid`,
        fix: 'Align spacing values to 4px or 8px grid system',
      });
      score -= 5;
    }

    recommendations.push(
      score > 90
        ? 'Spacing system follows best practices'
        : 'Consider aligning all spacing values to a consistent grid system',
    );

    return {
      isValid: score >= 70,
      score: Math.max(0, score),
      issues,
      recommendations,
    };
  }

  /**
   * Validate elevation and shadow system
   */
  private validateElevation(): ValidationResult {
    const issues: ValidationIssue[] = [];
    const recommendations: string[] = [];
    let score = 100;

    // Check elevation levels
    const requiredElevations = ['none', 'soft', 'medium', 'high', 'floating', 'organic'];
    requiredElevations.forEach((level) => {
      if (!ELEVATION[level as keyof typeof ELEVATION]) {
        issues.push({
          severity: 'error',
          category: 'Elevation',
          message: `Missing elevation level: ${level}`,
          fix: 'Add missing elevation level to ELEVATION system',
        });
        score -= 15;
      }
    });

    // Validate glassmorphism effects
    const requiredGlass = ['light', 'medium', 'strong', 'dark', 'navigation'];
    requiredGlass.forEach((effect) => {
      if (!GLASSMORPHISM[effect as keyof typeof GLASSMORPHISM]) {
        issues.push({
          severity: 'warning',
          category: 'Glassmorphism',
          message: `Missing glassmorphism effect: ${effect}`,
          fix: 'Add missing glassmorphism effect',
        });
        score -= 10;
      }
    });

    recommendations.push(
      score > 90
        ? 'Elevation system provides good depth hierarchy'
        : 'Consider expanding elevation system for better visual hierarchy',
    );

    return {
      isValid: score >= 70,
      score: Math.max(0, score),
      issues,
      recommendations,
    };
  }

  /**
   * Validate border radius system
   */
  private validateBorderRadius(): ValidationResult {
    const issues: ValidationIssue[] = [];
    const recommendations: string[] = [];
    let score = 100;

    const requiredRadius = ['none', 'sm', 'md', 'lg', 'xl', '2xl', 'organic', 'pill'];
    requiredRadius.forEach((size) => {
      if (!BORDER_RADIUS[size as keyof typeof BORDER_RADIUS]) {
        issues.push({
          severity: 'error',
          category: 'Border Radius',
          message: `Missing border radius size: ${size}`,
          fix: 'Add missing border radius to BORDER_RADIUS system',
        });
        score -= 10;
      }
    });

    return {
      isValid: score >= 70,
      score: Math.max(0, score),
      issues,
      recommendations: ['Border radius system supports organic design language'],
    };
  }

  /**
   * Validate component implementations
   */
  private validateComponents(): ValidationResult {
    const issues: ValidationIssue[] = [];
    const recommendations: string[] = [];
    let score = 100;

    const componentResults = this.validateComponentImplementations();

    componentResults.forEach((result) => {
      if (!result.accessibility.touchTargetSize) {
        issues.push({
          severity: 'error',
          category: 'Accessibility',
          message: `${result.component} has insufficient touch target size`,
          fix: 'Ensure minimum 44px touch targets',
        });
        score -= 10;
      }

      if (!result.accessibility.colorContrast) {
        issues.push({
          severity: 'error',
          category: 'Accessibility',
          message: `${result.component} has poor color contrast`,
          fix: 'Improve color contrast ratios',
        });
        score -= 10;
      }

      if (result.performance.renderTime > 16) {
        issues.push({
          severity: 'warning',
          category: 'Performance',
          message: `${result.component} render time exceeds 16ms`,
          fix: 'Optimize component rendering performance',
        });
        score -= 5;
      }
    });

    recommendations.push(
      score > 90
        ? 'Components follow design system guidelines well'
        : score > 70
          ? 'Some components need accessibility improvements'
          : 'Significant component improvements needed',
    );

    return {
      isValid: score >= 70,
      score: Math.max(0, score),
      issues,
      recommendations,
    };
  }

  /**
   * Validate accessibility compliance
   */
  private validateAccessibility(): ValidationResult {
    const issues: ValidationIssue[] = [];
    const recommendations: string[] = [];
    let score = 100;

    // Check WCAG compliance
    const wcagResults = this.checkWCAGCompliance();
    if (wcagResults.colorContrast < 0.9) {
      issues.push({
        severity: 'error',
        category: 'WCAG Compliance',
        message: 'Color contrast compliance below 90%',
        fix: 'Improve color contrast ratios across the app',
      });
      score -= 20;
    }

    if (wcagResults.touchTargets < 0.95) {
      issues.push({
        severity: 'error',
        category: 'WCAG Compliance',
        message: 'Touch target size compliance below 95%',
        fix: 'Ensure all interactive elements meet minimum size requirements',
      });
      score -= 15;
    }

    recommendations.push(
      score > 95
        ? 'Excellent accessibility compliance'
        : score > 80
          ? 'Good accessibility with room for improvement'
          : 'Accessibility needs significant attention',
    );

    return {
      isValid: score >= 80,
      score: Math.max(0, score),
      issues,
      recommendations,
    };
  }

  /**
   * Validate performance metrics
   */
  private validatePerformance(): ValidationResult {
    const issues: ValidationIssue[] = [];
    const recommendations: string[] = [];
    let score = 100;

    const metrics = this.measurePerformanceMetrics();
    this.performanceMetrics = metrics;

    if (metrics.themeLoadTime > 100) {
      issues.push({
        severity: 'warning',
        category: 'Performance',
        message: 'Theme loading time exceeds 100ms',
        fix: 'Optimize theme loading and caching',
      });
      score -= 10;
    }

    if (metrics.animationFrameRate < 55) {
      issues.push({
        severity: 'error',
        category: 'Performance',
        message: 'Animation frame rate below 55fps',
        fix: 'Optimize animations for 60fps performance',
      });
      score -= 15;
    }

    if (metrics.bundleSize > 500000) {
      // 500KB
      issues.push({
        severity: 'warning',
        category: 'Performance',
        message: 'Design system bundle size exceeds 500KB',
        fix: 'Consider code splitting and tree shaking',
      });
      score -= 5;
    }

    recommendations.push(
      score > 90
        ? 'Performance metrics are excellent'
        : score > 70
          ? 'Performance is good with minor optimizations needed'
          : 'Performance needs significant optimization',
    );

    return {
      isValid: score >= 70,
      score: Math.max(0, score),
      issues,
      recommendations,
    };
  }

  /**
   * Validate layout systems
   */
  private validateLayoutSystems(): ValidationResult {
    const issues: ValidationIssue[] = [];
    const recommendations: string[] = [];
    let score = 100;

    // Check responsive breakpoints
    const { width } = Dimensions.get('window');
