/**
 * KVKK Consent Service - Türkiye Kişisel Verilerin Korunması Kanunu uyumluluğu
 *
 * Bu servis KVKK kapsamında:
 * - Kullanıcı rızalarının yönetimi
 * - Veri işleme amaçlarının takibi
 * - Rıza geçmişinin kayıt altında tutulması
 * - Kullanıcı haklarının uygulanması
 */

import AsyncStorage from '@react-native-async-storage/async-storage';

import { supabase } from '@/lib/supa';
import { errorInDev } from '@/utils/consoleSuppress';
('../config/supabaseClient');

export interface KVKKConsent {
  id: string;
  userId: string;
  consentType: ConsentType;
  purpose: DataProcessingPurpose;
  granted: boolean;
  timestamp: Date;
  ipAddress?: string;
  userAgent?: string;
  version: string; // KVKK politika versiyonu
  expiryDate?: Date;
  withdrawnAt?: Date;
  legalBasis: LegalBasis;
}

export enum ConsentType {
  MARKETING = 'marketing',
  ANALYTICS = 'analytics',
  PERSONALIZATION = 'personalization',
  LOCATION = 'location',
  NOTIFICATIONS = 'notifications',
  AI_PROCESSING = 'ai_processing',
  DATA_SHARING = 'data_sharing',
  CRASH_REPORTING = 'crash_reporting',
}

export enum DataProcessingPurpose {
  SERVICE_PROVISION = 'service_provision',
  PERSONALIZED_RECOMMENDATIONS = 'personalized_recommendations',
  MARKETING_COMMUNICATION = 'marketing_communication',
  ANALYTICS_IMPROVEMENT = 'analytics_improvement',
  SECURITY_FRAUD_PREVENTION = 'security_fraud_prevention',
  CUSTOMER_SUPPORT = 'customer_support',
  LEGAL_COMPLIANCE = 'legal_compliance',
  AI_PROCESSING = 'ai_processing',
  ACCOUNT_MANAGEMENT = 'account_management',
}

export enum LegalBasis {
  CONSENT = 'consent', // Açık rıza
  CONTRACT = 'contract', // Sözleşmenin ifası
  LEGITIMATE_INTEREST = 'legitimate_interest', // Meşru menfaat
  LEGAL_OBLIGATION = 'legal_obligation', // Hukuki yükümlülük
  VITAL_INTERESTS = 'vital_interests', // Yaşamsal çıkarlar
  PUBLIC_TASK = 'public_task', // Kamu görevi
}

export interface UserDataRights {
  accessRequested: boolean;
  rectificationRequested: boolean;
  erasureRequested: boolean;
  restrictionRequested: boolean;
  portabilityRequested: boolean;
  objectionRequested: boolean;
  lastRequestDate?: Date;
  requestStatus: 'pending' | 'processing' | 'completed' | 'rejected';
}

export interface KVKKSettings {
  consentVersion: string;
  lastConsentUpdate: Date;
  dataRetentionPeriod: number; // gün cinsinden
  automaticDeletion: boolean;
  consentReminders: boolean;
  dataProcessingLog: boolean;
}

class KVKKConsentService {
  private static instance: KVKKConsentService;
  private readonly STORAGE_KEY = 'kvkk_consent_data';
  private readonly CURRENT_VERSION = '1.0';
  private consents: Map<string, KVKKConsent> = new Map();
  private settings: KVKKSettings;

  private constructor() {
    this.settings = {
      consentVersion: this.CURRENT_VERSION,
      lastConsentUpdate: new Date(),
      dataRetentionPeriod: 730, // 2 yıl
      automaticDeletion: true,
      consentReminders: true,
      dataProcessingLog: true,
    };
    this.loadConsents();
  }

  public static getInstance(): KVKKConsentService {
    if (!KVKKConsentService.instance) {
      KVKKConsentService.instance = new KVKKConsentService();
    }
    return KVKKConsentService.instance;
  }

  /**
   * Kullanıcı rızasını kaydet
   */
  async grantConsent(
    userId: string,
    consentType: ConsentType,
    purpose: DataProcessingPurpose,
    legalBasis: LegalBasis = LegalBasis.CONSENT,
    expiryDays?: number,
  ): Promise<void> {
    const consentId = `${userId}_${consentType}_${Date.now()}`;
    const now = new Date();

    const consent: KVKKConsent = {
      id: consentId,
      userId,
      consentType,
      purpose,
      granted: true,
      timestamp: now,
      version: this.CURRENT_VERSION,
      legalBasis,
      expiryDate: expiryDays
        ? new Date(now.getTime() + expiryDays * 24 * 60 * 60 * 1000)
        : undefined,
    };

    this.consents.set(consentId, consent);
    await this.saveConsents();
    await this.syncToDatabase(consent);
  }

  /**
   * Kullanıcı rızasını geri çek
   */
  async withdrawConsent(userId: string, consentType: ConsentType): Promise<void> {
    const userConsents = Array.from(this.consents.values()).filter(
      (c) => c.userId === userId && c.consentType === consentType && c.granted,
    );

    for (const consent of userConsents) {
      consent.granted = false;
      consent.withdrawnAt = new Date();
      this.consents.set(consent.id, consent);
    }

    await this.saveConsents();
    await this.syncWithdrawalToDatabase(userId, consentType);
  }

  /**
   * Belirli bir rıza türü için onay durumunu kontrol et
   */
  hasValidConsent(userId: string, consentType: ConsentType): boolean {
    const userConsents = Array.from(this.consents.values()).filter(
      (c) =>
        c.userId === userId &&
        c.consentType === consentType &&
        c.granted &&
        !c.withdrawnAt &&
        (!c.expiryDate || c.expiryDate > new Date()),
    );

    return userConsents.length > 0;
  }

  /**
   * Kullanıcının tüm rızalarını getir
   */
  getUserConsents(userId: string): KVKKConsent[] {
    return Array.from(this.consents.values())
      .filter((c) => c.userId === userId)
      .sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());
  }

  /**
   * Rıza geçmişini getir (denetim için)
   */
  getConsentHistory(userId: string, consentType?: ConsentType): KVKKConsent[] {
    let history = Array.from(this.consents.values()).filter((c) => c.userId === userId);

    if (consentType) {
      history = history.filter((c) => c.consentType === consentType);
    }

    return history.sort((a, b) => b.timestamp.getTime() - a.timestamp.getTime());
  }

  /**
   * KVKK ayarlarını getir
   */
  async getKVKKSettings(userId: string): Promise<KVKKSettings> {
    return { ...this.settings };
  }

  /**
   * KVKK ayarlarını güncelle
   */
  async updateKVKKSettings(userId: string, newSettings: Partial<KVKKSettings>): Promise<void> {
    this.settings = { ...this.settings, ...newSettings };
  }

  /**
   * Kullanıcı verilerini dışa aktar (KVKK hakkı)
   */
  async exportUserData(userId: string): Promise<{
    userData: unknown;
    consents: KVKKConsent[];
    exportDate: string;
    version: string;
    rights: {
      access: string;
      rectification: string;
      erasure: string;
      restriction: string;
      portability: string;
      objection: string;
    };
  }> {
    try {
      const { data: userData, error } = await supabase
        .from('user_data_export')
        .select('*')
        .eq('user_id', userId)
        .single();

      if (error) throw error;

      const consents = this.getUserConsents(userId);

      return {
        userData,
        consents,
        exportDate: new Date().toISOString(),
        version: this.CURRENT_VERSION,
        rights: {
          access: 'Kişisel verilerinize erişim hakkınız vardır',
          rectification: 'Yanlış verilerin düzeltilmesini talep edebilirsiniz',
          erasure: 'Verilerinizin silinmesini talep edebilirsiniz',
          restriction: 'Veri işlemenin kısıtlanmasını talep edebilirsiniz',
          portability: 'Verilerinizi başka platforma taşıyabilirsiniz',
          objection: 'Veri işlemeye itiraz edebilirsiniz',
        },
      };
    } catch (error) {
      errorInDev('KVKK: Veri dışa aktarma hatası:', error);
      throw error;
    }
  }

  /**
   * Kullanıcı verilerini sil (KVKK hakkı)
   */
  async deleteUserData(userId: string): Promise<void> {
    try {
      // Veritabanından kullanıcı verilerini sil
      const { error } = await supabase.rpc('delete_user_data_kvkk', {
        user_id: userId,
      });

      if (error) throw error;

      // Yerel rızaları temizle
      const userConsentIds = Array.from(this.consents.entries())
        .filter(([_, consent]) => consent.userId === userId)
        .map(([id, _]) => id);

      userConsentIds.forEach((id) => this.consents.delete(id));
      await this.saveConsents();
    } catch (error) {
      errorInDev('KVKK: Veri silme hatası:', error);
      throw error;
    }
  }

  /**
   * Veri işleme faaliyetlerini logla
   */
  async logDataProcessing(
    userId: string,
    activity: string,
    purpose: DataProcessingPurpose,
    legalBasis: LegalBasis,
    dataTypes: string[],
  ): Promise<void> {
    if (!this.settings.dataProcessingLog) return;

    try {
      const { error } = await supabase.from('kvkk_processing_log').insert({
        user_id: userId,
        activity,
        purpose,
        legal_basis: legalBasis,
        data_types: dataTypes,
        timestamp: new Date().toISOString(),
        version: this.CURRENT_VERSION,
      });

      if (error) throw error;
    } catch (error) {
      errorInDev('KVKK: Veri işleme loglama hatası:', error);
    }
  }

  /**
   * Süresi dolan rızaları temizle
   */
  async cleanupExpiredConsents(): Promise<void> {
    const now = new Date();
    let cleanupCount = 0;

    for (const [id, consent] of this.consents.entries()) {
      if (consent.expiryDate && consent.expiryDate < now) {
        consent.granted = false;
        consent.withdrawnAt = now;
        this.consents.set(id, consent);
        cleanupCount++;
      }
    }

    if (cleanupCount > 0) {
      await this.saveConsents();
      // Cleanup completed silently
    }
  }

  /**
   * KVKK uyumluluk durumunu kontrol et
   */
  async checkCompliance(userId: string): Promise<{
    compliant: boolean;
    issues: string[];
    recommendations: string[];
  }> {
    const issues: string[] = [];
    const recommendations: string[] = [];

    const userConsents = this.getUserConsents(userId);

    // Gerekli rızaların kontrolü
    const requiredConsents = [ConsentType.NOTIFICATIONS, ConsentType.AI_PROCESSING];
    for (const required of requiredConsents) {
      if (!this.hasValidConsent(userId, required)) {
        issues.push(`Eksik rıza: ${required}`);
      }
    }

    // Rıza yaşı kontrolü
    const oldConsents = userConsents.filter((c) => {
      const ageInDays = (Date.now() - c.timestamp.getTime()) / (1000 * 60 * 60 * 24);
      return ageInDays > 365; // 1 yıldan eski
    });

    if (oldConsents.length > 0) {
      recommendations.push('1 yıldan eski rızalar yenilenmelidir');
    }

    // Veri saklama süresi kontrolü
    if (this.settings.dataRetentionPeriod > 730) {
      // 2 yıldan fazla
      recommendations.push('Veri saklama süresi KVKK önerilerine uygun şekilde azaltılmalıdır');
    }

    return {
      compliant: issues.length === 0,
      issues,
      recommendations,
    };
  }

  /**
   * Yerel depolamaya kaydet
   */
  private async saveConsents(): Promise<void> {
    try {
      const data = {
        consents: Array.from(this.consents.entries()),
        settings: this.settings,
        lastUpdate: new Date().toISOString(),
      };
      await AsyncStorage.setItem(this.STORAGE_KEY, JSON.stringify(data));
    } catch (error) {
      errorInDev('KVKK: Yerel kaydetme hatası:', error);
    }
  }

  /**
   * Yerel depolamadan yükle
   */
  private async loadConsents(): Promise<void> {
    try {
      const data = await AsyncStorage.getItem(this.STORAGE_KEY);
      if (data) {
        const parsed = JSON.parse(data);
        this.consents = new Map(parsed.consents || []);
        this.settings = { ...this.settings, ...parsed.settings };
      }
    } catch (error) {
      errorInDev('KVKK: Yerel yükleme hatası:', error);
    }
  }

  /**
   * Veritabanına senkronize et
   */
  private async syncToDatabase(consent: KVKKConsent): Promise<void> {
    try {
      const { error } = await supabase.from('kvkk_consents').upsert({
        id: consent.id,
        user_id: consent.userId,
        consent_type: consent.consentType,
        purpose: consent.purpose,
        granted: consent.granted,
        timestamp: consent.timestamp.toISOString(),
        version: consent.version,
        legal_basis: consent.legalBasis,
        expiry_date: consent.expiryDate?.toISOString(),
        withdrawn_at: consent.withdrawnAt?.toISOString(),
      });

      if (error) throw error;
    } catch (error) {
      errorInDev('KVKK: Veritabanı senkronizasyon hatası:', error);
    }
  }

  /**
   * Rıza geri çekme işlemini veritabanına senkronize et
   */
  private async syncWithdrawalToDatabase(userId: string, consentType: ConsentType): Promise<void> {
    try {
      const { error } = await supabase
        .from('kvkk_consents')
        .update({
          granted: false,
          withdrawn_at: new Date().toISOString(),
        })
        .eq('user_id', userId)
        .eq('consent_type', consentType)
        .eq('granted', true);

      if (error) throw error;
    } catch (error) {
      errorInDev('KVKK: Rıza geri çekme senkronizasyon hatası:', error);
    }
  }
}

export const kvkkConsentService = KVKKConsentService.getInstance();
export default kvkkConsentService;

// Navigation Integration Service - Cohesive User Experience Management
// Ensures smooth navigation, data flow validation, and polished transitions

import * as Haptics from 'expo-haptics';
import { router } from 'expo-router';
import { Alert } from 'react-native';

import { supabase } from '@/config/supabaseClient';
import { isSupabaseOk, wrap } from '@/utils/supabaseResult';

// Known application route names (extend as new screens are added)
export type RouteName =
  | 'index'
  | 'onboarding'
  | 'style-profile'
  | 'wardrobe'
  | 'wardrobe-add-item'
  | 'outfit-builder'
  | 'ayna-mirror'
  | 'feedback'
  | 'style-insights'
  | 'discover'
  | 'product-detail'
  | 'bag'
  | 'checkout'
  | '/auth/sign-in';

// Structured (but still flexible) user journey data container
export interface UserJourneyData {
  userProfile?: unknown;
  stylePreferences?: unknown;
  wardrobeItems?: unknown[]; // Could be refined to WardrobeItem[] once type imported
  selectedProduct?: unknown;
  outfitData?: {
    id: string;
    name: string;
    items: string[];
    occasion?: string;
  };
  feedbackData?: unknown;
  selectedItems?: string[];
  mirrorAnalysis?: {
    styleScore: number;
    recommendations: string[];
    confidence: number;
  };
  bagItems?: unknown[];
  [key: string]: unknown; // Allow forward-compatible extension without reverting to any
}

export interface NavigationState {
  currentScreen: RouteName;
  previousScreen?: RouteName;
  navigationHistory: RouteName[];
  userJourneyData: UserJourneyData;
}

export interface UserJourney {
  id: string;
  name: string;
  screens: string[];
  requiredData?: string[];
  validationRules?: ((state: NavigationState) => boolean)[];
}

class NavigationIntegrationService {
  private navigationState: NavigationState = {
    currentScreen: 'index',
    navigationHistory: ['index'],
    userJourneyData: {},
  };

  private userJourneys: UserJourney[] = [
    {
      id: 'onboarding-to-wardrobe',
      name: 'New User Onboarding to Wardrobe Setup',
      screens: ['onboarding', 'style-profile', 'wardrobe', 'wardrobe-add-item'],
      requiredData: ['userProfile', 'stylePreferences'],
      validationRules: [
        (state) => !!state.userJourneyData.userProfile,
        (state) => !!state.userJourneyData.stylePreferences,
      ],
    },
    {
      id: 'wardrobe-to-outfit',
      name: 'Wardrobe Management to Outfit Creation',
      screens: ['wardrobe', 'outfit-builder', 'ayna-mirror'],
      requiredData: ['wardrobeItems'],
      validationRules: [
        (state) =>
          Array.isArray(state.userJourneyData.wardrobeItems) &&
          state.userJourneyData.wardrobeItems.length > 0,
      ],
    },
    {
      id: 'discover-to-purchase',
      name: 'Product Discovery to Purchase Flow',
      screens: ['discover', 'product-detail', 'bag', 'checkout'],
      requiredData: ['selectedProduct'],
      validationRules: [(state) => !!state.userJourneyData.selectedProduct],
    },
    {
      id: 'mirror-feedback-loop',
      name: 'AYNA Mirror Feedback and Learning',
      screens: ['ayna-mirror', 'feedback', 'style-insights', 'wardrobe'],
      requiredData: ['outfitData', 'feedbackData'],
      validationRules: [
        (state) => !!state.userJourneyData.outfitData,
        (state) => !!state.userJourneyData.feedbackData,
      ],
    },
  ];

  // Navigation with transition management
  async navigateWithTransition(
    destination: RouteName,
    params?: Partial<UserJourneyData>,
    transitionType: 'push' | 'replace' | 'modal' = 'push',
  ): Promise<void> {
    try {
      // Validate navigation
      const canNavigate = await this.validateNavigation(destination, params);
      if (!canNavigate) {
        return;
      }

      // Add haptic feedback
      await Haptics.impactAsync(Haptics.ImpactFeedbackStyle.Light);

      // Update navigation state
      this.updateNavigationState(destination, params);

      // Perform navigation based on type
      switch (transitionType) {
        case 'push':
          router.push(destination as never);
          break;
        case 'replace':
          router.replace(destination as never);
          break;
        case 'modal':
          router.push(destination as never);
          break;
      }

      // Log navigation for analytics
      await this.logNavigation(destination, params);
    } catch (error) {
      // Navigation error
      this.handleNavigationError(error);
    }
  }

  // Validate navigation based on user journey rules
  private async validateNavigation(
    destination: RouteName,
    _params?: Partial<UserJourneyData>,
  ): Promise<boolean> {
    // Check if user is authenticated for protected routes
    const protectedRoutes = ['wardrobe', 'ayna-mirror', 'profile', 'bag', 'checkout'];
    if (protectedRoutes.includes(destination)) {
      const {
        data: { session },
      } = await supabase.auth.getSession();
      if (!session) {
        Alert.alert('Authentication Required', 'Please sign in to access this feature.', [
          { text: 'Cancel', style: 'cancel' },
          { text: 'Sign In', onPress: () => router.push('/auth/sign-in') },
        ]);
        return false;
      }
    }

    // Validate data requirements for specific journeys
    const activeJourney = this.getActiveUserJourney(destination);
    if (activeJourney) {
      const isValid = this.validateJourneyRequirements(activeJourney);
      if (!isValid) {
        await this.handleInvalidJourney(activeJourney, destination);
        return false;
      }
    }

    return true;
  }

  // Update navigation state and user journey data
  private updateNavigationState(destination: RouteName, params?: Partial<UserJourneyData>): void {
    this.navigationState.previousScreen = this.navigationState.currentScreen;
    this.navigationState.currentScreen = destination;
    this.navigationState.navigationHistory.push(destination);

    // Merge params into user journey data
    if (params) {
      this.navigationState.userJourneyData = {
        ...this.navigationState.userJourneyData,
        ...params,
      };
    }

    // Limit history to last 20 screens
    if (this.navigationState.navigationHistory.length > 20) {
      this.navigationState.navigationHistory = this.navigationState.navigationHistory.slice(-20);
    }
  }

  // Get active user journey based on current navigation
  private getActiveUserJourney(destination: RouteName): UserJourney | null {
    return (
      this.userJourneys.find(
        (journey) =>
          journey.screens.includes(destination) &&
          journey.screens.includes(this.navigationState.currentScreen),
      ) || null
    );
  }

  // Validate journey requirements
  private validateJourneyRequirements(journey: UserJourney): boolean {
    // Check required data
    if (journey.requiredData) {
      for (const dataKey of journey.requiredData) {
        if (!this.navigationState.userJourneyData[dataKey]) {
          return false;
        }
      }
    }

    // Check validation rules
    if (journey.validationRules) {
      for (const rule of journey.validationRules) {
        if (!rule(this.navigationState)) {
          return false;
        }
      }
    }

    return true;
  }

  // Handle invalid journey navigation
  private async handleInvalidJourney(journey: UserJourney, _destination: string): Promise<void> {
    // ensure async contains at least one await (lint require-await)
    await Promise.resolve();
    const missingData =
      journey.requiredData?.filter((dataKey) => !this.navigationState.userJourneyData[dataKey]) ||
      [];

    if (missingData.length > 0) {
      Alert.alert(
        'Setup Required',
        `Please complete your ${missingData.join(', ')} before proceeding.`,
        [
          { text: 'Cancel', style: 'cancel' },
          {
            text: 'Complete Setup',
            onPress: () => {
              const first = missingData[0];
              if (first) {
                this.navigateToSetup(first);
              }
            },
          },
        ],
      );
    }
  }

  // Navigate to setup screen for missing data
  private navigateToSetup(dataType: string): void {
    const setupRoutes: Record<string, string> = {
      userProfile: '/onboarding',
      stylePreferences: '/style-profile',
      wardrobeItems: '/wardrobe',
      selectedProduct: '/discover',
    };

    const setupRoute = setupRoutes[dataType];
    if (setupRoute) {
      router.push(setupRoute as never);
    }
  }

  // Log navigation for analytics
  private async logNavigation(
    destination: RouteName,
    params?: Partial<UserJourneyData>,
  ): Promise<void> {
    try {
      const {
        data: { user },
      } = await supabase.auth.getUser();
      if (!user) {
        return;
      }

      const navRes = await wrap(
        async () =>
          await supabase
            .from('navigation_analytics')
            .insert({
              user_id: user.id,
              from_screen: this.navigationState.previousScreen,
              to_screen: destination,
              navigation_params: params,
              timestamp: new Date().toISOString(),
              session_id: this.getSessionId(),
            })
            .select('*')
            .single(),
      );
      if (!isSupabaseOk(navRes)) {
        // Silent fail; optionally could log in dev
      }
    } catch (error) {
      // Failed to log navigation
    }
  }

  // Handle navigation errors
  private handleNavigationError(error: unknown): void {
    // Basic dev logging without exposing internals to user
    // (Optional) Integrate with errorHandlingService if available
    Alert.alert('Navigation Error', 'Something went wrong. Please try again.', [{ text: 'OK' }]);
  }

  // Get current session ID
  private getSessionId(): string {
    return `session_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
  }

  // Public methods for external use
  public getCurrentScreen(): RouteName {
    return this.navigationState.currentScreen;
  }

  public getNavigationHistory(): RouteName[] {
    return [...this.navigationState.navigationHistory];
  }

  public getUserJourneyData(): UserJourneyData {
    return { ...this.navigationState.userJourneyData };
  }

  public setUserJourneyData(data: Partial<UserJourneyData>): void {
    this.navigationState.userJourneyData = {
      ...this.navigationState.userJourneyData,
      ...data,
    };
  }

  // Test complete user journeys
  public async testUserJourney(journeyId: string): Promise<boolean> {
    const journey = this.userJourneys.find((j) => j.id === journeyId);
    if (!journey) {
      // Journey not found
      return false;
    }

    // Testing user journey

    // Simulate navigation through journey screens
    for (const screen of journey.screens) {
      if (!this.isKnownRoute(screen)) {
        continue;
      } // skip unknown legacy screens
      const canNavigate = await this.validateNavigation(screen);
      if (!canNavigate) {
        // Failed to navigate to screen
        return false;
      }
    }

    // User journey test completed successfully
    return true;
  }

  // Get journey progress
  public getJourneyProgress(journeyId: string): number {
    const journey = this.userJourneys.find((j) => j.id === journeyId);
    if (!journey) {
      return 0;
    }

    const completedScreens = journey.screens.filter(
      (screen) =>
        this.isKnownRoute(screen) && this.navigationState.navigationHistory.includes(screen),
    );

    return completedScreens.length / journey.screens.length;
  }

  private isKnownRoute(value: string): value is RouteName {
    const routes: RouteName[] = [
      'index',
      'onboarding',
      'style-profile',
      'wardrobe',
      'wardrobe-add-item',
      'outfit-builder',
      'ayna-mirror',
      'feedback',
      'style-insights',
      'discover',
      'product-detail',
      'bag',
      'checkout',
      '/auth/sign-in',
    ];
    return (routes as string[]).includes(value);
  }

  // Reset navigation state (for testing)
  public resetNavigationState(): void {
    this.navigationState = {
      currentScreen: 'index',
      navigationHistory: ['index'],
      userJourneyData: {},
    };
  }
}

export const navigationIntegrationService = new NavigationIntegrationService();
export default navigationIntegrationService;

// Notification Handler Service
// Handles deep linking and notification responses for AYNA Mirror

// P0 Notifications: convert static expo-notifications import to dynamic lazy loader to reduce startup & comply with lazy policy
// Narrowed lazy module reference; internal use only
let Notifications: typeof import('expo-notifications') | null = null;
async function ensureNotifications() {
  if (!Notifications) {
    Notifications = await import('expo-notifications');
  }
  return Notifications;
}
import * as Linking from 'expo-linking';
import { router } from 'expo-router';
import { Platform } from 'react-native';

import { errorInDev, logInDev } from '@/utils/consoleSuppress';

// Shared discriminated union for notification payload types
type NotificationType = 'daily_mirror' | 'feedback_prompt' | 're_engagement';
interface BasePayload {
  type: NotificationType;
  userId: string;
  timestamp?: number;
}
interface DailyMirrorPayload extends BasePayload {
  type: 'daily_mirror';
}
interface FeedbackPromptPayload extends BasePayload {
  type: 'feedback_prompt';
  outfitId: string;
}
interface ReEngagementPayload extends BasePayload {
  type: 're_engagement';
  daysSinceLastUse?: number;
}
type AnyNotificationPayload =
  | DailyMirrorPayload
  | FeedbackPromptPayload
  | ReEngagementPayload
  | BasePayload;

// Minimal runtime-safe structural types (avoids compile-time dependency on module namespace)
export interface NotificationEnvelope {
  request: { content: { data: unknown } };
}
export interface NotificationResponse {
  notification: NotificationEnvelope;
  actionIdentifier: string;
}

class NotificationHandler {
  private static instance: NotificationHandler;
  private isInitialized = false;

  private constructor() {}

  static getInstance(): NotificationHandler {
    if (!NotificationHandler.instance) {
      NotificationHandler.instance = new NotificationHandler();
    }
    return NotificationHandler.instance;
  }

  /**
   * Initialize notification response handlers
   */
  async initialize(): Promise<void> {
    if (this.isInitialized) {
      return;
    }

    try {
      // Handle notification responses (when user taps notification)
      const N = await ensureNotifications();
      N.addNotificationResponseReceivedListener(this.handleNotificationResponse);

      // Handle notifications received while app is in foreground
      N.addNotificationReceivedListener(this.handleNotificationReceived);

      // Handle deep links from notifications
      Linking.addEventListener('url', this.handleDeepLink);

      // Handle initial URL if app was opened from notification
      const initialUrl = await Linking.getInitialURL();
      if (initialUrl) {
        this.handleDeepLink({ url: initialUrl });
      }

      this.isInitialized = true;
      logInDev('Notification handler initialized');
    } catch (error) {
      errorInDev(
        'Failed to initialize notification handler:',
        error instanceof Error ? error : String(error),
      );
    }
  }

  /**
   * Handle notification response when user taps on notification
   */
  private handleNotificationResponse = (response: NotificationResponse): void => {
    try {
      const { notification } = response;
      const raw = notification.request.content.data;
      const data = this.normalisePayload(raw);

      logInDev('Notification response received:', data);

      // Handle different notification types
      if (data.type === 'daily_mirror') {
        this.handleDailyMirrorNotification(data as DailyMirrorPayload);
      } else if (data.type === 'feedback_prompt' && 'outfitId' in data) {
        this.handleFeedbackPromptNotification(data);
      } else if (data.type === 're_engagement') {
        this.handleReEngagementNotification(data as ReEngagementPayload);
      } else {
        {
          const unknownType = (data as { type?: unknown })?.type;
          logInDev(
            'Unknown notification type:',
            unknownType instanceof Error ? unknownType : String(unknownType),
          );
        }
      }
    } catch (error) {
      errorInDev(
        'Error handling notification response:',
        error instanceof Error ? error : String(error),
      );
    }
  };

  /**
   * Handle notification received while app is in foreground
   */
  private handleNotificationReceived = (notification: NotificationEnvelope): void => {
    try {
      const data = this.normalisePayload(notification.request.content.data);
      logInDev('Notification received in foreground:', data);

      // You can show custom in-app notifications here if needed
      // For now, we'll let the system handle it
    } catch (error) {
      errorInDev(
        'Error handling received notification:',
        error instanceof Error ? error : String(error),
      );
    }
  };

  /**
   * Handle deep links from notifications or external sources
   */
  private handleDeepLink = ({ url }: { url: string }): void => {
    try {
      logInDev('Deep link received:', url);

      const parsedUrl = Linking.parse(url);
      const { hostname, path, queryParams } = parsedUrl;

      // Handle AYNA Mirror deep links
      if (hostname === 'ayna-mirror' || path === '/ayna-mirror') {
        // Normalise possible array params to first value (simple use-case)
        const normalised: Record<string, string | number | boolean | undefined> | undefined =
          queryParams
            ? Object.fromEntries(
                Object.entries(queryParams).map(([k, v]) => [
                  k,
                  Array.isArray(v) ? (v.length > 0 ? v[0] : undefined) : v,
                ]),
              )
            : undefined;
        this.navigateToAynaMirror(normalised);
      } else if (path === '/ayna-mirror/settings') {
        this.navigateToAynaMirrorSettings();
      } else if (path === '/onboarding') {
        this.navigateToOnboarding();
      } else {
        // Default navigation
        this.navigateToAynaMirror();
      }
    } catch (error) {
      errorInDev('Error handling deep link:', error instanceof Error ? error : String(error));
      // Fallback to AYNA Mirror
      this.navigateToAynaMirror();
    }
  };

  /**
   * Handle daily mirror notification tap
   */
  private handleDailyMirrorNotification(data: DailyMirrorPayload): void {
    try {
      logInDev('Handling daily mirror notification for user:', data.userId);

      // Navigate to AYNA Mirror screen
      this.navigateToAynaMirror();

      // Track notification engagement
      void this.trackNotificationEngagement('daily_mirror', data.userId);
    } catch (error) {
      errorInDev(
        'Error handling daily mirror notification:',
        error instanceof Error ? error : String(error),
      );
    }
  }

  /**
   * Handle feedback prompt notification tap
   */
  private handleFeedbackPromptNotification(data: FeedbackPromptPayload): void {
    try {
      logInDev('Handling feedback prompt notification for outfit:', data.outfitId);

      // Navigate to AYNA Mirror with feedback parameter
      this.navigateToAynaMirror({ feedback: data.outfitId });

      // Track notification engagement
      void this.trackNotificationEngagement('feedback_prompt', data.userId);
    } catch (error) {
      errorInDev(
        'Error handling feedback prompt notification:',
        error instanceof Error ? error : String(error),
      );
    }
  }

  /**
   * Handle re-engagement notification tap
   */
  private handleReEngagementNotification(data: ReEngagementPayload): void {
    try {
      logInDev('Handling re-engagement notification for user:', data.userId);

      // Navigate to AYNA Mirror screen
      this.navigateToAynaMirror();

      // Track notification engagement
      void this.trackNotificationEngagement('re_engagement', data.userId);
    } catch (error) {
      errorInDev(
        'Error handling re-engagement notification:',
        error instanceof Error ? error : String(error),
      );
    }
  }

  /**
   * Navigate to AYNA Mirror screen with optional parameters
   */
  private navigateToAynaMirror(
    params?: Record<string, string | number | boolean | undefined>,
  ): void {
    try {
      // Use setTimeout to ensure navigation happens after app is ready
      setTimeout(() => {
        if (params && Object.keys(params).length > 0) {
          // Convert values to strings for query params, dropping undefined & boolean -> 'true'/'false'
          const qp: Record<string, string> = {};
          for (const [k, v] of Object.entries(params)) {
            if (v === undefined) {
              continue;
            }
            qp[k] = String(v);
          }
          const queryString = new URLSearchParams(qp).toString();
          router.push(`/(app)/ayna-mirror?${queryString}`);
        } else {
          // Navigate to main AYNA Mirror screen
          router.push('/(app)/ayna-mirror');
        }
      }, 100);
    } catch (error) {
      errorInDev(
        'Error navigating to AYNA Mirror:',
        error instanceof Error ? error : String(error),
      );
      // Fallback navigation
      setTimeout(() => {
        router.push('/(app)/ayna-mirror');
      }, 100);
    }
  }

  /**
   * Navigate to AYNA Mirror settings screen
   */
  private navigateToAynaMirrorSettings(): void {
    try {
      setTimeout(() => {
        router.push('/ayna-mirror-settings');
      }, 100);
    } catch (error) {
      errorInDev(
        'Error navigating to AYNA Mirror settings:',
        error instanceof Error ? error : String(error),
      );
    }
  }

  /**
   * Navigate to onboarding screen
   */
  private navigateToOnboarding(): void {
    try {
      setTimeout(() => {
        router.push('/onboarding');
      }, 100);
    } catch (error) {
      errorInDev('Error navigating to onboarding:', error instanceof Error ? error : String(error));
    }
  }

  /**
   * Track notification engagement for analytics
   */
  private async trackNotificationEngagement(type: string, userId: string): Promise<void> {
    try {
      // Store engagement data for analytics
      const engagementData = {
        type,
        userId,
        timestamp: new Date().toISOString(),
        platform: Platform.OS,
      };

      logInDev('Notification engagement tracked:', engagementData);

      // Send to analytics service
      const { analyticsService } = await import('./analyticsService');
      analyticsService.trackEvent('notification_engagement', engagementData);
    } catch (error) {
      errorInDev(
        'Error tracking notification engagement:',
        error instanceof Error ? error : String(error),
      );
    }
  }

  /**
   * Clean up listeners (for testing or app shutdown)
   */
  cleanup(): void {
    try {
      // Remove listeners
      // Updated API: remove all known subscriptions if tracked elsewhere; fallback no-op
      // Keeping for compatibility; Expo Notifications doesn't expose a global removeAll in v0.20+
      // This is a safe no-op in current setup.
      // Linking.removeAllListeners is not available in expo-linking; listeners are subscription-based.

      this.isInitialized = false;
      logInDev('Notification handler cleaned up');
    } catch (error) {
      errorInDev(
        'Error cleaning up notification handler:',
        error instanceof Error ? error : String(error),
      );
    }
  }

  /**
   * Check if handler is initialized
   */
  isReady(): boolean {
    return this.isInitialized;
  }

  /**
   * Runtime normalisation & type-narrowing for incoming notification payloads.
   * Ensures we always return an object with at least a `type` & `userId` when possible.
   */
  private normalisePayload(raw: unknown): AnyNotificationPayload {
    if (raw && typeof raw === 'object') {
      const r = raw as Record<string, unknown>;
      const type = typeof r.type === 'string' ? (r.type as NotificationType) : 'daily_mirror';
      const userId = typeof r.userId === 'string' ? r.userId : 'unknown';
      if (type === 'feedback_prompt' && typeof r.outfitId === 'string') {
        return { type, userId, outfitId: r.outfitId, timestamp: r.timestamp as number | undefined };
      }
      if (type === 're_engagement') {
        return {
          type,
          userId,
          daysSinceLastUse: r.daysSinceLastUse as number | undefined,
          timestamp: r.timestamp as number | undefined,
        };
      }
      if (type === 'daily_mirror') {
        return { type, userId, timestamp: r.timestamp as number | undefined };
      }
      return { type, userId };
    }
    return { type: 'daily_mirror', userId: 'unknown' };
  }
}

