        <View style={styles.statItem}>
          <Text style={styles.statValue}>8.7</Text>
          <Text style={styles.statLabel}>Stil Puanı</Text>
        </View>
      </LinearGradient>
    </View>
  );

  return (
    <LinearGradient
      colors={[AynamodaColors.background.primary, AynamodaColors.background.secondary]}
      style={styles.container}
    >
      <StatusBar barStyle="dark-content" backgroundColor="transparent" translucent />

      {renderFloatingHeader()}

      <AnimatedScrollView
        style={styles.scrollView}
        contentContainerStyle={[
          styles.scrollContent,
          { paddingBottom: insets.bottom + DesignSystem.spacing.xxxl },
        ]}
        onScroll={scrollHandler}
        scrollEventThrottle={16}
        showsVerticalScrollIndicator={false}
      >
        {renderProfileHeader()}

        {/* Account Management */}
        <ProfileSection title="Hesap">
          <ProfileOption
            icon="person-outline"
            title="Kişisel Bilgiler"
            subtitle="Hesap detaylarınızı yönetin"
            onPress={() => {
              Alert.alert('Yakında Gelecek', 'Bu özellik yakında kullanıma sunulacak.');
            }}
          />
          <View style={styles.divider} />
          <ProfileOption
            icon="shield-checkmark-outline"
            title="Gizlilik ve Güvenlik"
