  fullHeight: {
    height: '100%',
  },
  overflowHidden: {
    overflow: 'hidden',
  },
  reducedMotionOpacity: {
    opacity: 1,
  },
  spinnerBase: {
    borderWidth: 2,
  },
  staticOpacity: {
    opacity: 0.6,
  },
  transparentTop: {
    borderTopColor: 'transparent',
  },
});

