// Gesture Animations - Touch-based interactive animations
import React, { useCallback, useRef } from 'react';
import { Animated, Dimensions, StyleSheet, ViewStyle } from 'react-native';
import {
  GestureHandlerRootView,
  PanGestureHandler,
  PanGestureHandlerGestureEvent,
  PinchGestureHandler,
  PinchGestureHandlerGestureEvent,
  State,
  TapGestureHandler,
  TapGestureHandlerGestureEvent,
} from 'react-native-gesture-handler';

import { useFadeAnimation } from '@/hooks/useAnimation';
import { SPRING, TIMING } from '@/theme/foundations/Animation';

const { width: _SCREEN_WIDTH, height: _SCREEN_HEIGHT } = Dimensions.get('window');

/**
 * Draggable Component with spring physics
 */
interface DraggableProps {
  children: React.ReactNode;
  style?: ViewStyle;
  onDragStart?: () => void;
  onDragEnd?: (x: number, y: number) => void;
  bounds?: {
    left?: number;
    right?: number;
    top?: number;
    bottom?: number;
  };
  snapToGrid?: boolean;
  gridSize?: number;
  disabled?: boolean;
}

export const Draggable: React.FC<DraggableProps> = ({
  children,
  style,
  onDragStart,
  onDragEnd,
  bounds,
  snapToGrid = false,
  gridSize = 20,
  disabled = false,
}) => {
  const translateX = useRef(new Animated.Value(0)).current;
  const translateY = useRef(new Animated.Value(0)).current;
  const scale = useRef(new Animated.Value(1)).current;
  const { isReducedMotionEnabled } = useFadeAnimation();

  const lastOffset = useRef({ x: 0, y: 0 });

  const onGestureEvent = Animated.event(
    [
      {
        nativeEvent: {
          translationX: translateX,
          translationY: translateY,
        },
      },
    ],
    { useNativeDriver: true },
  );

  const onHandlerStateChange = useCallback(
    (event: PanGestureHandlerGestureEvent) => {
      if (disabled || isReducedMotionEnabled) {
        return;
      }

      const { state, translationX, translationY } = event.nativeEvent;

      switch (state) {
        case State.BEGAN:
          if (onDragStart) {
            onDragStart();
          }

          // Scale up slightly to indicate drag start
          Animated.spring(scale, {
            toValue: 1.05,
            ...SPRING.gentle,
            useNativeDriver: true,
          }).start();
          break;

        case State.END:
        case State.CANCELLED:
          // Calculate final position
          let finalX = lastOffset.current.x + translationX;
          let finalY = lastOffset.current.y + translationY;

          // Apply bounds
          if (bounds) {
            if (bounds.left !== undefined) {
              finalX = Math.max(bounds.left, finalX);
            }
            if (bounds.right !== undefined) {
              finalX = Math.min(bounds.right, finalX);
            }
            if (bounds.top !== undefined) {
              finalY = Math.max(bounds.top, finalY);
            }
            if (bounds.bottom !== undefined) {
              finalY = Math.min(bounds.bottom, finalY);
            }
          }

          // Snap to grid if enabled
          if (snapToGrid) {
            finalX = Math.round(finalX / gridSize) * gridSize;
            finalY = Math.round(finalY / gridSize) * gridSize;
          }

          // Update last offset
          lastOffset.current = { x: finalX, y: finalY };

          // Animate to final position
          Animated.parallel([
            Animated.spring(translateX, {
              toValue: finalX,
              ...SPRING.gentle,
              useNativeDriver: true,
            }),
            Animated.spring(translateY, {
              toValue: finalY,
              ...SPRING.gentle,
              useNativeDriver: true,
            }),
            Animated.spring(scale, {
              toValue: 1,
              ...SPRING.gentle,
              useNativeDriver: true,
            }),
          ]).start();

          if (onDragEnd) {
            onDragEnd(finalX, finalY);
          }
          break;
      }
    },
    [
      disabled,
      isReducedMotionEnabled,
      onDragStart,
      onDragEnd,
      bounds,
      snapToGrid,
      gridSize,
      scale,
      translateX,
      translateY,
    ],
  );

  if (disabled || isReducedMotionEnabled) {
    return <Animated.View style={style}>{children}</Animated.View>;
  }

  return (
    <PanGestureHandler
      onGestureEvent={onGestureEvent}
      onHandlerStateChange={(event) => void onHandlerStateChange(event)}
    >
      <Animated.View
        style={[
          style,
          {
            transform: [{ translateX }, { translateY }, { scale }],
          },
        ]}
      >
        {children}
      </Animated.View>
    </PanGestureHandler>
  );
};

/**
 * Swipeable Component with directional callbacks
 */
interface SwipeableProps {
  children: React.ReactNode;
  style?: ViewStyle;
  onSwipeLeft?: () => void;
  onSwipeRight?: () => void;
  onSwipeUp?: () => void;
  onSwipeDown?: () => void;
  threshold?: number;
  disabled?: boolean;
}

export const Swipeable: React.FC<SwipeableProps> = ({
  children,
  style,
  onSwipeLeft,
  onSwipeRight,
  onSwipeUp,
  onSwipeDown,
  threshold = 50,
  disabled = false,
}) => {
  const translateX = useRef(new Animated.Value(0)).current;
  const translateY = useRef(new Animated.Value(0)).current;
  const { isReducedMotionEnabled } = useFadeAnimation();

  const onGestureEvent = Animated.event(
    [
      {
        nativeEvent: {
          translationX: translateX,
          translationY: translateY,
        },
      },
    ],
    { useNativeDriver: true },
  );

  const onHandlerStateChange = useCallback(
    (event: PanGestureHandlerGestureEvent) => {
      if (disabled) {
        return;
      }

      const {
        state,
        translationX,
        translationY,
        velocityX: _velocityX,
        velocityY: _velocityY,
      } = event.nativeEvent;

      if (state === State.END) {
        const absX = Math.abs(translationX);
        const absY = Math.abs(translationY);

        // Determine swipe direction
        if (absX > absY && absX > threshold) {
          // Horizontal swipe
          if (translationX > 0 && onSwipeRight) {
            onSwipeRight();
          } else if (translationX < 0 && onSwipeLeft) {
            onSwipeLeft();
          }
        } else if (absY > threshold) {
          // Vertical swipe
          if (translationY > 0 && onSwipeDown) {
            onSwipeDown();
          } else if (translationY < 0 && onSwipeUp) {
            onSwipeUp();
          }
        }

        // Reset position
        if (!isReducedMotionEnabled) {
          Animated.parallel([
            Animated.spring(translateX, {
              toValue: 0,
              ...SPRING.gentle,
              useNativeDriver: true,
            }),
            Animated.spring(translateY, {
              toValue: 0,
              ...SPRING.gentle,
              useNativeDriver: true,
            }),
          ]).start();
        }
      }
    },
    [
      disabled,
      isReducedMotionEnabled,
      onSwipeLeft,
      onSwipeRight,
      onSwipeUp,
      onSwipeDown,
      threshold,
      translateX,
      translateY,
    ],
  );

  if (disabled || isReducedMotionEnabled) {
    return <Animated.View style={style}>{children}</Animated.View>;
  }

  return (
    <PanGestureHandler
      onGestureEvent={onGestureEvent}
      onHandlerStateChange={(event) => void onHandlerStateChange(event)}
    >
      <Animated.View
        style={[
          style,
          {
            transform: [{ translateX }, { translateY }],
          },
        ]}
      >
        {children}
      </Animated.View>
    </PanGestureHandler>
  );
};

/**
 * Pinchable Component for zoom gestures
 */
interface PinchableProps {
  children: React.ReactNode;
  style?: ViewStyle;
  onPinchStart?: () => void;
  onPinchEnd?: (scale: number) => void;
  minScale?: number;
  maxScale?: number;
  disabled?: boolean;
}

export const Pinchable: React.FC<PinchableProps> = ({
  children,
  style,
  onPinchStart,
  onPinchEnd,
  minScale = 0.5,
  maxScale = 3,
  disabled = false,
}) => {
  const scale = useRef(new Animated.Value(1)).current;
  const { isReducedMotionEnabled } = useFadeAnimation();

  const lastScale = useRef(1);

  const onGestureEvent = Animated.event(
    [
      {
        nativeEvent: { scale },
      },
    ],
    { useNativeDriver: true },
  );

  const onHandlerStateChange = useCallback(
    (event: PinchGestureHandlerGestureEvent) => {
      if (disabled || isReducedMotionEnabled) {
        return;
      }

      const { state, scale: gestureScale } = event.nativeEvent;

      switch (state) {
        case State.BEGAN:
          if (onPinchStart) {
            onPinchStart();
          }
          break;

        case State.END:
        case State.CANCELLED:
          let finalScale = lastScale.current * gestureScale;

          // Apply scale bounds
          finalScale = Math.max(minScale, Math.min(maxScale, finalScale));

          lastScale.current = finalScale;

          // Animate to final scale
          Animated.spring(scale, {
            toValue: finalScale,
            ...SPRING.gentle,
            useNativeDriver: true,
          }).start();

          if (onPinchEnd) {
            onPinchEnd(finalScale);
          }
          break;
      }
    },
    [disabled, isReducedMotionEnabled, onPinchStart, onPinchEnd, minScale, maxScale, scale],
  );

  if (disabled || isReducedMotionEnabled) {
    return <Animated.View style={style}>{children}</Animated.View>;
  }

  return (
    <PinchGestureHandler
      onGestureEvent={onGestureEvent}
      onHandlerStateChange={onHandlerStateChange}
    >
      <Animated.View
        style={[
          style,
          {
            transform: [{ scale }],
          },
        ]}
      >
        {children}
      </Animated.View>
    </PinchGestureHandler>
  );
};

/**
 * Tappable Component with press animations
 */
interface TappableProps {
  children: React.ReactNode;
  style?: ViewStyle;
  onTap?: () => void;
  onDoubleTap?: () => void;
  onLongPress?: () => void;
  pressScale?: number;
  disabled?: boolean;
}

export const Tappable: React.FC<TappableProps> = ({
  children,
  style,
  onTap,
  onDoubleTap,
  onLongPress,
  pressScale = 0.95,
  disabled = false,
}) => {
  const scale = useRef(new Animated.Value(1)).current;
  const { isReducedMotionEnabled } = useFadeAnimation();

  const doubleTapRef = useRef<TapGestureHandler>(null);

  const animatePress = useCallback(
    (toValue: number) => {
      if (isReducedMotionEnabled) {
        return;
      }

      Animated.spring(scale, {
        toValue,
        ...SPRING.gentle,
        useNativeDriver: true,
      }).start();
    },
    [isReducedMotionEnabled, scale],
  );

  const onSingleTapEvent = useCallback(
    (event: TapGestureHandlerGestureEvent) => {
      if (disabled) {
        return;
      }

      const { state } = event.nativeEvent;

      if (state === State.BEGAN) {
        animatePress(pressScale);
      } else if (state === State.END) {
        animatePress(1);
        if (onTap) {
          onTap();
        }
      } else if (state === State.CANCELLED || state === State.FAILED) {
        animatePress(1);
      }
    },
    [disabled, animatePress, pressScale, onTap],
  );

  const onDoubleTapEvent = useCallback(
    (event: TapGestureHandlerGestureEvent) => {
      if (disabled) {
        return;
      }

      const { state } = event.nativeEvent;

      if (state === State.ACTIVE && onDoubleTap) {
        onDoubleTap();
      }
    },
    [disabled, onDoubleTap],
  );

  if (disabled) {
    return <Animated.View style={[style, styles.disabledOpacity]}>{children}</Animated.View>;
  }

  const TapComponent = onDoubleTap ? (
    <TapGestureHandler ref={doubleTapRef} onHandlerStateChange={onDoubleTapEvent} numberOfTaps={2}>
      <TapGestureHandler onHandlerStateChange={onSingleTapEvent} waitFor={doubleTapRef}>
        <Animated.View
          style={[
            style,
            {
              transform: isReducedMotionEnabled ? [] : [{ scale }],
            },
          ]}
        >
          {children}
        </Animated.View>
      </TapGestureHandler>
    </TapGestureHandler>
  ) : (
    <TapGestureHandler onHandlerStateChange={onSingleTapEvent}>
      <Animated.View
        style={[
          style,
          {
            transform: isReducedMotionEnabled ? [] : [{ scale }],
          },
        ]}
      >
        {children}
      </Animated.View>
    </TapGestureHandler>
  );

  return TapComponent;
};

/**
 * Pull to Refresh Component
 */
interface PullToRefreshProps {
  children: React.ReactNode;
  onRefresh: () => Promise<void>;
  refreshing?: boolean;
  threshold?: number;
  style?: ViewStyle;
}

export const PullToRefresh: React.FC<PullToRefreshProps> = ({
  children,
  onRefresh,
  refreshing: _refreshing = false,
  threshold = 80,
  style,
}) => {
  const translateY = useRef(new Animated.Value(0)).current;
  const refreshOpacity = useRef(new Animated.Value(0)).current;
  const { isReducedMotionEnabled } = useFadeAnimation();

  const isRefreshing = useRef(false);

  const onGestureEvent = Animated.event(
    [
      {
        nativeEvent: {
          translationY: translateY,
        },
      },
    ],
    { useNativeDriver: true },
  );

  const onHandlerStateChange = useCallback(
    async (event: PanGestureHandlerGestureEvent) => {
      if (isReducedMotionEnabled) {
        return;
      }

      const { state, translationY } = event.nativeEvent;

      if (state === State.END && translationY > threshold && !isRefreshing.current) {
        isRefreshing.current = true;

        // Show refresh indicator
        Animated.parallel([
          Animated.spring(translateY, {
            toValue: threshold,
            ...SPRING.gentle,
            useNativeDriver: true,
          }),
          Animated.timing(refreshOpacity, {
            toValue: 1,
            duration: TIMING.quick,
            useNativeDriver: true,
          }),
        ]).start();

        try {
          await onRefresh();
        } finally {
          // Hide refresh indicator
          Animated.parallel([
            Animated.spring(translateY, {
              toValue: 0,
              ...SPRING.gentle,
              useNativeDriver: true,
            }),
            Animated.timing(refreshOpacity, {
              toValue: 0,
              duration: TIMING.quick,
              useNativeDriver: true,
            }),
          ]).start(() => {
            isRefreshing.current = false;
          });
        }
      } else if (state === State.END) {
        // Reset position
        Animated.spring(translateY, {
          toValue: 0,
          ...SPRING.gentle,
          useNativeDriver: true,
        }).start();
      }
    },
    [isReducedMotionEnabled, threshold, onRefresh, refreshOpacity, translateY],
  );

  if (isReducedMotionEnabled) {
    return <Animated.View style={[{ flex: 1 }, style]}>{children}</Animated.View>;
  }

  return (
    <PanGestureHandler
      onGestureEvent={onGestureEvent}
      onHandlerStateChange={(event) => void onHandlerStateChange(event)}
    >
      <Animated.View
        style={[
          { flex: 1 },
          style,
          {
            transform: [{ translateY }],
          },
        ]}
      >
        {/* Refresh Indicator */}
        <Animated.View
          style={[
            styles.refreshIndicator,
            {
              top: -threshold,
              height: threshold,
              opacity: refreshOpacity,
            },
          ]}
        >
          {/* Add your refresh indicator here */}
        </Animated.View>

        {children}
      </Animated.View>
    </PanGestureHandler>
  );
};

/**
 * Gesture Root Provider
 */
interface GestureRootProps {
  children: React.ReactNode;
}

export const GestureRoot: React.FC<GestureRootProps> = ({ children }) => {
  return <GestureHandlerRootView style={styles.gestureRoot}>{children}</GestureHandlerRootView>;
};

const styles = StyleSheet.create({
  disabledOpacity: {
    opacity: 0.6,
  },
  gestureRoot: {
    flex: 1,
  },
  refreshIndicator: {
    alignItems: 'center',
    justifyContent: 'center',
    left: 0,
    position: 'absolute',
    right: 0,
  },
});

export default {
  Draggable,
  Swipeable,
  Pinchable,
  Tappable,
  PullToRefresh,
  GestureRoot,
};

// Page Transitions - Screen-to-screen animation system
import React, { useEffect, useRef } from 'react';
import { Animated, Dimensions, StyleSheet, ViewStyle } from 'react-native';

import { useFadeAnimation, useSpringAnimation } from '@/hooks/useAnimation';
import { AnimationSystem, EASING, TIMING } from '@/theme/foundations/Animation';

const { width: SCREEN_WIDTH, height: SCREEN_HEIGHT } = Dimensions.get('window');

/**
 * Base Page Transition Container
 */
interface PageTransitionProps {
  children: React.ReactNode;
  isActive: boolean;
  transitionType?: 'fade' | 'slide' | 'scale' | 'push' | 'modal';
  direction?: 'left' | 'right' | 'up' | 'down';
  duration?: number;
  style?: ViewStyle;
  onTransitionComplete?: () => void;
}

export const PageTransition: React.FC<PageTransitionProps> = ({
  children,
  isActive,
  transitionType = 'slide',
  direction = 'right',
  duration = TIMING.standard,
  style,
  onTransitionComplete,
}) => {
  const { opacity, fadeIn, fadeOut, isReducedMotionEnabled } = useFadeAnimation();
  const slideAnim = useRef(
    new Animated.Value(isActive ? 0 : getInitialSlideValue(direction)),
  ).current;
  const scaleAnim = useRef(new Animated.Value(isActive ? 1 : 0.8)).current;

  function getInitialSlideValue(dir: string): number {
    switch (dir) {
      case 'left':
        return -SCREEN_WIDTH;
      case 'right':
        return SCREEN_WIDTH;
      case 'up':
        return -SCREEN_HEIGHT;
      case 'down':
        return SCREEN_HEIGHT;
      default:
        return SCREEN_WIDTH;
    }
  }

  function getSlideTransform() {
    if (direction === 'left' || direction === 'right') {
      return { translateX: slideAnim };
    }
    return { translateY: slideAnim };
  }

  useEffect(() => {
    if (isReducedMotionEnabled) {
      // Skip animations for accessibility
      if (onTransitionComplete) {
        onTransitionComplete();
      }
      return;
    }

    const animations: Animated.CompositeAnimation[] = [];

    if (isActive) {
      // Entering animations
      switch (transitionType) {
        case 'fade':
          fadeIn(onTransitionComplete);
          break;

        case 'slide':
          animations.push(
            Animated.timing(slideAnim, {
              toValue: 0,
              duration,
              easing: EASING.organic.gentle,
              useNativeDriver: true,
            }),
          );
          break;

        case 'scale':
          animations.push(
            Animated.spring(scaleAnim, {
              toValue: 1,
              ...AnimationSystem.spring.gentle,
              useNativeDriver: true,
            }),
          );
          fadeIn();
          break;

        case 'push':
          animations.push(
            Animated.timing(slideAnim, {
              toValue: 0,
              duration,
              easing: EASING.enter,
              useNativeDriver: true,
            }),
          );
          break;

        case 'modal':
          animations.push(
            Animated.spring(slideAnim, {
              toValue: 0,
              ...AnimationSystem.spring.bouncy,
              useNativeDriver: true,
            }),
          );
          fadeIn();
          break;
      }
    } else {
      // Exiting animations
      switch (transitionType) {
        case 'fade':
          fadeOut(onTransitionComplete);
          break;

        case 'slide':
          animations.push(
            Animated.timing(slideAnim, {
              toValue: getInitialSlideValue(direction),
              duration,
              easing: EASING.organic.gentle,
              useNativeDriver: true,
            }),
          );
          break;

        case 'scale':
          animations.push(
            Animated.spring(scaleAnim, {
              toValue: 0.8,
              ...AnimationSystem.spring.gentle,
              useNativeDriver: true,
            }),
          );
          fadeOut();
          break;

        case 'push':
          animations.push(
            Animated.timing(slideAnim, {
              toValue: -getInitialSlideValue(direction),
              duration,
              easing: EASING.exit,
              useNativeDriver: true,
            }),
          );
          break;

        case 'modal':
          animations.push(
            Animated.timing(slideAnim, {
              toValue: SCREEN_HEIGHT,
              duration: TIMING.quick,
              easing: EASING.exit,
              useNativeDriver: true,
            }),
          );
          fadeOut();
          break;
      }
    }

    if (animations.length > 0) {
      Animated.parallel(animations).start((finished) => {
        if (finished && onTransitionComplete) {
          onTransitionComplete();
        }
      });
    }
  }, [
    isActive,
    transitionType,
    direction,
    duration,
    isReducedMotionEnabled,
    fadeIn,
    fadeOut,
    onTransitionComplete,
    scaleAnim,
    slideAnim,
  ]);

  const getAnimatedStyle = (): ViewStyle => {
    if (isReducedMotionEnabled) {
      return { ...styles.reducedMotionStyle, opacity: isActive ? 1 : 0 };
    }

    const baseStyle = styles.baseStyle;

    switch (transitionType) {
      case 'fade':
        return {
          ...baseStyle,
          opacity,
        };

      case 'slide':
      case 'push':
        return {
          ...baseStyle,
          transform: [getSlideTransform()],
        };

      case 'scale':
        return {
          ...baseStyle,
          opacity,
          transform: [{ scale: scaleAnim }],
        };

      case 'modal':
        return {
          ...baseStyle,
          opacity,
          transform: [{ translateY: slideAnim }],
        };

      default:
        return baseStyle;
    }
  };

  return <Animated.View style={[getAnimatedStyle(), style]}>{children}</Animated.View>;
};

/**
 * Stack Navigator Transition
 */
interface StackTransitionProps {
  children: React.ReactNode;
  isActive: boolean;
  index: number;
  direction?: 'horizontal' | 'vertical';
  style?: ViewStyle;
}

export const StackTransition: React.FC<StackTransitionProps> = ({
  children,
  isActive,
  index,
  direction = 'horizontal',
  style,
}) => {
  const translateAnim = useRef(new Animated.Value(isActive ? 0 : SCREEN_WIDTH)).current;
  const { isReducedMotionEnabled } = useFadeAnimation();

  useEffect(() => {
    if (isReducedMotionEnabled) {
      return;
    }

    Animated.timing(translateAnim, {
      toValue: isActive ? 0 : direction === 'horizontal' ? SCREEN_WIDTH : SCREEN_HEIGHT,
      duration: TIMING.standard,
      easing: EASING.organic.gentle,
      useNativeDriver: true,
    }).start();
  }, [isActive, direction, isReducedMotionEnabled, translateAnim]);

  const transform =
    direction === 'horizontal' ? { translateX: translateAnim } : { translateY: translateAnim };

  return (
    <Animated.View
      style={[
        {
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          transform: isReducedMotionEnabled ? [] : [transform],
        },
        style,
      ]}
    >
      {children}
    </Animated.View>
  );
};

/**
 * Tab Navigator Transition
 */
interface TabTransitionProps {
  children: React.ReactNode;
  isActive: boolean;
  tabIndex: number;
  style?: ViewStyle;
}

export const TabTransition: React.FC<TabTransitionProps> = ({
  children,
  isActive,
  tabIndex,
  style,
}) => {
  const { opacity, fadeIn, fadeOut, isReducedMotionEnabled } = useFadeAnimation();
  const scaleAnim = useRef(new Animated.Value(isActive ? 1 : 0.95)).current;

  useEffect(() => {
    if (isReducedMotionEnabled) {
      return;
    }

    if (isActive) {
      fadeIn();
      Animated.spring(scaleAnim, {
        toValue: 1,
        ...AnimationSystem.spring.gentle,
        useNativeDriver: true,
      }).start();
    } else {
      fadeOut();
      Animated.spring(scaleAnim, {
        toValue: 0.95,
        ...AnimationSystem.spring.gentle,
        useNativeDriver: true,
      }).start();
    }
  }, [isActive, isReducedMotionEnabled, fadeIn, fadeOut, scaleAnim]);

  return (
    <Animated.View
      style={[
        {
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          opacity: isReducedMotionEnabled ? (isActive ? 1 : 0) : opacity,
          transform: isReducedMotionEnabled ? [] : [{ scale: scaleAnim }],
        },
        style,
      ]}
    >
      {children}
    </Animated.View>
  );
};

/**
 * Modal Transition
 */
interface ModalTransitionProps {
  children: React.ReactNode;
  visible: boolean;
  animationType?: 'slide' | 'fade' | 'scale';
  onAnimationComplete?: () => void;
  style?: ViewStyle;
}

export const ModalTransition: React.FC<ModalTransitionProps> = ({
  children,
  visible,
  animationType = 'slide',
  onAnimationComplete,
  style,
}) => {
  const { opacity, fadeIn, fadeOut, isReducedMotionEnabled } = useFadeAnimation();
  const slideAnim = useRef(new Animated.Value(visible ? 0 : SCREEN_HEIGHT)).current;
  const scaleAnim = useRef(new Animated.Value(visible ? 1 : 0.8)).current;

  useEffect(() => {
    if (isReducedMotionEnabled) {
      if (onAnimationComplete) {
        onAnimationComplete();
      }
      return;
    }

    const animations: Animated.CompositeAnimation[] = [];

    if (visible) {
      switch (animationType) {
        case 'fade':
          fadeIn(onAnimationComplete);
          break;

        case 'slide':
          animations.push(
            Animated.spring(slideAnim, {
              toValue: 0,
              ...AnimationSystem.spring.bouncy,
              useNativeDriver: true,
            }),
          );
          fadeIn();
          break;

        case 'scale':
          animations.push(
            Animated.spring(scaleAnim, {
              toValue: 1,
              ...AnimationSystem.spring.gentle,
              useNativeDriver: true,
            }),
          );
          fadeIn();
          break;
      }
    } else {
      switch (animationType) {
        case 'fade':
          fadeOut(onAnimationComplete);
          break;

        case 'slide':
          animations.push(
            Animated.timing(slideAnim, {
              toValue: SCREEN_HEIGHT,
              duration: TIMING.quick,
              easing: EASING.exit,
              useNativeDriver: true,
            }),
          );
          fadeOut();
          break;

        case 'scale':
          animations.push(
            Animated.timing(scaleAnim, {
              toValue: 0.8,
              duration: TIMING.quick,
              easing: EASING.exit,
              useNativeDriver: true,
            }),
          );
          fadeOut();
          break;
      }
    }

    if (animations.length > 0) {
      Animated.parallel(animations).start((finished) => {
        if (finished && onAnimationComplete) {
          onAnimationComplete();
        }
      });
    }
  }, [
    visible,
    animationType,
    isReducedMotionEnabled,
    fadeIn,
    fadeOut,
    onAnimationComplete,
    scaleAnim,
    slideAnim,
  ]);

  const getAnimatedStyle = (): ViewStyle => {
    if (isReducedMotionEnabled) {
      return { ...styles.reducedMotionStyle, opacity: visible ? 1 : 0 };
    }

    const baseStyle = styles.baseStyle;

    switch (animationType) {
      case 'fade':
        return {
          ...baseStyle,
          opacity,
        };

      case 'slide':
        return {
          ...baseStyle,
          opacity,
          transform: [{ translateY: slideAnim }],
        };

      case 'scale':
        return {
          ...baseStyle,
          opacity,
          transform: [{ scale: scaleAnim }],
        };

      default:
        return baseStyle;
    }
  };

  if (!visible && isReducedMotionEnabled) {
    return null;
  }

  return <Animated.View style={[getAnimatedStyle(), style]}>{children}</Animated.View>;
};

/**
 * Shared Element Transition (for hero animations)
 */
interface SharedElementTransitionProps {
  children: React.ReactNode;
  sharedElementId: string;
  isSource: boolean;
  style?: ViewStyle;
}

export const SharedElementTransition: React.FC<SharedElementTransitionProps> = ({
  children,
  sharedElementId,
  isSource,
  style,
}) => {
  const { animatedValue, springTo, isReducedMotionEnabled } = useSpringAnimation(isSource ? 1 : 0);
  const { opacity, fadeIn, fadeOut } = useFadeAnimation();

  useEffect(() => {
    if (isReducedMotionEnabled) {
      return;
    }

    if (isSource) {
      fadeIn();
      springTo(1, AnimationSystem.spring.gentle);
    } else {
      fadeOut();
      springTo(0, AnimationSystem.spring.gentle);
    }
  }, [isSource, isReducedMotionEnabled, fadeIn, fadeOut, springTo]);

  return (
    <Animated.View
      style={[
        {
          opacity: isReducedMotionEnabled ? (isSource ? 1 : 0) : opacity,
          transform: isReducedMotionEnabled ? [] : [{ scale: animatedValue }],
        },
        style,
      ]}
    >
      {children}
    </Animated.View>
  );
};

/**
 * Page Transition Manager - Handles multiple page states
 */
interface PageTransitionManagerProps {
  pages: React.ReactNode[];
  activeIndex: number;
  transitionType?: 'fade' | 'slide' | 'scale' | 'push' | 'modal';
  direction?: 'left' | 'right' | 'up' | 'down';
  style?: ViewStyle;
}

export const PageTransitionManager: React.FC<PageTransitionManagerProps> = ({
  pages,
  activeIndex,
  transitionType = 'slide',
  direction = 'right',
  style,
}) => {
  return (
    <Animated.View style={[{ flex: 1 }, style]}>
      {pages.map((page, index) => (
        <PageTransition
          key={index}
          isActive={index === activeIndex}
          transitionType={transitionType}
          direction={direction}
        >
          {page}
        </PageTransition>
      ))}
    </Animated.View>
  );
};

/**
 * Gesture-based Page Transition
 */
interface GesturePageTransitionProps {
  children: React.ReactNode;
  onSwipeLeft?: () => void;
  onSwipeRight?: () => void;
  onSwipeUp?: () => void;
  onSwipeDown?: () => void;
  style?: ViewStyle;
}

export const GesturePageTransition: React.FC<GesturePageTransitionProps> = ({
  children,
  onSwipeLeft,
  onSwipeRight,
  onSwipeUp,
  onSwipeDown,
  style,
}) => {
  const translateX = useRef(new Animated.Value(0)).current;
  const translateY = useRef(new Animated.Value(0)).current;
  const { isReducedMotionEnabled } = useFadeAnimation();

  // Note: In a real implementation, you would use react-native-gesture-handler
  // for proper gesture recognition. This is a simplified version.

  const _resetPosition = () => {
    if (isReducedMotionEnabled) {
      return;
    }

    Animated.parallel([
      Animated.spring(translateX, {
        toValue: 0,
        ...AnimationSystem.spring.gentle,
        useNativeDriver: true,
      }),
      Animated.spring(translateY, {
        toValue: 0,
        ...AnimationSystem.spring.gentle,
        useNativeDriver: true,
      }),
    ]).start();
  };

  return (
    <Animated.View
      style={[
        {
          flex: 1,
          transform: isReducedMotionEnabled ? [] : [{ translateX }, { translateY }],
        },
        style,
      ]}
    >
      {children}
    </Animated.View>
  );
};

const styles = StyleSheet.create({
  baseStyle: {
    bottom: 0,
    left: 0,
    position: 'absolute',
    right: 0,
    top: 0,
  },
  reducedMotionStyle: {},
});

export default PageTransition;

