/**
 * Input Atom
 *
 * The fundamental text input element in the design system.
 * Supports various input types and follows unified styling.
 */

import React, { useState } from 'react';
import { StyleSheet, Text, TextInput, TextInputProps, View } from 'react-native';

import { DesignSystem } from '@/theme/DesignSystem';
import { InputComponentProps } from '@/types/componentProps';

export interface InputProps
  extends Omit<InputComponentProps, 'leftIcon' | 'rightIcon' | 'variant'>,
    Omit<TextInputProps, 'style'> {
  value: string;
  onChangeText: (text: string) => void;
  placeholder?: string;
  label?: string;
  error?: string;
  hint?: string;
  variant?: 'default' | 'glass' | 'luxury' | 'minimal';
  size?: 'small' | 'medium' | 'large';
  leftIcon?: React.ReactNode;
  rightIcon?: React.ReactNode;
  multiline?: boolean;
  secureTextEntry?: boolean;
  disabled?: boolean;
  required?: boolean;
}

const Input: React.FC<InputProps> = ({
  value,
  onChangeText,
  placeholder,
  label,
  error,
  hint,
  variant = 'default',
  size = 'medium',
  leftIcon,
  rightIcon,
  multiline = false,
  secureTextEntry = false,
  disabled = false,
  required = false,
  style,
  inputStyle,
  labelStyle,
  errorStyle,
  testID,
  accessibilityLabel,
  ...props
}) => {
  const [isFocused, setIsFocused] = useState(false);

  const containerStyle = [styles.container, style];

  const inputContainerStyle = [
    styles.inputContainer,
    styles[variant],
    styles[size],
    isFocused && styles.focused,
    error && styles.error,
    disabled && styles.disabled,
  ];

  const textInputStyle = [
    styles.input,
    styles[`${size}Input`],
    multiline && styles.multiline,
    inputStyle,
  ];

  const labelTextStyle = [styles.label, required && styles.requiredLabel, labelStyle];

  const errorTextStyle = [styles.errorText, errorStyle];

  return (
    <View style={containerStyle}>
      {label && (
        <Text style={labelTextStyle}>
          {label}
          {required && <Text style={styles.asterisk}> *</Text>}
        </Text>
      )}

      <View style={inputContainerStyle}>
        {leftIcon && <View style={styles.iconContainer}>{leftIcon}</View>}

        <TextInput
          style={textInputStyle}
          value={value}
          onChangeText={onChangeText}
          placeholder={placeholder}
          placeholderTextColor={DesignSystem.colors.neutral.slate}
          multiline={multiline}
          secureTextEntry={secureTextEntry}
          editable={!disabled}
          onFocus={() => setIsFocused(true)}
          onBlur={() => setIsFocused(false)}
          testID={testID}
          accessibilityLabel={accessibilityLabel || label}
          {...props}
        />

        {rightIcon && <View style={styles.iconContainer}>{rightIcon}</View>}
      </View>

      {error && <Text style={errorTextStyle}>{error}</Text>}

      {hint && !error && <Text style={styles.hint}>{hint}</Text>}
    </View>
  );
};

