      borderRadius: DesignSystem.borderRadius.xl + 4,
      flex: 1,
    },
    glowContainer: {
      borderRadius: DesignSystem.borderRadius.xl + 4,
      bottom: -4,
      left: -4,
      position: 'absolute',
      right: -4,
      top: -4,
      zIndex: -1,
    },
    gradient: {
      alignItems: 'center',
      flexDirection: 'row',
      gap: DesignSystem.spacing.sm,
      justifyContent: 'center',
      minHeight: dimensions.isTablet ? 48 : 44,
      paddingHorizontal: dimensions.padding,
      paddingVertical: dimensions.padding * 0.75,
    },
    text: {
      ...DesignSystem.typography.button,
      fontSize: dimensions.fontSize,
      fontWeight: '600',
      textAlign: 'center',
    },
    textColor: {
      color: variantStyles.textColor,
    },
  });

import React, { useEffect } from 'react';
import { Dimensions, StyleSheet, View } from 'react-native';
import Animated, {
  Easing,
  interpolate,
  useAnimatedStyle,
  useSharedValue,
  withRepeat,
  withTiming,
} from 'react-native-reanimated';

import { DesignSystem } from '../../theme/DesignSystem';

const { width, height } = Dimensions.get('window');

interface CircleProps {
  style?: object;
  delay: number;
  size: number;
  initialX: number;
  initialY: number;
}

const Circle = ({ style, delay, size, initialX, initialY }: CircleProps) => {
  const progress = useSharedValue(0);

  useEffect(() => {
    progress.value = withRepeat(
      withTiming(1, { duration: 25000, easing: Easing.linear }),
      -1,
      true,
    );
  }, [progress]);

  const animatedStyle = useAnimatedStyle(() => {
    const translateX = interpolate(
      progress.value,
      [0, 1],
      [0, width / 4 - Math.random() * (width / 2)],
    );
    const translateY = interpolate(
      progress.value,
      [0, 1],
      [0, height / 4 - Math.random() * (height / 2)],
    );
    const scale = interpolate(progress.value, [0, 0.5, 1], [1, 1.2, 1]);
    const opacity = interpolate(progress.value, [0, 0.5, 1], [0.4, 0.6, 0.4]);

    return {
      transform: [{ translateX }, { translateY }, { scale }],
      opacity: opacity,
    };
  });

  return (
    <Animated.View
      style={[
        styles.circle,
        {
          width: size,
          height: size,
          borderRadius: size / 2,
          top: initialY,
          left: initialX,
        },
        style,
        animatedStyle,
      ]}
    />
  );
};

const AnimatedPastelCircleBackground = () => {
  return (
    <View style={[StyleSheet.absoluteFill, styles.background]}>
      <Circle
        style={{ backgroundColor: DesignSystem.colors.neutral[300] + '20' }}
        size={width * 1.6}
        initialX={-width * 0.3}
        initialY={-width * 0.8}
        delay={0}
      />
      <Circle
        style={{ backgroundColor: DesignSystem.colors.gold[300] + '15' }}
        size={width * 1.2}
        initialX={width * 0.1}
        initialY={height * 0.5}
        delay={1000}
      />
      <Circle
        style={{ backgroundColor: DesignSystem.colors.sage[300] + '10' }}
        size={width}
        initialX={-width * 0.5}
        initialY={height * 0.2}
        delay={2000}
      />
    </View>
  );
};

const styles = StyleSheet.create({
  background: {
    backgroundColor: DesignSystem.colors.background.primary,
    height: '100%',
    overflow: 'hidden',
    width: '100%',
  },
