export default AnimatedPastelCircleBackground;

/**
 * AnimatedWrapper Component
 * Reanimated transform property uyarılarını düzeltmek için wrapper component
 * Layout animasyonları ile transform özelliklerini güvenli şekilde kullanır
 */
import React from 'react';
import { TextStyle, View, ViewStyle } from 'react-native';
import Animated, { AnimatedStyle } from 'react-native-reanimated';

interface AnimatedWrapperProps {
  children: React.ReactNode;
  style?: ViewStyle | AnimatedStyle<ViewStyle>;
  animatedStyle?: AnimatedStyle<ViewStyle>;
}

interface AnimatedTextWrapperProps {
  children: React.ReactNode;
  style?: TextStyle | AnimatedStyle<TextStyle>;
  animatedStyle?: AnimatedStyle<TextStyle>;
}

// Animated View Wrapper - Transform uyarılarını önler
const AnimatedWrapper: React.FC<AnimatedWrapperProps> = ({ children, style, animatedStyle }) => {
  return (
    <View style={style as any}>
      <Animated.View style={(animatedStyle as any) || {}}>{children}</Animated.View>
    </View>
  );
};

// Animated Text Wrapper - Transform uyarılarını önler
const AnimatedTextWrapper: React.FC<AnimatedTextWrapperProps> = ({
  children,
  style,
  animatedStyle,
}) => {
  return (
    <View>
      <Animated.Text style={[style as any, animatedStyle]}>{children}</Animated.Text>
    </View>
  );
};

// Safe Animated View - Layout animasyonları için güvenli
const SafeAnimatedView: React.FC<AnimatedWrapperProps> = ({ children, style, animatedStyle }) => {
  return (
    <Animated.View style={style as any}>
      <View style={(animatedStyle as any) || {}}>{children}</View>
    </Animated.View>
  );
};

export { AnimatedTextWrapper, AnimatedWrapper, SafeAnimatedView };

import { Ionicons } from '@expo/vector-icons';
import React, { Component, ReactNode } from 'react';
import { StyleSheet, Text, TouchableOpacity, View } from 'react-native';

import { DesignSystem } from '@/theme/DesignSystem';
import { errorInDev } from '@/utils/consoleSuppress';

// Type declaration for __DEV__ global
declare const __DEV__: boolean;

interface Props {
  children: ReactNode;
  fallback?: ReactNode;
}

interface State {
  hasError: boolean;
  error?: Error;
}

export default class ErrorBoundary extends Component<Props, State> {
  constructor(props: Props) {
    super(props);
    this.state = { hasError: false };
  }

  static getDerivedStateFromError(error: Error): State {
    // Update state so the next render will show the fallback UI
    return { hasError: true, error };
  }

  componentDidCatch(error: Error, errorInfo: React.ErrorInfo) {
    // Log the error for debugging
    errorInDev('ErrorBoundary caught an error:', error, errorInfo);

    // In development, you might want to send this to a crash reporting service
    if (__DEV__) {
      console.log('Error details:', {
        message: error.message,
        stack: error.stack,
        componentStack: errorInfo.componentStack,
      });
    }
  }

  handleRetry = () => {
    this.setState({ hasError: false, error: undefined });
  };

  render() {
    if (this.state.hasError) {
      // Custom fallback UI
      if (this.props.fallback) {
        return this.props.fallback;
      }

      // Default fallback UI
      return (
        <View style={styles.container}>
          <View style={styles.content}>
            <View style={styles.iconContainer}>
              <Ionicons name="warning-outline" size={48} color={DesignSystem.colors.gold[500]} />
            </View>

            <Text style={styles.title}>Something went wrong</Text>
            <Text style={styles.subtitle}>
              We encountered an unexpected error. Don't worry, your data is safe.
            </Text>

            {__DEV__ && this.state.error && (
              <View style={styles.errorDetails}>
                <Text style={styles.errorText}>{this.state.error.message}</Text>
              </View>
            )}

            <TouchableOpacity
              style={styles.retryButton}
              onPress={this.handleRetry}
              activeOpacity={0.8}
              accessibilityRole="button"
              accessibilityLabel="Try again"
              accessibilityHint="Retry the operation that failed"
            >
              <Text style={styles.retryButtonText}>Try Again</Text>
            </TouchableOpacity>
          </View>
        </View>
      );
    }

    return this.props.children;
  }
}

