  isSelected = false,
}) => {
  const imageUri = item.imageUrl || item.imageUri || '';
  const displayPrice = item.purchasePrice || item.price;
  const brandName = item.brand || 'AYNAMODA';

  // Premium gradient colors based on category
  const getGradientColors = () => {
