      case 'shoes':
        return ['#F8F0E8', '#F0E0D0', '#E8D0B8'] as const;
      case 'accessories':
        return ['#F0E8F8', '#E0D0F0', '#D0B8E8'] as const;
      default:
        return ['#F5F5F5', '#EEEEEE', '#E0E0E0'] as const;
    }
  };

  const gradientColors = getGradientColors();

  return (
    <TouchableOpacity
      style={[styles.card, style, isSelected && styles.selectedCard]}
      onPress={onPress}
      activeOpacity={0.95}
      accessibilityRole="button"
      accessibilityLabel={`${brandName} ${item.name || item.category}`}
      accessibilityHint="Tap to view item details"
    >
      <LinearGradient
        colors={gradientColors}
        style={styles.gradientBackground}
        start={{ x: 0, y: 0 }}
        end={{ x: 1, y: 1 }}
      >
        {/* Premium Card Header */}
        <View style={styles.cardHeader}>
          <View style={styles.brandContainer}>
            <Text style={styles.brandText}>{brandName}</Text>
            <View style={styles.premiumBadge}>
              <Ionicons name="diamond-outline" size={12} color="#8B5A3C" />
            </View>
          </View>
          {onFavoriteToggle && (
            <TouchableOpacity
              onPress={onFavoriteToggle}
              style={styles.favoriteButton}
              hitSlop={{ top: 10, bottom: 10, left: 10, right: 10 }}
              accessibilityRole="button"
              accessibilityLabel={isSelected ? 'Remove from favorites' : 'Add to favorites'}
              accessibilityHint={
                isSelected
                  ? 'Tap to remove this item from your favorites'
                  : 'Tap to add this item to your favorites'
              }
              accessibilityState={{ selected: isSelected }}
            >
              <Ionicons
                name={isSelected ? 'heart' : 'heart-outline'}
                size={20}
                color={isSelected ? '#D4A574' : '#8B5A3C'}
              />
            </TouchableOpacity>
          )}
        </View>

        {/* Premium Image Container */}
        <View style={styles.imageContainer}>
          <Image source={{ uri: imageUri }} style={styles.image} resizeMode="cover" />

          {/* Category Badge */}
          <View style={styles.categoryBadge}>
            <Text style={styles.categoryText}>{item.category.toUpperCase()}</Text>
          </View>
        </View>

        {/* Premium Card Footer */}
        <View style={styles.cardFooter}>
          <View style={styles.itemInfo}>
            <Text style={styles.itemName} numberOfLines={2}>
              {item.name || 'Elegant Piece'}
            </Text>

            {/* Color Indicators */}
            {item.colors && item.colors.length > 0 && (
              <View style={styles.colorsContainer}>
                {item.colors.slice(0, 3).map((color, index) => (
                  <View
                    key={index}
                    style={[styles.colorDot, { backgroundColor: color.toLowerCase() }]}
                  />
                ))}
                {item.colors.length > 3 && (
                  <Text style={styles.moreColors}>+{item.colors.length - 3}</Text>
                )}
              </View>
            )}
          </View>

          {/* Price Display */}
          {displayPrice && (
            <View style={styles.priceContainer}>
              <Text style={styles.priceText}>${displayPrice.toFixed(0)}</Text>
              <Text style={styles.currencySymbol}>�</Text>
            </View>
          )}
        </View>

        {/* Premium Selection Indicator */}
        {isSelected && (
          <View style={styles.selectionOverlay}>
            <View style={styles.selectionIndicator}>
              <Ionicons name="checkmark" size={16} color="#FFFFFF" />
            </View>
          </View>
        )}
      </LinearGradient>
    </TouchableOpacity>
  );
};

