import { Ionicons } from '@expo/vector-icons';
import React, { useState } from 'react';
import { StyleSheet, TouchableOpacity, View } from 'react-native';
import Animated, {
  Easing,
  useAnimatedStyle,
  useSharedValue,
  withSequence,
  withTiming,
} from 'react-native-reanimated';

import WaveOfLight from '@/components/luxury/WaveOfLight';
import { DesignSystem } from '@/theme/DesignSystem';

interface ElegantLikeButtonProps {
  isLiked: boolean;
  onPress: () => void;
  size?: number;
}

export const ElegantLikeButton: React.FC<ElegantLikeButtonProps> = ({
  isLiked,
  onPress,
  size = 24,
}) => {
  const [showWave, setShowWave] = useState(false);
  const heartScale = useSharedValue(1);
  const heartRotation = useSharedValue(0);

  const handlePress = () => {
    // Trigger heart animation
    heartScale.value = withSequence(
      withTiming(1.2, {
        duration: DesignSystem.motion.duration.quick,
        easing: Easing.out(Easing.cubic),
      }),
      withTiming(1, {
        duration: DesignSystem.motion.duration.smooth,
        easing: Easing.out(Easing.cubic),
      }),
    );

    heartRotation.value = withSequence(
      withTiming(15, {
        duration: DesignSystem.motion.duration.quick,
        easing: Easing.out(Easing.cubic),
      }),
      withTiming(0, {
        duration: DesignSystem.motion.duration.smooth,
        easing: Easing.out(Easing.cubic),
      }),
    );

    // Trigger wave animation only when liking (not unliking)
    if (!isLiked) {
      setShowWave(true);
    }

    onPress();
  };

  const heartAnimatedStyle = useAnimatedStyle(() => ({
    transform: [{ scale: heartScale.value }, { rotate: `${heartRotation.value}deg` }],
  }));

  const handleWaveComplete = () => {
    setShowWave(false);
  };

  return (
    <View style={styles.container}>
      <TouchableOpacity
        style={[styles.button, { height: size + 16, width: size + 16 }]}
        onPress={handlePress}
        activeOpacity={0.8}
      >
        <Animated.View style={heartAnimatedStyle}>
          <Ionicons
            name={isLiked ? 'heart' : 'heart-outline'}
            size={size}
            color={isLiked ? DesignSystem.colors.gold[500] : DesignSystem.colors.neutral.charcoal}
            style={styles.heartIcon}
          />
        </Animated.View>
      </TouchableOpacity>

      {showWave && (
        <WaveOfLight
          isActive={showWave}
          size={size * 2.5}
          onAnimationComplete={handleWaveComplete}
        />
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  button: {
    alignItems: 'center',
    backgroundColor: 'transparent',
    borderRadius: 20,
    justifyContent: 'center',
  },

  container: {
    alignItems: 'center',
    justifyContent: 'center',
    position: 'relative',
  },
  heartIcon: {
    // Thin, elegant line-art style
    textShadowColor: 'rgba(0, 0, 0, 0.1)',
    textShadowOffset: { width: 0, height: 0.5 },
    textShadowRadius: 1,
  },
});

export default ElegantLikeButton;

import React, { useEffect, useRef } from 'react';
import {
  Dimensions,
  ScrollView,
  StyleSheet,
  TouchableOpacity,
  View,
  ViewStyle,
} from 'react-native';
import type { SharedValue } from 'react-native-reanimated';
import Animated, {
  Extrapolate,
  interpolate,
  runOnJS,
  useAnimatedScrollHandler,
  useAnimatedStyle,
  useSharedValue,
} from 'react-native-reanimated';

import { DesignSystem } from '@/theme/DesignSystem';

interface Tab {
  id: string;
  label: string;
  content?: React.ReactNode;
}

interface ElegantTabsProps {
  tabs: Tab[];
  activeTab: string;
  onTabPress: (tabId: string) => void;
  style?: ViewStyle;
  contentStyle?: ViewStyle;
  showContent?: boolean;
}

const { width: screenWidth } = Dimensions.get('window');

type TabLabelProps = {
  index: number;
  isActive: boolean;
  label: string;
  scrollX: SharedValue<number>;
  tabWidth: number;
};

const TabLabel: React.FC<TabLabelProps> = ({ index, isActive, label, scrollX, tabWidth }) => {
  const animatedStyle = useAnimatedStyle(() => {
    const progress = scrollX.value / tabWidth;
    const distance = Math.abs(progress - index);
    const opacity = interpolate(distance, [0, 1], [1, 0.6], Extrapolate.CLAMP);
    const scale = interpolate(distance, [0, 1], [1, 0.95], Extrapolate.CLAMP);

    return {
      opacity,
      transform: [{ scale }],
    };
  });

  return (
    <Animated.Text style={[styles.tabLabel, animatedStyle, isActive && styles.activeTabLabel]}>
      {label}
    </Animated.Text>
  );
};

export const ElegantTabs: React.FC<ElegantTabsProps> = ({
  tabs,
  activeTab,
  onTabPress,
  style,
  contentStyle,
  showContent = false,
}) => {
  // Single source of truth for scroll position
  const scrollX = useSharedValue(0);
  const scrollViewRef = useRef<ScrollView>(null);

  const tabWidth = screenWidth;
  const activeIndex = tabs.findIndex((tab) => tab.id === activeTab);

  // Synchronized spring configuration for perfect harmony
  const SPRING_CONFIG = {
    damping: 20,
    stiffness: 300,
    mass: 1,
  };

  // Scroll handler for 1-to-1 synchronization
  const scrollHandler = useAnimatedScrollHandler({
    onScroll: (event) => {
      scrollX.value = event.contentOffset.x;
    },
  });

  // Handle tab press with synchronized animation
  const handleTabPress = (tabId: string, index: number) => {
    // Scroll content to new position
    scrollViewRef.current?.scrollTo({
      x: index * tabWidth,
      animated: true,
    });

    // Update active tab
    runOnJS(onTabPress)(tabId);
  };

  // Sync scroll position when activeTab changes externally
  useEffect(() => {
    if (scrollViewRef.current) {
      scrollViewRef.current.scrollTo({
        x: activeIndex * tabWidth,
        animated: true,
      });
    }
  }, [activeIndex, tabWidth]);

  // Indicator animation synchronized with scroll
  const indicatorStyle = useAnimatedStyle(() => {
    // Calculate indicator position based on scroll position
    const indicatorPosition = interpolate(
      scrollX.value,
      tabs.map((_, index) => index * tabWidth),
      tabs.map((_, index) => (index * screenWidth) / tabs.length),
      Extrapolate.CLAMP,
    );

    // Dynamic width based on active tab label
    const progress = scrollX.value / tabWidth;
    const currentIndex = Math.round(progress);
    const activeTabLabel = tabs[currentIndex]?.label || '';
    const estimatedWidth = Math.min(activeTabLabel.length * 8 + 32, screenWidth / tabs.length - 16);

    return {
      transform: [
        {
          translateX: indicatorPosition + (screenWidth / tabs.length - estimatedWidth) / 2,
        },
      ],
      width: estimatedWidth,
    };
  });

  return (
    <View style={[styles.container, style]}>
      {/* Tab buttons */}
      <View style={styles.tabsContainer}>
        {tabs.map((tab, index) => (
          <TouchableOpacity
            key={tab.id}
            style={[styles.tab, { width: screenWidth / tabs.length }]}
            onPress={() => handleTabPress(tab.id, index)}
            activeOpacity={0.8}
          >
            <TabLabel
              index={index}
              isActive={activeTab === tab.id}
              label={tab.label}
              scrollX={scrollX}
              tabWidth={tabWidth}
            />
          </TouchableOpacity>
        ))}
      </View>

      {/* Synchronized animated indicator */}
      <Animated.View style={[styles.indicator, indicatorStyle]} />

      {/* Optional content area with synchronized scrolling */}
      {showContent && (
        <ScrollView
          ref={scrollViewRef}
          horizontal
          pagingEnabled
          showsHorizontalScrollIndicator={false}
          onScroll={scrollHandler}
          scrollEventThrottle={16}
          style={[styles.contentContainer, contentStyle]}
        >
          {tabs.map((tab, index) => (
            <View key={tab.id} style={[styles.contentPage, { width: tabWidth }]}>
              {tab.content}
            </View>
          ))}
        </ScrollView>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  activeTabLabel: {
    color: DesignSystem.colors.neutral.slate,
