/**
 * Modern Input Component
 * Enhanced input field with validation, animations, and multiple variants
 */

import React, { memo, useCallback, useRef, useState } from 'react';
import {
  NativeSyntheticEvent,
  StyleSheet,
  Text,
  TextInput,
  TextInputFocusEventData,
  TextInputProps,
  TextStyle,
  TouchableOpacity,
  View,
  ViewStyle,
} from 'react-native';
import Animated, {
  interpolate,
  interpolateColor,
  useAnimatedStyle,
  useSharedValue,
  withSpring,
  withTiming,
} from 'react-native-reanimated';

import { ModernDesignSystem } from '../../theme/ModernDesignSystem';

interface ModernInputProps extends Omit<TextInputProps, 'style'> {
  label?: string;
  error?: string;
  hint?: string;
  variant?: 'outlined' | 'filled' | 'underlined';
  size?: 'small' | 'medium' | 'large';
  leftIcon?: React.ReactNode;
  rightIcon?: React.ReactNode;
  onRightIconPress?: () => void;
  required?: boolean;
  disabled?: boolean;
  style?: ViewStyle;
  inputStyle?: TextStyle;
  labelStyle?: TextStyle;
}

const AnimatedTextInput = Animated.createAnimatedComponent(TextInput);

const ModernInput: React.FC<ModernInputProps> = memo(
  ({
    label,
    error,
    hint,
    variant = 'outlined',
    size = 'medium',
    leftIcon,
    rightIcon,
    onRightIconPress,
    required = false,
    disabled = false,
    style,
    inputStyle,
    labelStyle,
    value,
    onFocus,
    onBlur,
    ...textInputProps
  }) => {
    const [isFocused, setIsFocused] = useState(false);
    const inputRef = useRef<TextInput>(null);

    const focusAnimation = useSharedValue(0);
    const labelAnimation = useSharedValue(value ? 1 : 0);
    const errorAnimation = useSharedValue(error ? 1 : 0);

    const handleFocus = useCallback(
      (e: NativeSyntheticEvent<TextInputFocusEventData>) => {
        setIsFocused(true);
        focusAnimation.value = withTiming(1, {
          duration: ModernDesignSystem.animations.duration.normal,
        });
        labelAnimation.value = withTiming(1, {
          duration: ModernDesignSystem.animations.duration.normal,
        });
        onFocus?.(e);
      },
      [onFocus],
    );

    const handleBlur = useCallback(
      (e: NativeSyntheticEvent<TextInputFocusEventData>) => {
        setIsFocused(false);
        focusAnimation.value = withTiming(0, {
          duration: ModernDesignSystem.animations.duration.normal,
        });
        if (!value) {
          labelAnimation.value = withTiming(0, {
            duration: ModernDesignSystem.animations.duration.normal,
          });
        }
        onBlur?.(e);
      },
      [onBlur, value],
    );

    React.useEffect(() => {
      errorAnimation.value = withSpring(error ? 1 : 0, {
        damping: 15,
        stiffness: 300,
      });
    }, [error]);

    React.useEffect(() => {
      labelAnimation.value = withTiming(value ? 1 : 0, {
        duration: ModernDesignSystem.animations.duration.normal,
      });
    }, [value]);

    const containerAnimatedStyle = useAnimatedStyle(() => {
      const borderColor = interpolateColor(
        focusAnimation.value,
        [0, 1],
        [
          error
            ? ModernDesignSystem.colors.semantic.feedback.error
            : ModernDesignSystem.colors.tokens.border.primary,
          error
            ? ModernDesignSystem.colors.semantic.feedback.error
            : ModernDesignSystem.colors.semantic.brand.primary,
        ],
      );

      return {
        borderColor,
        borderWidth: interpolate(focusAnimation.value, [0, 1], [1, 2]),
      };
    });

    const labelAnimatedStyle = useAnimatedStyle(() => {
      const translateY = interpolate(labelAnimation.value, [0, 1], [0, -24]);

      const scale = interpolate(labelAnimation.value, [0, 1], [1, 0.85]);

      const color = interpolateColor(
        focusAnimation.value,
        [0, 1],
        [
          error
            ? ModernDesignSystem.colors.semantic.feedback.error
            : ModernDesignSystem.colors.tokens.content.secondary,
          error
            ? ModernDesignSystem.colors.semantic.feedback.error
            : ModernDesignSystem.colors.semantic.brand.primary,
        ],
      );

      return {
        transform: [{ translateY }, { scale }],
        color,
      };
    });

    const errorAnimatedStyle = useAnimatedStyle(() => {
      return {
        opacity: errorAnimation.value,
        transform: [
          {
            translateY: interpolate(errorAnimation.value, [0, 1], [-10, 0]),
          },
        ],
      };
    });

    const getContainerStyle = (): ViewStyle => {
      const baseStyle = styles.container;
      const variantStyle = styles[`${variant}Container` as keyof typeof styles] as ViewStyle;
      const sizeStyle = styles[`${size}Container` as keyof typeof styles] as ViewStyle;

      return {
        ...baseStyle,
        ...variantStyle,
        ...sizeStyle,
        ...(disabled && styles.disabledContainer),
        ...style,
      };
    };

    const getInputStyle = (): TextStyle => {
      const baseStyle = styles.input;
      const sizeStyle = styles[`${size}Input` as keyof typeof styles] as TextStyle;

      return {
        ...baseStyle,
        ...sizeStyle,
        ...(disabled && styles.disabledInput),
        ...inputStyle,
      };
    };

    return (
      <View style={styles.wrapper}>
        {label && (
          <Animated.Text style={[styles.label, labelStyle, labelAnimatedStyle]}>
            {label}
            {required && ' *'}
          </Animated.Text>
        )}

        <Animated.View
          style={[getContainerStyle(), variant === 'outlined' && containerAnimatedStyle]}
        >
          {leftIcon && <View style={styles.leftIconContainer}>{leftIcon}</View>}

          <AnimatedTextInput
            ref={inputRef}
            style={getInputStyle()}
            value={value}
            onFocus={handleFocus}
            onBlur={handleBlur}
            editable={!disabled}
            placeholderTextColor={ModernDesignSystem.colors.tokens.content.tertiary}
            {...textInputProps}
          />

          {rightIcon && (
            <TouchableOpacity
              style={styles.rightIconContainer}
              onPress={onRightIconPress}
              disabled={!onRightIconPress}
              accessibilityRole="button"
              accessibilityLabel="Input action"
              accessibilityHint="Tap to perform action on this input field"
            >
              {rightIcon}
            </TouchableOpacity>
          )}
        </Animated.View>

        {(error || hint) && (
          <View style={styles.helperContainer}>
            {error ? (
              <Animated.Text style={[styles.errorText, errorAnimatedStyle]}>{error}</Animated.Text>
            ) : (
              <Text style={styles.hintText}>{hint}</Text>
            )}
          </View>
        )}
      </View>
    );
  },
);

const styles = StyleSheet.create({
  wrapper: {
    marginBottom: ModernDesignSystem.spacing.semantic.component.md,
  },

  label: {
    backgroundColor: ModernDesignSystem.colors.tokens.surface.primary,
    fontFamily: ModernDesignSystem.typography.fontStacks.body[0],
    left: ModernDesignSystem.spacing.semantic.component.md,
    paddingHorizontal: 4,
    position: 'absolute',
    top: ModernDesignSystem.spacing.semantic.component.md,
    zIndex: 1,
    ...ModernDesignSystem.typography.scale.label.medium,
  },

  container: {
    alignItems: 'center',
    backgroundColor: ModernDesignSystem.colors.tokens.surface.primary,
    flexDirection: 'row',
  },

  // Variant Styles
  outlinedContainer: {
    borderColor: ModernDesignSystem.colors.tokens.border.primary,
    borderRadius: ModernDesignSystem.borderRadius.semantic.input,
    borderWidth: 1,
  },
  filledContainer: {
    backgroundColor: ModernDesignSystem.colors.tokens.surface.secondary,
    borderBottomColor: ModernDesignSystem.colors.tokens.border.primary,
    borderBottomWidth: 2,
    borderRadius: ModernDesignSystem.borderRadius.semantic.input,
  },
  underlinedContainer: {
    borderBottomColor: ModernDesignSystem.colors.tokens.border.primary,
    borderBottomWidth: 1,
  },

  // Size Variants
  smallContainer: {
    minHeight: 36,
  },
  mediumContainer: {
    minHeight: 44,
  },
  largeContainer: {
    minHeight: 52,
  },

  input: {
    color: ModernDesignSystem.colors.tokens.content.primary,
    flex: 1,
    fontFamily: ModernDesignSystem.typography.fontStacks.body[0],
    paddingHorizontal: ModernDesignSystem.spacing.semantic.component.md,
  },

  // Input Size Variants
  smallInput: {
    ...ModernDesignSystem.typography.scale.body.small,
    paddingVertical: ModernDesignSystem.spacing.semantic.component.xs,
  },
  mediumInput: {
    ...ModernDesignSystem.typography.scale.body.medium,
    paddingVertical: ModernDesignSystem.spacing.semantic.component.sm,
  },
  largeInput: {
    ...ModernDesignSystem.typography.scale.body.large,
    paddingVertical: ModernDesignSystem.spacing.semantic.component.md,
  },

  leftIconContainer: {
    paddingLeft: ModernDesignSystem.spacing.semantic.component.md,
    paddingRight: ModernDesignSystem.spacing.semantic.component.xs,
  },

  rightIconContainer: {
    paddingLeft: ModernDesignSystem.spacing.semantic.component.xs,
    paddingRight: ModernDesignSystem.spacing.semantic.component.md,
  },

  helperContainer: {
    marginTop: ModernDesignSystem.spacing.semantic.component.xs,
    paddingHorizontal: ModernDesignSystem.spacing.semantic.component.md,
  },

  errorText: {
    color: ModernDesignSystem.colors.semantic.feedback.error,
    fontFamily: ModernDesignSystem.typography.fontStacks.body[0],
    ...ModernDesignSystem.typography.scale.label.small,
  },

  hintText: {
    color: ModernDesignSystem.colors.tokens.content.secondary,
    fontFamily: ModernDesignSystem.typography.fontStacks.body[0],
    ...ModernDesignSystem.typography.scale.label.small,
  },

  disabledContainer: {
    opacity: 0.6,
  },

  disabledInput: {
    color: ModernDesignSystem.colors.tokens.content.disabled,
  },
});

export default ModernInput;

/**
 * Modern Loading Component
 * Advanced loading indicators with multiple variants and animations
 */

import React, { memo, useEffect } from 'react';
import { Dimensions, StyleSheet, Text, TextStyle, View, ViewStyle } from 'react-native';
import Animated, {
  Easing,
  interpolate,
  useAnimatedStyle,
  useSharedValue,
  withDelay,
  withRepeat,
  withSequence,
  withTiming,
} from 'react-native-reanimated';

import { ModernDesignSystem } from '../../theme/ModernDesignSystem';

interface ModernLoadingProps {
  variant?: 'spinner' | 'dots' | 'pulse' | 'skeleton' | 'wave';
  size?: 'small' | 'medium' | 'large';
  color?: string;
  text?: string;
  overlay?: boolean;
  style?: ViewStyle;
  textStyle?: TextStyle;
}

const { width: screenWidth } = Dimensions.get('window');

const ModernLoading: React.FC<ModernLoadingProps> = memo(
  ({
    variant = 'spinner',
    size = 'medium',
    color = ModernDesignSystem.colors.semantic.brand.primary,
    text,
    overlay = false,
    style,
    textStyle,
  }) => {
    const rotation = useSharedValue(0);
    const scale = useSharedValue(1);
    const opacity = useSharedValue(1);
    const translateX = useSharedValue(0);
    const shimmerAnimation = useSharedValue(0);

    // Dot animations
    const dotAnimation1 = useSharedValue(0);
    const dotAnimation2 = useSharedValue(0);
    const dotAnimation3 = useSharedValue(0);

    useEffect(() => {
      switch (variant) {
        case 'spinner':
          rotation.value = withRepeat(
            withTiming(360, {
              duration: 1000,
              easing: Easing.linear,
            }),
            -1,
          );
          break;

        case 'dots':
          // Initialize dot animations
          dotAnimation1.value = withRepeat(
            withDelay(
              0,
              withSequence(withTiming(1, { duration: 400 }), withTiming(0, { duration: 400 })),
            ),
            -1,
          );
          dotAnimation2.value = withRepeat(
            withDelay(
              200,
              withSequence(withTiming(1, { duration: 400 }), withTiming(0, { duration: 400 })),
            ),
            -1,
          );
          dotAnimation3.value = withRepeat(
            withDelay(
              400,
              withSequence(withTiming(1, { duration: 400 }), withTiming(0, { duration: 400 })),
            ),
            -1,
          );
          break;
        case 'pulse':
          scale.value = withRepeat(
            withSequence(withTiming(1.2, { duration: 800 }), withTiming(1, { duration: 800 })),
            -1,
          );
          break;
        case 'skeleton':
          shimmerAnimation.value = withRepeat(
            withTiming(1, {
              duration: 1500,
              easing: Easing.linear,
            }),
            -1,
          );
          break;

        case 'wave':
          translateX.value = withRepeat(
            withSequence(
              withTiming(screenWidth, {
                duration: 1500,
                easing: Easing.inOut(Easing.ease),
              }),
              withTiming(-100, {
                duration: 0,
              }),
            ),
            -1,
          );
          break;
      }
    }, [
      variant,
      rotation,
      scale,
      opacity,
      translateX,
      shimmerAnimation,
      dotAnimation1,
      dotAnimation2,
      dotAnimation3,
    ]);

    // Animated styles at component level - prevent transform conflicts
    const spinnerAnimatedStyle = useAnimatedStyle(
      () => ({
        transform: [{ rotate: `${rotation.value}deg` }],
      }),
      [rotation],
    );

    const pulseAnimatedStyle = useAnimatedStyle(() => ({
      transform: [{ scale: scale.value }],
      opacity: interpolate(scale.value, [1, 1.2], [1, 0.7]),
    }));

    const shimmerAnimatedStyle = useAnimatedStyle(() => {
      const shimmerTranslateX = interpolate(shimmerAnimation.value, [0, 1], [-100, 300]);
      return {
        transform: [{ translateX: shimmerTranslateX }],
      };
    });

    // Add wave animated style at component scope to comply with hooks rules
    const waveAnimatedStyle = useAnimatedStyle(() => ({
      transform: [{ translateX: translateX.value }],
    }));

    const dot1AnimatedStyle = useAnimatedStyle(() => ({
      opacity: interpolate(dotAnimation1.value, [0, 1], [0.3, 1]),
      transform: [{ scale: interpolate(dotAnimation1.value, [0, 1], [0.8, 1.2]) }],
    }));

    const dot2AnimatedStyle = useAnimatedStyle(() => ({
      opacity: interpolate(dotAnimation2.value, [0, 1], [0.3, 1]),
      transform: [{ scale: interpolate(dotAnimation2.value, [0, 1], [0.8, 1.2]) }],
    }));

    const dot3AnimatedStyle = useAnimatedStyle(() => ({
      opacity: interpolate(dotAnimation3.value, [0, 1], [0.3, 1]),
      transform: [{ scale: interpolate(dotAnimation3.value, [0, 1], [0.8, 1.2]) }],
    }));

    const getSizeValue = () => {
      switch (size) {
        case 'small':
          return 24;
        case 'medium':
          return 40;
        case 'large':
          return 56;
        default:
          return 40;
      }
    };

    const renderSpinner = () => {
      const sizeValue = getSizeValue();

      return (
        <Animated.View
          style={[
            styles.spinner,
            {
              width: sizeValue,
              height: sizeValue,
              borderColor: `${color}20`,
              borderTopColor: color,
              borderWidth: sizeValue / 8,
            },
            spinnerAnimatedStyle,
          ]}
        />
      );
    };

    const renderDots = () => {
      const dotSize = getSizeValue() / 3;
      const animatedStyles = [dot1AnimatedStyle, dot2AnimatedStyle, dot3AnimatedStyle];

      const dots = [0, 1, 2].map((index) => {
        return (
          <Animated.View
            key={index}
            style={[
              styles.dot,
              {
                width: dotSize,
                height: dotSize,
                backgroundColor: color,
                marginHorizontal: dotSize / 4,
              },
              animatedStyles[index],
            ]}
          />
        );
      });

      return <View style={styles.dotsContainer}>{dots}</View>;
    };

    const renderPulse = () => {
      const sizeValue = getSizeValue();

      return (
        <Animated.View
          style={[
            styles.pulse,
            {
              width: sizeValue,
              height: sizeValue,
              backgroundColor: color,
            },
            pulseAnimatedStyle,
          ]}
        />
      );
    };

    const renderSkeleton = () => {
      return (
        <View style={styles.skeletonContainer}>
          <View style={[styles.skeletonLine, { width: '80%' }]} />
          <View style={[styles.skeletonLine, { width: '60%' }]} />
          <View style={[styles.skeletonLine, { width: '90%' }]} />
          <Animated.View style={[styles.shimmer, shimmerAnimatedStyle]} />
        </View>
      );
    };

    const renderWave = () => {
      return (
        <View style={styles.waveContainer}>
          <Animated.View style={[styles.wave, { backgroundColor: color }, waveAnimatedStyle]} />
        </View>
      );
    };

    const renderLoadingIndicator = () => {
      switch (variant) {
        case 'spinner':
          return renderSpinner();
        case 'dots':
          return renderDots();
        case 'pulse':
          return renderPulse();
        case 'skeleton':
          return renderSkeleton();
        case 'wave':
          return renderWave();
        default:
          return renderSpinner();
      }
    };

    const containerStyle = [styles.container, overlay && styles.overlay, style];

    return (
      <View style={containerStyle}>
        {renderLoadingIndicator()}
        {text && <Text style={[styles.text, { color }, textStyle]}>{text}</Text>}
      </View>
    );
  },
);

