 * Progressive loading with WebP support, lazy loading, and caching
 */

import React, { memo, useCallback, useEffect, useRef, useState } from 'react';
import {
  ActivityIndicator,
  Image,
  ImageErrorEventData,
  ImageResizeMode,
  ImageSourcePropType,
  ImageStyle,
  NativeSyntheticEvent,
  StyleSheet,
  Text,
  View,
  ViewStyle,
} from 'react-native';
import Animated, {
  runOnJS,
  useAnimatedStyle,
  useSharedValue,
  withSpring,
  withTiming,
} from 'react-native-reanimated';

import { PerformanceOptimizationService } from '@/services/performanceOptimizationService';
import { DesignSystem } from '@/theme/DesignSystem';
import { optimizeImageUri } from '@/utils/performanceUtils';

interface OptimizedImageProps {
  /** Image source URI */
  source: string | ImageSourcePropType;
  /** Image style */
  style?: ImageStyle;
  /** Container style */
  containerStyle?: ViewStyle;
  /** Resize mode */
  resizeMode?: ImageResizeMode;
  /** Enable lazy loading */
  lazy?: boolean;
  /** Enable progressive loading */
  progressive?: boolean;
  /** Enable WebP format when possible */
  enableWebP?: boolean;
  /** Placeholder component */
  placeholder?: React.ReactNode;
  /** Error placeholder component */
  errorPlaceholder?: React.ReactNode;
  /** Loading indicator color */
  loadingColor?: string;
  /** Fade animation duration */
  fadeDuration?: number;
  /** Cache the image */
  cache?: boolean;
  /** Image quality (0-100) */
  quality?: number;
  /** Target width for optimization */
  targetWidth?: number;
  /** Target height for optimization */
  targetHeight?: number;
  /** Callback when image loads */
  onLoad?: () => void;
  /** Callback when image fails to load */
  onError?: (error: NativeSyntheticEvent<ImageErrorEventData>) => void;
  /** Callback when loading starts */
  onLoadStart?: () => void;
  /** Accessibility label */
  accessibilityLabel?: string;
}

interface ImageState {
  loading: boolean;
  error: boolean;
  loaded: boolean;
}

export const OptimizedImage: React.FC<OptimizedImageProps> = memo(
  ({
    source,
    style,
    containerStyle,
    resizeMode = 'cover',
    lazy = true,
    progressive = true,
    enableWebP = true,
    placeholder,
    errorPlaceholder,
    loadingColor = DesignSystem.colors.primary[500],
    fadeDuration = 300,
    cache = true,
    quality = 80,
    targetWidth,
    targetHeight,
    onLoad,
    onError,
    onLoadStart,
    accessibilityLabel,
  }) => {
    const [imageState, setImageState] = useState<ImageState>({
      loading: false,
      error: false,
      loaded: false,
    });
    const [optimizedSource, setOptimizedSource] = useState<ImageSourcePropType | undefined>(
      typeof source === 'string' ? { uri: source } : source,
    );
    const [isInView, setIsInView] = useState(!lazy);
    const viewRef = useRef<View>(null);

    // Animation values
    const opacity = useSharedValue(0);
    const scale = useSharedValue(0.95);
    const loadingOpacity = useSharedValue(0);

    // Optimize image source
    useEffect(() => {
      const optimizeSource = async () => {
        if (typeof source === 'string' && cache) {
          try {
            let optimizedUri = source;

            // Apply WebP format if supported
            if (enableWebP && !source.includes('.gif')) {
              if (source.includes('unsplash.com') || source.includes('images.')) {
                const separator = source.includes('?') ? '&' : '?';
                optimizedUri = `${source}${separator}fm=webp&q=${quality}`;
              }
            }

            // Apply size optimization
            if (targetWidth && targetHeight) {
              optimizedUri = optimizeImageUri(optimizedUri, targetWidth, targetHeight);
            }

            // Use performance service for caching
            const cachedUri =
              await PerformanceOptimizationService.optimizeImageLoading(optimizedUri);
            setOptimizedSource({ uri: cachedUri });
          } catch (error) {
            console.warn('Image optimization failed:', error);
            setOptimizedSource(typeof source === 'string' ? { uri: source } : source);
          }
        } else {
          setOptimizedSource(typeof source === 'string' ? { uri: source } : source);
        }
      };

      optimizeSource();
    }, [source, cache, enableWebP, quality, targetWidth, targetHeight]);

    // Lazy loading intersection observer simulation
    useEffect(() => {
      if (!lazy) {
        return;
      }

      const checkVisibility = () => {
        if (viewRef.current) {
          // Simple visibility check - in a real implementation,
          // you might use react-native-intersection-observer or similar
          setIsInView(true);
        }
      };

      const timer = setTimeout(checkVisibility, 100);
      return () => clearTimeout(timer);
    }, [lazy]);

    // Handle loading start
    const handleLoadStart = useCallback(() => {
      setImageState((prev) => ({ ...prev, loading: true, error: false }));
      loadingOpacity.value = withTiming(1, { duration: 200 });
      onLoadStart?.();
    }, [loadingOpacity, onLoadStart]);

    // Handle successful load
    const handleLoad = useCallback(() => {
      const completeLoad = () => {
        setImageState((prev) => ({ ...prev, loading: false, loaded: true }));
        onLoad?.();
      };

      if (progressive) {
        // Progressive loading animation
        opacity.value = withTiming(1, { duration: fadeDuration });
        scale.value = withSpring(1, {
          damping: 15,
          stiffness: 150,
        });
        loadingOpacity.value = withTiming(0, { duration: 200 }, () => {
          runOnJS(completeLoad)();
        });
      } else {
        opacity.value = 1;
        scale.value = 1;
        loadingOpacity.value = 0;
        completeLoad();
      }
    }, [opacity, scale, loadingOpacity, progressive, fadeDuration, onLoad]);

    // Handle load error
    const handleError = useCallback(
      (error: NativeSyntheticEvent<ImageErrorEventData>) => {
        setImageState((prev) => ({ ...prev, loading: false, error: true }));
        loadingOpacity.value = withTiming(0, { duration: 200 });
        onError?.(error);
      },
      [loadingOpacity, onError],
    );

    // Animated styles
    const imageAnimatedStyle = useAnimatedStyle(() => ({
      opacity: opacity.value,
      transform: [{ scale: scale.value }],
    }));

    const loadingAnimatedStyle = useAnimatedStyle(() => ({
      opacity: loadingOpacity.value,
    }));

    // Render loading placeholder
    const renderLoadingPlaceholder = () => {
      if (placeholder) {
        return placeholder;
      }

      return (
        <Animated.View style={[styles.loadingContainer, loadingAnimatedStyle]}>
          <ActivityIndicator size="small" color={loadingColor} />
        </Animated.View>
      );
    };

    // Render error placeholder
    const renderErrorPlaceholder = () => {
      if (errorPlaceholder) {
        return errorPlaceholder;
      }

      return (
        <View style={styles.errorContainer}>
          <Text style={styles.errorText}>Failed to load image</Text>
        </View>
      );
    };

    // Don't render if lazy loading and not in view
    if (lazy && !isInView) {
      return (
        <View ref={viewRef} style={[styles.container, containerStyle]}>
          {placeholder || <View style={[styles.placeholder, style]} />}
        </View>
      );
    }

    return (
      <View ref={viewRef} style={[styles.container, containerStyle]}>
        {/* Main Image */}
        {isInView && (
          <Animated.View style={[styles.imageWrapper, imageAnimatedStyle]}>
            <Image
              source={optimizedSource}
              style={[styles.image, style]}
              resizeMode={resizeMode}
              onLoadStart={handleLoadStart}
              onLoad={handleLoad}
              onError={handleError}
              accessibilityLabel={accessibilityLabel}
            />
          </Animated.View>
        )}

        {/* Loading Overlay */}
        {imageState.loading && renderLoadingPlaceholder()}

        {/* Error Overlay */}
        {imageState.error && renderErrorPlaceholder()}
      </View>
    );
  },
);

OptimizedImage.displayName = 'OptimizedImage';

const styles = StyleSheet.create({
  container: {
    overflow: 'hidden',
    position: 'relative',
  },
  errorContainer: {
    alignItems: 'center',
    backgroundColor: DesignSystem.colors.background.secondary,
    bottom: 0,
    justifyContent: 'center',
    left: 0,
    position: 'absolute',
    right: 0,
    top: 0,
  },
  errorText: {
    ...DesignSystem.typography.scale.caption,
    color: DesignSystem.colors.text.secondary,
    textAlign: 'center',
  },
  image: {
    height: '100%',
    width: '100%',
  },
  imageWrapper: {
    height: '100%',
    width: '100%',
  },
  loadingContainer: {
    alignItems: 'center',
    backgroundColor: 'rgba(255, 255, 255, 0.8)',
    bottom: 0,
    justifyContent: 'center',
    left: 0,
    position: 'absolute',
    right: 0,
    top: 0,
  },
  placeholder: {
    backgroundColor: DesignSystem.colors.background.secondary,
    height: '100%',
    width: '100%',
  },
});

export default OptimizedImage;

/**
 * ItemDetailsForm Organism
 *
 * A complex form component that combines multiple molecules and atoms
 * to create a complete clothing item details input interface.
 */

import React, { useState } from 'react';
import { ScrollView, StyleSheet, View } from 'react-native';

import Button from '@/components/atoms/Button';
import Text from '@/components/atoms/Text';
import Card from '@/components/molecules/Card';
import FormField from '@/components/molecules/FormField';
import { DesignSystem } from '@/theme/DesignSystem';
import { BaseComponentProps } from '@/types/componentProps';

export interface ClothingItemSubmission {
  name: string;
  category: string;
  subcategory: string;
  brand: string;
  color: string;
  size: string;
  price: string;
  purchaseDate: string;
  notes: string;
}

export interface ItemDetailsFormProps extends BaseComponentProps {
  initialValues?: Partial<ClothingItemSubmission>;
  onSubmit: (values: ClothingItemSubmission) => void;
  onCancel?: () => void;
  loading?: boolean;
  categories?: string[];
  subcategories?: Record<string, string[]>;
  colors?: string[];
  sizes?: string[];
}

const ItemDetailsForm: React.FC<ItemDetailsFormProps> = ({
  initialValues = {},
  onSubmit,
  onCancel,
  loading = false,
  categories = ['Tops', 'Bottoms', 'Dresses', 'Outerwear', 'Shoes', 'Accessories'],
  subcategories = {
    Tops: ['T-Shirt', 'Blouse', 'Sweater', 'Tank Top'],
    Bottoms: ['Jeans', 'Trousers', 'Skirt', 'Shorts'],
    Dresses: ['Casual', 'Formal', 'Cocktail', 'Maxi'],
    Outerwear: ['Jacket', 'Coat', 'Blazer', 'Cardigan'],
    Shoes: ['Sneakers', 'Heels', 'Flats', 'Boots'],
    Accessories: ['Bag', 'Jewelry', 'Scarf', 'Hat'],
  },
  colors = ['Black', 'White', 'Gray', 'Navy', 'Brown', 'Beige', 'Red', 'Blue', 'Green', 'Pink'],
  sizes = ['XS', 'S', 'M', 'L', 'XL', 'XXL'],
  style,
  testID,
  ...props
}) => {
  const [formData, setFormData] = useState<ClothingItemSubmission>({
    name: initialValues.name || '',
    category: initialValues.category || '',
    subcategory: initialValues.subcategory || '',
    brand: initialValues.brand || '',
    color: initialValues.color || '',
    size: initialValues.size || '',
    price: initialValues.price || '',
    purchaseDate: initialValues.purchaseDate || '',
    notes: initialValues.notes || '',
  });

  const [errors, setErrors] = useState<Partial<Record<keyof ClothingItemSubmission, string>>>({});
  const [touched, setTouched] = useState<Partial<Record<keyof ClothingItemSubmission, boolean>>>(
    {},
  );

  const updateField = (field: keyof ClothingItemSubmission, value: string) => {
    setFormData((prev) => ({ ...prev, [field]: value }));

    // Clear error when user starts typing
    if (errors[field]) {
      setErrors((prev) => ({ ...prev, [field]: undefined }));
    }
  };

  const markFieldTouched = (field: keyof ClothingItemSubmission) => {
    setTouched((prev) => ({ ...prev, [field]: true }));
  };

  const validateForm = (): boolean => {
    const newErrors: Partial<Record<keyof ClothingItemSubmission, string>> = {};

    if (!formData.name.trim()) {
      newErrors.name = 'Item name is required';
    }

    if (!formData.category) {
      newErrors.category = 'Category is required';
    }

    if (!formData.color) {
      newErrors.color = 'Color is required';
    }

    if (!formData.size) {
      newErrors.size = 'Size is required';
    }

    if (formData.price && isNaN(parseFloat(formData.price))) {
      newErrors.price = 'Please enter a valid price';
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleSubmit = () => {
    // Mark all fields as touched
    const allFields = Object.keys(formData) as (keyof ClothingItemSubmission)[];
    const touchedState = allFields.reduce((acc, field) => ({ ...acc, [field]: true }), {});
    setTouched(touchedState);

    if (validateForm()) {
      onSubmit(formData);
    }
  };

  const availableSubcategories = formData.category ? subcategories[formData.category] || [] : [];

  return (
    <Card
      variant="default"
      padding="large"
      style={StyleSheet.flatten([styles.container, style])}
      testID={testID}
      {...props}
    >
      <Text variant="headline" weight="bold" style={styles.title}>
        Add Clothing Item
      </Text>

      <ScrollView style={styles.scrollView} showsVerticalScrollIndicator={false}>
        <FormField
          label="Item Name"
          value={formData.name}
          onChangeText={(value) => updateField('name', value)}
          onBlur={() => markFieldTouched('name')}
          placeholder="e.g., Blue Cotton T-Shirt"
          error={errors.name}
          touched={touched.name}
          required
        />

        <FormField
          label="Brand"
          value={formData.brand}
          onChangeText={(value) => updateField('brand', value)}
          onBlur={() => markFieldTouched('brand')}
          placeholder="e.g., Nike, Zara, H&M"
          error={errors.brand}
          touched={touched.brand}
        />

        <FormField
          label="Category"
          value={formData.category}
          onChangeText={(value) => {
            updateField('category', value);
            // Reset subcategory when category changes
            if (formData.subcategory && !subcategories[value]?.includes(formData.subcategory)) {
              updateField('subcategory', '');
            }
          }}
          onBlur={() => markFieldTouched('category')}
          placeholder="Select category"
          error={errors.category}
          touched={touched.category}
          required
        />

        {availableSubcategories.length > 0 && (
          <FormField
            label="Subcategory"
            value={formData.subcategory}
            onChangeText={(value) => updateField('subcategory', value)}
            onBlur={() => markFieldTouched('subcategory')}
            placeholder="Select subcategory"
            error={errors.subcategory}
            touched={touched.subcategory}
          />
        )}

        <FormField
          label="Color"
          value={formData.color}
          onChangeText={(value) => updateField('color', value)}
          onBlur={() => markFieldTouched('color')}
          placeholder="Select or enter color"
          error={errors.color}
          touched={touched.color}
          required
        />

        <FormField
          label="Size"
          value={formData.size}
          onChangeText={(value) => updateField('size', value)}
          onBlur={() => markFieldTouched('size')}
          placeholder="Select size"
          error={errors.size}
          touched={touched.size}
          required
        />

        <FormField
          label="Price"
          value={formData.price}
          onChangeText={(value) => updateField('price', value)}
          onBlur={() => markFieldTouched('price')}
          placeholder="0.00"
          keyboardType="numeric"
          error={errors.price}
          touched={touched.price}
          hint="Optional: Enter purchase price"
        />

        <FormField
          label="Purchase Date"
          value={formData.purchaseDate}
          onChangeText={(value) => updateField('purchaseDate', value)}
          onBlur={() => markFieldTouched('purchaseDate')}
          placeholder="MM/DD/YYYY"
          error={errors.purchaseDate}
          touched={touched.purchaseDate}
          hint="Optional: When did you buy this item?"
        />

        <FormField
          label="Notes"
          value={formData.notes}
          onChangeText={(value) => updateField('notes', value)}
          onBlur={() => markFieldTouched('notes')}
          placeholder="Any additional notes about this item..."
          multiline
          error={errors.notes}
          touched={touched.notes}
          hint="Optional: Care instructions, styling notes, etc."
        />
      </ScrollView>

      <View style={styles.buttonContainer}>
        {onCancel && (
          <Button
            title="Cancel"
            variant="secondary"
            onPress={onCancel}
            style={styles.cancelButton}
            disabled={loading}
          />
        )}

        <Button
          title="Add Item"
          variant="primary"
          onPress={handleSubmit}
          style={styles.submitButton}
          loading={loading}
          disabled={loading}
        />
      </View>
    </Card>
  );
};

const styles = StyleSheet.create({
  buttonContainer: {
    borderTopColor: DesignSystem.colors.neutral[200],
    borderTopWidth: 1,
    flexDirection: 'row',
    justifyContent: 'space-between',
    paddingTop: DesignSystem.spacing.md,
  },

  cancelButton: {
    flex: 1,
    marginRight: DesignSystem.spacing.sm,
  },

  container: {
    flex: 1,
    maxHeight: '90%',
  },

  scrollView: {
    flex: 1,
    marginBottom: DesignSystem.spacing.md,
  },

  submitButton: {
    flex: 1,
    marginLeft: DesignSystem.spacing.sm,
  },

  title: {
    marginBottom: DesignSystem.spacing.lg,
    textAlign: 'center',
  },
});

export default ItemDetailsForm;

/**
 * Premium Brand Card Component
 *
 * Implements ARUOM, FIRED, and AYNAMODA premium brand cards
 * with sophisticated design and luxury aesthetics
 */

import { Ionicons } from '@expo/vector-icons';
import { LinearGradient } from 'expo-linear-gradient';
import React from 'react';
import {
  Dimensions,
  ImageBackground,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
} from 'react-native';

import { DesignSystem } from '@/theme/DesignSystem';

const { width } = Dimensions.get('window');

export type BrandType = 'ARUOM' | 'FIRED' | 'AYNAMODA';

export interface PremiumBrandCardData {
  id: string;
  title: string;
  subtitle?: string;
  brand: BrandType;
  price: string;
  originalPrice?: string;
  imageUrl: string;
  category: string;
  tags?: string[];
  isLiked?: boolean;
  isNew?: boolean;
  discount?: number;
}

interface PremiumBrandCardProps {
  item: PremiumBrandCardData;
  onPress?: () => void;
  onLongPress?: () => void;
  onLikeToggle?: () => void;
  variant?: 'standard' | 'featured' | 'compact';
  showBrandBadge?: boolean;
}

const PremiumBrandCard: React.FC<PremiumBrandCardProps> = ({
  item,
  onPress,
  onLikeToggle,
  variant = 'standard',
  showBrandBadge = true,
}) => {
  // Brand-specific styling
  const getBrandTheme = (brand: BrandType) => {
    switch (brand) {
      case 'ARUOM':
        return {
          primary: '#1A1A1A',
          secondary: '#F5F5F5',
          accent: '#D4AF37',
          gradient: ['#1A1A1A', '#2D2D2D', '#404040'] as const,
          textColor: '#FFFFFF',
          badgeColor: '#D4AF37',
          philosophy: 'Minimalist Luxury',
        };
      case 'FIRED':
        return {
          primary: '#8B0000',
          secondary: '#FFE4E1',
          accent: '#FF6B35',
          gradient: ['#8B0000', '#A52A2A', '#CD5C5C'] as const,
          textColor: '#FFFFFF',
          badgeColor: '#FF6B35',
          philosophy: 'Bold Expression',
        };
      case 'AYNAMODA':
        return {
          primary: '#2C3E50',
          secondary: '#ECF0F1',
          accent: '#E67E22',
          gradient: ['#2C3E50', '#34495E', '#5D6D7E'] as const,
          textColor: '#FFFFFF',
          badgeColor: '#E67E22',
          philosophy: 'Confident Elegance',
        };
      default:
        return {
          primary: DesignSystem.colors.neutral[900],
          secondary: DesignSystem.colors.neutral[50],
          accent: DesignSystem.colors.primary[500],
          gradient: [DesignSystem.colors.neutral[800], DesignSystem.colors.neutral[700]] as const,
          textColor: DesignSystem.colors.neutral[50],
          badgeColor: DesignSystem.colors.primary[500],
          philosophy: 'Premium Fashion',
        };
    }
  };

  const brandTheme = getBrandTheme(item.brand);
  const cardWidth = variant === 'compact' ? (width - 48) / 2 : width - 32;

  return (
    <TouchableOpacity
      style={[
        styles.premiumCard,
        { width: cardWidth },
        variant === 'featured' && styles.featuredCard,
      ]}
      onPress={onPress}
      activeOpacity={0.95}
      accessibilityRole="button"
      accessibilityLabel={`${item.brand} ${item.title}`}
    >
      <ImageBackground
        source={{ uri: item.imageUrl }}
        style={styles.imageBackground}
        imageStyle={styles.backgroundImage}
      >
        <LinearGradient
          colors={[...brandTheme.gradient, 'transparent']}
          style={styles.gradientOverlay}
          start={{ x: 0, y: 0 }}
          end={{ x: 0, y: 1 }}
        >
          {/* Header Section */}
          <View style={styles.cardHeader}>
            {showBrandBadge && (
              <View style={[styles.brandBadge, { backgroundColor: brandTheme.badgeColor }]}>
                <Text style={styles.brandText}>{item.brand}</Text>
              </View>
            )}

            {item.isNew && (
              <View style={styles.newBadge}>
                <Text style={styles.newText}>YENİ</Text>
              </View>
            )}

            <TouchableOpacity
              style={styles.likeButton}
              onPress={onLikeToggle}
              hitSlop={{ top: 10, bottom: 10, left: 10, right: 10 }}
              accessibilityRole="button"
              accessibilityLabel={item.isLiked ? 'Remove from favorites' : 'Add to favorites'}
              accessibilityHint={
                item.isLiked
                  ? 'Tap to remove this item from your favorites'
                  : 'Tap to add this item to your favorites'
              }
              accessibilityState={{ selected: item.isLiked }}
            >
              <Ionicons
                name={item.isLiked ? 'heart' : 'heart-outline'}
                size={20}
                color={item.isLiked ? '#FF6B6B' : '#FFFFFF'}
              />
            </TouchableOpacity>
          </View>

          {/* Content Section */}
          <View style={styles.cardContent}>
            <View style={styles.categoryContainer}>
              <Text style={styles.categoryText}>{item.category.toUpperCase()}</Text>
            </View>

            <Text style={styles.titleText} numberOfLines={2}>
              {item.title}
            </Text>

            {item.subtitle && (
              <Text style={styles.subtitleText} numberOfLines={1}>
                {item.subtitle}
              </Text>
            )}

            <Text style={styles.philosophyText}>{brandTheme.philosophy}</Text>
          </View>

          {/* Footer Section */}
          <View style={styles.cardFooter}>
            <View style={styles.priceContainer}>
              {item.originalPrice && <Text style={styles.originalPrice}>{item.originalPrice}</Text>}
              <Text style={styles.currentPrice}>{item.price}</Text>
              {item.discount && (
                <View style={styles.discountBadge}>
                  <Text style={styles.discountText}>%{item.discount}</Text>
                </View>
              )}
            </View>

            {item.tags && item.tags.length > 0 && (
              <View style={styles.tagsContainer}>
                {item.tags.slice(0, 2).map((tag, index) => (
                  <View key={index} style={styles.tag}>
                    <Text style={styles.tagText}>{tag}</Text>
                  </View>
                ))}
              </View>
            )}
          </View>
        </LinearGradient>
      </ImageBackground>
    </TouchableOpacity>
  );
};

const styles = StyleSheet.create({
  backgroundImage: {
    borderRadius: DesignSystem.borderRadius.xl,
  },
  brandBadge: {
    borderRadius: DesignSystem.borderRadius.md,
    elevation: 4,
    paddingHorizontal: DesignSystem.spacing.sm,
    paddingVertical: DesignSystem.spacing.xs,
    shadowColor: DesignSystem.colors.neutral[900],
    shadowOffset: { width: 0, height: 2 },
    shadowOpacity: 0.3,
    shadowRadius: 4,
  },
  brandText: {
    ...DesignSystem.typography.caption,
    color: DesignSystem.colors.neutral[50],
    fontWeight: '800',
    letterSpacing: 1.2,
  },
  cardContent: {
    flex: 1,
    justifyContent: 'center',
  },
  cardFooter: {
    alignItems: 'flex-end',
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  cardHeader: {
    alignItems: 'flex-start',
    flexDirection: 'row',
    justifyContent: 'space-between',
  },
  categoryContainer: {
    marginBottom: DesignSystem.spacing.sm,
  },
  categoryText: {
    ...DesignSystem.typography.caption,
    color: 'rgba(255, 255, 255, 0.8)',
    fontWeight: '600',
    letterSpacing: 1.5,
