interface PremiumHomeScreenProps {
  onNavigateToWardrobe?: () => void;
  onNavigateToDiscover?: () => void;
  onNavigateToMirror?: () => void;
}

const PremiumHomeScreen: React.FC<PremiumHomeScreenProps> = ({
