import { Ionicons } from '@expo/vector-icons';
import React, { useRef, useState } from 'react';
import {
  NativeSyntheticEvent,
  StyleSheet,
  Text,
  TextInput,
  TextInputFocusEventData,
  TextInputProps,
  TextStyle,
  TouchableOpacity,
  View,
  ViewStyle,
} from 'react-native';
import Animated, {
  interpolate,
  interpolateColor,
  useAnimatedStyle,
  useSharedValue,
  withTiming,
} from 'react-native-reanimated';

import { DesignSystem } from '@/theme/DesignSystem';

const AnimatedView = Animated.createAnimatedComponent(View);

type BaseTextInputProps = Omit<TextInputProps, 'style'>;

interface PremiumInputProps extends BaseTextInputProps {
  label?: string;
  error?: string;
  hint?: string;
  leftIcon?: keyof typeof Ionicons.glyphMap;
  rightIcon?: keyof typeof Ionicons.glyphMap;
  onRightIconPress?: () => void;
  variant?: 'default' | 'glass' | 'luxury';
  size?: 'medium' | 'large';
  style?: ViewStyle;
  inputStyle?: TextStyle;
  labelStyle?: TextStyle;
}

const PremiumInput: React.FC<PremiumInputProps> = ({
  label,
  error,
  hint,
  leftIcon,
  rightIcon,
  onRightIconPress,
  variant = 'default',
  size = 'medium',
  style,
  inputStyle,
  labelStyle,
  ...textInputProps
}) => {
  const [isFocused, setIsFocused] = useState(false);
  const inputRef = useRef<TextInput>(null);

  const focusAnimation = useSharedValue(0);
  const errorAnimation = useSharedValue(0);

  React.useEffect(() => {
    focusAnimation.value = withTiming(isFocused ? 1 : 0, { duration: 200 });
  }, [isFocused, focusAnimation]);

  React.useEffect(() => {
    errorAnimation.value = withTiming(error ? 1 : 0, { duration: 200 });
  }, [error, errorAnimation]);

  const animatedContainerStyle = useAnimatedStyle(() => {
    const borderColor = interpolateColor(
      focusAnimation.value,
      [0, 1],
      [DesignSystem.colors.border.primary, DesignSystem.colors.gold[500]],
    );

    const errorBorderColor = interpolateColor(
      errorAnimation.value,
      [0, 1],
      [borderColor, DesignSystem.colors.error[500]],
    );

    const shadowOpacity = interpolate(focusAnimation.value, [0, 1], [0.05, 0.15]);

    return {
      borderColor: errorBorderColor,
      shadowOpacity,
    };
  });

  const animatedLabelStyle = useAnimatedStyle(() => {
    const labelColor = interpolateColor(
      focusAnimation.value,
      [0, 1],
      [DesignSystem.colors.text.secondary, DesignSystem.colors.gold[600]],
    );

    const errorLabelColor = interpolateColor(
      errorAnimation.value,
      [0, 1],
      [labelColor, DesignSystem.colors.error[500]],
    );

    return {
      color: errorLabelColor,
    };
  });

  const getContainerStyle = (): ViewStyle => {
    const baseStyle: ViewStyle = {
      borderWidth: 1.5,
      borderRadius: DesignSystem.borderRadius.lg,
      flexDirection: 'row',
      alignItems: 'center',
      backgroundColor: DesignSystem.colors.surface.secondary,
      ...DesignSystem.elevation.soft,
    };

    const sizeStyles: Record<string, ViewStyle> = {
      medium: {
        paddingHorizontal: DesignSystem.spacing.lg,
        paddingVertical: DesignSystem.spacing.md,
        minHeight: 56,
      },
      large: {
        paddingHorizontal: DesignSystem.spacing.xl,
        paddingVertical: DesignSystem.spacing.lg,
        minHeight: 64,
      },
    };

    const variantStyles: Record<string, ViewStyle> = {
      default: {
        backgroundColor: DesignSystem.colors.surface.secondary,
      },
      glass: {
        backgroundColor: 'rgba(255, 255, 255, 0.1)',
        // backdropFilter removed – unsupported in native. Consider using BlurView parent if needed.
      },
      luxury: {
        backgroundColor: DesignSystem.colors.surface.primary,
        borderColor: DesignSystem.colors.gold[200],
        ...DesignSystem.elevation.medium,
      },
    };

    return {
      ...baseStyle,
      ...sizeStyles[size],
      ...variantStyles[variant],
    };
  };

  const getInputStyle = (): TextStyle => {
    const baseStyle = {
      ...DesignSystem.typography.body.medium,
      color: DesignSystem.colors.text.primary,
      flex: 1,
      paddingVertical: 0, // Remove default padding
    };

    return {
      ...baseStyle,
      ...inputStyle,
    };
  };

  const getLabelStyle = (): TextStyle => {
    return {
      ...DesignSystem.typography.scale.caption,
      color: DesignSystem.colors.text.secondary,
      marginBottom: DesignSystem.spacing.sm,
      ...labelStyle,
    };
  };

  const getIconSize = (): number => {
    return size === 'large' ? 24 : 20;
  };

  const getIconColor = (): string => {
    if (error) {
      return DesignSystem.colors.error[500];
    }
    if (isFocused) {
      return DesignSystem.colors.gold[500];
    }
    return DesignSystem.colors.text.tertiary;
  };

  const handleFocus = (e: NativeSyntheticEvent<TextInputFocusEventData>) => {
    setIsFocused(true);
    textInputProps.onFocus?.(e);
  };

  const handleBlur = (e: NativeSyntheticEvent<TextInputFocusEventData>) => {
    setIsFocused(false);
    textInputProps.onBlur?.(e);
  };

  return (
    <View style={[styles.container, style]}>
      {label && (
        <Animated.Text style={[getLabelStyle(), animatedLabelStyle]}>{label}</Animated.Text>
      )}

      <AnimatedView style={[getContainerStyle(), animatedContainerStyle]}>
        {leftIcon && (
          <Ionicons
            name={leftIcon}
            size={getIconSize()}
            color={getIconColor()}
            style={styles.leftIcon}
          />
        )}

        <TextInput
          ref={inputRef}
          style={getInputStyle()}
          placeholderTextColor={DesignSystem.colors.text.tertiary}
          onFocus={handleFocus}
          onBlur={handleBlur}
          {...textInputProps}
        />

        {rightIcon && (
          <TouchableOpacity
            onPress={onRightIconPress}
            style={styles.rightIconContainer}
            accessibilityRole="button"
            accessibilityLabel="Input action button"
            accessibilityHint="Performs an action related to this input field"
          >
            <Ionicons name={rightIcon} size={getIconSize()} color={getIconColor()} />
          </TouchableOpacity>
        )}
      </AnimatedView>

      {error && <Text style={styles.errorText}>{error}</Text>}

      {hint && !error && <Text style={styles.hintText}>{hint}</Text>}
    </View>
  );
};

const styles = StyleSheet.create({
  container: {
    marginBottom: DesignSystem.spacing.md,
  },
  errorText: {
    ...DesignSystem.typography.scale.caption,
    color: DesignSystem.colors.error[500],
    marginLeft: DesignSystem.spacing.sm,
    marginTop: DesignSystem.spacing.xs,
  },
  generateButtonText: {
    // placeholder to satisfy style references if any
  },
  hintText: {
    ...DesignSystem.typography.scale.caption,
    color: DesignSystem.colors.text.tertiary,
    marginLeft: DesignSystem.spacing.sm,
    marginTop: DesignSystem.spacing.xs,
  },
  leftIcon: {
    marginRight: DesignSystem.spacing.md,
  },
  rightIconContainer: {
    marginLeft: DesignSystem.spacing.md,
    padding: DesignSystem.spacing.xs,
  },
});

export default PremiumInput;

import { LinearGradient } from 'expo-linear-gradient';
import React, { useEffect } from 'react';
import { Dimensions, StyleSheet, Text, View } from 'react-native';
import Animated, {
  interpolate,
  useAnimatedStyle,
  useSharedValue,
  withRepeat,
  withSequence,
  withTiming,
} from 'react-native-reanimated';

import { DesignSystem } from '@/theme/DesignSystem';

const { width, height } = Dimensions.get('window');

interface PremiumLoadingScreenProps {
  message?: string;
  showProgress?: boolean;
  progress?: number;
}

const PremiumLoadingScreen: React.FC<PremiumLoadingScreenProps> = ({
  message = 'Crafting your experience...',
  showProgress = false,
  progress = 0,
}) => {
  // Animation values
  const logoScale = useSharedValue(0.8);
  const logoOpacity = useSharedValue(0);
  const shimmerPosition = useSharedValue(-width);
  const progressValue = useSharedValue(0);
  const breathingScale = useSharedValue(1);
  const particleOpacity = useSharedValue(0);

  useEffect(() => {
    // Logo entrance animation
    logoOpacity.value = withTiming(1, { duration: 800 });
    logoScale.value = withTiming(1, { duration: 1000 });

    // Breathing animation for logo
    breathingScale.value = withRepeat(
      withSequence(withTiming(1.05, { duration: 2000 }), withTiming(1, { duration: 2000 })),
      -1,
      false,
    );

    // Shimmer effect
    shimmerPosition.value = withRepeat(withTiming(width, { duration: 2000 }), -1, false);

    // Particle animation
    particleOpacity.value = withRepeat(
      withSequence(withTiming(1, { duration: 1500 }), withTiming(0, { duration: 1500 })),
      -1,
      false,
    );
  }, [breathingScale, logoOpacity, logoScale, particleOpacity, shimmerPosition]);

  useEffect(() => {
    if (showProgress) {
      progressValue.value = withTiming(progress, {
        duration: 500,
      });
    }
  }, [progress, showProgress, progressValue]);

  const logoAnimatedStyle = useAnimatedStyle(() => {
    return {
      opacity: logoOpacity.value,
      transform: [{ scale: logoScale.value * breathingScale.value }],
    };
  });

  const shimmerAnimatedStyle = useAnimatedStyle(() => {
    return {
      transform: [{ translateX: shimmerPosition.value }],
    };
  });

  const progressAnimatedStyle = useAnimatedStyle(() => {
    const width = interpolate(progressValue.value, [0, 100], [0, 200]);
    return {
      width,
    };
  });

  const particleAnimatedStyle = useAnimatedStyle(() => {
    return {
      opacity: particleOpacity.value,
    };
  });

  const renderFloatingParticles = () => {
    const particles = Array.from({ length: 12 }, (_, index) => (
      <Animated.View
        key={index}
        style={[
          styles.particle,
          particleAnimatedStyle,
          {
            left: `${Math.random() * 100}%`,
            top: `${Math.random() * 100}%`,
          },
        ]}
      />
    ));
    return particles;
  };

  return (
    <View style={styles.container}>
      {/* Background Gradient */}
      <LinearGradient
        colors={[
          DesignSystem.colors.background.primary,
          DesignSystem.colors.background.secondary,
          DesignSystem.colors.background.tertiary,
        ]}
        style={StyleSheet.absoluteFill}
        start={{ x: 0, y: 0 }}
        end={{ x: 1, y: 1 }}
      />

      {/* Floating Particles */}
      <View style={styles.particlesContainer}>{renderFloatingParticles()}</View>

      {/* Main Content */}
      <View style={styles.content}>
        {/* Logo Section */}
        <Animated.View style={[styles.logoContainer, logoAnimatedStyle]}>
          <View style={styles.logoBackground}>
            <LinearGradient
              colors={[
                DesignSystem.colors.gold[400],
                DesignSystem.colors.gold[500],
                DesignSystem.colors.gold[600],
              ]}
              style={styles.logoGradient}
              start={{ x: 0, y: 0 }}
              end={{ x: 1, y: 1 }}
            />

            {/* Shimmer Effect */}
            <Animated.View style={[styles.shimmer, shimmerAnimatedStyle]}>
              <LinearGradient
                colors={['transparent', 'rgba(255,255,255,0.3)', 'transparent']}
                style={styles.shimmerGradient}
                start={{ x: 0, y: 0 }}
                end={{ x: 1, y: 0 }}
              />
            </Animated.View>
          </View>

          <Text style={styles.logoText}>AYNAMODA</Text>
          <Text style={styles.logoSubtext}>Style Sanctuary</Text>
        </Animated.View>

        {/* Loading Message */}
        <View style={styles.messageContainer}>
          <Text style={styles.loadingMessage}>{message}</Text>

          {/* Progress Bar */}
          {showProgress && (
            <View style={styles.progressContainer}>
              <View style={styles.progressTrack}>
                <Animated.View style={[styles.progressBar, progressAnimatedStyle]} />
              </View>
              <Text style={styles.progressText}>{Math.round(progress)}%</Text>
            </View>
          )}

          {/* Loading Dots */}
          {!showProgress && (
            <View style={styles.dotsContainer}>
              {[0, 1, 2].map((index) => (
                <Animated.View key={index} style={styles.dot} />
              ))}
            </View>
          )}
        </View>

        {/* Inspirational Quote */}
        <View style={styles.quoteContainer}>
          <Text style={styles.quote}>
            "Style is a way to say who you are without having to speak"
          </Text>
          <Text style={styles.quoteAuthor}>— Rachel Zoe</Text>
        </View>
      </View>
    </View>
  );
};

