import { Ionicons } from '@expo/vector-icons';
import type { BottomTabBarProps } from '@react-navigation/bottom-tabs';
import { BlurView } from 'expo-blur';
import { LinearGradient } from 'expo-linear-gradient';
import * as React from 'react';
import { Dimensions, StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import Animated, {
  useAnimatedStyle,
  useSharedValue,
  withSpring,
  withTiming,
} from 'react-native-reanimated';
import { useSafeAreaInsets } from 'react-native-safe-area-context';

import { AynamodaColors } from '../../theme/AynamodaColors';
import { DesignSystem } from '../../theme/DesignSystem';

const { width } = Dimensions.get('window');
const TAB_BAR_HEIGHT = 64; // Base tab bar height without safe area inset
const AnimatedTouchableOpacity = Animated.createAnimatedComponent(TouchableOpacity);

interface TabItem {
  key: string;
  title: string;
  icon: keyof typeof Ionicons.glyphMap;
  iconFocused: keyof typeof Ionicons.glyphMap;
}

interface PremiumTabBarProps {
  state: BottomTabBarProps['state'];
  descriptors: BottomTabBarProps['descriptors'];
  navigation: BottomTabBarProps['navigation'];
}

const tabs: TabItem[] = [
  {
    key: 'index',
    title: 'Home',
    icon: 'home-outline',
    iconFocused: 'home',
  },
  {
    key: 'wardrobe',
    title: 'Wardrobe',
    icon: 'shirt-outline',
    iconFocused: 'shirt',
  },
  {
    key: 'ayna-mirror',
    title: 'Mirror',
    icon: 'glasses-outline',
    iconFocused: 'glasses',
  },
  {
    key: 'discover',
    title: 'Discover',
    icon: 'sparkles-outline',
    iconFocused: 'sparkles',
  },
  {
    key: 'profile',
    title: 'Profile',
    icon: 'person-circle-outline',
    iconFocused: 'person-circle',
  },
];

// Child component so hooks are used at the top level of a component, not inside a loop/callback
const TabButton: React.FC<{
  tab: TabItem;
  isActive: boolean;
  onPress: () => void;
  onLongPress: () => void;
}> = ({ tab, isActive, onPress, onLongPress }) => {
  const scale = useSharedValue(1);
  const opacity = useSharedValue(0.6);
  const translateY = useSharedValue(0);

  React.useEffect(() => {
    scale.value = withSpring(isActive ? 1.1 : 1, DesignSystem.animations.spring.confident);
    opacity.value = withTiming(isActive ? 1 : 0.6, { duration: 200 });
    translateY.value = withSpring(isActive ? -2 : 0, DesignSystem.animations.spring.gentle);
  }, [isActive, opacity, scale, translateY]);

  const animatedStyle = useAnimatedStyle(() => ({
    transform: [{ scale: scale.value }, { translateY: translateY.value }],
    opacity: opacity.value,
  }));

  return React.createElement(
    AnimatedTouchableOpacity,
    {
      style: [styles.tab, animatedStyle],
      onPress,
      onLongPress,
      activeOpacity: 0.7,
      accessibilityRole: 'tab',
      accessibilityLabel: tab.title,
      accessibilityHint: `Navigate to ${tab.title} screen`,
      accessibilityState: { selected: isActive },
    },
    React.createElement(
      View,
      { style: styles.tabContent },
      React.createElement(Ionicons, {
        name: isActive ? tab.iconFocused : tab.icon,
        size: 24,
        color: isActive ? AynamodaColors.primary.terracotta : AynamodaColors.text.secondary,
      }),
      React.createElement(
        Text,
        {
          style: [
            styles.tabLabel,
            {
              color: isActive ? AynamodaColors.primary.terracotta : AynamodaColors.text.secondary,
              fontWeight: isActive ? '600' : '400',
            },
          ],
        },
        tab.title,
      ),
      isActive && React.createElement(View, { style: styles.activeIndicator }),
    ),
  );
};

const PremiumTabBar: React.FC<PremiumTabBarProps> = ({ state, descriptors, navigation }) => {
  const insets = useSafeAreaInsets();
  const activeIndex = state.index;

  // Floating indicator animation
  const indicatorPosition = useSharedValue(0);
  const indicatorOpacity = useSharedValue(1);

  React.useEffect(() => {
    // Update indicator position
    const tabWidth = (width - DesignSystem.spacing.xl * 2) / tabs.length;
    indicatorPosition.value = withSpring(
      activeIndex * tabWidth,
      DesignSystem.animations.spring.gentle,
    );
  }, [activeIndex, indicatorPosition]);

  const floatingIndicatorStyle = useAnimatedStyle(() => {
    const tabWidth = (width - DesignSystem.spacing.xl * 2) / tabs.length;

    return {
      transform: [{ translateX: indicatorPosition.value }],
      width: tabWidth,
      opacity: indicatorOpacity.value,
    };
  });

  return React.createElement(
    View,
    { style: [styles.container, { paddingBottom: insets.bottom }] },
    React.createElement(
      View,
      { style: styles.tabBarBackground },
      React.createElement(BlurView, {
        intensity: 95,
        tint: 'light',
        style: StyleSheet.absoluteFill,
      }),
      React.createElement(LinearGradient, {
        colors: AynamodaColors.gradients.cream,
        style: {
          position: 'absolute',
          left: 0,
          right: 0,
          bottom: 0,
          height: TAB_BAR_HEIGHT + insets.bottom,
        },
        start: { x: 0, y: 0 },
        end: { x: 1, y: 1 },
      }),
    ),
    React.createElement(Animated.View, {
      style: [styles.floatingIndicator, floatingIndicatorStyle],
    }),
    React.createElement(
      View,
      { style: styles.tabsContainer },
      state.routes.map((route, index) => {
        const tab = tabs[index];
        if (!tab) {
          return null;
        }
        const isActive = index === activeIndex;

        const onPress = () => {
          const event = navigation.emit({
            type: 'tabPress',
            target: route.key,
            canPreventDefault: true,
          });

          if (!isActive && !event.defaultPrevented) {
            navigation.navigate(route.name);
          }
        };

        const onLongPress = () => {
          navigation.emit({
            type: 'tabLongPress',
            target: route.key,
          });
        };

        return React.createElement(TabButton, {
          key: tab.key,
          tab,
          isActive,
          onPress,
          onLongPress,
        });
      }),
    ),
  );
};

const styles = StyleSheet.create({
  activeIndicator: {
    backgroundColor: AynamodaColors.primary.terracotta,
    borderRadius: 3,
    bottom: -DesignSystem.spacing.md,
    height: 6,
    position: 'absolute',
    width: 6,
    ...DesignSystem.elevation.soft,
  },
  container: {
    bottom: 0,
    left: DesignSystem.spacing.lg,
    marginBottom: DesignSystem.spacing.lg,
    position: 'absolute',
    right: DesignSystem.spacing.lg,
  },
  floatingIndicator: {
    backgroundColor: AynamodaColors.primary.terracotta,
    borderRadius: 2,
    height: 4,
    position: 'absolute',
    top: DesignSystem.spacing.xs,
    ...DesignSystem.elevation.soft,
  },
  tab: {
    alignItems: 'center',
    flex: 1,
    justifyContent: 'center',
    paddingVertical: DesignSystem.spacing.sm,
  },
  tabBarBackground: {
    ...StyleSheet.absoluteFillObject,
    borderRadius: DesignSystem.borderRadius.xl,
    overflow: 'hidden',
    ...DesignSystem.elevation.medium,
  },
  tabContent: {
    alignItems: 'center',
    justifyContent: 'center',
    position: 'relative',
  },
  tabLabel: {
    ...DesignSystem.typography.scale.caption,
    fontSize: 10,
    letterSpacing: 0.5,
    marginTop: DesignSystem.spacing.xs,
  },
  tabsContainer: {
    flexDirection: 'row',
    paddingHorizontal: DesignSystem.spacing.sm,
    paddingTop: DesignSystem.spacing.lg,
    paddingVertical: DesignSystem.spacing.md,
  },
});

export default PremiumTabBar;

/**
 * Unified Product Card Component
 *
 * A comprehensive product card system that supports multiple variants:
 * - Standard product cards for general use
 * - Premium product cards for luxury items
 * - Compact cards for grid layouts
 * - Featured cards for highlights
 */

import { Ionicons } from '@expo/vector-icons';
import { BlurView } from 'expo-blur';
import { LinearGradient } from 'expo-linear-gradient';
import React from 'react';
import {
  Dimensions,
  Image,
  ImageBackground,
  StyleSheet,
  Text,
  TouchableOpacity,
  View,
} from 'react-native';

import { DesignSystem } from '@/theme/DesignSystem';

const { width } = Dimensions.get('window');

export interface ProductCardData {
  id: string;
  title: string;
  subtitle?: string;
  description?: string;
  brand: string;
  price: string;
  originalPrice?: string;
  imageUrl: string;
  category: string;
  tags?: string[];
  colors?: string[];
  isLiked?: boolean;
  isNew?: boolean;
  discount?: number;
  rating?: number;
  reviewCount?: number;
}

export interface ProductCardProps {
  /** Product data to display */
  product: ProductCardData;

  /** Card variant */
  variant?: 'standard' | 'premium' | 'compact' | 'featured';

  /** Card size */
  size?: 'small' | 'medium' | 'large';

  /** Whether to show like button */
  showLikeButton?: boolean;

  /** Whether to show price */
  showPrice?: boolean;

  /** Whether to show brand */
  showBrand?: boolean;

  /** Whether to show category badge */
  showCategory?: boolean;

  /** Whether to show discount badge */
  showDiscount?: boolean;

  /** Callback when card is pressed */
  onPress?: (product: ProductCardData) => void;

  /** Callback when like button is pressed */
  onLike?: (product: ProductCardData) => void;

  /** Custom style */
  style?: any;
}

const ProductCard: React.FC<ProductCardProps> = ({
  product,
  variant = 'standard',
  size = 'medium',
  showLikeButton = true,
  showPrice = true,
  showBrand = true,
  showCategory = true,
  showDiscount = true,
  onPress,
  onLike,
  style,
}) => {
  const cardWidth = getCardWidth(size, variant);
  const cardHeight = getCardHeight(size, variant);

  const handlePress = () => {
    onPress?.(product);
  };

  const handleLike = () => {
    onLike?.(product);
  };

  const renderStandardCard = () => (
    <TouchableOpacity
      style={[
        styles.cardContainer,
        { width: cardWidth, height: cardHeight },
        variant === 'premium' && styles.premiumCard,
        style,
      ]}
      onPress={handlePress}
      activeOpacity={0.9}
      accessibilityRole="button"
      accessibilityLabel={`${product.brand} ${product.title}, ${product.price}`}
      accessibilityHint="Tap to view product details"
    >
      <View style={styles.imageContainer}>
        <Image
          source={{ uri: product.imageUrl }}
          style={styles.productImage}
          resizeMode="cover"
          accessibilityRole="image"
          accessibilityLabel={`${product.title} product image`}
        />

        {/* Overlay Gradient */}
        <LinearGradient colors={['transparent', 'rgba(0,0,0,0.3)']} style={styles.imageOverlay} />

        {/* Like Button */}
        {showLikeButton && (
          <TouchableOpacity
            style={styles.likeButton}
            onPress={handleLike}
            hitSlop={{ top: 10, bottom: 10, left: 10, right: 10 }}
            accessibilityRole="button"
            accessibilityLabel={product.isLiked ? 'Remove from favorites' : 'Add to favorites'}
            accessibilityHint={
              product.isLiked
                ? 'Tap to remove this product from your favorites'
                : 'Tap to add this product to your favorites'
            }
            accessibilityState={{ selected: product.isLiked }}
          >
            <View style={styles.likeButtonBackground}>
              <Ionicons
                name={product.isLiked ? 'heart' : 'heart-outline'}
                size={18}
                color={product.isLiked ? '#FF6B6B' : '#666'}
              />
            </View>
          </TouchableOpacity>
        )}

        {/* Category Badge */}
        {showCategory && (
          <View style={styles.categoryBadge}>
            <Text style={styles.categoryText}>{product.category.toUpperCase()}</Text>
          </View>
        )}

        {/* New Badge */}
        {product.isNew && (
          <View style={styles.newBadge}>
            <Text style={styles.newText}>YENİ</Text>
          </View>
        )}

        {/* Discount Badge */}
        {showDiscount && product.discount && (
          <View style={styles.discountBadge}>
            <Text style={styles.discountText}>%{product.discount}</Text>
          </View>
        )}
      </View>

      {/* Product Info */}
      <View style={styles.productInfo}>
        {showBrand && <Text style={styles.brandName}>{product.brand}</Text>}

        <Text style={styles.productName} numberOfLines={2}>
          {product.title}
        </Text>

        {product.subtitle && (
          <Text style={styles.productSubtitle} numberOfLines={1}>
            {product.subtitle}
          </Text>
        )}

        {/* Colors */}
        {product.colors && product.colors.length > 0 && (
          <View style={styles.colorsContainer}>
            {product.colors.slice(0, 4).map((color, index) => (
              <View
                key={index}
                style={[styles.colorDot, { backgroundColor: color.toLowerCase() }]}
              />
            ))}
            {product.colors.length > 4 && (
              <Text style={styles.moreColors}>+{product.colors.length - 4}</Text>
            )}
          </View>
        )}

        {/* Price */}
        {showPrice && (
          <View style={styles.priceContainer}>
            {product.originalPrice && (
              <Text style={styles.originalPrice}>{product.originalPrice}</Text>
            )}
            <Text style={styles.currentPrice}>{product.price}</Text>
          </View>
        )}

        {/* Rating */}
        {product.rating && (
          <View style={styles.ratingContainer}>
            <Ionicons name="star" size={12} color="#FFD700" />
            <Text style={styles.ratingText}>{product.rating.toFixed(1)}</Text>
            {product.reviewCount && <Text style={styles.reviewCount}>({product.reviewCount})</Text>}
          </View>
        )}
      </View>
    </TouchableOpacity>
  );

  const renderPremiumCard = () => (
    <TouchableOpacity
      style={[styles.premiumCardContainer, { width: cardWidth, height: cardHeight }, style]}
      onPress={handlePress}
      activeOpacity={0.95}
      accessibilityRole="button"
      accessibilityLabel={`Premium ${product.brand} ${product.title}, ${product.price}`}
      accessibilityHint="Tap to view premium product details"
    >
      <ImageBackground
        source={{ uri: product.imageUrl }}
        style={styles.premiumImageBackground}
        resizeMode="cover"
        accessibilityRole="image"
        accessibilityLabel={`${product.title} premium product image`}
      >
        <LinearGradient
          colors={['rgba(0,0,0,0.1)', 'rgba(0,0,0,0.3)', 'rgba(0,0,0,0.7)']}
          style={styles.premiumOverlay}
        >
          {/* Top Section */}
          <View style={styles.premiumTopSection}>
            {showLikeButton && (
              <TouchableOpacity
                style={styles.premiumLikeButton}
                onPress={handleLike}
                accessibilityRole="button"
                accessibilityLabel={product.isLiked ? 'Remove from favorites' : 'Add to favorites'}
                accessibilityHint={
                  product.isLiked
                    ? 'Tap to remove this premium product from your favorites'
                    : 'Tap to add this premium product to your favorites'
                }
                accessibilityState={{ selected: product.isLiked }}
              >
                <BlurView intensity={20} style={styles.likeBlurBackground}>
                  <Ionicons
                    name={product.isLiked ? 'heart' : 'heart-outline'}
                    size={20}
                    color={product.isLiked ? '#FF6B6B' : '#FFFFFF'}
                  />
                </BlurView>
              </TouchableOpacity>
            )}

            {product.isNew && (
              <View style={styles.premiumNewBadge}>
                <Text style={styles.premiumNewText}>YENİ</Text>
              </View>
            )}
          </View>

          {/* Bottom Section */}
          <View style={styles.premiumBottomSection}>
            {showBrand && <Text style={styles.premiumBrandName}>{product.brand}</Text>}

            <Text style={styles.premiumProductName} numberOfLines={2}>
              {product.title}
            </Text>

            {product.subtitle && (
              <Text style={styles.premiumProductSubtitle}>{product.subtitle}</Text>
            )}

            {showPrice && (
              <View style={styles.premiumPriceContainer}>
                {product.originalPrice && (
                  <Text style={styles.premiumOriginalPrice}>{product.originalPrice}</Text>
                )}
                <Text style={styles.premiumCurrentPrice}>{product.price}</Text>
                {product.discount && (
                  <View style={styles.premiumDiscountBadge}>
                    <Text style={styles.premiumDiscountText}>%{product.discount}</Text>
                  </View>
                )}
              </View>
            )}
          </View>
        </LinearGradient>
      </ImageBackground>
    </TouchableOpacity>
  );

  const renderCompactCard = () => (
    <TouchableOpacity
      style={[styles.compactCardContainer, { width: cardWidth, height: cardHeight }, style]}
      onPress={handlePress}
      activeOpacity={0.9}
      accessibilityRole="button"
      accessibilityLabel={`${product.brand} ${product.title}, ${product.price}`}
      accessibilityHint="Tap to view compact product details"
    >
      <View style={styles.compactImageContainer}>
        <Image
          source={{ uri: product.imageUrl }}
          style={styles.compactImage}
          resizeMode="cover"
          accessibilityRole="image"
          accessibilityLabel={`${product.title} compact product image`}
        />

        {showLikeButton && (
          <TouchableOpacity
            style={styles.compactLikeButton}
            onPress={handleLike}
            accessibilityRole="button"
            accessibilityLabel={product.isLiked ? 'Remove from favorites' : 'Add to favorites'}
            accessibilityHint={
              product.isLiked
                ? 'Tap to remove this compact product from your favorites'
                : 'Tap to add this compact product to your favorites'
            }
            accessibilityState={{ selected: product.isLiked }}
          >
            <Ionicons
              name={product.isLiked ? 'heart' : 'heart-outline'}
              size={16}
              color={product.isLiked ? '#FF6B6B' : '#666'}
            />
          </TouchableOpacity>
        )}
      </View>

      <View style={styles.compactInfo}>
        {showBrand && (
          <Text style={styles.compactBrand} numberOfLines={1}>
            {product.brand}
          </Text>
        )}
        <Text style={styles.compactName} numberOfLines={1}>
          {product.title}
        </Text>
        {showPrice && <Text style={styles.compactPrice}>{product.price}</Text>}
      </View>
    </TouchableOpacity>
  );

  switch (variant) {
    case 'premium':
      return renderPremiumCard();
    case 'compact':
      return renderCompactCard();
    case 'featured':
      return renderPremiumCard(); // Featured uses premium styling
    default:
      return renderStandardCard();
  }
};

// Helper functions
const getCardWidth = (size: string, variant: string) => {
  const baseWidth = width * 0.45;

  switch (size) {
    case 'small':
      return variant === 'compact' ? baseWidth * 0.8 : baseWidth * 0.9;
    case 'large':
      return variant === 'premium' ? width * 0.9 : baseWidth * 1.2;
    default:
      return baseWidth;
  }
};

const getCardHeight = (size: string, variant: string) => {
  const baseHeight = 280;

  switch (size) {
    case 'small':
      return variant === 'compact' ? 200 : baseHeight * 0.9;
    case 'large':
      return variant === 'premium' ? 400 : baseHeight * 1.2;
    default:
      return variant === 'compact' ? 220 : baseHeight;
  }
};

