const { width: screenWidth, height: screenHeight } = Dimensions.get('window');

const SAMPLE_OUTFITS: OutfitCard[] = [
  {
    id: '1',
    name: 'Serene Sunday',
    description: 'Effortless elegance for a peaceful day',
    category: 'Casual',
