import React from 'react';
import { ScrollView, StyleSheet, Text, TouchableOpacity, View, ViewStyle } from 'react-native';

import { useHapticFeedback } from '../../hooks/useHapticFeedback';
import { BORDER_RADIUS, SPACING, TYPOGRAPHY, UNIFIED_COLORS } from '../../theme/DesignSystem';

export interface ColorPickerProps {
  colors: string[];
  selectedColors?: string[];
  onColorSelect: (color: string) => void;
  onColorDeselect?: (color: string) => void;
  multiSelect?: boolean;
  style?: ViewStyle;
  title?: string;
}

const DEFAULT_COLORS = [
  '#FF6B6B', // Red
  '#4ECDC4', // Teal
  '#45B7D1', // Blue
  '#96CEB4', // Green
  '#FFEAA7', // Yellow
  '#DDA0DD', // Plum
  '#98D8C8', // Mint
  '#F7DC6F', // Gold
  '#BB8FCE', // Lavender
  '#85C1E9', // Sky Blue
  '#F8C471', // Orange
  '#82E0AA', // Light Green
];

const ColorPicker: React.FC<ColorPickerProps> = ({
  colors = DEFAULT_COLORS,
  selectedColors = [],
  onColorSelect,
  onColorDeselect,
  multiSelect = false,
  style,
  title = 'Colors',
}) => {
  const { triggerSelection } = useHapticFeedback();

  const handleColorPress = (color: string) => {
    triggerSelection();

    const isSelected = selectedColors.includes(color);

    if (isSelected && onColorDeselect) {
      onColorDeselect(color);
    } else if (!isSelected) {
      onColorSelect(color);
    }
  };

  const isColorSelected = (color: string) => {
    return selectedColors.includes(color);
  };

  return (
    <View style={[styles.container, style]}>
      {title && <Text style={styles.title}>{title}</Text>}
      <ScrollView
        horizontal
        showsHorizontalScrollIndicator={false}
        contentContainerStyle={styles.scrollContent}
      >
        {colors.map((color, index) => {
          const isSelected = isColorSelected(color);
          return (
            <TouchableOpacity
              key={`${color}-${index}`}
              style={[
                styles.colorButton,
                { backgroundColor: color },
                isSelected && styles.selectedColorButton,
              ]}
              onPress={() => handleColorPress(color)}
              activeOpacity={0.8}
              accessibilityRole="button"
              accessibilityLabel={`Color ${color}`}
              accessibilityState={{ selected: isSelected }}
              accessibilityHint={isSelected ? 'Color is selected' : 'Tap to select this color'}
            >
              {isSelected && (
                <View style={styles.checkmark}>
                  <Text style={styles.checkmarkText}>✓</Text>
                </View>
              )}
            </TouchableOpacity>
          );
        })}
      </ScrollView>
    </View>
  );
};

const styles = StyleSheet.create({
  checkmark: {
    alignItems: 'center',
    backgroundColor: UNIFIED_COLORS.background.primary,
    borderRadius: BORDER_RADIUS.full,
