  ViewStyle,
} from 'react-native';

import { useHapticFeedback } from '@/hooks/useHapticFeedback';
import { WardrobeItem } from '@/types';

export interface ItemDetailsProps {
  item: WardrobeItem;
  onEdit?: () => void;
  onDelete?: () => void;
  onFavoriteToggle?: () => void;
  onClose?: () => void;
  style?: ViewStyle;
}

const ItemDetails: React.FC<ItemDetailsProps> = ({
  item,
  onEdit,
  onDelete,
  onFavoriteToggle,
  onClose,
  style,
}) => {
  const { triggerSelection, triggerLight } = useHapticFeedback();

  const handleFavoriteToggle = () => {
    triggerSelection();
    onFavoriteToggle?.();
  };

  const handleEdit = () => {
    triggerSelection();
    onEdit?.();
  };

  const handleDelete = () => {
    triggerLight();
    onDelete?.();
  };

  return (
    <ScrollView style={[styles.container, style]} showsVerticalScrollIndicator={false}>
      {/* Header */}
      <View style={styles.header}>
        <TouchableOpacity
          onPress={onClose}
          style={styles.closeButton}
          accessibilityRole="button"
          accessibilityLabel="Close item details"
          accessibilityHint="Tap to close the item details view"
        >
          <Text style={styles.closeButtonText}>✕</Text>
        </TouchableOpacity>

        {!!onFavoriteToggle && (
          <TouchableOpacity
            onPress={handleFavoriteToggle}
            style={styles.favoriteButton}
            accessibilityRole="button"
            accessibilityLabel="Toggle favorite"
            accessibilityHint="Tap to add or remove this item from favorites"
          >
            <Text style={styles.favoriteIcon}>♡</Text>
          </TouchableOpacity>
        )}
      </View>

      {/* Image */}
      <View style={styles.imageContainer}>
        {item.imageUri ? (
          <Image
            source={{ uri: item.imageUri }}
            style={styles.image}
            accessibilityLabel={`Image of ${item.name}`}
            accessibilityRole="image"
          />
        ) : (
          <View style={styles.placeholderImage}>
            <Text style={styles.placeholderText}>No Image</Text>
          </View>
        )}
      </View>

      {/* Content */}
      <View style={styles.content}>
        <Text style={styles.name}>{item.name}</Text>

        <View style={styles.basicInfo}>
          <Text style={styles.category}>{item.category}</Text>
          {item.brand && <Text style={styles.brand}>{item.brand}</Text>}
        </View>

        {/* Description field is not in canonical type; keep placeholder if needed */}

        {/* Colors */}
        {item.colors && item.colors.length > 0 && (
          <View style={styles.section}>
            <Text style={styles.sectionTitle}>Colors</Text>
            <View style={styles.colorsContainer}>
              {item.colors.map((color, index) => (
                <View key={index} style={styles.colorItem}>
