            accessibilityHint="Enter the name of your wardrobe item"
          />
        </View>

        <View style={styles.divider} />

        {/* Basic Information */}
        <View style={styles.formSection}>
          <View style={styles.row}>
            <View style={styles.halfWidth}>
              <Text style={styles.label}>Category</Text>
