    isLoading,
    error,
    loadPreferences,
    updatePreferences,
  };
};

// Animation Hook - Organic Motion with Accessibility Support
import { useCallback, useEffect, useRef, useState } from 'react';
import { AccessibilityInfo, Animated, Platform } from 'react-native';

import {
  AnimationConfig,
  AnimationSystem,
  EASING,
  SPRING,
  TIMING,
} from '../theme/foundations/Animation';

/**
 * Custom hook for managing animations with accessibility support
 *
 * Features:
 * - Automatic reduced motion detection
 * - 60fps performance optimization
 * - Organic easing curves
 * - Accessibility compliance
 * - Memory leak prevention
 */

interface UseAnimationOptions {
  reducedMotion?: boolean;
  autoStart?: boolean;
  loop?: boolean;
  resetOnUnmount?: boolean;
}

interface AnimationControls {
  start: (callback?: () => void) => void;
  stop: () => void;
  reset: () => void;
  reverse: () => void;
}

export const useAnimation = (initialValue: number = 0, options: UseAnimationOptions = {}) => {
  const animatedValue = useRef(new Animated.Value(initialValue)).current;
  const [isReducedMotionEnabled, setIsReducedMotionEnabled] = useState(false);
  const [isAnimating, setIsAnimating] = useState(false);
  const currentAnimation = useRef<Animated.CompositeAnimation | null>(null);

  // Check for reduced motion preference
  useEffect(() => {
    const checkReducedMotion = async () => {
      try {
        if (Platform.OS === 'ios') {
          const isEnabled = await AccessibilityInfo.isReduceMotionEnabled();
          setIsReducedMotionEnabled(isEnabled);
        } else {
          // Android doesn't have a direct equivalent, but we can check for other accessibility features
          const isScreenReaderEnabled = await AccessibilityInfo.isScreenReaderEnabled();
          setIsReducedMotionEnabled(isScreenReaderEnabled);
        }
      } catch (error) {
        console.warn('Could not check reduced motion preference:', error);
      }
    };

    checkReducedMotion();

    // Listen for accessibility changes
    const subscription = AccessibilityInfo.addEventListener(
      'reduceMotionChanged',
      setIsReducedMotionEnabled,
    );

    return () => {
      subscription?.remove();
    };
  }, []);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (currentAnimation.current) {
        currentAnimation.current.stop();
      }
      if (options.resetOnUnmount) {
        animatedValue.setValue(initialValue);
      }
    };
  }, [animatedValue, initialValue, options.resetOnUnmount]);

  // Create animation with accessibility support
  const createAnimation = useCallback(
    (toValue: number, config: Partial<AnimationConfig>) => {
      const shouldUseReducedMotion = options.reducedMotion ?? isReducedMotionEnabled;

      if (shouldUseReducedMotion) {
        return Animated.timing(animatedValue, {
          toValue,
          duration: TIMING.instant,
          easing: EASING.standard,
          useNativeDriver: true,
        });
      }

      return Animated.timing(animatedValue, {
        toValue,
        useNativeDriver: true,
        ...config,
      });
    },
    [animatedValue, isReducedMotionEnabled, options.reducedMotion],
  );

  // Animation control functions
  const start = useCallback(
    (toValue: number, config: Partial<AnimationConfig>, callback?: () => void) => {
      if (currentAnimation.current) {
        currentAnimation.current.stop();
      }

      setIsAnimating(true);
      currentAnimation.current = createAnimation(toValue, config);

      currentAnimation.current.start((finished) => {
        setIsAnimating(false);
        if (finished && callback) {
          callback();
        }

        if (options.loop && finished) {
          // Restart animation for looping
          start(toValue, config, callback);
        }
      });
    },
    [createAnimation, options.loop],
  );

  const stop = useCallback(() => {
    if (currentAnimation.current) {
      currentAnimation.current.stop();
      setIsAnimating(false);
    }
  }, []);

  const reset = useCallback(() => {
    stop();
    animatedValue.setValue(initialValue);
  }, [animatedValue, initialValue, stop]);

  const reverse = useCallback(() => {
    if (currentAnimation.current) {
      // Get current value and animate back to initial
      start(initialValue, { duration: TIMING.standard, easing: EASING.exit });
    }
  }, [animatedValue, initialValue, start]);

  return {
    animatedValue,
    isAnimating,
    isReducedMotionEnabled,
    start,
    stop,
    reset,
    reverse,
  };
};

/**
 * Hook for fade animations
 */
export const useFadeAnimation = (options: UseAnimationOptions = {}) => {
  const { animatedValue, start, stop, reset, isAnimating, isReducedMotionEnabled } = useAnimation(
    0,
    options,
  );

  const fadeIn = useCallback(
    (callback?: () => void) => {
      start(1, AnimationSystem.animations.fade.in, callback);
    },
    [start],
  );

  const fadeOut = useCallback(
    (callback?: () => void) => {
      start(0, AnimationSystem.animations.fade.out, callback);
    },
    [start],
  );

  return {
    opacity: animatedValue,
    fadeIn,
    fadeOut,
    stop,
    reset,
    isAnimating,
    isReducedMotionEnabled,
  };
};

/**
 * Hook for scale animations
 */
export const useScaleAnimation = (initialScale: number = 1, options: UseAnimationOptions = {}) => {
  const { animatedValue, start, stop, reset, isAnimating, isReducedMotionEnabled } = useAnimation(
    initialScale,
    options,
  );

  const scaleIn = useCallback(
    (callback?: () => void) => {
      start(1, AnimationSystem.animations.scale.in, callback);
    },
    [start],
  );

  const scaleOut = useCallback(
    (callback?: () => void) => {
      start(0, AnimationSystem.animations.scale.out, callback);
    },
    [start],
  );

  const press = useCallback(
    (callback?: () => void) => {
      start(0.95, AnimationSystem.animations.scale.press, () => {
        start(1, AnimationSystem.animations.scale.press, callback);
      });
    },
    [start],
  );

  return {
    scale: animatedValue,
    scaleIn,
    scaleOut,
    press,
    stop,
    reset,
    isAnimating,
    isReducedMotionEnabled,
  };
};

/**
 * Hook for slide animations
 */
export const useSlideAnimation = (
  initialPosition: number = 0,
  options: UseAnimationOptions = {},
) => {
  const { animatedValue, start, stop, reset, isAnimating, isReducedMotionEnabled } = useAnimation(
    initialPosition,
    options,
  );

  const slideUp = useCallback(
    (distance: number = 20, callback?: () => void) => {
      start(-distance, AnimationSystem.animations.slide.up, callback);
    },
    [start],
  );

  const slideDown = useCallback(
    (distance: number = 20, callback?: () => void) => {
      start(distance, AnimationSystem.animations.slide.down, callback);
    },
    [start],
  );

  const slideLeft = useCallback(
    (distance: number = 20, callback?: () => void) => {
      start(-distance, AnimationSystem.animations.slide.left, callback);
    },
    [start],
  );

  const slideRight = useCallback(
    (distance: number = 20, callback?: () => void) => {
      start(distance, AnimationSystem.animations.slide.right, callback);
    },
    [start],
  );

  const slideToPosition = useCallback(
    (position: number, callback?: () => void) => {
      start(position, AnimationSystem.animations.slide.up, callback);
    },
    [start],
  );

  return {
    translateY: animatedValue,
    translateX: animatedValue,
    slideUp,
    slideDown,
    slideLeft,
    slideRight,
    slideToPosition,
    stop,
    reset,
    isAnimating,
    isReducedMotionEnabled,
  };
};

/**
 * Hook for spring animations with organic feel
 */
export const useSpringAnimation = (initialValue: number = 0, options: UseAnimationOptions = {}) => {
  const animatedValue = useRef(new Animated.Value(initialValue)).current;
  const [isReducedMotionEnabled, setIsReducedMotionEnabled] = useState(false);
  const [isAnimating, setIsAnimating] = useState(false);
  const currentAnimation = useRef<Animated.CompositeAnimation | null>(null);

  // Check for reduced motion preference
  useEffect(() => {
    const checkReducedMotion = async () => {
      try {
        if (Platform.OS === 'ios') {
          const isEnabled = await AccessibilityInfo.isReduceMotionEnabled();
          setIsReducedMotionEnabled(isEnabled);
        }
      } catch (error) {
        console.warn('Could not check reduced motion preference:', error);
      }
    };

    checkReducedMotion();
  }, []);

  const springTo = useCallback(
    (toValue: number, config = SPRING.gentle, callback?: () => void) => {
      if (currentAnimation.current) {
        currentAnimation.current.stop();
      }

      setIsAnimating(true);

      if (isReducedMotionEnabled) {
        currentAnimation.current = Animated.timing(animatedValue, {
          toValue,
          duration: TIMING.instant,
          useNativeDriver: true,
        });
      } else {
        currentAnimation.current = Animated.spring(animatedValue, {
          toValue,
          ...config,
          useNativeDriver: true,
        });
      }

      currentAnimation.current.start((finished) => {
        setIsAnimating(false);
        if (finished && callback) {
          callback();
        }
      });
    },
    [animatedValue, isReducedMotionEnabled],
  );

  const stop = useCallback(() => {
    if (currentAnimation.current) {
      currentAnimation.current.stop();
      setIsAnimating(false);
    }
  }, []);

  const reset = useCallback(() => {
    stop();
    animatedValue.setValue(initialValue);
  }, [animatedValue, initialValue, stop]);

  return {
    animatedValue,
    springTo,
    stop,
    reset,
    isAnimating,
    isReducedMotionEnabled,
  };
};

/**
 * Hook for staggered list animations
 */
export const useStaggeredAnimation = (
  itemCount: number,
  staggerDelay: number = 50,
  options: UseAnimationOptions = {},
) => {
  const animations = useRef(Array.from({ length: itemCount }, () => new Animated.Value(0))).current;

  const [isReducedMotionEnabled, setIsReducedMotionEnabled] = useState(false);
  const [isAnimating, setIsAnimating] = useState(false);

  // Check for reduced motion preference
  useEffect(() => {
    const checkReducedMotion = async () => {
      try {
        if (Platform.OS === 'ios') {
          const isEnabled = await AccessibilityInfo.isReduceMotionEnabled();
          setIsReducedMotionEnabled(isEnabled);
        }
      } catch (error) {
        console.warn('Could not check reduced motion preference:', error);
      }
    };

    checkReducedMotion();
  }, []);

  const startStaggered = useCallback(
    (callback?: () => void) => {
      setIsAnimating(true);

      if (isReducedMotionEnabled) {
        // No stagger for reduced motion
        const parallelAnimations = animations.map((anim) =>
          Animated.timing(anim, {
            toValue: 1,
            duration: TIMING.instant,
            useNativeDriver: true,
          }),
        );

        Animated.parallel(parallelAnimations).start((finished) => {
          setIsAnimating(false);
          if (finished && callback) {
            callback();
          }
        });
      } else {
        const staggeredAnimations = animations.map((anim, index) =>
          Animated.timing(anim, {
            toValue: 1,
            duration: TIMING.standard,
            delay: index * staggerDelay,
            easing: EASING.organic.gentle,
            useNativeDriver: true,
          }),
        );

        Animated.parallel(staggeredAnimations).start((finished) => {
          setIsAnimating(false);
          if (finished && callback) {
            callback();
          }
        });
      }
    },
    [animations, staggerDelay, isReducedMotionEnabled],
  );

  const reset = useCallback(() => {
    animations.forEach((anim) => anim.setValue(0));
    setIsAnimating(false);
  }, [animations]);

  return {
    animations,
    startStaggered,
    reset,
    isAnimating,
    isReducedMotionEnabled,
  };
};

export default useAnimation;

// Authentication Hook
import { useCallback, useEffect, useState } from 'react';

import { supabase } from '../config/supabaseClient';
import { User, UserAppPreferences, UserProfile, UserSubscription } from '../types/user';
import { isObject, safeParse } from '../utils/safeJSON';
import { secureStorage } from '../utils/secureStorage';

export interface AuthState {
  user: User | null;
  loading: boolean;
  error: string | null;
  isAuthenticated: boolean;
}

export interface UseAuthReturn extends AuthState {
  signIn: (email: string, password: string) => Promise<{ success: boolean; error?: string }>;
  signUp: (
    email: string,
    password: string,
    userData?: Partial<User>,
  ) => Promise<{ success: boolean; error?: string }>;
  signOut: () => Promise<void>;
  updateProfile: (updates: Partial<User>) => Promise<{ success: boolean; error?: string }>;
  clearError: () => void;
}

export function useAuth(): UseAuthReturn {
  const [state, setState] = useState<AuthState>({
    user: null,
    loading: true,
    error: null,
    isAuthenticated: false,
  });

  // Load user from storage on mount
  useEffect(() => {
    const loadStoredUser = async () => {
      try {
        await secureStorage.initialize();
        const stored = await secureStorage.getItem('ayna_auth_user');
        if (stored) {
          const raw = safeParse<unknown>(stored, null);
          // Minimal structural guard; we intentionally avoid deep validation here
          if (isObject(raw) && typeof raw.id === 'string' && typeof raw.email === 'string') {
            // Reconstruct to satisfy typing while tolerating partial persisted shape
            const user: User = {
              id: String(raw.id),
              email: String(raw.email),
              name: typeof raw.name === 'string' ? raw.name : '',
              avatar: typeof raw.avatar === 'string' ? raw.avatar : undefined,
              // Provide defensive fallbacks for nested objects
              preferences: isObject(raw.preferences)
                ? {
                    theme:
                      typeof (raw.preferences as Partial<UserAppPreferences>).theme === 'string'
                        ? ((raw.preferences as Partial<UserAppPreferences>).theme as
                            | 'light'
                            | 'dark')
                        : 'light',
                    notifications: Boolean(
                      (raw.preferences as Partial<UserAppPreferences>).notifications ?? true,
                    ),
                    hapticFeedback: Boolean(
                      (raw.preferences as Partial<UserAppPreferences>).hapticFeedback ?? true,
                    ),
                    autoBackup: Boolean(
                      (raw.preferences as Partial<UserAppPreferences>).autoBackup ?? true,
                    ),
                    aiSuggestions: Boolean(
                      (raw.preferences as Partial<UserAppPreferences>).aiSuggestions ?? true,
                    ),
                    privacyMode: Boolean(
                      (raw.preferences as Partial<UserAppPreferences>).privacyMode ?? false,
                    ),
                  }
                : {
                    theme: 'light',
                    notifications: true,
                    hapticFeedback: true,
                    autoBackup: true,
                    aiSuggestions: true,
                    privacyMode: false,
                  },
              profile: isObject(raw.profile)
                ? {
                    style:
                      typeof (raw.profile as Partial<UserProfile>).style === 'string'
                        ? (raw.profile as Partial<UserProfile>).style!
                        : 'casual',
                    favoriteColors: Array.isArray(
                      (raw.profile as Partial<UserProfile>).favoriteColors,
                    )
                      ? (raw.profile as Partial<UserProfile>).favoriteColors!
                      : [],
                    bodyType:
                      typeof (raw.profile as Partial<UserProfile>).bodyType === 'string'
                        ? (raw.profile as Partial<UserProfile>).bodyType!
                        : 'average',
                    lifestyle:
                      typeof (raw.profile as Partial<UserProfile>).lifestyle === 'string'
                        ? (raw.profile as Partial<UserProfile>).lifestyle!
                        : 'casual',
                    budget: ['low', 'medium', 'high'].includes(
                      (raw.profile as Partial<UserProfile>).budget!,
                    )
                      ? ((raw.profile as Partial<UserProfile>).budget as 'low' | 'medium' | 'high')
                      : 'medium',
                    sustainabilityGoals: Array.isArray(
                      (raw.profile as Partial<UserProfile>).sustainabilityGoals,
                    )
                      ? (raw.profile as Partial<UserProfile>).sustainabilityGoals!
                      : [],
                  }
                : {
                    style: 'casual',
                    favoriteColors: [],
                    bodyType: 'average',
                    lifestyle: 'casual',
                    budget: 'medium',
                    sustainabilityGoals: [],
                  },
              subscription: isObject(raw.subscription)
                ? {
                    plan:
                      (raw.subscription as Partial<UserSubscription>).plan &&
                      ['free', 'premium', 'pro'].includes(
                        (raw.subscription as Partial<UserSubscription>).plan!,
                      )
                        ? (raw.subscription as Partial<UserSubscription>).plan!
                        : 'free',
                    status:
                      (raw.subscription as Partial<UserSubscription>).status &&
                      ['active', 'inactive', 'cancelled', 'expired'].includes(
                        (raw.subscription as Partial<UserSubscription>).status!,
                      )
                        ? (raw.subscription as Partial<UserSubscription>).status!
                        : 'active',
                    expiresAt: (raw.subscription as Partial<UserSubscription>).expiresAt
                      ? new Date((raw.subscription as Partial<UserSubscription>).expiresAt!)
                      : new Date(Date.now() + 365 * 24 * 60 * 60 * 1000),
                  }
                : {
                    plan: 'free',
                    status: 'active',
                    expiresAt: new Date(Date.now() + 365 * 24 * 60 * 60 * 1000),
                  },
              createdAt: (raw as { createdAt?: string | Date }).createdAt
                ? new Date((raw as { createdAt?: string | Date }).createdAt as string)
                : new Date(),
              updatedAt: (raw as { updatedAt?: string | Date }).updatedAt
                ? new Date((raw as { updatedAt?: string | Date }).updatedAt as string)
                : new Date(),
            };
            setState((prev) => ({
              ...prev,
              user,
              isAuthenticated: true,
              loading: false,
            }));
            return;
          }
        }
        setState((prev) => ({ ...prev, loading: false }));
      } catch (error) {
        console.warn('Failed to load stored user:', error);
        setState((prev) => ({ ...prev, loading: false }));
      }
    };

    void loadStoredUser(); // eslint-disable-line no-void
  }, []);

  // Listen for auth state changes
  useEffect(() => {
    const {
      data: { subscription },
    } = supabase.auth.onAuthStateChange(async (event, session) => {
      if (event === 'SIGNED_IN' && session?.user) {
        // User signed in, state will be updated automatically
        console.log('User signed in');
      } else if (event === 'SIGNED_OUT') {
        setState({
          user: null,
          loading: false,
          error: null,
          isAuthenticated: false,
        });
        await secureStorage.removeItem('ayna_auth_user');
      }
    });

    return () => subscription.unsubscribe();
  }, []);

  const signIn = useCallback(async (email: string, password: string) => {
    try {
      setState((prev) => ({ ...prev, loading: true, error: null }));

      const { data, error } = await supabase.auth.signInWithPassword({
        email,
        password,
      });

      if (error) {
        setState((prev) => ({ ...prev, loading: false, error: error.message }));
        return { success: false, error: error.message };
      }

      if (data.user) {
        // Check if email is verified
        if (!data.user.email_confirmed_at) {
          setState((prev) => ({
            ...prev,
            loading: false,
            error: 'E-posta adresinizi doğrulamanız gerekiyor. Lütfen e-postanızı kontrol edin.',
          }));
          return {
            success: false,
            error: 'E-posta adresinizi doğrulamanız gerekiyor. Lütfen e-postanızı kontrol edin.',
          };
        }

        setState((prev) => ({ ...prev, loading: false, isAuthenticated: true }));
        return { success: true };
      }

      return { success: false, error: 'Sign in failed' };
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'Sign in failed';
      setState((prev) => ({ ...prev, loading: false, error: errorMessage }));
      return { success: false, error: errorMessage };
    }
  }, []);

  const signUp = useCallback(async (email: string, password: string, userData?: Partial<User>) => {
    try {
      setState((prev) => ({ ...prev, loading: true, error: null }));

      const { data, error } = await supabase.auth.signUp({
        email,
        password,
        options: {
          data: userData,
        },
      });

      if (error) {
        setState((prev) => ({ ...prev, loading: false, error: error.message }));
        return { success: false, error: error.message };
      }

      if (data.user) {
        // Create user profile in database
        const { error: profileError } = await supabase.from('user_profiles').insert({
          id: data.user.id,
          email: data.user.email,
          ...userData,
          created_at: new Date().toISOString(),
          updated_at: new Date().toISOString(),
        });

        if (profileError) {
          console.warn('Failed to create user profile:', profileError);
        }

        setState((prev) => ({ ...prev, loading: false, isAuthenticated: true }));
        return { success: true };
      }

      return { success: false, error: 'Sign up failed' };
    } catch (error) {
      const errorMessage = error instanceof Error ? error.message : 'Sign up failed';
      setState((prev) => ({ ...prev, loading: false, error: errorMessage }));
      return { success: false, error: errorMessage };
    }
  }, []);

  const signOut = useCallback(async () => {
    try {
      setState((prev) => ({ ...prev, loading: true }));

      const { error } = await supabase.auth.signOut();

      if (error) {
        console.warn('Sign out error:', error);
      }

      setState({
        user: null,
        loading: false,
        error: null,
        isAuthenticated: false,
      });

      await secureStorage.removeItem('ayna_auth_user');
    } catch (error) {
      console.warn('Sign out failed:', error);
      setState((prev) => ({ ...prev, loading: false }));
    }
  }, []);

  const updateProfile = useCallback(
    async (updates: Partial<User>) => {
      try {
        if (!state.user) {
          return { success: false, error: 'No user logged in' };
        }

        setState((prev) => ({ ...prev, loading: true, error: null }));

        const { error } = await supabase
          .from('user_profiles')
          .update({
            ...updates,
            updated_at: new Date().toISOString(),
          })
          .eq('id', state.user.id);

        if (error) {
          setState((prev) => ({ ...prev, loading: false, error: error.message }));
          return { success: false, error: error.message };
        }

        const updatedUser = { ...state.user, ...updates };
        setState((prev) => ({
          ...prev,
          user: updatedUser,
          loading: false,
        }));

        await secureStorage.setItem('ayna_auth_user', JSON.stringify(updatedUser));

        return { success: true };
      } catch (error) {
        const errorMessage = error instanceof Error ? error.message : 'Update failed';
        setState((prev) => ({ ...prev, loading: false, error: errorMessage }));
        return { success: false, error: errorMessage };
      }
    },
    [state.user],
  );

  const clearError = useCallback(() => {
    setState((prev) => ({ ...prev, error: null }));
  }, []);

  return {
    ...state,
    signIn,
    signUp,
    signOut,
    updateProfile,
    clearError,
  };
}

export default useAuth;

import { useCallback, useEffect, useState } from 'react';

import {
  EfficiencyGoal,
  EfficiencyScore,
  efficiencyScoreService,
} from '@/services/efficiencyScoreService';
import { errorInDev } from '@/utils/consoleSuppress';

import { useAuth } from './useAuth';

export interface UseEfficiencyScoreReturn {
  // State
  efficiencyScore: EfficiencyScore | null;
  goals: EfficiencyGoal[];
  isLoading: boolean;
  isGenerating: boolean;
  error: string | null;

  // Actions
  generateScore: () => Promise<void>;
  refreshScore: () => Promise<void>;
  createGoal: (goal: Omit<EfficiencyGoal, 'id' | 'createdAt' | 'updatedAt'>) => Promise<void>;
  refreshGoals: () => Promise<void>;
  clearError: () => void;

  // Computed values
  hasScore: boolean;
  overallGrade: string;
  strongestCategory: string;
  weakestCategory: string;
  improvementPotential: number;
  isImproving: boolean;

  // Helper functions
  getCategoryInsights: (category: keyof EfficiencyScore['breakdown']) => string[];
  getScoreColor: (score: number) => string;
  getGradeFromScore: (score: number) => string;
  getPerformanceLevel: (score: number) => 'excellent' | 'good' | 'fair' | 'poor';
}

export const useEfficiencyScore = (): UseEfficiencyScoreReturn => {
  const { user } = useAuth();
  const [efficiencyScore, setEfficiencyScore] = useState<EfficiencyScore | null>(null);
  const [goals, setGoals] = useState<EfficiencyGoal[]>([]);
  const [isLoading, setIsLoading] = useState(false);
  const [isGenerating, setIsGenerating] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Load initial data
  useEffect(() => {
    if (user?.id) {
      refreshScore();
      refreshGoals();
    }
  }, [user?.id]);

  const generateScore = useCallback(async () => {
    if (!user?.id) {
      return;
    }

    try {
      setIsGenerating(true);
      setError(null);

      const score = await efficiencyScoreService.calculateEfficiencyScore(user.id);
      setEfficiencyScore(score);

      // Store the score for historical tracking
      await efficiencyScoreService.storeEfficiencyScore(user.id, score);
    } catch (err) {
      errorInDev('Failed to generate efficiency score:', err instanceof Error ? err : String(err));
      setError('Failed to generate efficiency score');
    } finally {
      setIsGenerating(false);
    }
  }, [user?.id]);

  const refreshScore = useCallback(async () => {
    if (!user?.id) {
      return;
    }

    try {
      setIsLoading(true);
      setError(null);

      // Try to get cached score first, then generate if needed
      const score = await efficiencyScoreService.calculateEfficiencyScore(user.id);
      setEfficiencyScore(score);
    } catch (err) {
      errorInDev('Failed to refresh efficiency score:', err instanceof Error ? err : String(err));
      setError('Failed to load efficiency score');
    } finally {
      setIsLoading(false);
    }
  }, [user?.id]);

  const createGoal = useCallback(
    async (goalData: Omit<EfficiencyGoal, 'id' | 'createdAt' | 'updatedAt'>) => {
      if (!user?.id) {
        return;
      }

      try {
        setError(null);
        const newGoal = await efficiencyScoreService.createEfficiencyGoal(goalData);
        setGoals((prev) => [newGoal, ...prev]);
      } catch (err) {
        errorInDev('Failed to create efficiency goal:', err instanceof Error ? err : String(err));
        setError('Failed to create goal');
      }
    },
    [user?.id],
  );

  const refreshGoals = useCallback(async () => {
    if (!user?.id) {
      return;
    }

    try {
      const userGoals = await efficiencyScoreService.getEfficiencyGoals(user.id);
      setGoals(userGoals);
    } catch (err) {
      errorInDev('Failed to refresh efficiency goals:', err instanceof Error ? err : String(err));
      setError('Failed to load goals');
    }
  }, [user?.id]);

  const clearError = useCallback(() => {
    setError(null);
  }, []);

  // Computed values
  const hasScore = efficiencyScore !== null;

  const overallGrade = efficiencyScore ? getGradeFromScore(efficiencyScore.overall) : 'N/A';

  const strongestCategory = efficiencyScore
    ? Object.entries(efficiencyScore.breakdown).reduce(
        (strongest, [category, score]) =>
          score > strongest.score ? { category, score } : strongest,
        { category: '', score: 0 },
      ).category
    : '';

  const weakestCategory = efficiencyScore
    ? Object.entries(efficiencyScore.breakdown).reduce(
        (weakest, [category, score]) => (score < weakest.score ? { category, score } : weakest),
        { category: '', score: 100 },
      ).category
    : '';

  const improvementPotential = efficiencyScore ? 100 - efficiencyScore.overall : 0;

  const isImproving = efficiencyScore ? efficiencyScore.trends.trajectory === 'improving' : false;

  // Helper functions
  const getCategoryInsights = useCallback(
    (category: keyof EfficiencyScore['breakdown']): string[] => {
      if (!efficiencyScore) {
        return [];
      }

      const score = efficiencyScore.breakdown[category];
      const insights: string[] = [];

      switch (category) {
        case 'utilization':
          if (score >= 80) {
            insights.push('Excellent wardrobe utilization');
            insights.push('You wear most of your items regularly');
          } else if (score >= 60) {
            insights.push('Good utilization with room for improvement');
            insights.push('Try wearing neglected items more often');
          } else {
            insights.push('Low utilization - many items go unworn');
            insights.push('Consider the rediscovery challenge');
          }
          break;

        case 'costEfficiency':
          if (score >= 80) {
            insights.push('Excellent cost per wear optimization');
            insights.push('Your investment is paying off');
          } else if (score >= 60) {
            insights.push('Good cost efficiency');
            insights.push('Focus on wearing expensive items more');
          } else {
            insights.push('Poor cost efficiency');
            insights.push('Many items have high cost per wear');
          }
          break;

        case 'sustainability':
          if (score >= 80) {
            insights.push('Excellent sustainability practices');
            insights.push('You care for your items well');
          } else if (score >= 60) {
            insights.push('Good sustainability habits');
            insights.push('Consider improving item care routines');
          } else {
            insights.push('Room for sustainability improvement');
            insights.push('Focus on item longevity and care');
          }
          break;

        case 'versatility':
          if (score >= 80) {
            insights.push('Highly versatile wardrobe');
            insights.push('Items work well together');
          } else if (score >= 60) {
            insights.push('Good versatility');
            insights.push('Try new outfit combinations');
          } else {
            insights.push('Limited versatility');
            insights.push('Items may be too specialized');
          }
          break;

        case 'curation':
          if (score >= 80) {
            insights.push('Well-curated wardrobe');
            insights.push('Quality pieces that complement each other');
          } else if (score >= 60) {
            insights.push('Good curation');
            insights.push('Consider quality over quantity');
          } else {
            insights.push('Poor curation');
            insights.push('Focus on cohesive, quality pieces');
          }
          break;
      }

      return insights;
    },
    [efficiencyScore],
  );

  const getScoreColor = useCallback((score: number): string => {
    if (score >= 80) {
      return '#4CAF50';
    } // Green
    if (score >= 60) {
      return '#FF9800';
    } // Orange
    if (score >= 40) {
      return '#FFC107';
    } // Yellow
    return '#F44336'; // Red
  }, []);

  function getGradeFromScore(score: number): string {
    if (score >= 90) {
      return 'A+';
    }
    if (score >= 85) {
      return 'A';
    }
    if (score >= 80) {
      return 'A-';
    }
    if (score >= 75) {
      return 'B+';
    }
    if (score >= 70) {
      return 'B';
    }
    if (score >= 65) {
      return 'B-';
    }
    if (score >= 60) {
      return 'C+';
    }
    if (score >= 55) {
      return 'C';
    }
    if (score >= 50) {
      return 'C-';
    }
    if (score >= 45) {
      return 'D+';
    }
    if (score >= 40) {
      return 'D';
    }
    return 'F';
  }

  const getPerformanceLevel = useCallback(
    (score: number): 'excellent' | 'good' | 'fair' | 'poor' => {
      if (score >= 80) {
        return 'excellent';
      }
      if (score >= 60) {
        return 'good';
      }
      if (score >= 40) {
        return 'fair';
      }
      return 'poor';
    },
    [],
  );

  return {
    // State
    efficiencyScore,
    goals,
    isLoading,
    isGenerating,
    error,

    // Actions
    generateScore,
    refreshScore,
    createGoal,
    refreshGoals,
    clearError,

    // Computed values
    hasScore,
    overallGrade,
    strongestCategory,
    weakestCategory,
    improvementPotential,
    isImproving,

    // Helper functions
    getCategoryInsights,
    getScoreColor,
    getGradeFromScore,
    getPerformanceLevel,
  };
};

export default useEfficiencyScore;

// Error Recovery Hooks - Automatic retry and recovery strategies
import { useCallback, useEffect, useRef, useState } from 'react';
import { AppState, AppStateStatus } from 'react-native';

import { ErrorReporting } from '../services/ErrorReporting';
import { AppError, ErrorCategory, ErrorSeverity } from '../utils/ErrorHandler';

/**
 * Retry Configuration
 */
export interface RetryConfig {
  /**
   * Total attempts INCLUDING the initial attempt.
   * (Legacy API exposed this as `maxRetries` meaning the number of retries AFTER the first attempt.
   * We map `maxRetries` -> `maxAttempts = maxRetries + 1` for compatibility.)
   */
  maxAttempts: number;
  baseDelay: number; // milliseconds
  maxDelay: number; // milliseconds
  backoffMultiplier: number;
  jitter: boolean;
  retryCondition?: (error: AppError, attempt: number) => boolean;
  /** Maximum consecutive failures before giving up (legacy: failureThreshold). If provided overrides maxAttempts derivation */
  failureThreshold?: number;
}

/**
 * Default Retry Configurations
 */
export const RETRY_CONFIGS = {
  network: {
    maxAttempts: 3,
    baseDelay: 1000,
    maxDelay: 10000,
    backoffMultiplier: 2,
    jitter: true,
    retryCondition: (error: AppError) =>
      error.category === ErrorCategory.NETWORK && error.severity !== ErrorSeverity.CRITICAL,
  },
  aiService: {
    maxAttempts: 2,
    baseDelay: 2000,
    maxDelay: 8000,
    backoffMultiplier: 2,
    jitter: true,
    retryCondition: (error: AppError) =>
      error.category === ErrorCategory.AI_SERVICE &&
      !(error as any)?.message?.includes?.('QUOTA_EXCEEDED'),
  },
  imageProcessing: {
    maxAttempts: 2,
    baseDelay: 1500,
    maxDelay: 6000,
    backoffMultiplier: 2,
    jitter: false,
    retryCondition: (error: AppError) =>
      error.category === ErrorCategory.IMAGE_PROCESSING &&
      error.severity !== ErrorSeverity.CRITICAL,
  },
  default: {
    maxAttempts: 1,
    baseDelay: 1000,
    maxDelay: 5000,
    backoffMultiplier: 1.5,
    jitter: true,
  },
};

/**
 * Recovery State
 */
export interface RecoveryState {
  isRetrying: boolean;
  attempt: number;
  lastError: AppError | null;
  nextRetryIn: number;
  canRetry: boolean;
}

/**
 * Use Error Recovery Hook
 */
// Backwards compatible configuration normalizer (accepts legacy keys like maxRetries)
type LegacyRetryConfig = Partial<RetryConfig> & { maxRetries?: number };
function normalizeRetryConfig(partial?: LegacyRetryConfig): RetryConfig {
  const maxRetries: number | undefined = partial?.maxRetries; // legacy key
  const failureThreshold: number | undefined = partial?.failureThreshold; // legacy key
  const base: Partial<RetryConfig> = { ...partial };
  delete (base as LegacyRetryConfig).maxRetries;
  // failureThreshold maps to maxAttempts = failureThreshold
  let derivedMaxAttempts = base.maxAttempts || RETRY_CONFIGS.default.maxAttempts;
  if (typeof maxRetries === 'number') {
    derivedMaxAttempts = Math.max(1, maxRetries + 1);
  }
  if (typeof failureThreshold === 'number') {
    derivedMaxAttempts = Math.max(1, failureThreshold);
  }
  return {
    ...RETRY_CONFIGS.default,
    ...base,
    maxAttempts: derivedMaxAttempts,
    failureThreshold,
  } as RetryConfig;
}

// Overloads to support both (operation, config) and (config) signatures used in legacy tests
// Unified signature: (operation?, config?) where first arg can be config object
export function useErrorRecovery<T>(
  operationOrConfig?:
    | (() => Promise<T>)
    | (Partial<RetryConfig> & { maxRetries?: number; failureThreshold?: number }),
  maybeConfig?: Partial<RetryConfig> & { maxRetries?: number; failureThreshold?: number },
) {
  const isFn = typeof operationOrConfig === 'function';
  const operation = isFn ? (operationOrConfig as () => Promise<T>) : undefined;
  const config = (isFn ? maybeConfig : operationOrConfig) as
    | (Partial<RetryConfig> & { maxRetries?: number; failureThreshold?: number })
    | undefined;
  return _useErrorRecovery<T>(operation, config);
}

function _useErrorRecovery<T>(
  operation?: () => Promise<T>,
  config?: Partial<RetryConfig> & { maxRetries?: number },
) {
  const [state, setState] = useState<RecoveryState>({
    isRetrying: false,
    attempt: 0,
    lastError: null,
    nextRetryIn: 0,
    // Legacy tests expect canRetry === true immediately even before a failure
    canRetry: true,
  });
  const [data, setData] = useState<T | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const retryTimeoutRef = useRef<ReturnType<typeof setTimeout> | null>(null);
  const countdownRef = useRef<ReturnType<typeof setTimeout> | null>(null);
  const operationRef = useRef<(() => Promise<T>) | null>(operation || null);

  const retryConfig: RetryConfig = normalizeRetryConfig(config);

  /**
   * Calculate delay with exponential backoff and jitter
   */
  const calculateDelay = useCallback(
    (attempt: number): number => {
      let delay = Math.min(
        retryConfig.baseDelay * Math.pow(retryConfig.backoffMultiplier, attempt),
        retryConfig.maxDelay,
      );

      if (retryConfig.jitter) {
        delay = delay * (0.5 + Math.random() * 0.5);
      }

      return Math.floor(delay);
    },
    [retryConfig],
  );

  /**
   * Start countdown timer
   */
  const startCountdown = useCallback((delay: number) => {
    setState((prev) => ({ ...prev, nextRetryIn: delay }));

    const startTime = Date.now();
    const updateCountdown = () => {
      const elapsed = Date.now() - startTime;
      const remaining = Math.max(0, delay - elapsed);

      setState((prev) => ({ ...prev, nextRetryIn: remaining }));

      if (remaining > 0) {
        countdownRef.current = setTimeout(updateCountdown, 100);
      }
    };

    updateCountdown();
  }, []);

  /**
   * Execute operation with retry logic
   */
  const execute = useCallback(
    async (opOverride?: () => Promise<T>): Promise<T | null> => {
      setIsLoading(true);

      try {
        if (opOverride) {
          operationRef.current = opOverride;
        }
        const op = operationRef.current;
        if (!op) {
          // No operation provided yet; treat as successful no-op
          setIsLoading(false);
          return null;
        }
        const result = await op();
        setData(result);

        // Reset state on success
        setState({
          isRetrying: false,
          attempt: 0,
          lastError: null,
          nextRetryIn: 0,
          canRetry: false,
        });

        return result;
      } catch (error) {
        const appError = error as AppError;
        const newAttempt = state.attempt + 1;
        const effectiveMax = retryConfig.failureThreshold || retryConfig.maxAttempts;
        const canRetry =
          newAttempt < effectiveMax &&
          (!retryConfig.retryCondition || retryConfig.retryCondition(appError, newAttempt));

        setState((prev) => ({
          ...prev,
          attempt: newAttempt,
          lastError: appError,
          canRetry,
        }));

        // Report error
        ErrorReporting.reportError(appError, {
          operation: 'useErrorRecovery',
          attempt: newAttempt,
          canRetry,
        });

        if (canRetry) {
          const delay = calculateDelay(newAttempt - 1);
          setState((prev) => ({ ...prev, isRetrying: true }));
          startCountdown(delay);

          retryTimeoutRef.current = setTimeout(() => {
            execute();
          }, delay);
        } else {
          setIsLoading(false);
          throw appError;
        }

        return null;
      }
    },
    [state.attempt, retryConfig, calculateDelay, startCountdown],
  );

  /**
   * Manual retry
   */
  const retry = useCallback(() => {
    if (retryTimeoutRef.current) {
      clearTimeout(retryTimeoutRef.current);
    }
    if (countdownRef.current) {
      clearTimeout(countdownRef.current);
    }
    setState((prev) => ({ ...prev, isRetrying: false, nextRetryIn: 0 }));
    execute();
  }, [execute]);

  /**
   * Reset state
   */
  const reset = useCallback(() => {
    if (retryTimeoutRef.current) {
      clearTimeout(retryTimeoutRef.current);
    }
    if (countdownRef.current) {
      clearTimeout(countdownRef.current);
    }

    setState({
      isRetrying: false,
      attempt: 0,
      lastError: null,
      nextRetryIn: 0,
      canRetry: false,
    });

    setData(null);
    setIsLoading(false);
  }, []);

  // Cleanup on unmount
  useEffect(() => {
    return () => {
      if (retryTimeoutRef.current) {
        clearTimeout(retryTimeoutRef.current);
      }
      if (countdownRef.current) {
        clearTimeout(countdownRef.current);
      }
    };
  }, []);

  // Backwards compatibility aliases
  const executeWithRetry = (op?: () => Promise<T>) => execute(op);

  return {
    execute,
    executeWithRetry, // legacy name expected in tests
    retry,
    reset,
    data,
    isLoading,
    retryCount: state.attempt, // legacy alias
    ...state,
  };
}

