import { Ionicons } from '@expo/vector-icons';
import * as ImagePicker from 'expo-image-picker';

import { supabase } from '@/config/supabaseClient';
import { API_CONFIG, ERROR_MESSAGES, IMAGE_CONFIG } from '@/constants/AppConstants';
import { useAuth } from '@/context/AuthContext';

// Interface for UI callbacks that the hook will use
export interface UICallbacks {
  showLoading: (title: string, subtitle?: string) => void;
  hideLoading: () => void;
  showCustomAlert: (
    title: string,
    message: string,
    buttons: Array<{
      text: string;
      onPress?: () => void;
      style?: 'default' | 'cancel' | 'destructive' | 'primary';
      icon?: keyof typeof Ionicons.glyphMap;
    }>,
    type?: 'default' | 'success' | 'warning' | 'error' | 'info',
  ) => void;
  hideCustomAlert: () => void;
}

export const useImageUploader = (callbacks: UICallbacks) => {
  const { user } = useAuth();
  const { showLoading, hideLoading, showCustomAlert, hideCustomAlert } = callbacks;

  // Camera upload function
  const pickImageWithCameraAsync = async () => {
    try {
      // Request camera permissions
      const { status } = await ImagePicker.requestCameraPermissionsAsync();
      if (status !== 'granted') {
        showCustomAlert(
          'Camera Permission Required',
          'We need access to your camera to take photos of your wardrobe items. Please enable camera permissions in your device settings.',
          [{ text: 'OK', onPress: hideCustomAlert }],
          'warning',
        );
        return;
      }

      showLoading('Opening Camera', 'Get ready to capture your item');

      const result = await ImagePicker.launchCameraAsync({
        mediaTypes: ['images'],
        allowsEditing: true,
        aspect: IMAGE_CONFIG.ASPECT_RATIO,
        quality: IMAGE_CONFIG.QUALITY,
      });

      hideLoading();

      if (!result.canceled && result.assets && result.assets.length > 0) {
        const asset = result.assets[0];
        if (asset?.uri) {
          await uploadImage(asset.uri);
        }
      }
    } catch (error) {
      console.error('Camera error:', error);
      hideLoading();
      showCustomAlert(
        'Camera Error',
        'There was an issue accessing your camera. Please try again.',
        [{ text: 'OK', onPress: hideCustomAlert }],
        'error',
      );
    }
  };

  // Gallery upload function
  const pickImageWithGalleryAsync = async () => {
    try {
      // Request media library permissions
      const { status } = await ImagePicker.requestMediaLibraryPermissionsAsync();
      if (status !== 'granted') {
        showCustomAlert(
          'Gallery Permission Required',
          'We need access to your photo gallery to select images of your wardrobe items. Please enable gallery permissions in your device settings.',
          [{ text: 'OK', onPress: hideCustomAlert }],
          'warning',
        );
        return;
      }

      showLoading('Opening Gallery', 'Select your wardrobe item photo');

      const result = await ImagePicker.launchImageLibraryAsync({
        mediaTypes: ['images'],
        allowsEditing: true,
        aspect: IMAGE_CONFIG.ASPECT_RATIO,
        quality: IMAGE_CONFIG.QUALITY,
      });

      hideLoading();

      if (!result.canceled && result.assets && result.assets.length > 0) {
        const asset = result.assets[0];
        if (asset?.uri) {
          await uploadImage(asset.uri);
        }
      }
    } catch (error) {
      console.error('Gallery error:', error);
      hideLoading();
      showCustomAlert(
        'Gallery Error',
        'There was an issue accessing your photo gallery. Please try again.',
        [{ text: 'OK', onPress: hideCustomAlert }],
        'error',
      );
    }
  };

  // Upload image to Supabase and analyze with AI
  const uploadImage = async (imageUri: string) => {
    try {
      if (!user) {
        showCustomAlert(
          'Authentication Required',
          'Please sign in to upload images to your wardrobe.',
          [{ text: 'OK', onPress: hideCustomAlert }],
          'warning',
        );
        return;
      }

      showLoading('Processing Image', 'Uploading and analyzing your item...');

      // Create a unique filename
      const filename = `wardrobe_${user.id}_${Date.now()}.jpg`;

      // Convert image to blob for upload
      const response = await fetch(imageUri);
      const blob = await response.blob();

      // Upload to Supabase Storage
      console.log('Uploading image to Supabase...');
      const { data: uploadData, error: uploadError } = await supabase.storage
        .from(API_CONFIG.SUPABASE_STORAGE_BUCKET)
        .upload(filename, blob, {
          contentType: 'image/jpeg',
          upsert: false,
        });

      if (uploadError) {
        console.error('Upload error:', uploadError);
        throw new Error(`Upload failed: ${uploadError.message}`);
      }

      console.log('Image uploaded successfully:', uploadData.path);

      // Get the public URL for the uploaded image
      const { data: urlData } = supabase.storage
        .from(API_CONFIG.SUPABASE_STORAGE_BUCKET)
        .getPublicUrl(uploadData.path);

      const imageUrl = urlData.publicUrl;
      console.log('Public URL:', imageUrl);

      // Analyze the image with AI
      await analyzeImageWithAI(imageUrl);
    } catch (error) {
      console.error('Upload process error:', error);
      hideLoading();

      showCustomAlert(
        'Upload Failed',
        ERROR_MESSAGES.NETWORK_ERROR,
        [{ text: 'OK', onPress: hideCustomAlert }],
        'error',
      );
    }
  };

  // Helper function to ask specific questions to the VQA model
  const askQuestionToAI = async (imageUrl: string, question: string): Promise<string> => {
    // For cost-efficiency, prefer text reasoning via OpenRouter. For VQA (image+question),
    // use OpenAI vision via ai-proxy, sending the image URL and question.
    try {
      const { aiProxyChatCompletion } = await import('@/config/aiProxy');
      const completion = await aiProxyChatCompletion({
        provider: 'openai',
        model: 'gpt-4o',
        messages: [
          {
            role: 'user',
            content: [
              { type: 'text', text: `Answer concisely: ${question}` },
              { type: 'image_url', image_url: { url: imageUrl } },
            ],
          },
        ],
        max_tokens: 200,
        temperature: 0.1,
      });
      const content = completion?.choices?.[0]?.message?.content?.trim();
      return content || 'unknown';
    } catch (err) {
      console.error('VQA via ai-proxy failed, returning unknown:', err);
      return 'unknown';
    }
  };

  // Analyze image with AI using Visual Question Answering
  const analyzeImageWithAI = async (imageUrl: string) => {
    try {
      console.log('Starting AI analysis for image:', imageUrl);

      // Ask specific questions to get structured data
      const categoryQuestion = 'What is the category of this clothing item?';
      const colorQuestion = 'What are the main colors as comma separated list?';

      // Get answers from the VQA model
      const categoryAnswer = await askQuestionToAI(imageUrl, categoryQuestion);
      const colorAnswer = await askQuestionToAI(imageUrl, colorQuestion);

      console.log('AI Analysis Results:', {
        category: categoryAnswer,
        colors: colorAnswer,
      });

      // Process the colors into an array
      const colorsArray = colorAnswer
        .split(',')
        .map((color) => color.trim().toLowerCase())
        .filter((color) => color.length > 0 && color !== 'unknown');

      // Prepare the data for database insertion
      const wardrobeItem = {
        user_id: user?.id,
        image_url: imageUrl,
        category: categoryAnswer !== 'unknown' ? categoryAnswer : 'clothing',
        colors: colorsArray.length > 0 ? colorsArray : ['unspecified'],
        description: categoryAnswer !== 'unknown' ? categoryAnswer : 'clothing item',
        created_at: new Date().toISOString(),
      };

      console.log('Saving to database:', wardrobeItem);

      // Save to Supabase database
      const { error: dbError } = await supabase.from('wardrobe_items').insert([wardrobeItem]);

      if (dbError) {
        console.error('Database error:', dbError);
        throw new Error(`Failed to save to database: ${dbError.message}`);
      }

      hideLoading();

      showCustomAlert(
        'Item Added Successfully! ✨',
        `Your ${wardrobeItem.category} has been analyzed and added to your wardrobe with colors: ${wardrobeItem.colors.join(', ')}.`,
        [{ text: 'Awesome!', onPress: hideCustomAlert }],
        'success',
      );
    } catch (error) {
      console.error('AI analysis error:', error);
      hideLoading();

      showCustomAlert(
        'AI Analysis Failed',
        "We couldn't analyze your image with AI right now. The image was uploaded but you may need to edit the details manually.",
        [{ text: 'OK', onPress: hideCustomAlert }],
        'warning',
      );
    }
  };

  // Return separate functions for camera and gallery uploads
  return {
    handleCameraUpload: pickImageWithCameraAsync,
    handleGalleryUpload: pickImageWithGalleryAsync,
  };
};

// Loading Hook - React hook for managing loading states
import { useCallback, useState } from 'react';

export interface UseLoadingReturn {
  isLoading: boolean;
  showLoading: (title?: string, subtitle?: string) => void;
  hideLoading: () => void;
}

export function useLoading(): UseLoadingReturn {
  const [isLoading, setIsLoading] = useState(false);
  const [loadingTitle, setLoadingTitle] = useState<string>('Loading...');
  const [loadingSubtitle, setLoadingSubtitle] = useState<string | undefined>();

  const showLoading = useCallback((title?: string, subtitle?: string) => {
    setLoadingTitle(title || 'Loading...');
    setLoadingSubtitle(subtitle);
    setIsLoading(true);
  }, []);

  const hideLoading = useCallback(() => {
    setIsLoading(false);
    setLoadingTitle('Loading...');
    setLoadingSubtitle(undefined);
  }, []);

  return {
    isLoading,
    showLoading,
    hideLoading,
  };
}

export default useLoading;

// useStyleDNA Hook
// Provides a clean interface for managing Style DNA operations

import { useCallback, useEffect, useState } from 'react';

import { useAuth } from '@/hooks/useAuth';
import { styleDNAService } from '@/services/styleDNAService';

interface UploadedPhoto {
  id: string;
  uri: string;
  timestamp: number;
}

interface StyleDNAProfile {
  userId: string;
  visualAnalysis: {
    dominantColors: string[];
    styleCategories: string[];
    formalityLevels: string[];
    patterns: string[];
    textures: string[];
    silhouettes: string[];
    occasions: string[];
    confidence: number;
  };
  stylePersonality: {
    primary: string;
    secondary: string;
    description: string;
  };
  colorPalette: {
    primary: string[];
    accent: string[];
    neutral: string[];
  };
  stylePreferences: {
    formality: 'casual' | 'business' | 'formal' | 'mixed';
    energy: 'calm' | 'bold' | 'creative' | 'classic';
    silhouette: 'fitted' | 'relaxed' | 'structured' | 'flowing';
  };
  recommendations: {
    strengths: string[];
    suggestions: string[];
    avoidances: string[];
  };
  confidence: number;
  createdAt: string;
}

interface UseStyleDNAReturn {
  // State
  styleDNA: StyleDNAProfile | null;
  isLoading: boolean;
  isGenerating: boolean;
  error: string | null;

  // Actions
  generateStyleDNA: (photos: UploadedPhoto[]) => Promise<StyleDNAProfile | null>;
  refreshStyleDNA: () => Promise<void>;
  clearError: () => void;

  // Computed values
  hasStyleDNA: boolean;
  confidenceLevel: 'high' | 'good' | 'moderate' | 'low';
  primaryColors: string[];
  stylePersonalityText: string;
}

export const useStyleDNA = (): UseStyleDNAReturn => {
  const { user } = useAuth();
  const [styleDNA, setStyleDNA] = useState<StyleDNAProfile | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [isGenerating, setIsGenerating] = useState(false);
  const [error, setError] = useState<string | null>(null);

  // Load existing Style DNA on mount
  useEffect(() => {
    if (user?.id) {
      loadExistingStyleDNA();
    }
  }, [user?.id]);

  const loadExistingStyleDNA = async () => {
    if (!user?.id) {
      return;
    }

    setIsLoading(true);
    setError(null);

    try {
      const existingStyleDNA = await styleDNAService.getStyleDNA(user.id);
      setStyleDNA(existingStyleDNA);
    } catch (err) {
      console.error('[useStyleDNA] Error loading existing Style DNA:', err);
      setError('Failed to load your Style DNA profile');
    } finally {
      setIsLoading(false);
    }
  };

  const generateStyleDNA = useCallback(
    async (photos: UploadedPhoto[]): Promise<StyleDNAProfile | null> => {
      if (!user?.id) {
        setError('User not authenticated');
        return null;
      }

      if (photos.length < 3) {
        setError('At least 3 photos are required for Style DNA generation');
        return null;
      }

      setIsGenerating(true);
      setError(null);

      try {
        console.log(`[useStyleDNA] Generating Style DNA for ${photos.length} photos`);
        const generatedStyleDNA = await styleDNAService.generateStyleDNA(user.id, photos);
        setStyleDNA(generatedStyleDNA);
        console.log('[useStyleDNA] Style DNA generated successfully');
        return generatedStyleDNA;
      } catch (err) {
        console.error('[useStyleDNA] Error generating Style DNA:', err);
        setError(err instanceof Error ? err.message : 'Failed to generate Style DNA');
        return null;
      } finally {
        setIsGenerating(false);
      }
    },
    [user?.id],
  );

  const refreshStyleDNA = useCallback(async () => {
    await loadExistingStyleDNA();
  }, [user?.id]);

  const clearError = useCallback(() => {
    setError(null);
  }, []);

  // Computed values
  const hasStyleDNA = styleDNA !== null;

  const confidenceLevel: 'high' | 'good' | 'moderate' | 'low' = (() => {
    if (!styleDNA) {
      return 'low';
    }
    const confidence = styleDNA.confidence;
    if (confidence >= 0.8) {
      return 'high';
    }
    if (confidence >= 0.6) {
      return 'good';
    }
    if (confidence >= 0.4) {
      return 'moderate';
    }
    return 'low';
  })();

  const primaryColors = styleDNA?.colorPalette.primary || [];

  const stylePersonalityText = styleDNA
    ? `${styleDNA.stylePersonality.primary} • ${styleDNA.stylePersonality.secondary}`
    : '';

  return {
    // State
    styleDNA,
    isLoading,
    isGenerating,
    error,

    // Actions
    generateStyleDNA,
    refreshStyleDNA,
    clearError,

    // Computed values
    hasStyleDNA,
    confidenceLevel,
    primaryColors,
    stylePersonalityText,
  };
};

// Helper functions for Style DNA analysis
export const getStyleDNAInsights = (styleDNA: StyleDNAProfile | null) => {
  if (!styleDNA) {
    return null;
  }

  const { visualAnalysis, stylePreferences, recommendations } = styleDNA;

  return {
    // Color insights
    dominantColorCount: visualAnalysis.dominantColors.length,
    hasColorPreference: visualAnalysis.dominantColors.length > 0,

    // Style insights
    styleVariety: visualAnalysis.styleCategories.length,
    isVersatile: visualAnalysis.formalityLevels.length > 1,

    // Pattern insights
    lovesPatterns: visualAnalysis.patterns.length > 2,
    prefersSimplicity: visualAnalysis.patterns.includes('solid'),

    // Recommendations summary
    strengthCount: recommendations.strengths.length,
    suggestionCount: recommendations.suggestions.length,

    // Overall assessment
    isWellDefined: styleDNA.confidence > 0.7,
    needsMoreData: styleDNA.confidence < 0.5,
  };
};

export const getStyleDNACompatibility = (userStyleDNA: StyleDNAProfile, itemTags: string[]) => {
  if (!userStyleDNA || !itemTags.length) {
    return 0;
  }

  let compatibilityScore = 0;
  let totalChecks = 0;

  // Check color compatibility
  const userColors = [
    ...userStyleDNA.colorPalette.primary,
    ...userStyleDNA.colorPalette.accent,
    ...userStyleDNA.colorPalette.neutral,
  ].map((color) => color.toLowerCase());

  itemTags.forEach((tag) => {
    if (userColors.some((color) => tag.toLowerCase().includes(color))) {
      compatibilityScore += 0.3;
    }
    totalChecks += 0.3;
  });

  // Check style category compatibility
  userStyleDNA.visualAnalysis.styleCategories.forEach((category) => {
    if (itemTags.some((tag) => tag.toLowerCase().includes(category.toLowerCase()))) {
      compatibilityScore += 0.4;
    }
    totalChecks += 0.4;
  });

  // Check formality compatibility
  userStyleDNA.visualAnalysis.formalityLevels.forEach((level) => {
    if (itemTags.some((tag) => tag.toLowerCase().includes(level.toLowerCase()))) {
      compatibilityScore += 0.3;
    }
    totalChecks += 0.3;
  });

  return totalChecks > 0 ? Math.min(compatibilityScore / totalChecks, 1) : 0;
};

// P0 Security: Remove hardcoded anon key & move to env, add client-side host allowlist validation
import { createClient } from '@supabase/supabase-js';

const supabaseUrl = process.env.EXPO_PUBLIC_SUPABASE_URL!;
const supabaseAnon = process.env.EXPO_PUBLIC_SUPABASE_ANON_KEY!;
if (!supabaseUrl || !supabaseAnon) {
  throw new Error('Missing Supabase environment variables');
}

const supabase = createClient(supabaseUrl, supabaseAnon);

// Allowlist hosts (client-side early rejection)
const HOST_ALLOWLIST = (
  process.env.EXPO_PUBLIC_AI_IMAGE_HOST_ALLOWLIST ||
  `res.cloudinary.com,${new URL(supabaseUrl).hostname}`
)
  .split(',')
  .map((h) => h.trim().toLowerCase())
  .filter(Boolean);

function assertAllowedImageHost(imageUrl: string) {
  try {
    const u = new URL(imageUrl);
    const host = u.hostname.toLowerCase();
    if (!HOST_ALLOWLIST.some((h) => host === h || host.endsWith(h))) {
      throw new Error(`Disallowed image host: ${host}`);
    }
  } catch (e) {
    throw new Error('Invalid or disallowed image URL');
  }
}

export async function callAiAnalysis(itemId: string, imageUrl: string) {
  assertAllowedImageHost(imageUrl);
  const { data: sess } = await supabase.auth.getSession();
  const token = sess.session?.access_token ?? '';
  const fnUrl = `${supabaseUrl.replace(/\/$/, '')}/functions/v1/ai-analysis`;
  const resp = await fetch(fnUrl, {
    method: 'POST',
    headers: {
      Authorization: `Bearer ${token}`,
      apikey: supabaseAnon,
      'Content-Type': 'application/json',
    },
    body: JSON.stringify({ imageUrl, itemId }),
  });
  const json = await resp.json().catch(() => ({}));
  if (!resp.ok) {
    throw new Error(`ai-analysis ${resp.status}: ${JSON.stringify(json)}`);
  }
  return json; // { cloudinary: { url }, analysis: {...}, ... }
}

// Centralized structured logger with redaction capabilities
// Scrubs sensitive information from logs for security

export type LogLevel = 'debug' | 'info' | 'warn' | 'error';

export interface LogMetadata {
  [key: string]: unknown;
}

export interface LogEntry {
  level: LogLevel;
  message: string;
  metadata?: LogMetadata;
  timestamp: string;
  userId?: string;
  sessionId?: string;
  component?: string;
}

// Sensitive patterns to redact from logs
const SENSITIVE_PATTERNS = [
  /access_token["']?\s*[:=]\s*["']?([^"'\s,}]+)/gi,
  /bearer\s+([a-zA-Z0-9._-]+)/gi,
  /jwt["']?\s*[:=]\s*["']?([^"'\s,}]+)/gi,
  /password["']?\s*[:=]\s*["']?([^"'\s,}]+)/gi,
  /email["']?\s*[:=]\s*["']?([^"'\s,}]+@[^"'\s,}]+)/gi,
  /api_key["']?\s*[:=]\s*["']?([^"'\s,}]+)/gi,
  /secret["']?\s*[:=]\s*["']?([^"'\s,}]+)/gi,
];

/**
 * Redacts sensitive information from log metadata
 */
export function redact(data: LogMetadata): LogMetadata {
  const redacted = { ...data };

  for (const [key, value] of Object.entries(redacted)) {
    if (typeof value === 'string') {
      let cleanValue = value;
      for (const pattern of SENSITIVE_PATTERNS) {
        cleanValue = cleanValue.replace(pattern, (match, captured) =>
          match.replace(captured, '[REDACTED]'),
        );
      }
      redacted[key] = cleanValue;
    } else if (typeof value === 'object' && value !== null) {
      redacted[key] = redact(value as LogMetadata);
    }
  }

  return redacted;
}

/**
 * Creates a structured log entry
 */
function createLogEntry(
  level: LogLevel,
  message: string,
  metadata?: LogMetadata,
  context?: { userId?: string; sessionId?: string; component?: string },
): LogEntry {
  return {
    level,
    message,
    metadata: metadata ? redact(metadata) : undefined,
    timestamp: new Date().toISOString(),
    userId: context?.userId,
    sessionId: context?.sessionId,
    component: context?.component,
  };
}

// Metadata normalizer to accept arbitrary inputs safely
function normalizeMetadata(input: unknown): LogMetadata | undefined {
  if (input == null) {
    return undefined;
  }
  if (typeof input === 'string') {
    return { message: input };
  }
  if (input instanceof Error) {
    const err = input as Error & Record<string, unknown>;
    const extra = Object.fromEntries(Object.entries(err).filter(([_, v]) => v !== undefined));
    return {
      name: err.name,
      message: err.message,
      stack: err.stack,
      ...extra,
    };
  }
  if (typeof input === 'object') {
    return input as LogMetadata;
  }
  return { value: String(input) };
}

/**
 * Sends logs to external monitoring service (Sentry integration)
 */
async function sendToMonitoring(entry: LogEntry): Promise<void> {
  const logData = {
    ...entry,
    environment: process.env.NODE_ENV || 'development',
    app: 'aynamoda',
  };

  // Send to Sentry for production monitoring
  if (!__DEV__) {
    try {
      const { Sentry } = await import('../config/sentry');

      if (entry.level === 'error') {
        Sentry.captureException(new Error(entry.message));
      } else if (entry.level === 'warn') {
        Sentry.captureMessage(entry.message);
      } else if (entry.level === 'info') {
        Sentry.addBreadcrumb({
          message: entry.message,
          category: entry.component || 'app',
          level: 'info',
          data: entry.metadata,
        });
      }
    } catch (error) {
      // Fallback to console if Sentry fails
      console.warn('Sentry logging failed:', error);
    }
  }

  // Always log to console in development or as fallback
  if (entry.level === 'error') {
    console.error(JSON.stringify(logData));
  } else if (entry.level === 'warn') {
    console.warn(JSON.stringify(logData));
  } else if (entry.level === 'info') {
    console.info(JSON.stringify(logData));
  } else if (entry.level === 'debug' && process.env.NODE_ENV === 'development') {
    console.debug(JSON.stringify(logData));
  }
}

/**
 * Structured logger with different log levels and monitoring integration
 */
export const logger = {
  debug: (
    message: string,
    metadata?: unknown,
    context?: { userId?: string; sessionId?: string; component?: string },
  ) => {
    const entry = createLogEntry('debug', message, normalizeMetadata(metadata), context);
    sendToMonitoring(entry);
  },

  info: (
    message: string,
    metadata?: unknown,
    context?: { userId?: string; sessionId?: string; component?: string },
  ) => {
    const entry = createLogEntry('info', message, normalizeMetadata(metadata), context);
    sendToMonitoring(entry);
  },

  warn: (
    message: string,
    metadata?: unknown,
    context?: { userId?: string; sessionId?: string; component?: string },
  ) => {
    const entry = createLogEntry('warn', message, normalizeMetadata(metadata), context);
    sendToMonitoring(entry);
  },

  error: (
    message: string,
    metadata?: unknown,
    context?: { userId?: string; sessionId?: string; component?: string },
  ) => {
    const entry = createLogEntry('error', message, normalizeMetadata(metadata), context);
    sendToMonitoring(entry);
  },

  // Utility method for performance logging
  performance: (
    operation: string,
    duration: number,
    metadata?: unknown,
    context?: { userId?: string; sessionId?: string; component?: string },
  ) => {
    const base = normalizeMetadata(metadata) || {};
    const entry = createLogEntry(
      'info',
      `Performance: ${operation}`,
      {
        ...base,
        duration_ms: duration,
        operation,
      },
      context,
    );
    sendToMonitoring(entry);
  },
};

