import { ThemeProvider } from '@shopify/restyle';
import * as SplashScreen from 'expo-splash-screen';
import React, { useEffect, useState } from 'react';
import { View } from 'react-native';
// Safe font loading without top-level await
const loadFonts = async () => {
  try {
    const [fontsModule, playfairModule] = await Promise.all([
      import('@expo-google-fonts/inter'),
      import('@expo-google-fonts/playfair-display'),
    ]);

    return {
      useFonts: fontsModule.useFonts,
      Inter_400Regular: fontsModule.Inter_400Regular,
      Inter_600SemiBold: fontsModule.Inter_600SemiBold,
      PlayfairDisplay_400Regular: playfairModule.PlayfairDisplay_400Regular,
      PlayfairDisplay_700Bold: playfairModule.PlayfairDisplay_700Bold,
    };
  } catch (error) {
    console.warn('Font loading failed, using fallback:', error);
    return null;
  }
};
import { ErrorBoundary } from '@/components/error/ErrorBoundary';
import { RestyleTheme } from '@/theme/DesignSystem';

// Create a safe theme that ensures compatibility with expo-router's ContextNavigator
const safeTheme = {
  ...RestyleTheme,
  colors: {
    ...RestyleTheme.colors,
    // Hardcoded values for Android compatibility
    primary: '#5C8A5C',
    background: '#FAF9F6',
    text: '#212529',
    card: '#F8F7F4',
    border: '#E5E7EB',
    notification: '#EF4444',
    error: '#EF4444',
    tint: '#5C8A5C',
    surface: '#FEFEFE',
  },
};

import { StatusBar } from 'expo-status-bar';
import { GestureHandlerRootView } from 'react-native-gesture-handler';

import { NetworkErrorBoundary } from '@/components/error/NetworkErrorBoundary';
import { AuthProvider } from '@/context/AuthContext';

interface AppProviderProps {
  children: React.ReactNode;
}

export function AppProvider({ children }: AppProviderProps) {
  const [appIsReady, setAppIsReady] = useState(false);
  const [fontsLoaded, setFontsLoaded] = useState(false);
  const [fontError, setFontError] = useState<string | null>(null);

  useEffect(() => {
    const initializeFonts = async () => {
      try {
        const fonts = await loadFonts();
        if (fonts?.useFonts) {
          // Use the hook directly without storing it in state
          const [loaded, error] = fonts.useFonts({
            Inter_400Regular: fonts.Inter_400Regular,
            Inter_600SemiBold: fonts.Inter_600SemiBold,
            PlayfairDisplay_400Regular: fonts.PlayfairDisplay_400Regular,
            PlayfairDisplay_700Bold: fonts.PlayfairDisplay_700Bold,
          });

          if (error) {
            console.warn('Font loading error:', error);
            setFontError(error.message || 'Font loading failed');
            setFontsLoaded(true); // Fallback to system fonts
          } else {
            setFontsLoaded(loaded);
          }
        } else {
          // Fallback to system fonts when fonts module is null
          console.warn('Font module not available, using system fonts');
          setFontsLoaded(true);
        }
      } catch (error) {
        console.warn('Font loading failed, using system fonts:', error);
        setFontError(error instanceof Error ? error.message : 'Unknown font error');
        setFontsLoaded(true); // Always fallback to system fonts
      }
    };

    initializeFonts();
  }, []);

  useEffect(() => {
    async function prepare() {
      try {
        // Keep the splash screen visible while we fetch resources
        await SplashScreen.preventAutoHideAsync();

        // Reduced delay for better performance
        await new Promise((resolve) => setTimeout(resolve, 500));
      } catch (e) {
        // Warning suppressed
      } finally {
        // Tell the application to render
        setAppIsReady(true);
      }
    }

    prepare();
  }, []);

  useEffect(() => {
    if (appIsReady && fontsLoaded) {
      SplashScreen.hideAsync();
    }
  }, [appIsReady, fontsLoaded]);

  // Always provide the theme, even during loading
  const content = !appIsReady ? (
    <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
      {/* Minimal loading state for better performance */}
    </View>
  ) : (
    <NetworkErrorBoundary>
      <GestureHandlerRootView style={{ flex: 1 }}>
        <AuthProvider>
          <StatusBar style="dark" />
          {children}
        </AuthProvider>
      </GestureHandlerRootView>
    </NetworkErrorBoundary>
  );

  return (
    <ErrorBoundary>
      <ThemeProvider theme={safeTheme}>{content}</ThemeProvider>
    </ErrorBoundary>
  );
}

// Error Provider - Global error state management and integration
import React, {
  createContext,
  ReactNode,
  useCallback,
  useContext,
  useEffect,
  useReducer,
} from 'react';
import { AppState, Platform } from 'react-native';

import { errorInDev } from '@/utils/consoleSuppress';

import { ErrorReporting } from '../services/ErrorReporting';
import {
  AppError,
  ErrorCategory,
  ErrorHandler,
  errorHandler,
  ErrorSeverity,
  RecoveryAction,
} from '../utils/ErrorHandler';

/**
 * Error State Interface
 */
export interface ErrorState {
  globalError: AppError | null;
  errors: Record<string, AppError>; // keyed by error ID or context
  isInitialized: boolean;
  config: ErrorConfig;
  statistics: ErrorStatistics;
}

/**
 * Error Configuration
 */
export interface ErrorConfig {
  enableReporting: boolean;
  enableRecovery: boolean;
  enableToasts: boolean;
  enableBoundaries: boolean;
  logLevel: 'debug' | 'info' | 'warn' | 'error';
  maxErrorsInMemory: number;
  autoRetryEnabled: boolean;
  hapticFeedbackEnabled: boolean;
}

/**
 * Error Statistics
 */
export interface ErrorStatistics {
  totalErrors: number;
  errorsByCategory: Record<ErrorCategory, number>;
  errorsBySeverity: Record<ErrorSeverity, number>;
  recoveredErrors: number;
  sessionStartTime: number;
}

/**
 * Error Actions
 */
type ErrorAction =
  | { type: 'INITIALIZE'; payload: { config: Partial<ErrorConfig> } }
  | { type: 'ADD_ERROR'; payload: { id: string; error: AppError } }
  | { type: 'REMOVE_ERROR'; payload: { id: string } }
  | { type: 'SET_GLOBAL_ERROR'; payload: { error: AppError | null } }
  | { type: 'CLEAR_ALL_ERRORS' }
  | { type: 'UPDATE_CONFIG'; payload: { config: Partial<ErrorConfig> } }
  | { type: 'INCREMENT_RECOVERED' }
  | { type: 'UPDATE_STATISTICS'; payload: { error: AppError } };

/**
 * Default Configuration
 */
const DEFAULT_CONFIG: ErrorConfig = {
  enableReporting: true,
  enableRecovery: true,
  enableToasts: true,
  enableBoundaries: true,
  logLevel: __DEV__ ? 'debug' : 'error',
  maxErrorsInMemory: 50,
  autoRetryEnabled: true,
  hapticFeedbackEnabled: true,
};

/**
 * Initial State
 */
const initialState: ErrorState = {
  globalError: null,
  errors: {},
  isInitialized: false,
  config: DEFAULT_CONFIG,
  statistics: {
    totalErrors: 0,
    errorsByCategory: {
      [ErrorCategory.NETWORK]: 0,
      [ErrorCategory.AUTHENTICATION]: 0,
      [ErrorCategory.PERMISSION]: 0,
      [ErrorCategory.VALIDATION]: 0,
      [ErrorCategory.UI]: 0,
      [ErrorCategory.SYSTEM]: 0,
      [ErrorCategory.AI_SERVICE]: 0,
      [ErrorCategory.IMAGE_PROCESSING]: 0,
      [ErrorCategory.STORAGE]: 0,
      [ErrorCategory.DATABASE]: 0,
      [ErrorCategory.UNKNOWN]: 0,
    },
    errorsBySeverity: {
      [ErrorSeverity.LOW]: 0,
      [ErrorSeverity.MEDIUM]: 0,
      [ErrorSeverity.HIGH]: 0,
      [ErrorSeverity.CRITICAL]: 0,
    },
    recoveredErrors: 0,
    sessionStartTime: Date.now(),
  },
};

/**
 * Error Reducer
 */
function errorReducer(state: ErrorState, action: ErrorAction): ErrorState {
  switch (action.type) {
    case 'INITIALIZE':
      return {
        ...state,
        isInitialized: true,
        config: { ...state.config, ...action.payload.config },
      };

    case 'ADD_ERROR': {
      const { id, error } = action.payload;
      const errors = { ...state.errors, [id]: error };

      // Limit errors in memory
      const errorIds = Object.keys(errors);
      if (errorIds.length > state.config.maxErrorsInMemory) {
        const oldestId = errorIds[0];
        if (oldestId) {
          delete (errors as Record<string, AppError>)[oldestId];
        }
      }

      return {
        ...state,
        errors,
        statistics: {
          ...state.statistics,
          totalErrors: state.statistics.totalErrors + 1,
          errorsByCategory: {
            ...state.statistics.errorsByCategory,
            [error.category]: state.statistics.errorsByCategory[error.category] + 1,
          },
          errorsBySeverity: {
            ...state.statistics.errorsBySeverity,
            [error.severity]: state.statistics.errorsBySeverity[error.severity] + 1,
          },
        },
      };
    }

    case 'REMOVE_ERROR': {
      const { [action.payload.id]: removed, ...errors } = state.errors;
      return { ...state, errors };
    }

    case 'SET_GLOBAL_ERROR':
      return {
        ...state,
        globalError: action.payload.error,
      };

    case 'CLEAR_ALL_ERRORS':
      return {
        ...state,
        globalError: null,
        errors: {},
      };

    case 'UPDATE_CONFIG':
      return {
        ...state,
        config: { ...state.config, ...action.payload.config },
      };

    case 'INCREMENT_RECOVERED':
      return {
        ...state,
        statistics: {
          ...state.statistics,
          recoveredErrors: state.statistics.recoveredErrors + 1,
        },
      };

    case 'UPDATE_STATISTICS':
      return {
        ...state,
        statistics: {
          ...state.statistics,
          totalErrors: state.statistics.totalErrors + 1,
          errorsByCategory: {
            ...state.statistics.errorsByCategory,
            [action.payload.error.category]:
              state.statistics.errorsByCategory[action.payload.error.category] + 1,
          },
          errorsBySeverity: {
            ...state.statistics.errorsBySeverity,
            [action.payload.error.severity]:
              state.statistics.errorsBySeverity[action.payload.error.severity] + 1,
          },
        },
      };

    default:
      return state;
  }
}

/**
 * Error Context
 */
export interface ErrorContextValue {
  state: ErrorState;

  // Error Management
  reportError: (
    error: AppError | Error,
    context?: string | { component?: string; action?: string },
  ) => void;
  clearError: (id?: string) => void; // id opsiyonel: testlerde clearError() çağrılıyor
  clearAllErrors: () => void;
  setGlobalError: (error: AppError | null) => void;

  // Recovery
  recoverFromError: (id: string, action: RecoveryAction) => Promise<void>;
  markAsRecovered: (id: string) => void;

  // Configuration
  updateConfig: (config: Partial<ErrorConfig>) => void;

  // Utilities
  hasErrors: () => boolean;
  getErrorById: (id: string) => AppError | undefined;
  getErrorsByCategory: (category: ErrorCategory) => AppError[];
  getErrorsBySeverity: (severity: ErrorSeverity) => AppError[];

  // Statistics
  getStatistics: () => ErrorStatistics;
  getErrorRate: () => number; // errors per minute

  // Legacy compatibility aliases expected by tests
  currentError: AppError | null;
  errorHistory: AppError[]; // simple chronological list
  statistics: ErrorStatistics; // direct alias for state.statistics
}

const ErrorContext = createContext<ErrorContextValue | undefined>(undefined);

/**
 * Error Provider Props
 */
export interface ErrorProviderProps {
  children: ReactNode;
  config?: Partial<ErrorConfig>;
  onError?: (error: AppError) => void;
  onRecovery?: (error: AppError, action: RecoveryAction) => void;
}

/**
 * Error Provider Component
 */
export const ErrorProvider: React.FC<ErrorProviderProps> = ({
  children,
  config = {},
  onError,
  onRecovery,
}) => {
  const [state, dispatch] = useReducer(errorReducer, initialState);
  const errorHandlerRef = React.useRef<ErrorHandler | null>(null);

  /**
   * Initialize error handling
   */
  useEffect(() => {
    const initializeErrorHandling = async () => {
      // Initialize ErrorHandler
      errorHandlerRef.current = errorHandler;
      // ErrorHandler is already initialized as singleton

      // Initialize ErrorReporting
      await ErrorReporting.initialize({
        enabled: state.config.enableReporting,
      });

      dispatch({ type: 'INITIALIZE', payload: { config } });
    };

    initializeErrorHandling();
  }, []);

  /**
   * Handle app state changes
   */
  useEffect(() => {
    const handleAppStateChange = (nextAppState: string) => {
      ErrorReporting.addBreadcrumb({
        category: 'system',
        message: `App state changed to ${nextAppState}`,
        level: 'info',
      });
    };

    const subscription = AppState.addEventListener('change', handleAppStateChange);
    return () => subscription?.remove();
  }, []);

  /**
   * Report error
   */
  const reportError = useCallback(
    (err: AppError | Error, context?: string | { component?: string; action?: string }) => {
      // Normalize incoming generic Error to AppError minimal shape
      const base: AppError = (err as AppError).id
        ? (err as AppError)
        : {
            id: `gen_${Date.now()}_${Math.random().toString(36).slice(2, 7)}`,
            message: err.message || 'Unknown error',
            userMessage: err.message || 'Something went wrong',
            category: ErrorCategory.UNKNOWN,
            severity: ErrorSeverity.MEDIUM,
            context: {
              timestamp: Date.now(),
              platform: Platform.OS,
            },
            isRecoverable: true,
            retryable: false,
            reportable: true,
          };

      // Ensure code always a string (legacy tests may assert .code contains substring)
      if (!base.code) {
        base.code = String(base.category) || 'unknown';
      }

      let derivedId: string;
      if (typeof context === 'string') {
        derivedId = context;
      } else if (context && typeof context === 'object') {
        derivedId = `${context.component || 'cmp'}_${Date.now()}`;
        // merge context into additionalData
        base.context = {
          ...base.context,
          action: context.action,
          screen: context.component,
        };
      } else {
        derivedId = `error_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`;
      }

      dispatch({ type: 'ADD_ERROR', payload: { id: derivedId, error: base } });

      if (state.config.enableReporting) {
        ErrorReporting.reportError(base, {
          context: typeof context === 'string' ? context : null,
          errorId: derivedId,
        });
      }
      if (base.severity === ErrorSeverity.CRITICAL) {
        dispatch({ type: 'SET_GLOBAL_ERROR', payload: { error: base } });
      }
      onError?.(base);
      if (
        state.config.logLevel === 'debug' ||
        (state.config.logLevel === 'info' && base.severity !== ErrorSeverity.LOW) ||
        (state.config.logLevel === 'warn' && base.severity >= ErrorSeverity.MEDIUM) ||
        (state.config.logLevel === 'error' && base.severity >= ErrorSeverity.HIGH)
      ) {
        errorInDev('Error reported:', base);
      }
    },
    [state.config, onError],
  );

  /**
   * Clear specific error
   */
  const clearError = useCallback(
    (id?: string) => {
      if (!id) {
        // if no id supplied clear most recent (legacy expectation in tests calling clearError())
        const keys = Object.keys(state.errors);
        const last = keys[keys.length - 1];
        if (last) {
          dispatch({ type: 'REMOVE_ERROR', payload: { id: last } });
        }
        return;
      }
      dispatch({ type: 'REMOVE_ERROR', payload: { id } });
    },
    [state.errors],
  );

  /**
   * Clear all errors
   */
  const clearAllErrors = useCallback(() => {
    dispatch({ type: 'CLEAR_ALL_ERRORS' });
  }, []);

  /**
   * Set global error
   */
  const setGlobalError = useCallback((error: AppError | null) => {
    dispatch({ type: 'SET_GLOBAL_ERROR', payload: { error } });
  }, []);

  /**
   * Recover from error
   */
  const recoverFromError = useCallback(
    async (id: string, action: RecoveryAction) => {
      const error = state.errors[id];
      if (!error) {
        return;
      }

      try {
        if (errorHandlerRef.current) {
          // Execute recovery action manually since executeRecoveryAction doesn't exist
          // The recovery logic should be handled by the calling code
        }

        // Mark as recovered
        dispatch({ type: 'INCREMENT_RECOVERED' });
        clearError(id);

        // Call external handler
        onRecovery?.(error, action);

        ErrorReporting.addBreadcrumb({
          category: 'system',
          message: `Recovered from error: ${error.id}`,
          level: 'info',
          data: { errorId: id, action: action.strategy },
        });
      } catch (recoveryError) {
        const appError = recoveryError as AppError;
        reportError(appError, `recovery_failed_${id}`);
      }
    },
    [state.errors, clearError, onRecovery, reportError],
  );

  /**
   * Mark error as recovered
   */
  const markAsRecovered = useCallback(
    (id: string) => {
      dispatch({ type: 'INCREMENT_RECOVERED' });
      clearError(id);
    },
    [clearError],
  );

  /**
   * Update configuration
   */
  const updateConfig = useCallback((newConfig: Partial<ErrorConfig>) => {
    dispatch({ type: 'UPDATE_CONFIG', payload: { config: newConfig } });

    // Update ErrorHandler configuration
    if (errorHandlerRef.current) {
      errorHandlerRef.current.updateConfig({
        enableReporting: newConfig.enableReporting,
      });
    }

    // Update ErrorReporting configuration
    if (newConfig.enableReporting !== undefined) {
      ErrorReporting.updateConfig({ enabled: newConfig.enableReporting });
    }
  }, []);

  /**
   * Check if there are any errors
   */
  const hasErrors = useCallback(() => {
    return Object.keys(state.errors).length > 0 || state.globalError !== null;
  }, [state.errors, state.globalError]);

  /**
   * Get error by ID
   */
  const getErrorById = useCallback(
    (id: string) => {
      return state.errors[id];
    },
    [state.errors],
  );

  /**
   * Get errors by category
   */
  const getErrorsByCategory = useCallback(
    (category: ErrorCategory) => {
      return Object.values(state.errors).filter((error) => error.category === category);
    },
    [state.errors],
  );

  /**
   * Get errors by severity
   */
  const getErrorsBySeverity = useCallback(
    (severity: ErrorSeverity) => {
      return Object.values(state.errors).filter((error) => error.severity === severity);
    },
    [state.errors],
  );

  /**
   * Get statistics
   */
  const getStatistics = useCallback(() => {
    return state.statistics;
  }, [state.statistics]);

  /**
   * Get error rate (errors per minute)
   */
  const getErrorRate = useCallback(() => {
    const sessionDuration = (Date.now() - state.statistics.sessionStartTime) / 1000 / 60; // minutes
    return sessionDuration > 0 ? state.statistics.totalErrors / sessionDuration : 0;
  }, [state.statistics]);

  const errorHistory = React.useMemo(() => Object.values(state.errors), [state.errors]);
  const currentError = state.globalError || errorHistory[errorHistory.length - 1] || null;

  const contextValue: ErrorContextValue = {
    state,
    reportError,
    clearError,
    clearAllErrors,
    setGlobalError,
    recoverFromError,
    markAsRecovered,
    updateConfig,
    hasErrors,
    getErrorById,
    getErrorsByCategory,
    getErrorsBySeverity,
    getStatistics,
    getErrorRate,
    // legacy aliases
    currentError,
    errorHistory,
    statistics: state.statistics,
  };

  return <ErrorContext.Provider value={contextValue}>{children}</ErrorContext.Provider>;
};

/**
 * Use Error Context Hook
 */
export function useErrorContext(): ErrorContextValue {
  const context = useContext(ErrorContext);
  if (context === undefined) {
    throw new Error('useErrorContext must be used within an ErrorProvider');
  }
  return context;
}

/**
 * Use Error Reporting Hook
 */
export function useErrorReporting() {
  const { reportError, clearError } = useErrorContext();

  const reportAndClear = useCallback(
    (error: AppError, context?: string, autoClearDelay?: number) => {
      const errorId = context || `temp_${Date.now()}`;
      reportError(error, errorId);

      if (autoClearDelay) {
        setTimeout(() => clearError(errorId), autoClearDelay);
      }

      return errorId;
    },
    [reportError, clearError],
  );

  return {
    reportError,
    reportAndClear,
    clearError,
  };
}

/**
 * Use Error Statistics Hook
 */
export function useErrorStatistics() {
  const { getStatistics, getErrorRate } = useErrorContext();

  return {
    statistics: getStatistics(),
    errorRate: getErrorRate(),
  };
}

/**
 * Use Error Recovery Hook
 */
export function useErrorRecoveryActions() {
  const { recoverFromError, markAsRecovered } = useErrorContext();

  return {
    recoverFromError,
    markAsRecovered,
  };
}

export default ErrorProvider;

// Haptic Provider - Global haptic feedback management
import React, {
  createContext,
  ReactNode,
  useCallback,
  useContext,
  useEffect,
  useState,
} from 'react';
import { AccessibilityInfo, AppState, AppStateStatus } from 'react-native';

import { errorInDev, logInDev } from '@/utils/consoleSuppress';

import hapticService, { HapticIntensity, HapticType } from '../services/HapticService';
import { AnimationContext } from './AnimationProvider';

/**
 * Haptic settings interface
 */
interface HapticSettings {
  enabled: boolean;
  globalIntensity: number;
  accessibilityMode: boolean;
  respectSystemSettings: boolean;
  respectReducedMotion: boolean;
  enableSoundEffects: boolean;
  enableVibrationFallback: boolean;
  customPatterns: Record<string, number[]>;
}

/**
 * Haptic context interface
 */
interface HapticContextType {
  settings: HapticSettings;
  updateSettings: (newSettings: Partial<HapticSettings>) => void;
  isAvailable: boolean;
  isEnabled: boolean;

  // Quick settings
  toggleHaptics: () => void;
  setIntensity: (intensity: number) => void;
  enableAccessibilityMode: (enabled: boolean) => void;

  // Haptic triggers
  trigger: (type: HapticType, customIntensity?: number) => void;
  triggerSequence: (types: HapticType[], delay?: number) => void;
  triggerCustomPattern: (pattern: number[], intensity?: number) => void;

  // Convenience methods
  success: () => void;
  error: () => void;
  warning: () => void;
  selection: () => void;
  confirmation: () => void;
  gentleTouch: () => void;
  luxuryTouch: () => void;
}

/**
 * Default haptic settings
 */
const DEFAULT_HAPTIC_SETTINGS: HapticSettings = {
  enabled: true,
  globalIntensity: 0.8,
  accessibilityMode: false,
  respectSystemSettings: true,
  respectReducedMotion: true,
  enableSoundEffects: false,
  enableVibrationFallback: true,
  customPatterns: {
    welcome: [100, 50, 100, 50, 200],
    goodbye: [200, 100, 100],
    notification: [50, 30, 50, 30, 50],
    achievement: [100, 50, 150, 50, 100, 50, 200],
  },
};

/**
 * Haptic Context
 */
export const HapticContext = createContext<HapticContextType | null>(null);

/**
 * Haptic Provider Props
 */
interface HapticProviderProps {
  children: ReactNode;
  initialSettings?: Partial<HapticSettings>;
}

/**
 * Haptic Provider Component
 */
export const HapticProvider: React.FC<HapticProviderProps> = ({
  children,
  initialSettings = {},
}) => {
  const [settings, setSettings] = useState<HapticSettings>({
    ...DEFAULT_HAPTIC_SETTINGS,
    ...initialSettings,
  });

  const [isAvailable, setIsAvailable] = useState<boolean>(false);
  const [reduceMotionEnabled, setReduceMotionEnabled] = useState<boolean>(false);
  const [appState, setAppState] = useState<AppStateStatus>(AppState.currentState);

  const animationContext = useContext(AnimationContext);

  /**
   * Initialize haptic service and accessibility settings
   */
  useEffect(() => {
    const initializeHaptics = async () => {
      try {
        // Check if haptics are available
        const available = hapticService.isHapticAvailable();
        setIsAvailable(available);

        // Check for reduced motion preference
        const reduceMotion = await AccessibilityInfo.isReduceMotionEnabled();
        setReduceMotionEnabled(reduceMotion);

        // Update haptic service configuration
        hapticService.updateConfig({
          enabled: settings.enabled,
          globalIntensity: settings.globalIntensity,
          accessibilityMode: settings.accessibilityMode,
          respectSystemSettings: settings.respectSystemSettings,
        });

        logInDev('Haptic Provider initialized:', {
          available,
          reduceMotion,
          settings,
        });
      } catch (error) {
        errorInDev(
          'Failed to initialize haptic provider:',
          error instanceof Error ? error : String(error),
        );
      }
    };

    initializeHaptics();
  }, []);

  /**
   * Listen for accessibility changes
   */
  useEffect(() => {
    const subscription = AccessibilityInfo.addEventListener(
      'reduceMotionChanged',
      setReduceMotionEnabled,
    );

    return () => subscription?.remove();
  }, []);

  /**
   * Listen for app state changes
   */
  useEffect(() => {
    const subscription = AppState.addEventListener('change', (nextAppState) => {
      setAppState(nextAppState);

      // Disable haptics when app is in background
      if (nextAppState === 'background' || nextAppState === 'inactive') {
        hapticService.setEnabled(false);
      } else if (nextAppState === 'active' && settings.enabled) {
        hapticService.setEnabled(true);
      }
    });

    return () => subscription?.remove();
  }, [settings.enabled]);

  /**
   * Sync with animation context for reduced motion
   */
  useEffect(() => {
    if (animationContext?.settings?.accessibility?.reduceMotion !== undefined) {
      setReduceMotionEnabled(!!animationContext.settings?.accessibility?.reduceMotion);
    }
  }, [animationContext?.settings?.accessibility?.reduceMotion]);

  /**
   * Update haptic service when settings change
   */
  useEffect(() => {
    hapticService.updateConfig({
      enabled: settings.enabled && appState === 'active',
      globalIntensity: settings.globalIntensity,
      accessibilityMode: settings.accessibilityMode,
      respectSystemSettings: settings.respectSystemSettings,
    });
  }, [settings, appState]);

  /**
   * Check if haptics should be enabled
   */
  const isEnabled =
    settings.enabled &&
    isAvailable &&
    appState === 'active' &&
    (!settings.respectReducedMotion || !reduceMotionEnabled);

  /**
   * Update settings
   */
  const updateSettings = useCallback((newSettings: Partial<HapticSettings>) => {
    setSettings((prev) => ({ ...prev, ...newSettings }));
  }, []);

  /**
   * Toggle haptics on/off
   */
  const toggleHaptics = useCallback(() => {
    updateSettings({ enabled: !settings.enabled });
  }, [settings.enabled, updateSettings]);

  /**
   * Set global intensity
   */
  const setIntensity = useCallback(
    (intensity: number) => {
      const clampedIntensity = Math.max(0, Math.min(1, intensity));
      updateSettings({ globalIntensity: clampedIntensity });
    },
    [updateSettings],
  );

  /**
   * Enable/disable accessibility mode
   */
  const enableAccessibilityMode = useCallback(
    (enabled: boolean) => {
      updateSettings({
        accessibilityMode: enabled,
        globalIntensity: enabled
          ? Math.max(0.7, settings.globalIntensity)
          : settings.globalIntensity,
      });
    },
    [settings.globalIntensity, updateSettings],
  );

  /**
   * Trigger haptic feedback
   */
  const trigger = useCallback(
    (type: HapticType, customIntensity?: number): void => {
      if (!isEnabled) {
        return;
      }

      try {
        hapticService.trigger(type, customIntensity);
      } catch (error) {
        errorInDev('Failed to trigger haptic:', error instanceof Error ? error : String(error));
      }
    },
    [isEnabled],
  );

  /**
   * Trigger haptic sequence
   */
  const triggerSequence = useCallback(
    (types: HapticType[], delay: number = 100): void => {
      if (!isEnabled) {
        return;
      }

      // keep async sequencing internal without exposing promise
      void (async () => {
        try {
          await hapticService.triggerSequence(types, delay);
        } catch (error) {
          errorInDev(
            'Failed to trigger haptic sequence:',
            error instanceof Error ? error : String(error),
          );
        }
      })();
    },
    [isEnabled],
  );

  /**
   * Trigger custom pattern
   */
  const triggerCustomPattern = useCallback(
    (pattern: number[], intensity: number = HapticIntensity.MODERATE): void => {
      if (!isEnabled) {
        return;
      }

      try {
        const customPattern = hapticService.createCustomPattern(pattern, intensity);
        hapticService.triggerCustom(customPattern);
      } catch (error) {
        errorInDev(
          'Failed to trigger custom haptic pattern:',
          error instanceof Error ? error : String(error),
        );
      }
    },
    [isEnabled],
  );

  /**
   * Convenience methods for common haptic patterns
   */
  const success = useCallback(() => trigger(HapticType.SUCCESS), [trigger]);
  const error = useCallback(() => trigger(HapticType.ERROR), [trigger]);
  const warning = useCallback(() => trigger(HapticType.WARNING), [trigger]);
  const selection = useCallback(() => trigger(HapticType.SELECTION), [trigger]);
  const confirmation = useCallback(() => trigger(HapticType.CONFIRMATION), [trigger]);
  const gentleTouch = useCallback(() => trigger(HapticType.GENTLE_TAP), [trigger]);
  const luxuryTouch = useCallback(() => trigger(HapticType.LUXURY_TOUCH), [trigger]);

  /**
   * Context value
   */
  const contextValue: HapticContextType = {
    settings,
    updateSettings,
    isAvailable,
    isEnabled,
    toggleHaptics,
    setIntensity,
    enableAccessibilityMode,
    trigger,
    triggerSequence,
    triggerCustomPattern,
    success,
    error,
    warning,
    selection,
    confirmation,
    gentleTouch,
    luxuryTouch,
  };

  return <HapticContext.Provider value={contextValue}>{children}</HapticContext.Provider>;
};

/**
 * Hook to use haptic context
 */
export const useHapticContext = (): HapticContextType => {
  const context = useContext(HapticContext);

  if (!context) {
    throw new Error('useHapticContext must be used within a HapticProvider');
  }

  return context;
};

/**
 * Hook for haptic settings management
 */
export const useHapticSettings = () => {
  const { settings, updateSettings, toggleHaptics, setIntensity, enableAccessibilityMode } =
    useHapticContext();

  const updateSetting = useCallback(
    <K extends keyof HapticSettings>(key: K, value: HapticSettings[K]) => {
      updateSettings({ [key]: value });
    },
    [updateSettings],
  );

  const resetToDefaults = useCallback(() => {
    updateSettings(DEFAULT_HAPTIC_SETTINGS);
  }, [updateSettings]);

  const createPreset = useCallback((name: string, preset: Partial<HapticSettings>) => {
    // In a real app, you might save this to AsyncStorage
    logInDev(`Haptic preset '${name}' created:`, preset);
  }, []);

  return {
    settings,
    updateSetting,
    updateSettings,
    toggleHaptics,
    setIntensity,
    enableAccessibilityMode,
    resetToDefaults,
    createPreset,
  };
};

/**
 * Hook for haptic status
 */
export const useHapticStatus = () => {
  const { isAvailable, isEnabled, settings } = useHapticContext();

  return {
    isAvailable,
    isEnabled,
    canVibrate: isAvailable && settings.enableVibrationFallback,
    hasCustomPatterns: Object.keys(settings.customPatterns).length > 0,
    accessibilityMode: settings.accessibilityMode,
    globalIntensity: settings.globalIntensity,
  };
};

export default HapticProvider;

// Wardrobe Provider - Context for wardrobe management
import React, { createContext, ReactNode, useContext, useEffect, useReducer } from 'react';

import { WardrobeItem } from '@/types/aynaMirror';

import { safeParse } from '../utils/safeJSON';
import { secureStorage } from '../utils/secureStorage';

interface WardrobeState {
  items: WardrobeItem[];
  favorites: string[];
  loading: boolean;
  error: string | null;
  searchQuery: string;
  selectedCategory: string | null;
  sortBy: 'name' | 'date' | 'category';
}

type WardrobeAction =
  | { type: 'SET_LOADING'; payload: boolean }
  | { type: 'SET_ERROR'; payload: string | null }
  | { type: 'SET_ITEMS'; payload: WardrobeItem[] }
  | { type: 'ADD_ITEM'; payload: WardrobeItem }
  | { type: 'UPDATE_ITEM'; payload: WardrobeItem }
  | { type: 'DELETE_ITEM'; payload: string }
  | { type: 'TOGGLE_FAVORITE'; payload: string }
  | { type: 'SET_SEARCH_QUERY'; payload: string }
  | { type: 'SET_SELECTED_CATEGORY'; payload: string | null }
  | { type: 'SET_SORT_BY'; payload: 'name' | 'date' | 'category' }
  | { type: 'SET_FAVORITES'; payload: string[] };

interface WardrobeContextType {
  state: WardrobeState;
  dispatch: React.Dispatch<WardrobeAction>;
  addItem: (item: WardrobeItem) => void;
  updateItem: (item: WardrobeItem) => void;
  deleteItem: (id: string) => void;
  toggleFavorite: (id: string) => void;
  setSearchQuery: (query: string) => void;
  setSelectedCategory: (category: string | null) => void;
  setSortBy: (sortBy: 'name' | 'date' | 'category') => void;
  getFilteredItems: () => WardrobeItem[];
  getFavoriteItems: () => WardrobeItem[];
}

const WardrobeContext = createContext<WardrobeContextType | undefined>(undefined);

const initialState: WardrobeState = {
  items: [],
  favorites: [],
  loading: false,
  error: null,
  searchQuery: '',
  selectedCategory: null,
  sortBy: 'date',
};

function wardrobeReducer(state: WardrobeState, action: WardrobeAction): WardrobeState {
  switch (action.type) {
    case 'SET_LOADING':
      return { ...state, loading: action.payload };
    case 'SET_ERROR':
      return { ...state, error: action.payload, loading: false };
    case 'SET_ITEMS':
      return { ...state, items: action.payload, loading: false, error: null };
    case 'ADD_ITEM':
      return { ...state, items: [...state.items, action.payload] };
    case 'UPDATE_ITEM':
      return {
        ...state,
        items: state.items.map((item) => (item.id === action.payload.id ? action.payload : item)),
      };
    case 'DELETE_ITEM':
      return {
        ...state,
        items: state.items.filter((item) => item.id !== action.payload),
        favorites: state.favorites.filter((id) => id !== action.payload),
      };
    case 'TOGGLE_FAVORITE':
      const isFavorite = state.favorites.includes(action.payload);
      return {
        ...state,
        favorites: isFavorite
          ? state.favorites.filter((id) => id !== action.payload)
          : [...state.favorites, action.payload],
      };
    case 'SET_SEARCH_QUERY':
      return { ...state, searchQuery: action.payload };
    case 'SET_SELECTED_CATEGORY':
      return { ...state, selectedCategory: action.payload };
    case 'SET_SORT_BY':
      return { ...state, sortBy: action.payload };
    case 'SET_FAVORITES':
      return { ...state, favorites: action.payload };
    default:
      return state;
  }
}

interface WardrobeProviderProps {
  children: ReactNode;
  initialItems?: WardrobeItem[];
}

export function WardrobeProvider({ children, initialItems = [] }: WardrobeProviderProps) {
  const [state, dispatch] = useReducer(wardrobeReducer, {
    ...initialState,
    items: initialItems,
  });

  // Load favorites from storage on mount
  useEffect(() => {
    const loadFavorites = async () => {
      try {
        await secureStorage.initialize();
        const stored = await secureStorage.getItem('wardrobe_favorites');
        if (stored) {
          const parsed = safeParse<unknown>(stored, []);
          const favs = Array.isArray(parsed)
            ? parsed.filter((v): v is string => typeof v === 'string')
            : [];
          dispatch({ type: 'SET_FAVORITES', payload: favs });
        }
      } catch (error) {
        console.warn('Failed to load favorites:', error);
      }
    };
    loadFavorites();
  }, []);

  // Save favorites to storage when they change
  useEffect(() => {
    const saveFavorites = async () => {
      try {
        await secureStorage.initialize();
        await secureStorage.setItem('wardrobe_favorites', JSON.stringify(state.favorites));
      } catch (error) {
        console.warn('Failed to save favorites:', error);
      }
    };
    saveFavorites();
  }, [state.favorites]);

  const addItem = (item: WardrobeItem) => {
    dispatch({ type: 'ADD_ITEM', payload: item });
  };

  const updateItem = (item: WardrobeItem) => {
    dispatch({ type: 'UPDATE_ITEM', payload: item });
  };

  const deleteItem = (id: string) => {
    dispatch({ type: 'DELETE_ITEM', payload: id });
  };

  const toggleFavorite = (id: string) => {
    dispatch({ type: 'TOGGLE_FAVORITE', payload: id });
  };

  const setSearchQuery = (query: string) => {
    dispatch({ type: 'SET_SEARCH_QUERY', payload: query });
  };

  const setSelectedCategory = (category: string | null) => {
    dispatch({ type: 'SET_SELECTED_CATEGORY', payload: category });
  };

  const setSortBy = (sortBy: 'name' | 'date' | 'category') => {
    dispatch({ type: 'SET_SORT_BY', payload: sortBy });
  };

  const getFilteredItems = (): WardrobeItem[] => {
    let filtered = [...state.items];

    // Apply search filter
    if (state.searchQuery) {
      const query = state.searchQuery.toLowerCase();
      filtered = filtered.filter(
        (item) =>
          item.name?.toLowerCase().includes(query) ||
          item.tags?.some((tag) => tag.toLowerCase().includes(query)),
      );
    }

    // Apply category filter
    if (state.selectedCategory) {
      filtered = filtered.filter((item) => item.category === state.selectedCategory);
    }

    // Apply sorting
    filtered.sort((a, b) => {
      switch (state.sortBy) {
        case 'name':
          return (a.name || '').localeCompare(b.name || '');
        case 'category':
          return (a.category || '').localeCompare(b.category || '');
        case 'date':
        default:
          return (
            new Date(b.createdAt || (b as any).created_at || 0).getTime() -
            new Date(a.createdAt || (a as any).created_at || 0).getTime()
          );
      }
    });

    return filtered;
  };

  const getFavoriteItems = (): WardrobeItem[] => {
    return state.items.filter((item) => state.favorites.includes(item.id));
  };

  const value: WardrobeContextType = {
    state,
    dispatch,
    addItem,
    updateItem,
    deleteItem,
    toggleFavorite,
    setSearchQuery,
    setSelectedCategory,
    setSortBy,
    getFilteredItems,
    getFavoriteItems,
  };

  return <WardrobeContext.Provider value={value}>{children}</WardrobeContext.Provider>;
}

export function useWardrobe(): WardrobeContextType {
  const context = useContext(WardrobeContext);
  if (context === undefined) {
    throw new Error('useWardrobe must be used within a WardrobeProvider');
  }
  return context;
}

export default WardrobeProvider;

